(window.webpackJsonp=window.webpackJsonp||[]).push([[85],{439:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1053-chem-giet-ngoc-diep-vuong"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1053-chem-giet-ngoc-diep-vuong"}},[n._v("#")]),n._v(" Chương 1053: Chém giết Ngọc Diệp Vương")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1053: Chém giết Ngọc Diệp Vương"),t("br"),t("br"),n._v("“A!”"),t("br"),t("br"),n._v("Ngọc Diệp Vương hừ lạnh, trong lòng nhanh chóng suy tư."),t("br"),t("br"),n._v("Hiện tại hắn thân thể bị hủy, thật muốn giết vị này Tử Diệp Vương, vị kia trời nhận danh sư hẳn phải chết không nghi ngờ, cái tên này chết một lần... Tất cả đều không có hy vọng."),t("br"),t("br"),n._v("Chỉ cần lưu hắn người sống, trở về trong tộc, cùng đối phương nói đồng dạng, đủ loại ban thưởng vô cùng vô tận, nguyên thần càng thêm cường đại, tu vi cao hơn, thậm chí đoạt xá lợi hại hơn tu luyện giả, đều không phải là việc khó gì."),t("br"),t("br"),n._v("“Không bằng đáp ứng trước, chỉ cần có thể sống sót, hồi tộc bên trong cần một đoạn thời gian rất dài, hoàn toàn có thể thừa dịp hắn không phòng bị, đem hắn giết...”"),t("br"),t("br"),n._v("Nguyên một đám mưu kế trong đầu chảy xuôi."),t("br"),t("br"),n._v("Từ nơi này về trong tộc, cần một đoạn thời gian rất dài, chỉ cần cái tên này xuất hiện chỗ sơ suất, hoàn toàn có thể làm tràng đánh chết!"),t("br"),t("br"),n._v("Đến lúc đó, cũng không tổn thương trời nhận danh sư, lại có thể báo thù rửa hận."),t("br"),t("br"),n._v("Mà giờ khắc này, đối phương rõ ràng phòng bị, tiếp tục giằng co nữa, thua thiệt nhất định là bản thân."),t("br"),t("br"),n._v("Dù sao trạng thái của hắn bây giờ là nguyên thần, thực lực cường đại không giả, thế nhưng không kiên trì được bao lâu."),t("br"),t("br"),n._v("“Ta xem trước một chút...”"),t("br"),t("br"),n._v("Suy tư xong xuôi, Ngọc Diệp Vương tinh thần khẽ động, đứng trên mặt đất khôi lỗi liền hướng hắn bay đi, thần thức thời gian nháy mắt nhìn kỹ một lần."),t("br"),t("br"),n._v("Cái này khôi lỗi, vô cùng khéo léo, đem đơn giản nhất tài liệu phát huy ra uy lực lớn nhất, quả thực là cái đồ tốt, mặc dù thực lực nhỏ yếu đi một chút, nhưng phối hợp hắn nguyên thần lời nói, đồng dạng có thể phát huy ra cực mạnh sức chiến đấu."),t("br"),t("br"),n._v("Cứ việc so với trước đó còn xa xa không bằng, nhưng cũng đầy đủ đối phó một cái Tử Diệp Vương."),t("br"),t("br"),n._v("Suy tính ra kết quả, lại đem cái này khôi lỗi trong trong ngoài ngoài kiểm tra một lần, cũng không phát hiện bất luận cái gì bẫy rập hoặc là cái khác, lúc này mới thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Vù vù!"),t("br"),t("br"),n._v("Nguyên thần khẽ động, trực tiếp chui vào."),t("br"),t("br"),n._v("Một lát sau, khôi lỗi mở mắt ra, giật giật tứ chi, lần nữa nhìn về phía cách đó không xa Tử Diệp Vương: “Được rồi, ngươi bây giờ có thể buông ra vị này trời nhận danh sư đi!”"),t("br"),t("br"),n._v("“Đương nhiên!”"),t("br"),t("br"),n._v("Nhẹ nhàng cười một tiếng, Trương Huyền tiện tay đem Phùng Huân ném xuống đất."),t("br"),t("br"),n._v("Vừa rồi cục gạch uy lực quá lớn, cái tên này đến bây giờ đều không có tỉnh lại."),t("br"),t("br"),n._v("“Thả?”"),t("br"),t("br"),n._v("Ngọc Diệp Vương ngẩn ngơ."),t("br"),t("br"),n._v("Dựa theo phỏng đoán của hắn, đối phương khẳng định sẽ tiếp tục áp chế, tốt cam đoan an toàn của mình, giờ phút này thế mà nghe lời thả... Thật chẳng lẽ không sợ bản thân, trở tay đem hắn giết?"),t("br"),t("br"),n._v("“Đây là bản thân tự tìm cái chết...”"),t("br"),t("br"),n._v("Ánh mắt sáng lên, bàn tay bỗng nhiên một trảo, đem vị kia nằm trên mặt đất hôn mê trời nhận danh sư, bảo vệ, cùng thời khắc đó, thân thể nhoáng một cái, liền đối trước mắt Tử Diệp Vương vọt tới."),t("br"),t("br"),n._v("Đã cái tên này tự tìm đường chết, hắn đường đường Ngọc Diệp Vương làm sao có thể bỏ qua cơ hội!"),t("br"),t("br"),n._v("Thượng ngũ vương xếp hạng thứ ba siêu cấp cường giả, bị đối phương trêu đùa, sớm đã khí sắp bùng nổ."),t("br"),t("br"),n._v("“Ai, cho ngươi sống sót cơ hội, nhưng không biết trân quý...”"),t("br"),t("br"),n._v("Thấy hắn vọt tới, đối diện Tử Diệp Vương cũng không tránh né hoặc là phản kích, mà là lắc đầu, than thở một tiếng, bàn tay giơ lên, xoa cái búng tay: “Nằm xuống đi!”"),t("br"),t("br"),n._v("Răng rắc!"),t("br"),t("br"),n._v("Đang tại vọt tới trước Ngọc Diệp Vương, ngay sau đó cảm thấy trong cơ thể một hồi đau đớn kịch liệt, thân thể lập tức cứng ngắc lên, “Phù phù!” Thoáng cái, liền té ngã trên đất."),t("br"),t("br"),n._v("“Ngươi... Ngươi tại đây cái khôi lỗi bên trong hạ độc?”"),t("br"),t("br"),n._v("Con ngươi co rụt lại, Ngọc Diệp Vương khóe mắt."),t("br"),t("br"),n._v("Chỉ một chút, hắn liền hiểu được, vì cái gì đối phương không có sợ hãi, nguyên lai cái này khôi lỗi đã bị hạ độc, ngay cả mình nguyên thần cường đại như vậy tồn tại, đều không có may mắn thoát khỏi, trực tiếp trúng chiêu!"),t("br"),t("br"),n._v("“Coi như ngươi thông minh, ngoan ngoãn nghe lời của ta, làm ta khôi lỗi, có thể tha cho ngươi khỏi chết!” Trương Huyền thản nhiên nói."),t("br"),t("br"),n._v("Đem khôi lỗi lấy ra, hắn liền làm xong định, đập mấy lần, đem trong cơ thể Thiên Đạo chân khí rót vào trong đó."),t("br"),t("br"),n._v("Chân khí không có bất kỳ cái gì thuộc tính, cho dù phát hiện, cũng tưởng là chỉ là khôi lỗi kèm theo chân khí, không có chút nào hoài nghi... Đợi hắn nguyên thần tiến vào bên trong, lại một lần kích phát, trực tiếp để trong đó độc, nguyên thần đều không ngoại lệ."),t("br"),t("br"),n._v("“Để cho ta nghe lời ngươi, nằm mơ...”"),t("br"),t("br"),n._v("Ngọc Diệp Vương cắn răng một cái, nguyên thần bỗng nhiên kích động, trực tiếp hướng khôi lỗi trên người huyệt đạo phóng đi."),t("br"),t("br"),n._v("Đã có thể chui vào nơi này, tự nhiên cũng có thể ra ngoài."),t("br"),t("br"),n._v("“Đã đi vào, lại nghĩ ra ngoài... Làm sao có thể!” Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Nghe nói như thế, Ngọc Diệp Vương trong lòng lạnh lẽo, lúc này mới phát hiện, khôi lỗi trên người tất cả huyệt đạo, đều bị một cỗ đặc thù chân khí bao vây, cho dù thực lực của hắn mạnh mẽ, cũng không cách nào lao ra."),t("br"),t("br"),n._v("Nói cách khác, đường lui đã toàn bộ đóng kín, cho dù thực lực của hắn bây giờ, cũng không có biện pháp gì."),t("br"),t("br"),n._v("Ngàn cẩn thận, vạn cẩn thận, cuối cùng vẫn là trúng mà tính toán. Ngọc Diệp Vương khí muốn hộc máu, lại phát hiện khôi lỗi không máu có thể nôn, càng nghĩ càng lạnh, càng nghĩ càng sợ hãi."),t("br"),t("br"),n._v("Trước mắt vị này, tính toán không bỏ sót, không chỉ để cho mình cùng Thạch Diệp Vương tự giết lẫn nhau, thậm chí ngay cả mình chiến thắng về sau, như thế nào đối phó đều thiết kế đến, sớm chuẩn bị ẩn chứa kịch độc khôi lỗi..."),t("br"),t("br"),n._v("Tử Diệp Vương lúc nào trở nên lợi hại như vậy?"),t("br"),t("br"),n._v("“Ngươi không phải... Tử Diệp Vương!”"),t("br"),t("br"),n._v("Đột nhiên, trong lòng hơi động nghĩ đến một loại khả năng."),t("br"),t("br"),n._v("“Không sai, coi như thông minh, đến cuối cùng kịp phản ứng...” Trương Huyền nhẹ nhàng cười một tiếng, lại không ngụy trang, bắp thịt toàn thân “Kẽo kẹt! Kẽo kẹt!” Vang vọng, một lát sau, biến trở về hình dạng của mình."),t("br"),t("br"),n._v("“Ngươi là nhân tộc? Chẳng lẽ, chẳng lẽ, ngươi mới là cái kia trời nhận danh sư?”"),t("br"),t("br"),n._v("Thân thể cứng đờ, Ngọc Diệp Vương con ngươi thu hẹp."),t("br"),t("br"),n._v("Trời nhận danh sư, là thiên địa đều công nhận tồn tại, độ hot như vậy vận gia thân, làm sao có thể bị dễ dàng bắt lấy, đến bây giờ đều hôn mê bất tỉnh? Kết hợp với trước đó cái tên này nhất định muốn đứng tại tế đàn bao phủ ánh sáng xuống... Rất dễ dàng suy đoán ra chân tướng."),t("br"),n._v("“Suy tính một chút, làm ta tôi tớ đi!”"),t("br"),t("br"),n._v("Không trả lời hắn, Trương Huyền thản nhiên nói."),t("br"),t("br"),n._v("Trời nhận danh sư sự tình, dính dáng quá lớn, tại Danh Sư đường hắn đều không thừa nhận, huống chi đối mặt một cái Dị Linh tộc nhân."),t("br"),t("br"),n._v("“Để cho ta làm tôi tớ? Nằm mơ... Bất quá, có thể chết ở vạn năm không ra một vị trời nhận danh sư trên tay, ta cũng nhận!”"),t("br"),t("br"),n._v("Ngọc Diệp Vương cười lạnh một tiếng, lắc đầu, khôi lỗi trong mắt chảy ra kim sắc chất lỏng, lập tức nặng nề té ngã trên đất."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Vội vàng đi tới trước mặt, lúc này mới phát hiện, cái tên này đã cắt đứt hô hấp."),t("br"),t("br"),n._v("Nguyên thần bị vây ở khôi lỗi trong cơ thể, trốn không thoát, lại không cách nào tự bạo, càng không muốn khuất phục bản thân, cái tên này cũng đủ quả quyết, trực tiếp lựa chọn tự sát!"),t("br"),t("br"),n._v("“Ngược lại là quả quyết, hơn nữa đối Dị Linh tộc rất là trung thành...” Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Biết không thể trốn đi đâu được, lựa chọn tự sát, vị này Ngọc Diệp Vương mặc dù có chút vì tư lợi, không so đo trời đồng bạn chết sống, nhưng ở hiệu trung Dị Linh tộc nhân bên trên, lại không mà nói."),t("br"),t("br"),n._v("“Chính là... Lãng phí ta một đầu khôi lỗi!”"),t("br"),t("br"),n._v("Cúi đầu nhìn về phía nằm dưới đất khôi lỗi, Trương Huyền một hồi đau lòng."),t("br"),t("br"),n._v("Nguyên thần của đối phương quá mạnh, mặc dù nhờ vào Thiên Đạo chân khí có thể đem hắn bao vây, không cách nào chạy ra, có thể lần này tự sát, cũng hoàn toàn phá hủy khôi lỗi kết cấu bên trong, thật vất vả tấn thăng đến Thánh vực tam trọng bảo vật, tương đương với hoàn toàn phế bỏ."),t("br"),t("br"),n._v("“Bất quá, hai cái này vương giả bảo bối thật nhiều, không nói cái khác, chuôi này trường đao cùng trường thương liền toàn bộ đạt đến Thánh khí cấp bậc, mặc dù chỉ là hạ phẩm, nhưng cũng không tệ...”"),t("br"),t("br"),n._v("Khôi lỗi hỏng, lấy hắn luyện khí năng lực, có vẻ như... Cũng tu bổ không được, có điều, giết chết hai người này, cũng không chịu thiệt."),t("br"),t("br"),n._v("Hắn chỉ là tổn thất một đầu khôi lỗi, có thể hai người này trên người bảo vật, nhưng bây giờ quá nhiều."),t("br"),t("br"),n._v("Trước đó giao chiến trường đao cùng trường thương, liền đã đạt đến Thánh khí cấp bậc."),t("br"),t("br"),n._v("Huống chi Ngọc Diệp Vương còn có vô số cúng tế vật phẩm, cùng cái kia cổ quái tế đàn."),t("br"),t("br"),n._v("“Thu lại nói...”"),t("br"),t("br"),n._v("Không đi nghĩ cái khác, trước đem hai người lưu lại trữ vật giới chỉ cất kỹ, Trương Huyền lúc này mới hướng cách đó không xa nằm thẳng dưới đất Lôi Nguyên châu đi tới."),t("br"),t("br"),n._v("Thứ này bị Thạch Diệp Vương chộp vào lòng bàn tay, bất quá tay của hắn chưởng bị chém đứt về sau, liền rơi trên mặt đất, rơi vào cách đó không xa, tản mát ra thong thả ánh sáng."),t("br"),t("br"),n._v("Mấy bước đi tới trước mặt, bàn tay nhẹ nhàng chạm đến tới."),t("br"),t("br"),n._v("Lốp bốp!"),t("br"),t("br"),n._v("Còn chưa tới đến trước mặt, liền cảm thấy một hồi nóng rực đau đớn, vô số lôi điện bổ tới, tựa hồ muốn hắn đốt thành tro bụi."),t("br"),t("br"),n._v("Thân thể nhoáng một cái, tránh thoát công kích, Trương Huyền khóe miệng giật một cái."),t("br"),t("br"),n._v("Cái này Lôi Nguyên châu rất tốt, rất cường đại, nhưng lấy thực lực của hắn bây giờ, có vẻ như... Căn bản là không có cách tới gần, chớ nói chi là luyện hóa."),t("br"),t("br"),n._v("“Đem lợi hại như vậy bảo vật ném đi, phung phí của trời cũng không dễ ah...”"),t("br"),t("br"),n._v("Xoa xoa mi tâm."),t("br"),t("br"),n._v("Không có gặp ngược lại cũng thôi, đều đã nhìn thấy, lại khoảng cách không xa, không đem lợi hại như thế bảo vật lấy đi, thực sự có lỗi với chính mình."),t("br"),t("br"),n._v("“Thử xem như vậy...”"),t("br"),t("br"),n._v("Xoắn xuýt một hồi, đột nhiên ánh mắt sáng lên, bàn tay một trảo, đem Thạch Diệp Vương thân thể ngăn tại trước mặt, lần nữa chậm rãi hướng Lôi Nguyên châu đi tới."),t("br"),t("br"),n._v("Đôm đốp!"),t("br"),t("br"),n._v("Dường như cảm giác được có sinh mệnh tới gần, lần nữa lôi điện đánh tới, có điều, đều bị Thạch Diệp Vương thân thể ngăn tại bên ngoài."),t("br"),t("br"),n._v("Một lát sau, Trương Huyền đi tới trước mặt, bàn tay bỗng nhiên ở phía trên nhẹ nhàng sờ một cái."),t("br"),t("br"),n._v("Răng rắc!"),t("br"),t("br"),n._v("Trong đầu mới hô lên “Thiếu hụt”, sau một khắc liền bay ngược ra ngoài, toàn thân cứng ngắc, tóc dựng lên, phả ra khói xanh, quần áo trên người, cũng bị nổ tan tành, hiển nhiên sét đánh sau bộ dáng."),t("br"),t("br"),n._v("“Cái này... Uy lực quá lớn...”"),t("br"),t("br"),n._v("Chậm hồi lâu lúc này mới tỉnh táo lại."),t("br"),t("br"),n._v("May mắn Vu hồn kinh qua lôi điện rèn luyện, trở nên mạnh hơn, thân thể lại đạt đến Thánh khí cấp bậc, nếu không, lần này, coi như không chết, cũng khẳng định muốn nằm lên mấy ngày, không cách nào động đậy."),t("br"),t("br"),n._v("“Nhìn một chút có hay không phương pháp, thực lực chưa đủ dưới tình huống, cũng có thể đem luyện hóa...”"),t("br"),t("br"),n._v("Biết hắn thực lực bây giờ còn chưa đủ, Trương Huyền không có chán nản, mà là hướng thư viện nhìn lại."),t("br"),t("br"),n._v("Vừa rồi chạm đến đồng thời, hô lên thiếu hụt, cứ việc mười phần chật vật, hẳn là hoàn thành mục đích, có đối ứng sách."),t("br"),t("br"),n._v("Quả nhiên, nương theo hắn ý nghĩ này, trên giá sách một quyển sách bay tới, rơi vào lòng bàn tay."),t("br"),t("br"),n._v("Tiện tay mở ra."),t("br"),t("br"),n._v("“Lôi Nguyên châu, lôi đình chỗ tụ tập thai nghén mà sinh, có thể làm thành trận pháp nền tảng, khống chế lôi đình chi hải đại trận...”"),t("br"),t("br"),n._v("Thư tịch bên trên nội dung, như nước chảy đi vào trong óc, thời gian qua một lát, liền biết thứ này thiếu hụt."),t("br"),t("br"),n._v("“Thì ra là thế... Lấy ta thực lực bây giờ, cũng có thể luyện hóa, có điều, muốn sử dụng, liền khó khăn, trừ phi đạt đến Nguyên Thần cảnh...”"),t("br"),t("br"),n._v("Hiểu chuyện gì xảy ra, Trương Huyền cười khổ."),t("br"),t("br"),n._v("Cái này lôi đình chi hải, cùng trước đó đoán đồng dạng, là cái cấp tám đại trận, chỉ có nguyên thần mới có thể khống chế, hắn cho dù biết Lôi Nguyên châu thiếu hụt, có thể thuận lợi luyện hóa, nhưng muốn hoàn toàn khống chế đại trận, để bản thân sử dụng, vẫn là làm không được."),t("br"),t("br"),n._v("“Quên đi, không muốn nhiều như vậy, trước luyện hóa lại nói...”"),t("br"),t("br"),n._v("Không đi xoắn xuýt vấn đề này, Trương Huyền lần nữa nhìn về phía cách đó không xa viên cầu, con mắt phóng ra quang mang."),t("br"),t("br"),n._v("Hiện tại không thể dùng, về sau thực lực tăng lên, lại dùng cũng được, cấp tám sát trận... Thế nhưng là có thể ngộ nhưng không thể cầu đồ tốt!"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1061"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1063"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);