(window.webpackJsonp=window.webpackJsonp||[]).push([[850],{1205:function(n,h,i){"use strict";i.r(h);var t=i(44),g=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-1739-khi-đoc-the-gioi-【-canh-3-】"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1739-khi-đoc-the-gioi-【-canh-3-】"}},[n._v("#")]),n._v(" Chương 1739: Khí độc thế giới 【 Canh [3] 】")]),n._v(" "),i("hr"),n._v(" "),i("p",[n._v("Chương 1739: Khí độc thế giới "),i("br"),i("br"),n._v("“Cái này còn tạm được!”"),i("br"),i("br"),n._v("Đem bình ngọc tiếp lấy, Trương Huyền thoả mãn gật gật đầu."),i("br"),i("br"),n._v("Buộc hắn luận võ, tự nhiên muốn trả giá đắt, Cổ Thánh cũng không thể ngoại lệ!"),i("br"),i("br"),n._v("“Ngươi chuôi này đao tấn thăng Cổ Thánh?”"),i("br"),i("br"),n._v("Trương gia vị lão tổ kia, dường như cũng không dám tin tưởng."),i("br"),i("br"),n._v("“Ừm!” Trương Huyền gật đầu."),i("br"),i("br"),n._v("“Tốt, quá tốt rồi!”"),i("br"),i("br"),n._v("Trương gia lão tổ cười ha ha một tiếng."),i("br"),i("br"),n._v("Những năm này liền dựa vào hắn một người chống đỡ lấy gia tộc, thực tế quá mệt mỏi, hiện tại có vũ khí tấn cấp, cũng liền cho thấy, Trương gia lần nữa nhiều hơn một vị Cổ Thánh cường giả tọa trấn."),i("br"),i("br"),n._v("Hơn nữa binh khí linh tính, tuổi thọ so với nhân loại càng thêm lâu dài, có thể bảo vệ lấy gia tộc, đi càng xa."),i("br"),i("br"),n._v("“Ta còn có học sinh, tại Chư Tử bách gia trong tay, hiện tại muốn đi cứu bọn họ, trước hết đi cáo từ...”"),i("br"),i("br"),n._v("Chư Tử bách gia Cổ Thánh cùng Dị Linh tộc Cổ Thánh rời đi, nơi này chỉ còn lại có một ít Thánh thú cùng rất nhiều danh sư, lại không nguy hiểm, Trương Huyền ôm quyền, nói."),i("br"),i("br"),n._v("“Đi đi!”"),i("br"),i("br"),n._v("Trương gia lão tổ khoát tay áo."),i("br"),i("br"),n._v("Biết không vị này, hắn không có khả năng đánh lén thành công, thậm chí lúc trước, liền có khả năng bị Dị Linh tộc vị kia Cổ Thánh giết, Trương Huyền lúc này cung kính thi lễ một cái, để Triệu Nhã dẫn đường, thẳng tắp hướng một cái phương hướng bay lượn mà đi."),i("br"),i("br"),n._v("Bay không biết bao xa, lần nữa quay đầu, Đại Thành điện đã có chút mơ hồ, Triệu Nhã ngừng lại."),i("br"),i("br"),n._v("“Ta cùng Chư Tử bách gia đám người, chính là từ nơi này xuất hiện!”"),i("br"),i("br"),n._v("Liếc mắt nhìn, Trương Huyền mỉm cười."),i("br"),i("br"),n._v("Hắn cùng Hồ Yêu Yêu, cũng là từ nơi này rời đi thế giới băng tuyết, nhìn tới Triệu Nhã, bọn họ cũng giống vậy, từ nơi này thế giới, đi tới nơi này."),i("br"),i("br"),n._v("“Phá!”"),i("br"),i("br"),n._v("Lúc trước, chỉ có thể mượn Dị Linh tộc nhân tế tự, xuyên toa không gian bích chướng, giờ phút này có Cổ Thánh cấp bậc Yêu Dị huyền đao, loại cấp bậc này bình chướng, cũng lại khó không được, trường đao nơi tay, khe khẽ chém một cái."),i("br"),i("br"),n._v("Rầm!"),i("br"),i("br"),n._v("Một vết nứt xuất hiện."),i("br"),i("br"),n._v("Kéo một phát Triệu Nhã trực tiếp nhảy xuống."),i("br"),i("br"),n._v("Sau một khắc, lại xuất hiện tại thế giới băng tuyết bên trong."),i("br"),i("br"),n._v("Bất quá, thời khắc này thế giới, đã không còn băng tuyết, có chỉ là sắc màu rực rỡ, màu xanh biếc dạt dào."),i("br"),i("br"),n._v("Nếu như không phải từ nơi này cách mở, lúc trước tâm huyết dâng lên lão giả vẫn còn, cũng hoài nghi, có phải hay không đi vào một thế giới khác."),i("br"),i("br"),n._v("Tới qua một lần, thế giới này cửa ra vào, rất dễ dàng liền bị tìm tới, hai người trực tiếp chui vào."),i("br"),i("br"),n._v("Hô!"),i("br"),i("br"),n._v("Xuất hiện lần nữa, đi tới một cái tràn đầy chướng khí núi rừng."),i("br"),i("br"),n._v("“Ngụy Như Yên hẳn là ngay tại thế giới này, đối ứng phân điện phía trong!”"),i("br"),i("br"),n._v("Liếc mắt nhìn hoàn cảnh chung quanh, Triệu Nhã nói."),i("br"),i("br"),n._v("Gật gật đầu, Trương Huyền cổ tay khẽ đảo, một cái đan dược xuất hiện tại lòng bàn tay: “Đem cái này ăn vào!”"),i("br"),i("br"),n._v("“Vâng!”"),i("br"),i("br"),n._v("Há miệng nuốt xuống, sau khi ăn xong, Triệu Nhã mới mang theo nghi ngờ nhìn qua: “Lão sư, đây là giải độc đan?”"),i("br"),i("br"),n._v("“Nơi này thể khí cùng nguồn nước, đều có độc, cẩn thận một chút!”"),i("br"),i("br"),n._v("Trương Huyền giải thích."),i("br"),i("br"),n._v("Bởi vì Tiên Thiên thai độc, hắn chuyên môn nhìn qua độc sư thư tịch, liếc mắt liền nhìn ra, cái này rừng rậm, không tưởng tượng bên trong đơn giản như vậy, thoạt nhìn mười phần yên tĩnh, không có lợi hại Thánh thú, trên thực tế đâu đâu cũng có kịch độc, để cho người ta khó lòng phòng bị."),i("br"),i("br"),n._v("Triệu Nhã theo sát tại lão sư sau lưng: “Lão sư, vậy chúng ta bây giờ làm sao bây giờ?”"),i("br"),i("br"),n._v("“Rất đơn giản, tìm tới thế giới này lối ra vị trí, không có gì bất ngờ xảy ra, Khổng miếu phân điện, ngay tại ngay phía trên!”"),i("br"),i("br"),n._v("Có mấy lần trước kinh nghiệm, đã biết quy luật."),i("br"),i("br"),n._v("Tùy tiện tìm phương hướng, chậm rãi tiến lên."),i("br"),n._v(" Trong rừng, đâu đâu cũng có độc hạt, rắn độc các loại đồ vật, có điều, Trương Huyền nắm giữ Thiên đạo chân khí, cũng không sợ hãi, một đường tiến lên, cũng không gặp được nguy hiểm gì."),i("br"),i("br"),n._v("Đi nửa canh giờ, gặp được một đám danh sư, giờ phút này tất cả đều sắc mặt tái xanh, trúng kịch độc."),i("br"),i("br"),n._v("Cho mấy cái, dùng Thiên đạo chân khí dung hợp đan dược, hỗ trợ giải độc, Trương Huyền kỹ càng hỏi thăm vài câu, đánh giá ra chỗ lối ra chỗ, chạy tới."),i("br"),i("br"),n._v("“Chính là chỗ này...”"),i("br"),i("br"),n._v("Một người giờ về sau, hai người tại một cái không lớn hồ nước trước mặt ngừng lại."),i("br"),i("br"),n._v("“Đây chỉ là cái bình thường hồ nước, lão sư vì sao cảm giác chính là lối ra?”"),i("br"),i("br"),n._v("Triệu Nhã nghi ngờ nhìn qua."),i("br"),i("br"),n._v("Lúc trước hỏi thăm, đối phương, nàng cẩn thận nghe, liền bọn họ cũng không biết lối ra ở nơi nào, lão sư là thế nào xác định?"),i("br"),i("br"),n._v("“Rất đơn giản...”"),i("br"),i("br"),n._v("Cũng không nhiều lời, Trương Huyền lăng không một trảo, một con kiến liền bị bóp tại đầu ngón tay."),i("br"),i("br"),n._v("Cái này kiến, chừng hạt đậu, toàn thân đỏ tươi, vừa nhìn liền biết độc tính mười phần."),i("br"),i("br"),n._v("Nhẹ nhàng cười một tiếng, đem kiến đặt ở mu bàn tay."),i("br"),i("br"),n._v("Phốc!"),i("br"),i("br"),n._v("Chỉ thoáng cái, miệng của đối phương liền đâm xuyên qua làn da, đem độc dịch truyền vào đi vào, Trương Huyền cũng không cần Thiên đạo chân khí khử trùng, ngược lại mặc cho độc dịch khuếch tán."),i("br"),i("br"),n._v("Thời gian qua một lát, mu bàn tay bắt đầu biến thành màu đen, xuất hiện sưng đỏ máu ứ đọng."),i("br"),i("br"),n._v("“Lão sư...”"),i("br"),i("br"),n._v("Không biết lão sư vì sao làm như vậy, Triệu Nhã tràn đầy lo lắng."),i("br"),i("br"),n._v("Độc, không phải những vật khác, một khi kèm theo huyết dịch chảy vào trái tim, liền thần tiên khó cứu."),i("br"),i("br"),n._v("“Không cần phải gấp...”"),i("br"),i("br"),n._v("Trương Huyền lại vồ một cái, hồ nước bên trong ao nước, bị lăng không nhiếp ra, rơi vào miệng vết thương."),i("br"),i("br"),n._v("Xì xì xì!"),i("br"),i("br"),n._v("Trên mu bàn tay kịch độc, giống như là gặp khắc tinh đồng dạng, toát ra khói trắng, lúc trước sưng đỏ, máu ứ đọng, nhanh chóng biến mất, rất nhanh, bàn tay khôi phục như lúc ban đầu, hình như không trúng qua độc đồng dạng."),i("br"),i("br"),n._v("“Cái này...”"),i("br"),i("br"),n._v("Triệu Nhã sững sờ tại nguyên chỗ: “Cái này hồ nước nước, có thể giải độc?”"),i("br"),i("br"),n._v("“Không sai!”"),i("br"),i("br"),n._v("Trương Huyền gật gật đầu: “Khổng sư lưu lại di tích mục đích, là vì tôi luyện hậu bối, mới có tư cách kế thừa hắn bảo tàng, cũng không phải là giết người! Vì lẽ đó... Lối đi ra, nhất định có giải độc đồ vật, nếu không, tất cả mọi người độc chết, lưu lại truyền thừa thì có ích lợi gì?”"),i("br"),i("br"),n._v("Triệu Nhã giật mình."),i("br"),i("br"),n._v("“Lúc trước gặp phải cái kia sóng danh sư, ta hướng bọn họ hỏi thăm, nơi nào độc trùng nhiều nhất, nguy hiểm nhất, nói cho ta biết là nơi này, giữa thiên địa, âm dương hòa hợp, nhất ẩm nhất trác, tự có thiên định! Khổng sư tu luyện công pháp, phù hợp tự nhiên, độc trùng nhiều chỗ, giải dược nhất định cũng tồn tại, vì lẽ đó, ta kết luận, nơi này chính là lối ra!”"),i("br"),i("br"),n._v("Trương Huyền cười nói."),i("br"),i("br"),n._v("Vừa rồi hỏi thăm đám kia danh sư, khẳng định không thể hỏi thăm lối ra ở đâu, nếu như bọn họ biết, khẳng định đã sớm chạy trốn, không đến mức ở đây, nơm nớp lo sợ."),i("br"),i("br"),n._v("Lối ra không biết, nhưng độc trùng nhiều địa phương, thời gian dài như vậy trải qua, nhất định biết."),i("br"),i("br"),n._v("Tìm tới nơi này, lại phát hiện, cái này hồ nước xung quanh, không độc trùng có thể tới gần, lại không xác định, thật sự choáng váng."),i("br"),i("br"),n._v("“Lão sư, chúng ta bây giờ liền phá vỡ không gian đi tìm Ngụy Như Yên bọn họ ư?”"),i("br"),i("br"),n._v("Tìm tới lối ra, Triệu Nhã nhịn không được nói."),i("br"),i("br"),n._v("Căn cứ suy tính, Ngụy Như Yên đám người chỗ đi phân điện, hẳn là ngay tại cái này hồ nước phía trên."),i("br"),i("br"),n._v("“Không cần phải gấp, ngươi ở chỗ này chờ, ta trước vào hồ nước nhìn một chút! Có thể cuồn cuộn không ngừng sinh ra hóa giải kịch độc ao nước, bên trong nhất định có trấn thủ thế giới trận nhãn! Trước đem thứ này lấy đi lại nói!”"),i("br"),i("br"),n._v("Trương Huyền nói."),i("br"),i("br"),n._v("Rừng rậm thế giới, là năm đại vương giả; Sa mạc thế giới, là rễ cây; Thế giới băng tuyết là hàn băng tinh thạch... Cái này khí độc thế giới, có lẽ cũng có trấn thủ đồ vật."),i("br"),i("br"),n._v("Cùng tinh thạch đồng dạng, đối đi vào phân điện có trợ giúp thật lớn."),i("br"),i("br"),n._v("Sớm chuẩn bị, lo trước khỏi hoạ."),i("br"),i("br"),n._v("Nói xong, cũng không nói nhảm, thân thể nhảy lên, thẳng tắp chui vào hồ nước, biến mất không thấy gì nữa."),i("br"),i("br"),n._v("Người đăng: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-1750"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-1752"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);