(window.webpackJsonp=window.webpackJsonp||[]).push([[851],{1206:function(n,h,c){"use strict";c.r(h);var t=c(44),i=Object(t.a)({},(function(){var n=this,h=n.$createElement,c=n._self._c||h;return c("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[c("h1",{attrs:{id:"chuong-1740-cau-ca-【-canh-4-】"}},[c("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1740-cau-ca-【-canh-4-】"}},[n._v("#")]),n._v(" Chương 1740: Câu cá 【 Canh [4] 】")]),n._v(" "),c("hr"),n._v(" "),c("p",[n._v("Chương 1740: Câu cá "),c("br"),c("br"),n._v("Ao nước không tính quá sâu, chỉ có hơn mười mét bộ dạng, lại xuống phương chính là phong ấn, không có gì bất ngờ xảy ra, từ nơi này đi vào, sẽ đi vào cái kế tiếp thế giới."),c("br"),c("br"),n._v("Trương Huyền cũng không tiến lên, mà là tại xung quanh bơi lên."),c("br"),c("br"),n._v("Một vòng sau đó, cũng không phát hiện cái gì, đừng nói sinh mệnh, liền khoáng thạch các loại đều không có tìm tới, sạch sẽ cùng nước lọc đồng dạng."),c("br"),c("br"),n._v("“Chẳng lẽ đoán sai? Nơi này không có trận tâm? Hoặc là... Đã sớm bị người khác nhanh chân đến trước?”"),c("br"),c("br"),n._v("Cau mày."),c("br"),c("br"),n._v("“Không đúng, nếu như trận tâm bị lấy đi, khí độc, không có khả năng như vậy đều đặn, khẳng định đã sớm hỗn loạn...”"),c("br"),c("br"),n._v("Sa mạc thế giới, đem rễ cây lấy đi, sa mạc biến mất, rừng rậm thế giới, năm đại vương giả rời đi, mặc dù không biết kết quả như thế nào, nhưng không có bọn chúng khống chế, rất nhiều Thánh thú nhất định sẽ không đi giống lúc trước như vậy nói quy củ..."),c("br"),c("br"),n._v("Cái này cũng giống vậy, khí độc phân bố đều đặn, hồ nước nắm giữ cực mạnh giải độc năng lực, nói rõ trận tâm cũng không gặp phải phá hoại."),c("br"),c("br"),n._v("“Minh Lý Chi Nhãn!”"),c("br"),c("br"),n._v("Hoa văn tại trong mắt xoay tròn, tại trong hồ nước nhìn quanh, quả nhiên phát hiện không đúng."),c("br"),c("br"),n._v("Trong nước có thể giúp người giải độc vật chất, tựa hồ tại dần dần giảm bớt."),c("br"),c("br"),n._v("Bay ra hồ nước, tại nguyên chỗ đợi phút chốc, lần nữa nhìn lại, loại này giải độc năng lực, đã khôi phục."),c("br"),c("br"),n._v("“Lão sư, tìm được chưa?”"),c("br"),c("br"),n._v("Triệu Nhã đi tới trước mặt."),c("br"),c("br"),n._v("“Không có, có điều, ta hẳn là đoán ra trận tâm là cái gì, qua bên kia cho ta bẻ căn thân trúc tới!” Mỉm cười, Trương Huyền bàn giao."),c("br"),c("br"),n._v("Triệu Nhã xoay người rời đi, thời gian không dài, cầm một cái thân trúc đi tới."),c("br"),c("br"),n._v("Nhận lấy thân trúc, nhẹ nhàng run lên, phía trên cành toàn bộ rơi mất sạch sẽ, chân khí hóa thành một đạo chỉ, từ cột ngay phía trước lan ra xuống, hướng về phía trước vung một cái, chân khí chồng chất tại phần cuối, tựa như một cái mỹ vị mồi câu."),c("br"),c("br"),n._v("“Đây là... Cần câu? Lão sư muốn câu cá?”"),c("br"),c("br"),n._v("Triệu Nhã ngẩn ngơ."),c("br"),c("br"),n._v("Lão sư vậy mà dùng thân trúc cùng chân khí, làm thành cần câu cá, tính toán câu cá."),c("br"),c("br"),n._v("“Đúng vậy a, tử câu mà không cương, dặc bất xạ túc, nếu như không có đoán sai, trận tâm chỉ có thể câu đi ra, phương pháp khác, đều vô dụng!”"),c("br"),c("br"),n._v("Trương Huyền cười một tiếng."),c("br"),c("br"),n._v("Lúc trước nhìn qua, liên quan tới Khổng sư không ít sách vở, trong đó một bộ môi giới thiệu, tử câu mà không cương, dặc bất xạ túc. Nói là, Khổng sư dùng cần câu câu cá mà không cần lưới đánh cá bắt cá, dùng dặc bắn phương thức thu hoạch con mồi, nhưng là cho tới nay không bắn lấy nghỉ ngơi chim thú."),c("br"),c("br"),n._v("Vốn cho rằng là Khổng sư, nhân từ biểu hiện, giờ phút này mới hiểu được, câu mà không cương là chỉ, trong này trận tâm, dùng lưới đánh cá, hai tay đi bắt, đều không làm được, duy nhất muốn thu hoạch phương pháp, chỉ có một cái... Vậy chính là thả câu!"),c("br"),c("br"),n._v("Đem độc trùng đặt ở trong nước, Trương Huyền yên tĩnh ngồi xuống."),c("br"),c("br"),n._v("Thả câu, có thể rèn luyện tâm cảnh, để cho người ta không kiêu không ngạo, càng thêm phù hợp tự nhiên."),c("br"),c("br"),n._v("Người bình thường câu cá, cũng có thể làm cho bản thân dung hợp vào thiên địa, yên tĩnh như là bức tranh, huống chi tu vi đạt tới hắn loại cảnh giới này."),c("br"),c("br"),n._v("Chân khí hóa thành chỉ, theo sóng nước bồng bềnh, Trương Huyền đột nhiên cảm thấy, cái này khí độc thế giới, không những không tưởng tượng bên trong đáng sợ, còn cho người một loại, chân thực, tự nhiên cảm giác."),c("br"),c("br"),n._v("Thời kỳ Thượng Cổ, linh khí đầy đủ, không có nhân loại đặt chân lão Lâm, độc trùng, khí độc, chỗ nào cũng có, cùng thế giới này hầu như tương đồng."),c("br"),c("br"),n._v("Nói cách khác, đây cũng không phải là khí độc thế giới, mà là một cái kéo dài vô số năm thâm sơn cổ lâm."),c("br"),c("br"),n._v("Nơi này, mới có thể tìm được chân chính tự nhiên chân lý."),c("br"),c("br"),n._v("Đột phá Đại Thánh lúc, tu luyện công pháp, chậm rãi ở trong người xoay tròn, cả người giống như là biến thành một khối nham thạch, một gốc cây mộc, một hạt cát trần... Cùng tự nhiên hoàn mỹ giao hòa."),c("br"),c("br"),n._v("Tâm cảnh, tại trong an tĩnh, chậm chạp tăng trưởng."),c("br"),c("br"),n._v("29. 91!"),c("br"),c("br"),n._v("29. 92!"),c("br"),c("br"),n._v("..."),c("br"),c("br"),n._v("Rầm!"),c("br"),c("br"),n._v("Ngay tại Trương Huyền chìm đắm thời điểm, trước mắt sóng nước đột nhiên một hồi nhộn nhạo, ngay sau đó một thanh âm vang lên."),c("br"),c("br"),n._v("“Lão sư, cá đã mắc câu!”"),c("br"),n._v("Trương Huyền cúi đầu nhìn lại, ngay sau đó nhìn thấy một cái màu đỏ tươi cá chép, treo ở chân khí chỉ bên trên, không ngừng lắc lư, đánh bọt nước văng khắp nơi."),c("br"),c("br"),n._v("“Đáng tiếc!”"),c("br"),c("br"),n._v("Cá mắc câu, để hắn từ cái loại này trạng thái đặc thù bên trong khôi phục lại."),c("br"),c("br"),n._v("Nếu không, có lẽ có thể trực tiếp trùng kích Cổ Thánh 30. 0, tâm cảnh đầu tiên đạt tới Cổ Thánh!"),c("br"),c("br"),n._v("Phần phật!"),c("br"),c("br"),n._v("Cổ tay đè thấp, nhẹ nhàng run lên, cá chép bị câu ra mặt nước."),c("br"),c("br"),n._v("Phần phật!"),c("br"),c("br"),n._v("Cá nhảy dựng đi ra, trên mặt đất nhảy nhót hai lần, ngay sau đó miệng không ngừng khép mở, giống như là hô hấp dồn dập, không chịu nổi, ngay sau đó lăn hai vòng, con mắt trừng lớn, không còn hô hấp."),c("br"),c("br"),n._v("“Lão sư, cá chết...” Triệu Nhã mang theo lo lắng."),c("br"),c("br"),n._v("Đây không phải trận tâm ư?"),c("br"),c("br"),n._v("Làm sao câu đi lên liền chết?"),c("br"),c("br"),n._v("Chẳng lẽ, câu sai?"),c("br"),c("br"),n._v("“Tốt rồi!”"),c("br"),c("br"),n._v("Trương Huyền dở khóc dở cười, một cước đá vào thân cá bên trên, để nó lộn mấy vòng, đồng thời cong ngón tay búng một cái, tối đen như mực sắc hỏa diễm trộn lẫn vào lôi điện quanh quẩn đầu ngón tay: “Ý tứ ý tứ liền được, làm sao, còn muốn kho ah!”"),c("br"),c("br"),n._v("“Khụ khụ!” Trên đất cá chép, nghiêng người, đuôi chạm đất, đứng lên: “Thật không tiện, thói quen, thói quen! Rất lâu không có lên bờ, vừa lên bờ, liền cảm thấy hô hấp khó khăn, còn tưởng rằng chết rồi...”"),c("br"),c("br"),n._v("“...”"),c("br"),c("br"),n._v("Triệu Nhã da mặt run lên."),c("br"),c("br"),n._v("Một con cá nhảy dựng lên nói chuyện..."),c("br"),c("br"),n._v("Hơn nữa, thế nào thấy, con cá này, như thế không đáng tin cậy?"),c("br"),c("br"),n._v("“Cái tên này là Bất Hủ cảnh đại viên mãn thực lực, đừng nói lên bờ, ném tới trong nồi, đều nấu không chết!” Trương Huyền giải thích."),c("br"),c("br"),n._v("Giật nảy mình, Triệu Nhã ngay sau đó tràn đầy nghi ngờ nhìn qua: “Đã loại thực lực này, làm sao lại bị câu đi lên?”"),c("br"),c("br"),n._v("Loại thực lực này, cùng nàng tương đồng, đã có cực kỳ cao cấp trí tuệ, dùng bình thường cần câu câu đi lên... Cảm giác làm sao như thế khó tin?"),c("br"),c("br"),n._v("“Cái này... Hết cách rồi, cũng là quen thuộc! Năm đó Khổng sư, tới câu ta, mỗi lần câu lên về sau, liền sẽ phóng thích, sau đó, ta lại tới bị câu, một mực lặp lại... Bình thường một ngày, phải bị câu đi lên mấy chục lần, thói quen, thói quen!”"),c("br"),c("br"),n._v("Cá chép miệng khép mở, tiếp tục nói."),c("br"),c("br"),n._v("“...” Triệu Nhã da mặt run rẩy."),c("br"),c("br"),n._v("Loại thực lực này, ngày ngày bị người câu... Còn làm không biết mệt, ngươi không phiền ah..."),c("br"),c("br"),n._v("Mấu chốt chính là, Khổng sư ngày ngày câu cùng một cái cá... Cũng thật là rảnh rỗi."),c("br"),c("br"),n._v("“Đừng nghe nó nói bậy, nếu như không có đoán sai, Khổng sư có lẽ cùng ta cũng như thế, dùng chính là chân khí làm tơ... Ngươi muốn thôn phệ, không riêng gì mồi câu, còn có dây câu đi!” Thấy mình người học sinh này, lý giải không được, Trương Huyền cười cười, nhìn về phía trước mắt cá chép."),c("br"),c("br"),n._v("“Cái này... Đều là phù vân!” Uốn éo người, cá chép có chút xấu hổ."),c("br"),c("br"),n._v("“Thì ra là thế!”"),c("br"),c("br"),n._v("Triệu Nhã bừng tỉnh hiểu ra."),c("br"),c("br"),n._v("Khó trách, một cái bình thường cá, liền có thể tu luyện tới Bất Hủ cảnh đại viên mãn, nguyên lai một mực thôn phệ Khổng sư chân khí."),c("br"),c("br"),n._v("Loại chuyện tốt này, tự nhiên không có khả năng nhường cho cái khác cá."),c("br"),c("br"),n._v("“Ta vừa rồi che giấu tốt như vậy, ngươi làm sao phát hiện?”"),c("br"),c("br"),n._v("Cá chép nghi ngờ nhìn về phía Trương Huyền."),c("br"),c("br"),n._v("Nó ẩn nấp phương pháp, Minh Lý Chi Nhãn đều khó mà nhìn thấu, ở giữa tới mấy đợt người, cũng không phát hiện, người trước mắt này lại trực tiếp dùng thả câu chi thuật, dẫn tới nó “Ngứa nghề không dứt”, không thể không cắn dây câu bị câu đi lên."),c("br"),c("br"),n._v("“Ngươi tại dưới nước, lưu lại giải độc khí tức, cùng chân khí của ta tương đương, ta liền đoán được!” Trương Huyền gật gật đầu: “Loại khí tức này, hẳn là Khổng sư đặc thù a!”"),c("br"),c("br"),n._v("Người đăng: ThấtDạ"),c("br"),c("br"),c("br"),c("br")]),n._v(" "),c("hr"),n._v(" "),c("p",[c("a",{attrs:{href:"./chap-1751"}},[n._v("Chương trước")]),n._v("  |  "),c("a",{attrs:{href:"./chap-1753"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);