(window.webpackJsonp=window.webpackJsonp||[]).push([[860],{1215:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1748-thanh-tich-thach-【-canh-thu-hai-】"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1748-thanh-tich-thach-【-canh-thu-hai-】"}},[n._v("#")]),n._v(" Chương 1748: Thánh tích thạch 【 canh thứ hai 】")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1748: Thánh tích thạch "),t("br"),t("br"),n._v("Mấy hơi thở sau đó, hai đầu Bất Hủ cảnh đại viên mãn, bị tại chỗ chém giết."),t("br"),t("br"),n._v("Hiện tại Trương Huyền, coi như không sử dụng năm đại vương giả, rễ cây, cá chép, cũng không phải hai đầu Dị Linh tộc nhân có thể chống lại."),t("br"),t("br"),n._v("Đem thi thể thu vào trữ vật giới chỉ, tiếp tục đi đến phía trước."),t("br"),t("br"),n._v("Xuất hiện ở trước mắt chính là cái rộng rãi đại điện, bốn phía treo đầy chân dung, mỗi một bức hoạ bên trên, đều thả ra ôn nhuận quang mang, vừa tiến vào trong, lập tức cảm thấy nồng đậm cảm giác áp bách, linh hồn lực yếu kém, nửa bước khó đi."),t("br"),t("br"),n._v("“Đối với người linh hồn, có cực lớn rèn luyện tác dụng!”"),t("br"),t("br"),n._v("Đi tới một bước, Trương Huyền âm thầm gật đầu."),t("br"),t("br"),n._v("Đại Thành điện, nắm giữ Cổ Thánh chi khí, có thể để người ta thành công tấn cấp Cổ Thánh, tẩm điện, nắm giữ để cho người ta tăng lên tâm cảnh bảo vật, mà ở trong đó, rất hiển nhiên, có thể rèn luyện linh hồn, để cho người ta hồn lực tăng thêm."),t("br"),t("br"),n._v("Đi tới mấy bước, cũng không phát hiện những người khác, Trương Huyền nhíu nhíu mày."),t("br"),t("br"),n._v("Giữ cửa hai đầu Dị Linh tộc, rõ ràng là sợ có nhân loại xông tới, đã như vậy, bên trong khẳng định còn có cái khác Dị Linh tộc cường giả, đang tìm kiếm toà này Thánh Tích điện hạch tâm bảo vật."),t("br"),t("br"),n._v("Làm sao không thấy tung tích?"),t("br"),t("br"),n._v("“Cái này cung điện có cái gì?”"),t("br"),t("br"),n._v("Trong lòng hơi động, nhìn về phía trước mặt truyền thế mẫu phù."),t("br"),t("br"),n._v("Cái tên này, ký ức bị phong cấm, đi vào đối ứng cung điện, mới có thể đánh thức, hiện tại đến Thánh Tích điện, nơi này có cái gì, nên biết hết sức rõ ràng."),t("br"),t("br"),n._v("“Thánh Tích điện, trọng yếu nhất chính là, Khổng sư lưu lại ! Thứ này, ẩn chứa Khổng sư lưu lại thánh tích, đối linh hồn tăng cường, có cực lớn hiệu quả...” Quả nhiên giống như là đạt được một loại nào đó ký ức, Tiểu Phù Phù giải thích nói."),t("br"),t("br"),n._v("“Thánh tích thạch?” Trương Huyền ánh mắt sáng lên: “Ở đâu?”"),t("br"),t("br"),n._v("Hắn vu hồn, đạt tới Kim Thân cảnh đỉnh phong, liền không chút tăng trưởng qua, nếu như có thể tìm tới thứ này, nhất định có thể nhanh chóng tăng thêm, phá tan Tâm Huyết Lai Triều, cũng chưa biết chừng!"),t("br"),t("br"),n._v("“Ngay tại phân điện đằng sau, từ cái kia cửa ra ngoài, liền có thể nhìn thấy!”"),t("br"),t("br"),n._v("Tiểu Phù Phù vặn vẹo một chút."),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Biết xác thực vị trí, Trương Huyền lại không quản bốn phía chân dung, sải bước đi thẳng về phía trước, đi ra cung điện cửa, quả nhiên thấy một cái rộng lớn sân nhỏ xuất hiện tại phía trước."),t("br"),t("br"),n._v("Trong sân, đâu đâu cũng có đá, như là một cái cỡ nhỏ rừng đá."),t("br"),t("br"),n._v("Vài đầu Dị Linh tộc nhân, đang ngồi ở rừng đá cửa vào chỗ không xa, mày nhíu lại chặt, không ngừng vò đầu bứt tai, tựa hồ tại tính toán cái gì, mà tìm không thấy đầu mối."),t("br"),t("br"),n._v("Rừng đá ở chính giữa chỗ, là cái không lớn bệ đá, phía trên bày đặt một cái hình tròn đá, bóng người ở phía trên di động, tản mát ra ôn nhuận quang mang, cho người ta một loại cảm giác đã từng quen biết."),t("br"),t("br"),n._v("“Bọn họ đang làm gì?”"),t("br"),t("br"),n._v("Không có sốt ruột tiến lên, Trương Huyền hỏi."),t("br"),t("br"),n._v("Không có gì bất ngờ xảy ra, cái này hình tròn đá, chính là cái gọi là thánh tích thạch, đám này Dị Linh tộc nhân, không đi lấy đi ra, ở đây ngồi xổm làm gì?"),t("br"),t("br"),n._v("“Bọn họ không phải là không muốn vào, mà là vào không được, thánh tích rừng đá, thoạt nhìn đơn giản, cũng chỉ có một con đường, chỉ cần đi nhầm một bước, liền sẽ bị ẩn giấu ở trong đó kiếm khí, tại chỗ đánh chết!”"),t("br"),t("br"),n._v("Tiểu Phù Phù hừ một tiếng."),t("br"),t("br"),n._v("“Kiếm khí?”"),t("br"),t("br"),n._v("Trương Huyền sững sờ, lần nữa nhìn lại, quả nhiên thấy có vài đầu đi ở phía trước Dị Linh tộc nhân, đã hai mắt nhắm nghiền, cắt đứt hô hấp."),t("br"),t("br"),n._v("“Ngươi cũng đã biết tiến vào chuẩn xác bước đi?”"),t("br"),t("br"),n._v("Trương Huyền nhìn qua."),t("br"),t("br"),n._v("“Không biết!” Tiểu Phù Phù lắc đầu, nói: “Đây là Khổng sư lưu lại khảo hạch hậu nhân, tự nhiên không thể tuỳ tiện trao cho, có điều, có lẽ cùng trước đó thông qua thế giới kia liên quan đến, ngươi nhìn kỹ một chút, những tảng đá này, mỗi một cái đều ẩn chứa công pháp chiêu số, đem những này chiêu số dựa theo chính xác trình tự kết nối, hẳn là tiến vào bên trong mấu chốt!”"),t("br"),t("br"),n._v("“Cũng phải...”"),t("br"),t("br"),n._v("Nhìn kỹ lại, Trương Huyền quả nhiên thấy rừng đá đá, cùng ngoại giới tương tự, đều ẩn chứa một loại nào đó công pháp và chiêu số."),t("br"),t("br"),n._v("“Thiếu hụt!”"),t("br"),n._v("Có trước đó kinh nghiệm, Trương Huyền đem trong tượng đá chiêu số, toàn bộ thu thập lên, rất nhanh tạo thành một bộ pháp quyết."),t("br"),t("br"),n._v("Bởi vì không có có thể tham khảo, sai lầm rất nhiều."),t("br"),t("br"),n._v("Dựa theo Dị Linh tộc nhân tiến vào phương vị, sửa đổi một chút, sai lầm lập tức ít đi rất nhiều."),t("br"),t("br"),n._v("Liên tục sửa đổi hơn trăm lần, rốt cục tạo thành chính xác trình tự, lại không một chỗ sai lầm."),t("br"),t("br"),n._v("“Ta đã suy tính ra trình tự, dựa theo cái này trình tự, có thể thông qua tượng đá ư?”"),t("br"),t("br"),n._v("Trương Huyền hỏi."),t("br"),t("br"),n._v("“Chỉ cần trình tự chính xác, liền có thể tiến vào bên trong, có điều, ngàn vạn không thể phạm sai lầm, nếu không, Khổng sư lưu lại kiếm khí, không dễ dàng như vậy phá giải...”"),t("br"),t("br"),n._v("Tiểu Phù Phù có chút lo lắng."),t("br"),t("br"),n._v("Trương Huyền gật gật đầu, đang muốn đặt chân trong đó, đột nhiên một chút dừng lại."),t("br"),t("br"),n._v("Những tảng đá này trình tự, đại biểu một bộ công pháp, nếu như... Bộ công pháp này, bản thân liền có sai lầm đâu?"),t("br"),t("br"),n._v("Hắn Thiên đạo công pháp, có thể bảo đảm là, chính xác nhất không sai, có thể... Đối phương đáp án, chưa chắc là chính xác nhất ah!"),t("br"),t("br"),n._v("Cùng trước đó rừng đá thế giới gặp phải đồng dạng, bóng người xuất hiện sai lầm, còn có thể sửa lại, kiếm khí xuất hiện sai lầm... Sẽ chết người đấy!"),t("br"),t("br"),n._v("“Huyền đao!”"),t("br"),t("br"),n._v("Dừng lại một chút, đem huyền đao lấy ra đặt ở lòng bàn tay."),t("br"),t("br"),n._v("Nếu quả thật có kiếm khí, Cổ Thánh cấp bậc Yêu Dị huyền đao, khẳng định có thể chặn lại, không đến mức tại chỗ bị đánh chết."),t("br"),t("br"),n._v("“Ta tiến vào, lừng lẫy, mấy người các ngươi theo ở phía sau, một khi những cái kia Dị Linh tộc nhân làm loạn, ngay tại chỗ đập chết! Ngàn vạn không thể trốn!”"),t("br"),t("br"),n._v("Để Kim Nguyên đỉnh cùng năm đại vương giả đi theo, Trương Huyền lúc này mới dựa theo thư viện cho ra trình tự, đi vào trong."),t("br"),t("br"),n._v("Rừng đá thế giới cửa ra vào hắn cũng không tìm tới, có điều, dựa theo thế giới khác quy luật, lối ra bên cạnh công pháp trình tự, có lẽ cùng nơi này là hoàn toàn tương đồng."),t("br"),t("br"),n._v("Như vậy tương đương tìm được trận tâm, đến lúc đó, có thể trực tiếp phá giải đi trước mắt cái này chướng ngại, đạt được thánh tích thạch."),t("br"),t("br"),n._v("Nếu đã đến nơi này, trở về thí nghiệm, khẳng định không còn kịp rồi, chỉ có thể đi một bước nhìn một bước, chỉ hy vọng, Khổng sư tự thân vật lưu lại, đừng có sai lầm!"),t("br"),t("br"),n._v("Đi tới cái thứ nhất đá trước mặt, thân ảnh tới sát qua, không có kích động công kích, Trương Huyền ngay sau đó đi vào trong."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Đi vài bước, liền gặp ở trong đó vò đầu bứt tai Dị Linh tộc nhân, chẳng muốn bán ra, thân thể nhẹ nhàng vụt qua, đã vượt qua đối phương, tiếp tục hướng phía trước."),t("br"),t("br"),n._v("“Tự tìm cái chết...”"),t("br"),t("br"),n._v("Thấy cái này đột nhiên nhân loại xuất hiện, trực tiếp lao vào trong, cái gì đều mặc kệ, Dị Linh tộc nhân cười lạnh."),t("br"),t("br"),n._v("Đồng bạn chết mấy cái, bọn họ hiện tại cũng coi như thăm dò quy luật, biết nơi này không thể làm loạn, nếu không, chết cũng không biết chết như thế nào."),t("br"),t("br"),n._v("Cái tên này, vừa đến liền hướng, vừa vặn có thể cho bọn hắn dò đường."),t("br"),t("br"),n._v("Trong lòng nghĩ như vậy, cười lạnh nhìn về phía đi vào rừng đá duỗi ra thanh niên, ai biết đối phương càng đi càng sâu, không bị đến một điểm tổn thương."),t("br"),t("br"),n._v("“Không đúng, hắn biết đường đi, mau đuổi theo...”"),t("br"),t("br"),n._v("Nhìn thấy cái này, ngu nữa cũng rõ ràng, cái tên này, khả năng biết lộ tuyến, cầm đầu Dị Linh tộc nhân hét lớn một tiếng, liền phải đuổi tới đi, còn chưa đi hơn mấy bước, liền cảm thấy từng đạo sát khí nghiền ép mà đến, vội vàng quay đầu, ngay sau đó nhìn thấy một đám Thánh thú, và vài kiện vũ khí, đồng loạt đi tới trước mặt."),t("br"),t("br"),n._v("“Động thủ!”"),t("br"),t("br"),n._v("Lừng lẫy hét lớn một tiếng, chúng thú cùng rất nhiều vũ khí, vọt thẳng tới."),t("br"),t("br"),n._v("Đùng đùng!"),t("br"),t("br"),n._v("Vài phút sau đó, vài đầu Dị Linh tộc nhân, liền toàn bộ nằm trên mặt đất, không còn hô hấp ——"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1759"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1761"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);