(window.webpackJsonp=window.webpackJsonp||[]).push([[861],{1216:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1749-ngoan-nhan-tinh-lai-【-canh-3-】"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1749-ngoan-nhan-tinh-lai-【-canh-3-】"}},[n._v("#")]),n._v(" Chương 1749: Ngoan Nhân tỉnh lại 【 Canh [3] 】")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1749: Ngoan Nhân tỉnh lại "),t("br"),t("br"),n._v("“Không có công kích... Chẳng lẽ, bộ này pháp quyết trình tự, cũng đạt tới Thiên đạo cấp bậc? Cái này... Ai có thể tu luyện đi ra?”"),t("br"),t("br"),n._v("Rất nhanh, vòng qua từng cái đá, đến ở chính giữa bệ đá chỗ, Trương Huyền một hồi kinh ngạc."),t("br"),t("br"),n._v("Hắn dùng chính là Thiên đạo trình tự, không có một tia sai lầm, mà mảnh này rừng đá, lại cũng là như vậy!"),t("br"),t("br"),n._v("Chẳng phải cho thấy, ghi lại bộ này pháp quyết, đã đạt đến Thiên đạo cấp bậc?"),t("br"),t("br"),n._v("“Nhìn tới, Khổng sư thật giống như ta, cũng nắm giữ Thiên đạo thư viện!” Trương Huyền âm thầm phỏng đoán."),t("br"),t("br"),n._v("Công pháp này tám chín phần mười là Khổng sư lưu lại, Thiên đạo công pháp đặc tính, biết rất rõ ràng, loại trừ nắm giữ Thiên đạo chân khí, người ngoài căn bản không có cách nào luyện thành."),t("br"),t("br"),n._v("Nếu không, đã sớm truyền thụ cho mấy cái học sinh, để cho bọn họ nhanh chóng tiến bộ."),t("br"),t("br"),n._v("Người bình thường không luyện được, Khổng sư lại có loại này pháp quyết, nói rõ... Hắn bản thân nhất định nắm giữ Thiên đạo chân khí, có thể tu luyện Thiên đạo công pháp, như vậy trải qua... Còn lại liền không cần nói cũng biết."),t("br"),t("br"),n._v("Rầu rĩ một hồi, biết cần lần nữa nhìn thấy Khổng sư, mới có thể xác nhận, Trương Huyền lại không rầu rĩ, ngưng thần tĩnh tức, chậm rãi hướng trên bệ đá thánh tích thạch bắt tới."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Cảm nhận được bàn tay đến, thánh tích thạch lấp lánh ra hào quang chói sáng, linh hồn trong nháy mắt, giống như là rơi vào dung nham, cực nóng cảm giác, chảy xuôi toàn thân."),t("br"),t("br"),n._v("“Hừ!”"),t("br"),t("br"),n._v("Hắn vu hồn chí tôn Thiên Hỏa thiêu đốt, đều vô sự, điểm ấy cảm giác áp bách, tính là cái gì, bỗng nhiên đè ép xuống, đem hắn chộp vào lòng bàn tay, đồng thời cắn nát đầu ngón tay, máu tươi nhấn tới."),t("br"),t("br"),n._v("Thời gian không dài, thánh tích thạch đình chỉ phản kháng, thuận lợi nhận chủ."),t("br"),t("br"),n._v("Đá nhận chủ, áp lực lập tức biến mất, thay vào đó chính là một loại cảm giác ấm áp, thánh tích trên đá bóng người du động, một cỗ tinh thuần hồn lực bổ sung mà đến, để hồn phách của hắn, chậm chạp tăng thêm."),t("br"),t("br"),n._v("Biết đây là thuế biến cơ duyên, Trương Huyền lập tức khoanh chân ngồi xuống, vu hồn ly thể, cùng trước mắt đá chạm đến."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Hồn lực tràn vào vu hồn, hồn thể lực lượng nhanh chóng tăng thêm, trong nháy mắt đã đột phá Kim Thân cảnh đỉnh phong ràng buộc, hướng tầng thứ cao hơn xung kích."),t("br"),t("br"),n._v("Một canh giờ sau."),t("br"),t("br"),n._v("Đã đạt đến Đại Thánh đệ tam trọng, Tâm Huyết Lai Triều đại viên mãn."),t("br"),t("br"),n._v("Đạt tới loại cảnh giới này, tiến bộ tốc độ cũng thay đổi chậm, cúi đầu nhìn lại, chỉ thấy thánh tích trên đá du động thân ảnh ngừng lại, dường như cũng xuất hiện mệt mỏi."),t("br"),t("br"),n._v("“Nhìn tới thứ này cũng cần năng lượng...”"),t("br"),t("br"),n._v("Nhẹ nhàng cười một tiếng."),t("br"),t("br"),n._v("Cái này thánh tích thạch, có thể tăng lên linh hồn cảnh giới, có điều, cũng cùng linh thạch đồng dạng, chỉ ẩn chứa số lượng nhất định hồn lực, không có khả năng để cho người ta một mực tấn cấp."),t("br"),t("br"),n._v("Bất quá, luyện hóa thứ này, cũng biết, chỉ cần tu dưỡng một đoạn thời gian, lại có thể tiếp tục sử dụng, cùng linh thạch một lần khác biệt."),t("br"),t("br"),n._v("Nếu không, cũng không có khả năng đáng giá Khổng sư đơn độc chứa đựng một điện, tiêu phí như vậy đánh đổi, tiến hành khảo hạch."),t("br"),t("br"),n._v("“Linh hồn đột phá Tâm Huyết Lai Triều, sẽ có phản ứng gì?”"),t("br"),t("br"),n._v("Tâm Huyết Lai Triều cảnh, là từ nơi sâu xa cảm ứng Thiên Cơ, để tu luyện giả, làm ra xu cát tị hung phản ứng, hắn thân có Thiên đạo thư viện, chân khí tu luyện tới cảnh giới này, rất dễ dàng gặp phải phản phệ, hiện tại vu hồn đạt đến, có thể hay không đồng dạng xảy ra vấn đề?"),t("br"),t("br"),n._v("Nghĩ đến điểm này, vu hồn đi vào thân thể, cảm ứng từ nơi sâu xa ý trời."),t("br"),t("br"),n._v("Răng rắc! Răng rắc!"),t("br"),t("br"),n._v("Một đoàn lôi điện kéo tới, không tự chủ được run rẩy."),t("br"),t("br"),n._v("“Quả nhiên...”"),t("br"),t("br"),n._v("Khóe miệng giật một cái, Trương Huyền da mặt biến thành màu đen."),t("br"),t("br"),n._v("Còn tưởng rằng, đã đạt tới đại viên mãn, liền sẽ không có loại tình huống này, không nghĩ tới, không có chút nào thay đổi."),t("br"),t("br"),n._v("Xem ra, đối với hắn mà nói, cho dù là vu hồn, cũng không thể nhìn trộm Thiên Cơ... Nếu không, cùng biển thủ không có gì khác nhau."),t("br"),t("br"),n._v("Tâm Huyết Lai Triều cường đại nhất năng lực, chính là trước thời hạn dự đoán nguy hiểm, năng lực này không thể dùng... Thực lực nhất định giảm bớt đi nhiều."),t("br"),t("br"),n._v("Bất quá, đối với hắn mà nói, cũng không có cái gì phương pháp, coi như lực lượng lần nữa tăng cường."),t("br"),t("br"),n._v("Trước đó, cùng Bất Hủ cảnh đại viên mãn cường giả đối chiến, sử dụng Long Cốt thần thương, miễn cưỡng chiến thắng, muốn đánh giết, nhất định phải đánh lén, hoặc là dùng thủ đoạn khác, mà bây giờ, vu hồn tấn cấp, loại thực lực này, đã có thể nghiền ép."),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Biết trong thời gian ngắn, lại không cách nào tấn cấp, Trương Huyền lật bàn tay một cái, vừa định đem hắn thu vào trữ vật giới chỉ, liền nghe đến một cái thanh âm vội vàng vang lên: “Chủ nhân, cái này viên thánh tích thạch, có thể trước cho ta mượn sử dụng ư?”"),t("br"),t("br"),n._v("“Ngoan Nhân? Ngươi đã tỉnh...”"),t("br"),t("br"),n._v("Trương Huyền ánh mắt sáng lên."),t("br"),t("br"),n._v("Không phải người khác, chính là nuốt lấy xương ngực về sau, rơi vào trạng thái ngủ say Ngoan Nhân."),t("br"),t("br"),n._v("Không nghĩ tới lúc này tỉnh lại."),t("br"),t("br"),n._v("“Thánh tích thạch lực lượng linh hồn tán dật, ta mới tỉnh lại, chủ nhân, có thể hay không đem thứ này trước cho ta mượn sử dụng... Ta hiện tại linh hồn suy yếu, có thứ này, có thể khôi phục nhanh chóng!” Ngoan Nhân nói."),t("br"),t("br"),n._v("“Cái này viên thánh tích trên đá lực lượng, đã bị ta hấp thu hết...” Trương Huyền nhíu nhíu mày."),t("br"),t("br"),n._v("Hắn vừa mới tấn thăng, đem bên trong ẩn chứa lực lượng, hấp thu sạch sẽ, cho đối phương lại như thế nào?"),t("br"),t("br"),n._v("Như là không có linh khí linh thạch, đặt ở trong tay, cũng vô dụng ah."),t("br"),t("br"),n._v("“Thánh tích thạch, chỉ dựa vào tự nhiên hấp thu, dự trữ đầy linh khí, không chỉ cần phải hao phí bao lâu, cũng không đủ xưng là chí bảo, chủ nhân, ngươi trước đem ta lấy ra, lại đem thứ này cho ta!”"),t("br"),t("br"),n._v("Ngoan Nhân nói."),t("br"),t("br"),n._v("“Tốt!” Trương Huyền gật đầu, cổ tay khẽ đảo, Thiên đạo chi sách xuất hiện, thánh tích thạch rơi vào phía trên."),t("br"),t("br"),n._v("Rầm!"),t("br"),t("br"),n._v("Sách mở ra, Ngoan Nhân lộ ra nửa người, ngón tay nhẹ nhàng điểm một cái, thánh tích thạch lập tức bay ra ngoài, đi tới vừa mới bị giết vài đầu Dị Linh tộc nhân trước mặt, quay tròn xoay tròn."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Một lát sau, mặt ngoài bóng người lần nữa du động, lần nữa tản mát ra hào quang chói sáng."),t("br"),t("br"),n._v("“Đây là... Hấp thu người khác linh hồn?”"),t("br"),t("br"),n._v("Trương Huyền đồng tử co rụt lại."),t("br"),t("br"),n._v("Lấy hắn Minh Lý Chi Nhãn, có thể rõ ràng nhìn ra, thánh tích thạch vừa mới hấp thu vừa mới chết đi vài đầu Dị Linh tộc nhân hồn phách, đồng thời đem hắn tinh lọc, biến thành năng lượng tinh thuần."),t("br"),t("br"),n._v("“Ừm, thánh tích thạch, nắm giữ tinh lọc linh hồn, thôn phệ hồn phách tác dụng, đối với linh hồn tu luyện giả tới nói, so Cổ Thánh chí bảo đều muốn quan trọng... Lúc trước, vu hồn sư công hội hồn ấn, chính là từ thứ này luyện chế mà thành!”"),t("br"),t("br"),n._v("Ngoan Nhân giải thích."),t("br"),t("br"),n._v("“Hồn ấn, là cái này luyện chế?”"),t("br"),t("br"),n._v("Trương Huyền sững sờ, ngay sau đó giật mình."),t("br"),t("br"),n._v("Khó trách lần đầu tiên nhìn thấy thánh tích thạch, sẽ có giống như đã từng quen biết cảm giác, thì ra là thế!"),t("br"),t("br"),n._v("Phải biết, hồn ấn, cũng tại trên tay hắn, đang bị chứa đựng tại nhẫn trữ vật bên trong, chỉ bất quá một mực không có sử dụng qua thôi."),t("br"),t("br"),n._v("“Có thể hấp thu linh hồn, thay đổi thành tinh thuần hồn lực... Loại lực lượng này, quả thực rất khủng bố!”"),t("br"),t("br"),n._v("Trương Huyền cuối cùng hiểu được."),t("br"),t("br"),n._v("Khó trách có thể cùng Bồ Đề quả, Cổ Thánh chi khí so sánh, đơn độc chứa đựng một điện, quả nhiên uy lực phi phàm."),t("br"),t("br"),n._v("“Ngươi cầm trước!”"),t("br"),t("br"),n._v("Loại năng lực này nghe tà ác, trên thực tế cũng phải nhìn người sử dụng là ai, tại hảo nhân thủ bên trong, lại tà ác đồ vật, cũng có thể làm ra chuyện tốt, mà tại người tà ác trong tay, khá hơn nữa đồ vật, cũng sẽ là lợi khí giết người."),t("br"),t("br"),n._v("Bởi vậy, cái này thánh tích thạch khảo hạch, xu hướng tại biến thái, có thể nói, tu luyện hay sao Thiên đạo công pháp, liền không cách nào tiến vào bên trong... Thì ra là thế!"),t("br"),t("br"),n._v("Trong cõi u minh, Trương Huyền có loại cảm giác, Khổng sư hình như biết hắn tồn tại đồng dạng, kiện vật phẩm này, rõ ràng là đang cho hắn lưu lại."),t("br"),t("br"),n._v("Đổi lại những người khác, coi như muốn lấy được, cũng lấy không đến tay bên trong."),t("br"),t("br"),n._v("“Đa tạ chủ nhân!”"),t("br"),t("br"),n._v("Ngoan Nhân vội vàng hấp thu thánh tích trong đá lực lượng linh hồn, củng cố hồn lực của mình ——"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1760"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1762"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);