(window.webpackJsonp=window.webpackJsonp||[]).push([[865],{1220:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1753-ngon-xuat-phap-tuy-mot-loi-đinh-xuan-thu-【-canh-thu-nhat-】"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1753-ngon-xuat-phap-tuy-mot-loi-đinh-xuan-thu-【-canh-thu-nhat-】"}},[n._v("#")]),n._v(" Chương 1753: Ngôn xuất pháp tùy, một lời định xuân thu 【 canh thứ nhất 】")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1753: Ngôn xuất pháp tùy, một lời định xuân thu "),t("br"),t("br"),n._v("Đi vào rừng hạnh, thuần phục rất nhiều Thánh thú, không phải người khác, chính là Trương Huyền."),t("br"),t("br"),n._v("Nhiều như vậy Thánh thú, so với năm đại vương giả đều không kém chút nào, tự nhiên không thể bỏ qua, nhìn thấy cành cây cuồng rút, lập tức động lòng trắc ẩn, đem hắn cứu ra, sau đó... Bản thân đánh!"),t("br"),t("br"),n._v("Quả nhiên, Thiên đạo chân khí trợ giúp chữa thương, hơn nữa hỗ trợ loại bỏ tồn tại ám thương, lập tức để đám người kia, đội ơn, cam tâm trở thành thú sủng."),t("br"),t("br"),n._v("Nhìn thấy vây tới cây hạnh, nhìn chằm chằm, tựa như lúc nào cũng sau đó sát thủ đem hắn quất lật, Trương Huyền cũng không lo lắng, mà là cổ tay khẽ đảo, một cái ngọc chất giống như rễ cây xuất hiện tại lòng bàn tay."),t("br"),t("br"),n._v("Rầm!"),t("br"),t("br"),n._v("Nhìn thấy rễ cây, rất nhiều cây hạnh, giống như là thấy được tín vật đồng dạng, đồng loạt tránh ra một cái lối đi."),t("br"),t("br"),n._v("“Trước không vội, các ngươi có thể cường đại như vậy, hẳn là nghe Khổng sư giảng bài, như vậy, ta cũng cho các ngươi nói một tiết!”"),t("br"),t("br"),n._v("Chần chờ một chút, Trương Huyền truyền âm."),t("br"),t("br"),n._v("Trước mắt mấy cây cây, lợi hại như vậy, bởi vì nghe Khổng sư giảng bài nguyên nhân, trong lòng của hắn nghi ngờ, đối phương cũng tu luyện chính là trời đạo công pháp, đã cây bọn họ nghe qua khóa, nhất định có thể khá là đi ra, chương trình học của chính mình tốt, còn là cái trước tốt."),t("br"),t("br"),n._v("Cũng liền có thể tốt hơn đánh giá ra, Khổng sư tu luyện, đến cùng phải hay không Thiên đạo cấp bậc."),t("br"),t("br"),n._v("“Rầm?”"),t("br"),t("br"),n._v("Lay động cành cây, rất nhiều cây hạnh từng cái tràn đầy nghi ngờ."),t("br"),t("br"),n._v("Cầm trong tay tín vật, đều để ngươi đi qua, nghĩ như thế nào giảng bài?"),t("br"),t("br"),n._v("“Phương pháp tu luyện, không chỉ người, thú, cây cối cũng có thể tiến bộ...”"),t("br"),t("br"),n._v("Không để ý tới nghi ngờ của bọn nó, Trương Huyền thanh âm truyền đi qua."),t("br"),t("br"),n._v("“Phần phật?”"),t("br"),t("br"),n._v("Cây hạnh vừa mới bắt đầu, còn cảm thấy kỳ lạ, nghe phút chốc, hưng phấn mà khoa tay múa chân, cũng lại kìm lại không được."),t("br"),t("br"),n._v("Đối phương đối tu luyện giảng giải, cùng Khổng sư đồng dạng thâm ảo, đối bọn chúng tới nói, chính giữa chỗ hiểm!"),t("br"),t("br"),n._v("Nói cách khác... Kẻ trước mắt này, thoạt nhìn cũng không như thế nào, nhưng ở giảng bài bên trên, vậy mà cùng Khổng sư đồng dạng."),t("br"),t("br"),n._v("Hắn bên này giảng bài, Nhan Tiết, rất nhiều Dị Linh tộc nhân, tất cả đều nhíu mày."),t("br"),t("br"),n._v("Vốn cho rằng, cái tên này đi vào, liền sẽ cùng trước đó Thánh thú đồng dạng, bị đánh mặt mũi bầm dập, lại không nhân loại bộ dáng, làm sao đều không có nghĩ đến... Rất nhiều cây hạnh cũng không động thủ, ngược lại từng cái ngây người..."),t("br"),t("br"),n._v("“Hắn ngồi xuống, hình như đang nói cái gì, chẳng lẽ... Đang giảng bài?”"),t("br"),t("br"),n._v("Không biết ai hô lên."),t("br"),t("br"),n._v("Nghe nói như thế, đám người quả nhiên phát hiện, bị vây ở rất nhiều cây ở giữa thanh niên, miệng lúc mở lúc đóng, không biết đang nói cái gì, thoạt nhìn như là giảng bài, lại giống là tại tới đối thoại."),t("br"),t("br"),n._v("Rầm!"),t("br"),t("br"),n._v("Khiếp sợ còn không có kết thúc, lại có mấy cây cây chạy tới, trên người tất cả cành cây, đồng thời kề sát ở mặt đất, một bộ thành kính bộ dáng."),t("br"),t("br"),n._v("“Bọn chúng thế nhưng là nghe qua Khổng sư chương trình học cây... Giờ phút này nghe cái tên này khóa, còn cái bộ dáng này?”"),t("br"),t("br"),n._v("Tất cả mọi người ngây người."),t("br"),t("br"),n._v("Nghe qua Khổng sư chương trình học cây cối, hơn nữa mỗi một gốc tu vi đều đạt đến Bất Hủ cảnh đại viên mãn... Cường đại như thế cây, làm sao lại nghe cái tên này lời nói, thoạt nhìn biết điều như vậy?"),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Ngay tại khiếp sợ, mặt đất lay động, ngay sau đó mọi người thấy trước đó rơi trên mặt đất lá khô, cũng từng cái đứng lên, một đường chạy chậm đi tới trước mặt, như si như say."),t("br"),t("br"),n._v("“Lá cây, cũng có thể nghe giảng?”"),t("br"),t("br"),n._v("Trong chốc lát, đám người cảm thấy tam quan toàn bộ lật đổ."),t("br"),t("br"),n._v("Cây có thể nghe giảng, ngược lại cũng thôi, dù sao, Khổng sư ở đây giảng bài nhiều năm, thay đổi một cách vô tri vô giác phía dưới, sinh ra linh tính, không gì đáng trách, có thể những này lá cây... Ngươi đều khô cạn, bất cứ lúc nào cũng sẽ biến thành bùn nhão... Cũng chạy tới nghe giảng bài, có muốn hay không khoa trương như vậy?"),t("br"),t("br"),n._v("“Những này cây, đều đạt tới Bất Hủ cảnh cảnh giới đại viên mãn... Làm sao lại lá rụng? Mấu chốt rơi xuống lá cây, làm sao lại khô héo?”"),t("br"),t("br"),n._v("Nhan Tiết đột nhiên mở miệng."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Sau lưng đám người, đồng thời sửng sốt."),t("br"),t("br"),n._v("Đúng a, cây đều đạt tới loại này cấp bậc, mỗi một cây cành cây, lá cây, đều có thể so với Đại Thánh chí bảo, xuân, hạ, thu, đông bốn mùa, lại thế nào biến hóa, cũng khẳng định không ảnh hưởng tới đi!"),t("br"),n._v("“Lá rụng, bao quát khô héo, chỉ là một loại hình thức, nói cách khác... Chờ đến mùa xuân, những này lá cây, sẽ tự động bay trở về cành cây, cũng không phải là mọc ra mới!”"),t("br"),t("br"),n._v("Giống như là nhìn ra cái gì, Nhan Tiết giải thích nói."),t("br"),t("br"),n._v("Vừa dứt lời, quả nhiên thấy ngay tại nghe giảng bài rất nhiều khô héo lá cây, đột nhiên lắc lư mấy lần, đồng loạt bay lên đầu cành, nguyên bản khô vàng sắc, lần nữa trở nên xanh biếc."),t("br"),t("br"),n._v("Cây cối cũng tựa như lần nữa phấn chấn thanh xuân."),t("br"),t("br"),n._v("“Ngôn xuất pháp tùy, một lời định xuân thu... Đây là Khổng sư mới có năng lực...”"),t("br"),t("br"),n._v("Nuốt ngụm nước bọt, Chư Tử bách gia một vị thanh niên, nghĩ tới điều gì, đồng tử co rút lại."),t("br"),t("br"),n._v("Truyền thuyết năm đó Khổng sư năm đó du lịch, nhìn thấy cổ thụ chết héo, lòng sinh thương hại, dưới tàng cây giảng bài, một lát sau, cổ thụ nở hoa, cây khô gặp mùa xuân, cây này, lần nữa mọc ra lá non, thậm chí kết ra trái cây, để hắn nhấm nháp."),t("br"),t("br"),n._v("Rất nhiều môn sinh, cảm thấy khiếp sợ, lưu lại “Một lời định xuân thu” điển cố..."),t("br"),t("br"),n._v("Chẳng lẽ, cái tên này, cũng có thể làm được như vậy?"),t("br"),t("br"),n._v("“Đây không phải một lời định xuân thu! Khổng sư giảng giải cây khô, đã tử vong, mà những này, đều là Bất Hủ cảnh cường giả, sao có thể nói làm một?”"),t("br"),t("br"),n._v("Nhan Tiết hàm răng cắn chặt."),t("br"),t("br"),n._v("Bọn họ Chư Tử bách gia, giữ lại có Khổng sư hoàn chỉnh nhất truyền thừa, kết quả... Đến nơi này, cái gì cũng không thể làm, chỉ có thể chờ đợi lấy, cái tên này lại mỗi một lần đều làm ra kinh người việc làm..."),t("br"),t("br"),n._v("Để hắn cảm nhận được nồng đậm nhục nhã."),t("br"),t("br"),n._v("“Nhan Tiết... Những này cây cùng lá cây, ngay tại nghe giảng bài, chúng ta có thể hay không nhân cơ hội đi qua? Nếu không, phải chờ tới lúc nào?”"),t("br"),t("br"),n._v("Một thanh niên truyền âm."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Dừng lại, Nhan Tiết lộ ra do dự."),t("br"),t("br"),n._v("Không sai, ngăn cản bọn họ tiến lên lớn nhất chướng ngại chính là những này cây hạnh, giờ phút này, những người này, đang bị cái này gọi Trương Huyền gia hỏa thu hút, đối bọn hắn tới nói, quả thực là cái cơ hội rất tốt."),t("br"),t("br"),n._v("“Ngươi đi lên thử một chút...”"),t("br"),t("br"),n._v("Dừng lại một chút, vẫn là có chút không yên lòng, chỉ hướng lời mới vừa nói người, nói."),t("br"),t("br"),n._v("Thanh niên gật gật đầu, nhấc chân đi thẳng về phía trước."),t("br"),t("br"),n._v("Sưu sưu sưu!"),t("br"),t("br"),n._v("Còn chưa tới đến trước mặt, một hồi nghẹn ngào thanh âm nhanh chóng vang lên, ngay sau đó, một mảng lớn lá cây, đập vào mặt!"),t("br"),t("br"),n._v("Phốc!"),t("br"),t("br"),n._v("Sau một khắc, đã bị mấy chục cái lá cây cắm ở ngực, máu tươi phun mạnh!"),t("br"),t("br"),n._v("Chỉ thoáng cái, liền trọng thương, liền phản kháng đều không có làm được!"),t("br"),t("br"),n._v("Yên tĩnh!"),t("br"),t("br"),n._v("Không nghĩ tới lá cây cũng hung hãn như vậy, xung quanh lại không ai dám tiến về phía trước một bước."),t("br"),t("br"),n._v("Nhan Tiết cũng khóe miệng run rẩy, không nói ra nửa câu tới."),t("br"),t("br"),n._v("Còn tưởng rằng là một cơ hội, không nghĩ tới, những này lá cây, cho dù nghe giảng bài, cũng không có khả năng để hắn đặt chân nửa bước."),t("br"),t("br"),n._v("“Gió nổi lên...”"),t("br"),t("br"),n._v("Trong đám người hoàn toàn yên tĩnh, chính không biết như thế nào cho phải, nhất đạo gió nhẹ lướt nhẹ qua mặt mà đến, ngay sau đó, càng lúc càng lớn, thổi đến lá cây, bay phất phới."),t("br"),t("br"),n._v("Mặt đất lần nữa đung đưa."),t("br"),t("br"),n._v("“Mau nhìn!”"),t("br"),t("br"),n._v("Đám người vội vàng ngẩng đầu, ngay sau đó nhìn thấy cách đó không xa đài cao, chẳng biết lúc nào, thả ra hào quang chói sáng, dường như có bóng người huyền phù trên không, ngay sau đó một cái hùng vĩ thanh âm, chậm rãi vang lên."),t("br"),t("br"),n._v("“Là... Thánh âm! Thánh âm, rốt cuộc đã đến...”"),t("br"),t("br"),n._v("Nhan Tiết mắt sáng rực lên, kích động thân thể run rẩy."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1764"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1766"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);