(window.webpackJsonp=window.webpackJsonp||[]).push([[869],{1224:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1757-chu-đien-xuat-hien-【-canh-thu-nhat-】"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1757-chu-đien-xuat-hien-【-canh-thu-nhat-】"}},[n._v("#")]),n._v(" Chương 1757: Chủ điện xuất hiện 【 canh thứ nhất 】")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1757: Chủ điện xuất hiện "),t("br"),t("br"),n._v("“Không phải thất bại trong gang tấc, ngươi chẳng lẽ không có phát hiện, là Viên Đào, tận lực áp chế tu vi? Tạm thời không làm đột phá?”"),t("br"),t("br"),n._v("Ngẩng đầu nhìn về phía trên không huyền phù thiếu niên, Lạc Nhược Hi nói."),t("br"),t("br"),n._v("“Tạm thời không đột phá?”"),t("br"),t("br"),n._v("Trương Huyền mang theo nghi ngờ."),t("br"),t("br"),n._v("“Không sai!” Lạc Nhược Hi gật gật đầu: “Cổ Thánh, một lần xung kích không thành công, lần thứ hai, độ khó sẽ gia tăng gấp đôi, nguyên nhân chính là như vậy, rất nhiều người xung kích, đều sẽ lựa chọn một lần thành công... Nhưng mà, như vậy thành công, thường thường là yếu nhất!”"),t("br"),t("br"),n._v("“Xung kích Cổ Thánh, trên thực tế là đang đối kháng với Thiên đạo, muốn siêu thoát đến Thiên đạo bên ngoài! Một lần thành công, mặc dù cũng có thể làm được điểm ấy, có thể cùng Thiên đạo đối kháng thời gian quá ít, lực lượng không thể thật tốt nắm chắc, trở nên càng thêm phù hợp, càng thêm tự nhiên! Lần đầu tiên không thành công, tích lũy một đoạn thời gian, lần nữa xung kích, thiên kiếp độ khó là tăng lên gấp đôi, nhưng ngươi đối lực lượng vận dụng, cũng sẽ tăng thêm không ít, lại có thể xông qua... Đối về sau tu vi tăng thêm, sẽ có rất lớn tiến bộ!”"),t("br"),t("br"),n._v("Lạc Nhược Hi nói."),t("br"),t("br"),n._v("“Cái này... Chẳng lẽ cùng Thiên Nhân Thánh giả cùng Khổng sư đột phá Đại Thánh phương pháp đồng dạng? Cũng là một loại rèn luyện?”"),t("br"),t("br"),n._v("Trương Huyền nhịn không được nói."),t("br"),t("br"),n._v("Đột phá Thánh vực, có Phong Thánh giải, trời nhận Thánh giả; Đột phá Đại Thánh, có thượng phẩm chi pháp, tích lũy càng thêm hùng hậu, lực lượng mạnh hơn. Chẳng lẽ, cái này cái gọi là xung kích Cổ Thánh, cũng có tương tự?"),t("br"),t("br"),n._v("“Cũng có thể nói như vậy, xung kích Cổ Thánh, liền giống như sóng lớn đãi cát, đãi số lần càng nhiều, chân khí cũng liền càng hết sức nhỏ, càng ngưng thực! Năm đó Khổng sư, liền từng liên tục ba lần xung kích Cổ Thánh, mà không đột phá! Lần thứ tư một khi công thành, thiên hạ lại không địch thủ. Lúc này mới thành tựu vạn thế chi sư, công che thiên hạ!”"),t("br"),t("br"),n._v("Hai tay chắp sau lưng, Lạc Nhược Hi ánh mắt thâm thúy: “Viên Đào, vừa rồi tuy là không có xung kích thành công, trên thực tế thân thể lại hấp thu Cổ Thánh chi huyết, đạt đến Cổ Thánh cấp bậc! Nói cách khác, hiện tại đã có Cổ Thánh chi thể! Mặc dù không phải Cổ Thánh, nhưng đồng dạng vừa mới đột phá Cổ Thánh, đã không phải là đối thủ!”"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Trương Huyền chấn động."),t("br"),t("br"),n._v("Viên Đào Cổ Thánh chi thể, đã đủ để cho người ta kinh ngạc, Khổng sư vậy mà ba lần xung kích Cổ Thánh mà không đột phá..."),t("br"),t("br"),n._v("Tu luyện giả, người người đều muốn bước ra cảnh giới này, thay đổi sinh mệnh cấp độ, trở nên rong ruổi thiên hạ, mà hắn lại có thể cố nén loại này cám dỗ... Nhưng đối tâm cảnh rèn luyện, liền không tầm thường."),t("br"),t("br"),n._v("Khó trách có thể dẫn dắt nhân tộc, đi đến hiện tại, phần này năng lực, quả thực để hắn mặc cảm."),t("br"),t("br"),n._v("“Không đúng, những bí ẩn này, người biết cũng không nhiều, Viên Đào làm sao... Sẽ làm như vậy?”"),t("br"),t("br"),n._v("Cảm khái một tiếng, đột nhiên sửng sốt."),t("br"),t("br"),n._v("Loại này xung kích Cổ Thánh phương pháp, ngay cả Dương sư, Hưng kiếm thánh bọn người không rõ ràng, Lạc Nhược Hi bởi vì thân thế đặc thù, biết những này, không gì đáng trách, có thể Viên Đào, cùng bản thân từ phía trên huyền vương quốc đi tới, làm sao lại rõ ràng những này, cố nén cám dỗ, mà lựa chọn dừng chân không tiến?"),t("br"),t("br"),n._v("“Hắn nuốt lấy Khổng sư lạc ấn, đối tu luyện lý giải, thậm chí đều vượt qua bảy mươi hai Thánh, biết những này rất bình thường!” Lạc Nhược Hi giải thích một câu, nói: “Đáng tiếc, cái này lạc ấn, luyện hóa về sau, rất dễ dàng nhận Khổng sư ảnh hưởng, đối ngươi trưởng thành bất lợi, nếu không, khẳng định cho ngươi nuốt!”"),t("br"),t("br"),n._v("“Trưởng thành bất lợi?”"),t("br"),t("br"),n._v("Trương Huyền cười khổ."),t("br"),t("br"),n._v("Nếu như còn có thể đột phá, hơn nữa lợi hại như vậy, còn quản cái gì trưởng thành không trưởng thành?"),t("br"),t("br"),n._v("“Ngươi là trời nhận danh sư, khẳng định muốn đi cùng Khổng sư không giống con đường, đi con đường của người khác, nhiều nhất trở thành cái kế tiếp bảy mươi hai Thánh, là vĩnh viễn không có khả năng vượt qua!” Thấy hắn bộ dáng này, Lạc Nhược Hi nói."),t("br"),t("br"),n._v("“Ừm!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("Muốn vượt qua tiền nhân, đầu tiên muốn phủ định tiền nhân."),t("br"),t("br"),n._v("Một mực nghe theo, sẽ chỉ làm bản thân giẫm chân tại chỗ."),t("br"),t("br"),n._v("Liền giống như bảy mươi hai Thánh, mỗi một cái đều là kinh tài tuyệt diễm nhân vật tuyệt đỉnh, thành tựu cũng vô khả hạn lượng, nhưng bởi vì hoàn mỹ tiếp nhận Khổng sư truyền thừa, cuối cùng đều không có vượt qua."),t("br"),t("br"),n._v("Có thể... Nếu như không chấp nhận truyền thừa của hắn, cũng có khả năng, liền Cổ Thánh đều không đạt tới, cả một đời chỉ có thể long đong vô vi."),t("br"),t("br"),n._v("Lựa chọn ra sao, chỉ ở nhất niệm."),t("br"),t("br"),n._v("Xoạt xoạt! Răng rắc! Răng rắc!"),t("br"),t("br"),n._v("Trong lòng đang cảm xúc, đột nhiên nhìn thấy trước mắt Hạnh Đàn, bắn ra một đạo hào quang, thẳng tắp phóng hướng thiên không."),t("br"),n._v(" Ngay sau đó, lại có năm đạo hướng lên trên không bắn ra, sáu đạo ánh sáng hội tụ, màu xanh đậm bầu trời, giống như là không chịu nổi lực lượng, nhất đạo đen kịt khe hở, chậm chạp xuất hiện."),t("br"),t("br"),n._v("Khe hở đằng sau, một cái càng thêm to lớn cung điện, chậm rãi hiện lên."),t("br"),t("br"),n._v("“Là Khổng miếu chủ điện, chủ điện xuất hiện...”"),t("br"),t("br"),n._v("Nhậm Thanh Viễn một tiếng la lên, thanh âm tràn đầy kích động."),t("br"),t("br"),n._v("Khổng miếu cho đến trước mắt, chỉ xuất hiện sáu nơi phân điện, chủ điện chưa hề xuất hiện qua, giờ phút này, sáu nơi toàn bộ kích hoạt, hội tụ mà xuất lực lượng, phá vỡ cuối cùng phong ấn, chủ điện... Hiện lên!"),t("br"),t("br"),n._v("Trong truyền thuyết có thể ảnh hưởng tình thế của đại lục Xuân Thu đại điển, chính là ở đây!"),t("br"),t("br"),n._v("“Đi qua!”"),t("br"),t("br"),n._v("Liếc mắt nhìn trên không hiện lên chủ điện, lại liếc mắt nhìn, cách đó không xa Viên Đào cùng Trương Huyền, Nhan Tiết mặc dù tràn đầy không cam lòng, nhưng cũng biết, lại không có cách nào thay đổi, nói một tiếng, thẳng tắp hướng vết nứt không gian xông tới."),t("br"),t("br"),n._v("Rất nhiều Dị Linh tộc nhân, cũng theo sát tới."),t("br"),t("br"),n._v("“Các ngươi đi trước đi!”"),t("br"),t("br"),n._v("Biết chủ điện chỉ là xuất hiện, còn không có mở ra, Trương Huyền cũng không lo lắng, thấy Nhậm sư đám người rục rà rục rịch, cười cười, bàn giao một câu, tiếp tục xem hướng cách đó không xa Viên Đào."),t("br"),t("br"),n._v("Chính mình cái này học sinh, giờ phút này lực lượng đã triệt để củng cố, thiên kiếp cũng bắt đầu lui tán."),t("br"),t("br"),n._v("“Lão sư!”"),t("br"),t("br"),n._v("Mở mắt ra, cảm nhận được trên người lực lượng, Viên Đào tràn đầy kích động, hướng về phía trước mấy bước đi tới trước mặt, trực tiếp quỳ mọp xuống đất."),t("br"),t("br"),n._v("Bị phong bế lục thức, tại lạc ấn vào nhập thể nội, cũng đã phá vỡ, biết có thể có lực lượng bây giờ, toàn bộ nhờ lão sư trợ giúp."),t("br"),t("br"),n._v("“Ừm!” Trương Huyền thoả mãn gật gật đầu."),t("br"),t("br"),n._v("Không thể không nói, cái cơ duyên này thực tế quá lớn, đem Khổng sư lưu lại lạc ấn nuốt, tu vi từ đó một lần đột phá, hắn giờ phút này, không chỉ vượt qua Triệu Nhã, Ngụy Như Yên, thậm chí ngay cả mình, đều tạm thời không đuổi theo kịp."),t("br"),t("br"),n._v("“Ngươi mặc dù có Cổ Thánh chi thân, nhưng tấn cấp quá nhanh, tâm cảnh có chút phù phiếm, ta hiện tại truyền thụ cho ngươi một bộ tăng cường tâm cảnh công pháp... Phối hợp cái này viên Bồ Đề quả, sẽ có không tưởng tượng được hiệu quả!”"),t("br"),t("br"),n._v("Cổ tay khẽ đảo, Trương Huyền lấy ra một cái Bồ Đề quả đưa tới."),t("br"),t("br"),n._v("Tuy là dung hợp Khổng sư giảng bài lạc ấn, nhưng tâm cảnh còn cần bản thân tăng lên, một bước một cái dấu chân mới được."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Nhận lấy trái cây, Viên Đào nuốt vào, quả nhiên cảm thấy tâm cảnh nhanh chóng tăng vọt."),t("br"),t("br"),n._v("Đổi lại Triệu Nhã đám người, nuốt Bồ Đề quả, khả năng còn cần sớm chuẩn bị, điều chỉnh trạng thái, hiện tại hắn đã có Cổ Thánh chi thể, căn bản không cần những thứ này."),t("br"),t("br"),n._v("Hô hô hô!"),t("br"),t("br"),n._v("Giao phó xong Viên Đào, Trương Huyền cổ tay lần nữa khẽ đảo, Triệu Nhã, Ngụy Như Yên, Lạc Thất Thất đám người đồng thời xuất hiện ở trước mắt."),t("br"),t("br"),n._v("“Chủ điện mở ra, các ngươi là nguyện ý cùng ta cùng một chỗ tìm kiếm cơ duyên, còn là tiếp tục tu luyện?”"),t("br"),t("br"),n._v("Khổng miếu chủ điện, vài vạn năm mới mở ra lần này, đối với bất kỳ người nào tới nói đều có cực lớn cơ duyên, ngàn vạn không thể bởi vì tu luyện mà bỏ qua."),t("br"),t("br"),n._v("“Chúng ta nếu đi qua nhìn một chút!”"),t("br"),t("br"),n._v("Triệu Nhã gật đầu."),t("br"),t("br"),n._v("“Ta cũng vậy!”"),t("br"),t("br"),n._v("Hai người khác, cũng đồng thời lên tiếng, nhìn về phía trên không cung điện, ánh mắt hừng hực."),t("br"),t("br"),n._v("Thấy Trương Huyền vậy mà từ không gian gấp, đem Lạc Thất Thất thả ra, Lạc Nhược Hi liếc qua, cũng không nói chuyện."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1768"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1770"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);