(window.webpackJsonp=window.webpackJsonp||[]).push([[87],{441:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1055-lac-nhuoc-hi-đe-nghi"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1055-lac-nhuoc-hi-đe-nghi"}},[n._v("#")]),n._v(" Chương 1055: Lạc Nhược Hi đề nghị")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1055: Lạc Nhược Hi đề nghị"),t("br"),t("br"),n._v("“Không thể?”"),t("br"),t("br"),n._v("Mọi người nhìn qua."),t("br"),t("br"),n._v("“Ừm, cái này Hắc Yên Trần Sa, đem đường phía trước toàn bộ bao phủ, có lẽ cùng trước đó dung nham đồng dạng, thuộc về khảo nghiệm, không thông qua lời nói, liền không có cách nào đi vào càng sâu chỗ, vĩnh viễn chỉ có thể ở bên ngoài quanh quẩn một chỗ.”"),t("br"),t("br"),n._v("Hàn hội trưởng nói."),t("br"),t("br"),n._v("“Ừm!” Nghe được hắn, Trương Huyền nhẹ gật đầu."),t("br"),t("br"),n._v("Đúng là như thế."),t("br"),t("br"),n._v("Loại này phong kín khảo nghiệm, chỉ có thông qua cửa này, cửa ải tiếp theo trận pháp mới có thể kích hoạt, không cách nào thông qua, chỉ có thể dừng lại tại nguyên chỗ."),t("br"),t("br"),n._v("Muốn đùa nghịch kỹ xảo, lừa gạt qua, rõ ràng là không thể nào."),t("br"),t("br"),n._v("Thiết kế cái này di tích, có thể là Khưu Ngô cổ thánh, loại này siêu cấp cường giả, làm sao có thể cho bọn hắn sơ hở có thể chui?"),t("br"),t("br"),n._v("Cho dù có sơ hở, thực lực của bọn hắn cũng không đủ chui qua, làm không cẩn thận còn có thể rơi vào lợi hại hơn khảo nghiệm, Sinh Tử không khỏi bản thân khống chế."),t("br"),t("br"),n._v("“Căn cứ dấu vết đến xem, Chương Dẫn Khâu đám người khẳng định là đi vào trong này, chính là... Chúng ta làm sao đi vào?”"),t("br"),t("br"),n._v("Tại nguyên chỗ quay một vòng, Ngô sư mày nhíu lại thành mụn nhọt."),t("br"),t("br"),n._v("Phát hiện lão viện trưởng bội kiếm, cũng phát hiện một chút chiến đấu dấu vết, rõ ràng hướng bên này tới."),t("br"),t("br"),n._v("Chỉ là, trước mắt cái này cát bụi, liền tuyệt phẩm Linh khí, đều có thể trong nháy mắt ăn mòn, tu luyện giả căn bản vào không được, cho dù có thể, đoán chừng cũng không cách nào xâm nhập, chớ nói chi là đi ra phạm vi."),t("br"),t("br"),n._v("Không để ý tới mọi người cuống cuồng, Trương Huyền mày nhăn lại, nhìn kỹ đi qua."),t("br"),t("br"),n._v("Trước mắt Hắc Yên Trần Sa, cùng trước đó lôi đình chi hải đồng dạng, đồng dạng thuộc về một cái đại trận, phạm vi quá lớn, cho dù biết thiếu hụt, bị giới hạn thực lực, cũng không cách nào phá giải."),t("br"),t("br"),n._v("Nghiên cứu một hồi, đồng dạng bất đắc dĩ lắc đầu."),t("br"),t("br"),n._v("Cùng bọn hắn kết quả đồng dạng, chỉ có xông vào đi qua, căn bản không có những biện pháp khác."),t("br"),t("br"),n._v("“Chúng ta một đường đến nay, không phải gặp được Dị Linh tộc nhân, chính là bị bọn họ mai phục... Hiện tại loại trừ cái này Hắc Yên Trần Sa, lại không có nửa điểm bóng người, có thể hay không... Nếu ta đoán không lầm, chúng ta tìm kiếm phương hướng là sai!”"),t("br"),t("br"),n._v("Mọi người ở đây nghĩ mãi không ra thời điểm, Lạc Nhược Hi đột nhiên nói."),t("br"),t("br"),n._v("“Truy tra phương hướng?” Mọi người không hiểu."),t("br"),t("br"),n._v("“Không sai, chúng ta bây giờ là đuổi theo lão viện trưởng Chương Dẫn Khâu dấu vết lưu lại đi, nhưng mà... Ngộ nhỡ thật có Dị Linh tộc nhân tại chúng ta phía trước thông qua, những này dấu vết, chưa chắc là thật... Chúng ta có hay không có thể tìm Dị Linh tộc nhân dấu vết tiến lên, như vậy trải qua, có khả năng càng thêm an toàn!”"),t("br"),t("br"),n._v("Lạc Nhược Hi đem phân tích của mình nói ra."),t("br"),t("br"),n._v("“Cái này...” Mọi người tất cả đều sửng sốt."),t("br"),t("br"),n._v("“Không sai, Lạc sư nói rất đúng!”"),t("br"),t("br"),n._v("Trương Huyền ánh mắt sáng lên."),t("br"),t("br"),n._v("Bọn họ đi vào di tích về sau, một mực tìm lão viện trưởng dấu vết lưu lại, mặc dù cách làm này không sai, có thể ngộ nhỡ những này dấu vết là Dị Linh tộc nhân bày ra bẫy rập, liền nguy rồi."),t("br"),t("br"),n._v("Dị Linh tộc Thanh Điền nhất mạch thập đại vương giả, hắn đã hại chết bảy cái, thu phục một cái, lợi hại nhất Thiên Diệp Vương, tại Độc điện mặc dù chưa từng gặp mặt, lại biết đáng sợ... Dựa theo tình huống bình thường, nhận được bản đồ, khẳng định cũng tới đến nơi đây..."),t("br"),t("br"),n._v("Lúc này lại một chút tung tích cũng không phát hiện, nói không kỳ quái là giả!"),t("br"),t("br"),n._v("Xếp hạng thứ ba, thứ tư Ngọc Diệp Vương, Thạch Diệp Vương đều tới, hơn nữa như vậy khó có thể đối phó, đứng hàng thứ nhất, thứ hai, khẳng định càng thêm đáng sợ."),t("br"),t("br"),n._v("Một mực không ra tay, nói rõ... Bọn họ dựa dẫm bản đồ, tất nhiên trước thời hạn tiến vào bên trong, so với chính mình đám người tốc độ phải nhanh!"),t("br"),t("br"),n._v("Đã như vậy, bọn họ đi con đường khẳng định là an toàn nhất, có thể dễ dàng thông qua."),t("br"),t("br"),n._v("“Điều tra thoáng cái...”"),t("br"),t("br"),n._v("Thân thể nhoáng một cái, bay lên, Trương Huyền Minh Lý Chi Nhãn nhúc nhích, hướng bốn phía nhìn lại."),t("br"),t("br"),n._v("Trước đó lấy lão viện trưởng dấu vết lưu lại làm mục tiêu, cái khác không chút chú ý, rất nhiều nơi không chú ý, giờ phút này cẩn thận quan sát, rất nhanh để hắn phát hiện không đúng."),t("br"),t("br"),n._v("Nơi này thật có Dị Linh tộc nhân đi qua, hơn nữa ngay tại mấy canh giờ trước đó."),t("br"),t("br"),n._v("“Phùng Huân, phiền phức dùng một chút ngươi Vô Cấu kính!”"),t("br"),t("br"),n._v("Rơi trên mặt đất, Trương Huyền đưa tay."),t("br"),t("br"),n._v("Những này Dị Linh tộc nhân, cố ý tiêu trừ dấu vết, thực lực của hắn bây giờ quá thấp, Minh Lý Chi Nhãn phát huy không được cực hạn, chỉ là mơ hồ có thể đánh giá ra có người đi qua, tìm không thấy cụ thể đường đi."),t("br"),t("br"),n._v("Trong vài canh giờ lời nói, Chiến sư đường Vô Cấu kính, hẳn là còn có hiệu quả."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Phùng Huân gật đầu đem đồ vật đưa tới, hỏi rõ ràng cách dùng, Trương Huyền bắt chước làm theo, cắn nát ngón tay chỉ đi lên."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Vô Cấu kính lóe lên hai lần, chỉ rõ phương hướng."),t("br"),t("br"),n._v("Theo phương hướng đi tới, cẩn thận phân biệt một hồi, Trương Huyền nhìn về phía mọi người: “Dị Linh tộc nhân quả thực đi ở phía trước đi qua, hẳn là từ nơi này tiến vào Hắc Yên Trần Sa...”"),t("br"),t("br"),n._v("“Từ nơi này đi vào? Bọn họ chẳng lẽ không sợ cái này?”"),t("br"),t("br"),n._v("Một vị Chiến sư nghi ngờ hỏi."),t("br"),t("br"),n._v("“Ta cũng không biết...” Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Căn cứ Vô Cấu kính chỉ ra phương hướng, mấy canh giờ con đường phía trước qua Dị Linh tộc nhân, chính là từ nơi này tiến vào."),t("br"),t("br"),n._v("Bất quá, vừa mới cũng cẩn thận nghiên cứu, nơi này đồng dạng không phải thiếu hụt, bão cát vẫn như cũ đáng sợ, đối phương đến cùng là như thế nào đi vào, như thế nào lại lông tóc không thương, liền xem như hắn, cũng nhìn không ra tới."),t("br"),t("br"),n._v("“Ta thử một chút, có lẽ nơi này bão cát thoạt nhìn đáng sợ, trên thực tế có cái ẩn hình thông đạo...”"),t("br"),t("br"),n._v("Hít sâu một hơi, Ngô sư đi tới."),t("br"),n._v("“Cẩn thận một chút...”"),t("br"),t("br"),n._v("Sửng sốt một chút, Hàn hội trưởng dặn dò một tiếng."),t("br"),t("br"),n._v("Loại sự tình này, tại trong trận pháp cũng là bình thường xuất hiện."),t("br"),t("br"),n._v("Rõ ràng là sinh môn, từ bên ngoài nhìn, mười phần đáng sợ, dùng bất kỳ thủ đoạn nào đều đo lường không ra, chỉ có tự thể nghiệm, mới có thể tìm ra."),t("br"),t("br"),n._v("“Ừm!” Nhẹ gật đầu, Ngô sư cũng không nhiều lời, hít sâu một hơi, chân khí trong cơ thể bừng lên, tại bên ngoài thân tạo thành một cái màu bạc nhạt khôi giáp."),t("br"),t("br"),n._v("Thánh cấp võ kỹ, Chân Long ngân giáp!"),t("br"),t("br"),n._v("Đây là hắn mạnh nhất phòng ngự võ kỹ, có thể đem toàn thân đều bao phủ ở bên trong, dùng để đối phó cái này Hắc Yên Trần Sa không thể thích hợp hơn."),t("br"),t("br"),n._v("Nhấc chân đi vào trong."),t("br"),t("br"),n._v("Răng rắc! Răng rắc! Răng rắc!"),t("br"),t("br"),n._v("Đi vào cát bụi, liền nghe đến va chạm thanh âm không ngừng truyền đến, như là mưa to đánh lấy chuối tây."),t("br"),t("br"),n._v("Đi về phía trước bảy, tám bước, Ngô sư liền khuôn mặt trắng bệch lui đi ra, chân khí hình thành khôi giáp, giờ phút này đã vỡ vụn, cả người vẻ mặt trắng bệch, đi đường có chút lảo đảo."),t("br"),t("br"),n._v("“Không được, chỗ này cát bụi, không những không yếu, còn càng thêm đáng sợ, căn bản là không có cách thông qua...”"),t("br"),t("br"),n._v("Điều tức một hồi, hoàn toàn khôi phục lại, Ngô sư lắc đầu."),t("br"),t("br"),n._v("Cát bụi đối chân khí tiêu hao cực lớn, dung nham còn có thể đi vào bốn mươi mét, mà ở trong đó, bảy, tám bước cho dù đi đến phần cuối."),t("br"),t("br"),n._v("“Không có cách nào thông qua, cái kia Dị Linh tộc nhân như thế nào đi vào hơn nữa lông tóc không hao tổn?”"),t("br"),t("br"),n._v("Trương Huyền nhịn không được nói."),t("br"),t("br"),n._v("Cho dù Thiên Diệp Vương, lá đỏ vua, là thập đại vương giả đứng đầu, cũng khẳng định không mạnh như vậy, làm không được trực tiếp đi vào, không bị thương tổn a?"),t("br"),t("br"),n._v("Nhưng vì cái gì bọn họ có thể đi vào, nhóm người mình nhưng không có biện pháp gì?"),t("br"),t("br"),n._v("“Trừ phi... Bọn họ dùng pháp bảo nào đó!”"),t("br"),t("br"),n._v("Hàn hội trưởng nói."),t("br"),t("br"),n._v("“Pháp bảo?”"),t("br"),t("br"),n._v("“Ừm, một chút pháp bảo lợi hại, hoàn toàn có thể ngăn cản những này bão cát, nếu như chúng ta hiện tại có một kiện thượng phẩm Thánh khí, liền có thể không quan tâm, trực tiếp ngạnh xông đi qua, sẽ không nhận tổn thương...”"),t("br"),t("br"),n._v("Hàn hội trưởng gật đầu."),t("br"),t("br"),n._v("Hắc Yên Trần Sa mặc dù lợi hại, đối tuyệt phẩm Linh khí, di tích một chút cấp thấp Thánh khí có thương tổn, có thể gặp được thượng phẩm Thánh khí loại pháp bảo cấp bậc này, liền không có bất kỳ chỗ dùng nào."),t("br"),t("br"),n._v("Nếu có loại cấp bậc này bảo vật, cho dù không cần tìm cái gì sinh môn, đều có thể ngạnh xông đi qua."),t("br"),t("br"),n._v("“Thượng phẩm Thánh khí... Đi nơi nào tìm...”"),t("br"),t("br"),n._v("Hắn nói đơn giản, nhưng Trương Huyền biết, gần như không có khả năng."),t("br"),t("br"),n._v("Kim Nguyên đỉnh lợi hại như vậy pháp bảo, đi qua bản thân không tỳ vết chút nào rèn luyện, đều chỉ miễn cưỡng tấn thăng đến trung phẩm Thánh khí, hơn nữa còn là trong đó hạng chót, muốn luyện chế chế được một cái thượng phẩm, độ khó bao lớn, có thể tưởng tượng được... Chỉ cần không đạt tới bát phẩm luyện khí sư, có thể nói, hầu như hoàn toàn không có khả năng!"),t("br"),t("br"),n._v("“Thật ra thì, không phải thượng phẩm Thánh khí, một chút đặc thù bảo vật, cũng là có thể! Hắc Yên Trần Sa, mang theo ăn mòn tác dụng, bình thường vật chất pháp bảo, trừ phi đạt tới cấp bậc nhất định, nếu không, không có khả năng chịu được! Mà nếu là linh hồn loại hình bảo vật liền không đồng dạng, không bị xâm hại, hoàn toàn có thể mượn nhờ trong đó tán dật hồn lực, đem hắn ngăn tại bên ngoài...”"),t("br"),t("br"),n._v("Lạc Nhược Hi quan sát một hồi, chậm rãi nói."),t("br"),t("br"),n._v("Nàng là Thánh Nhân quý tộc hậu nhân, kiến thức rộng rãi, biết không ít bí văn cùng trận pháp ứng đối biện pháp."),t("br"),t("br"),n._v("“Linh hồn loại hình bảo vật? Thứ này hẳn là so với Thượng phẩm Thánh khí càng khó tìm hơn tìm đi?”"),t("br"),t("br"),n._v("Một vị Chiến sư nhịn không được nói."),t("br"),t("br"),n._v("Thượng phẩm Thánh khí, chỉ cần luyện khí sư cấp bậc đạt tới, tài liệu đầy đủ, hoàn toàn có thể luyện chế ra đến, nhưng mà dính dáng linh hồn phương diện bảo vật liền trân quý."),t("br"),t("br"),n._v("Bình thường đều là tự nhiên hình thành, rất khó luyện chế, có thể luyện chế Hồn khí pháp bảo luyện khí sư, đều có địa vị cực cao, không kém gì cùng cấp bậc danh sư, thậm chí càng thêm hiếm có."),t("br"),t("br"),n._v("“Đúng vậy a! Linh hồn loại bảo vật, quá khó tìm, chúng ta Thanh Nguyên phong hào đế quốc Danh Sư đường, cũng không tìm tới một kiện!”"),t("br"),t("br"),n._v("“Chúng ta Vân Hư học viện cũng tìm không thấy một kiện...”"),t("br"),t("br"),n._v("Mọi người tất cả đều lắc đầu."),t("br"),t("br"),n._v("Cho dù linh hồn loại bảo vật, có thể ngăn cản Hắc Yên Trần Sa, nhưng bọn hắn cũng tìm không thấy, nói tương đương nói vô ích."),t("br"),t("br"),n._v("“Đáng tiếc, ta nghe nói Hồng Viễn đế quốc hoàng thất, có một gốc Thánh vực Bồ Đề thụ, là linh hồn loại Tiên Thiên thực vật, sớm biết nơi này có thứ này, trực tiếp mượn tới, liền có thể miễn trừ không ít phiền phức...”"),t("br"),t("br"),n._v("Một bên Mộc sư, than thở một tiếng."),t("br"),t("br"),n._v("“Mấu chốt ai có thể nghĩ tới nhiều như vậy, trong di tích thế mà lại có loại này kỳ lạ trận pháp...”"),t("br"),t("br"),n._v("Ngô sư lắc đầu."),t("br"),t("br"),n._v("“Thật ra thì nghĩ đến cũng vô dụng, hoàng thất gốc kia Bồ Đề thụ, ta nghe nói đã nhanh chết rồi, mượn tới đoán chừng cũng không sống được, không cách nào xua đuổi cát bụi, lại nói, đây là bọn họ hoàng thất điểm chí mạng, chắc chắn sẽ không mượn...” Ô Thiên Khung nói."),t("br"),t("br"),n._v("“Đúng vậy a... Dính dáng một cái hoàng thất tồn vong, làm sao có thể cho mượn đến, trừ phi, trực tiếp đánh tới cứng rắn đoạt, hoặc là lấy Danh Sư đường danh nghĩa, đáp ứng một ít điều kiện, nếu không không thể nào làm được!”"),t("br"),t("br"),n._v("Mấy người cảm khái còn chưa nói xong, chỉ thấy cách đó không xa Trương Huyền, mặt mũi ngẩn người: “Các ngươi nói cái gì? Thánh vực Bồ Đề thụ... Cũng được?”"),t("br"),t("br"),n._v("“Đương nhiên, thứ này mặc dù không phải linh hồn loại pháp bảo, lại là linh hồn loại Tiên Thiên thực vật, dùng để đối kháng cái này Hắc Yên Trần Sa khẳng định sẽ hữu hiệu quả...”"),t("br"),t("br"),n._v("Hàn hội trưởng nhẹ gật đầu, đang muốn nói tiếp, chỉ thấy cách đó không xa Trương viện trưởng, cổ tay khẽ đảo, một cây đại thụ xuất hiện tại lòng bàn tay, bị nâng giơ lên: “Thứ này... Thật có thể đi?”"),t("br"),t("br"),n._v("“Đây là nguyên một khỏa... Thánh vực Bồ Đề thụ?”"),t("br"),t("br"),n._v("“Trương viện trưởng, ngươi có phải hay không đem người ta Hồng Viễn hoàng thất cho... Mang?”"),t("br"),t("br"),n._v("Mọi người trợn mắt hốc mồm."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1063"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1065"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);