(window.webpackJsonp=window.webpackJsonp||[]).push([[872],{1227:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1759-bach-ton-gia-【-canh-3-】"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1759-bach-ton-gia-【-canh-3-】"}},[n._v("#")]),n._v(" Chương 1759: Bạch tôn giả 【 Canh [3] 】")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1759: Bạch tôn giả "),t("br"),t("br"),n._v("“Tốt, ta nghe ngươi! Bất quá, chúng ta Thánh thú, ít nhất phải hai cái danh ngạch, không thỏa mãn được, cùng lắm thì huyên náo mọi người ai cũng không đi được!”"),t("br"),t("br"),n._v("Tên là Bạch tôn giả Thánh thú, cười lạnh."),t("br"),t("br"),n._v("Đầu thánh thú này, bản thể là một đầu bạch hồ, huyền phù trên không, chừng dài hơn mười thước, tuy là không có thi triển lực lượng, lại cho người ta một loại chấn nhiếp hư không cảm giác."),t("br"),t("br"),n._v("Thật muốn nổi giận chiến đấu, trên quảng trường hơn phân nửa người, đều có thể không chịu nổi."),t("br"),t("br"),n._v("“Vừa mở miệng liền muốn hai cái danh ngạch, ngươi đây là tính toán đoạt?”"),t("br"),t("br"),n._v("Danh Sư đường trên không, một người trung niên bộ dáng cường giả, cười lạnh."),t("br"),t("br"),n._v("“Cướp lại như thế nào?”"),t("br"),t("br"),n._v("Đầu lâu quay lại, Bạch tôn giả nhất đạo sát khí, thẳng tắp lao qua, kích thích linh hồn của con người."),t("br"),t("br"),n._v("Xem ra, nó đối công kích linh hồn, vô cùng am hiểu."),t("br"),t("br"),n._v("“Muốn cướp rất đơn giản, liền nhìn ta kiếm trong tay, có đáp ứng hay không! Ta nhớ không lầm, hai vạn năm trước, ta Trương gia Trương Vũ tiên tổ, từng một người một kiếm, trấn áp các ngươi chín vị Cổ Thánh, không dám động đậy!” Trung niên nhân cười lạnh: “Ta hôm nay, cũng muốn thử một chút, có thể làm được hay không điểm ấy!”"),t("br"),t("br"),n._v("“Trương gia? Vị này chính là vị kia tổ tiên của Trương gia?”"),t("br"),t("br"),n._v("Nghe được hắn nói như vậy, Trương Huyền chỉ cảm thấy nhiệt huyết sôi trào, tràn đầy kích động."),t("br"),t("br"),n._v("Trước đó cùng vị này tiên tổ, từng có gặp mặt một lần, biết hắn mười phần khí phách, không nghĩ tới đối mặt Thánh thú bên trong mạnh nhất Cổ Thánh, cũng không chút nào ở vào hạ phong."),t("br"),t("br"),n._v("Tại Đại Thành điện bên ngoài, chỉ nghe được qua thanh âm, chưa thấy qua bản thân, giờ phút này nhìn kỹ lại, một thân áo xanh, lông mi như kiếm, một thanh trường kiếm treo ở bên hông, cái khác không có vật gì khác nữa, bộ dáng có chút bình thường, nhưng lại có để cho người ta không thể coi thường mạnh mẽ."),t("br"),t("br"),n._v("“Hắn chính là Trương gia vị cuối cùng Cổ Thánh, Trương Hoằng Thiên! Tính cách phóng khoáng, thiên tư vô song, tâm tình hiệp nghĩa, ghét ác như cừu, chỉ cần có người không phù hợp đạo nghĩa, liền giận dữ phản kích. Lúc trước, giữa thiên địa, Cổ Thánh lực lượng biến mất, hắn hết sức trẻ tuổi, vốn có lấy rất nhiều tuổi tác tiêu hao, lại việc nghĩa chẳng từ nan lựa chọn ngủ say...”"),t("br"),t("br"),n._v("Dương sư thanh âm vang lên."),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Nghe nói vị này tiên tổ đột phá Cổ Thánh thời điểm, mới hơn năm trăm tuổi, từng tuổi này, đổi lại những người khác, vừa mới thành gia lập nghiệp, chính hưởng thụ nhân sinh tốt đẹp thời gian, mà hắn lại không chậm trễ chút nào lựa chọn ngủ say, cùng người thân từng cái từ biệt..."),t("br"),t("br"),n._v("Phần này trả giá, đáng giá được hậu nhân ngưỡng mộ."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Không nghĩ tới cái tên này một điểm tình cảm và thể diện cũng không cho, Bạch tôn giả to lớn đầu sói vung một cái, chòm râu thổi lên: “Của ngươi vô song kiếm, quả thực rất lợi hại, nhưng muốn giết ta, cũng không dễ dàng như vậy... Chỉ cần không giết được ta, các ngươi Trương gia hậu bối, hẳn là rất dễ dàng tìm tới, ta ngược lại muốn xem xem, ngươi có thể bảo vệ mấy cái!”"),t("br"),t("br"),n._v("“Có thể ah, nếu như ta nhớ không lầm, của ngươi bạch hồ quần, cũng không ít, đồ tử đồ tôn càng là một mảng lớn, dám đụng đến ta Trương gia một người, ta giết ngươi mười đầu, giết ta mười người, ta diệt ngươi toàn tộc!”"),t("br"),t("br"),n._v("Trương Hoằng Thiên sống lưng thẳng tắp, thanh âm lãnh đạm: “Không tin... Ngươi thử một chút!”"),t("br"),t("br"),n._v("“Ngươi dám...”"),t("br"),t("br"),n._v("Hàm răng cắn chặt, Bạch tôn giả gào thét."),t("br"),t("br"),n._v("“Có dám hay không, có thể thử! Ta Trương Hoằng Thiên, từ khi xuất đạo đến nay, còn chưa hề có không dám thời điểm, không tin hỏi bọn họ một chút!”"),t("br"),t("br"),n._v("Nhẹ nhàng cười một tiếng, Trương Hoằng Thiên chỉ hướng Dị Linh tộc nhân mấy vị Cổ Thánh."),t("br"),t("br"),n._v("“Hừ!”"),t("br"),t("br"),n._v("Dị Linh tộc lời mới vừa nói Cổ Thánh hừ lạnh một tiếng, trên mặt không vui."),t("br"),t("br"),n._v("“Đây chính là vị kia Dục lão quỷ...”"),t("br"),t("br"),n._v("Nghe thanh âm, Trương Huyền nhận ra được."),t("br"),t("br"),n._v("Cái tên này, vừa rồi tâng bốc Nhan Thanh Cổ Thánh thời điểm, liền cảm thấy thanh âm có chút quen thuộc, giờ phút này cùng Trương Hoằng Thiên đối thoại, mới nhớ tới, chính là được hắn đánh lén, lưu lại máu tươi Dục lão quỷ."),t("br"),t("br"),n._v("“Ngươi thật sự cái gì cũng dám, đúng, Bạch tôn giả, ta làm sao nghe nói, ngươi một nhóm lớn hậu bối, tại Hạnh Đàn bên ngoài, bị Trương gia hậu nhân thuần phục?” Hừ xong, Dục lão quỷ một mặt cười lạnh nhìn lại."),t("br"),t("br"),n._v("“Thuần phục? Ngươi nói cái gì?”"),t("br"),t("br"),n._v("Bạch tôn giả sững sờ, tiếng rống giận dữ vang lên."),t("br"),n._v("Hiển nhiên, chuyện này cũng không hiểu rõ tình hình."),t("br"),t("br"),n._v("“Không tin ngươi xem một chút, phái đi Hạnh Đàn những cái kia hậu bối, còn ở đây? Khả năng liên hệ với?” Dục lão quỷ khóe miệng nâng lên."),t("br"),t("br"),n._v("Lần trước bị cái này Nhân tộc tiểu tử thiết kế, làm hại chịu một kiếm, đã sớm khó chịu, lúc này có khiêu khích cơ hội, đương nhiên sẽ không bỏ qua."),t("br"),t("br"),n._v("Bạch tôn giả mắt sói trừng một cái, quay đầu dặn dò sau lưng một vị Cổ Thánh cấp bậc Thánh thú, một lát sau, đầu thánh thú này tại nó bên tai nói thứ gì, vẻ mặt lập tức trầm xuống, thẳng tắp hướng Trương Huyền phương hướng nhìn tới."),t("br"),t("br"),n._v("Ánh mắt như là bếp lò, thiêu hủy bát hoang."),t("br"),t("br"),n._v("“Nằm thương...”"),t("br"),t("br"),n._v("Trương Huyền một mặt im lặng."),t("br"),t("br"),n._v("Hắn chỉ là cái Kim Thân cảnh tiểu nhân vật, các ngươi rất nhiều Cổ Thánh giao chiến, dắt ta làm gì..."),t("br"),t("br"),n._v("“Ngươi đem hắn đồ tử đồ tôn thuần phục?”"),t("br"),t("br"),n._v("Thấy nó ánh mắt mang theo hung ác, nhìn thẳng vị này gọi Trương Huyền vãn bối, nhớ tới Đại Thành điện bên ngoài trải qua, Trương Hoằng Thiên cũng nhịn không được nữa nhìn lại."),t("br"),t("br"),n._v("Đại Thành điện bên ngoài Thánh thú, thực lực đều không mạnh, cũng không phải là Bạch tôn giả hậu bối, có thể đi Hạnh Đàn lại khác biệt."),t("br"),t("br"),n._v("Hạnh Đàn tầm quan trọng, không cần nói cũng biết, có thể được phái đi trong đó, đều là một phương thế lực cao nhất tồn tại, chẳng lẽ, chính mình cái này hậu bối, liền loại này kiêu ngạo thiên tài, đều có thể thuần phục?"),t("br"),t("br"),n._v("“Đó là nó đồ tử đồ tôn? Ta không biết... Vừa rồi đi Hạnh Đàn thời điểm, nhìn thấy một đám Thánh thú, ngay tại chịu cây hạnh cuồng đánh, ngay lúc sắp giết chết, ngay sau đó động lòng trắc ẩn, đưa chúng nó cứu... Vì cảm kích ân cứu mạng, liền tất cả đều thần phục... Nơi nào hình như, không có bạch hồ ah!”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("Thần phục, là bởi vì hắn sử dụng đánh thuần thú pháp, đương nhiên, lúc này hiển nhiên không tiện nói như vậy."),t("br"),t("br"),n._v("“Lòng trắc ẩn? Ân cứu mạng?” Trương Hoằng Thiên cũng nhịn không được nữa: “Ha ha, làm xinh đẹp!”"),t("br"),t("br"),n._v("Đối phương nói có đúng không là nói thật, hắn thân là Cổ Thánh, tự nhiên có thể liếc mắt nhìn ra, để hắn cười là, vị này Bạch tôn giả, mới vừa như vậy ngạo mạn, sau một khắc, đồ tử đồ tôn liền bị bản thân hậu nhân thuần phục..."),t("br"),t("br"),n._v("Đánh mặt quả thực không nên quá nhanh!"),t("br"),t("br"),n._v("“Bạch tôn giả, ngươi xem ngươi hậu bối, đều thần phục hậu bối của ta, nếu không như vậy, cho ngươi một cơ hội, cũng thần phục với ta, trở thành ta thú sủng, danh ngạch nha... Đến lúc đó cho ngươi hai cái, ta liền không có ý kiến!”"),t("br"),t("br"),n._v("Nín cười, Trương Hoằng Thiên nhìn qua."),t("br"),t("br"),n._v("“Ngươi tự tìm cái chết...”"),t("br"),t("br"),n._v("Nghe được trêu chọc, Bạch tôn giả cũng nhịn không được nữa, gào lên một tiếng, thân hình cao lớn bỗng nhiên nhảy lên, hướng về phía Trương Hoằng Thiên liền lôi kéo tới."),t("br"),t("br"),n._v("Trên không xuất hiện đen kịt trảo ấn, không gian nứt thành bốn mảnh."),t("br"),t("br"),n._v("Đồng dạng là Cổ Thánh, đầu này Bạch tôn giả rõ ràng cực mạnh."),t("br"),t("br"),n._v("“Tìm không muốn chết, không phải ngươi nói tính, mà là chính ta...”"),t("br"),t("br"),n._v("Không ngần ngại chút nào, Trương Hoằng Thiên nhẹ nhàng cười một tiếng, trên lưng trường kiếm run lên, cũng không ra khỏi vỏ, nhẹ nhàng run lên, mang theo vỏ kiếm, liền hướng về phía trước nhanh đâm."),t("br"),t("br"),n._v("Ô ô!"),t("br"),t("br"),n._v("Kiếm khí gào thét, cùng móng vuốt đối đầu."),t("br"),t("br"),n._v("Rầm rầm rầm!"),t("br"),t("br"),n._v("Không gian mất đi ra một khối lớn đen kịt khe hở, Bạch tôn giả kìm lòng không được lui về sau một bước, đang muốn tiếp tục vọt tới trước, liền thấy lời mới vừa nói Nhan Thanh, thân thể vụt qua, đi tới ở giữa."),t("br"),t("br"),n._v("Bàn tay vạch một cái, bình chướng xuất hiện, giống như là hai khối màn sân khấu, ngăn lại hai người con đường đi tới."),t("br"),t("br"),n._v("“Tốt, chúng ta bây giờ trọng yếu nhất chính là đi vào chủ điện, mà không phải tranh đấu, thật muốn đánh, về sau có thể ra ngoài lại nói! Hiện tại đừng tổn thương hòa khí!”"),t("br"),t("br"),n._v("Song chưởng dựng thẳng lên, Nhan Thanh thản nhiên nói."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1770"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1772"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);