(window.webpackJsonp=window.webpackJsonp||[]).push([[878],{1234:function(n,h,i){"use strict";i.r(h);var t=i(44),c=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-1765-co-thanh-4-cai-canh-gioi"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1765-co-thanh-4-cai-canh-gioi"}},[n._v("#")]),n._v(" Chương 1765: Cổ Thánh 4 cái cảnh giới")]),n._v(" "),i("hr"),n._v(" "),i("p",[n._v("“Cụ thể đẳng cấp, cũng không có xác định, chỉ là hậu nhân dựa theo thực lực chênh lệch, cùng đặc thù, tạm thời cho xưng hô, chỉ có thể làm làm tham khảo!”"),i("br"),i("br"),n._v("Trương Hoằng Thiên chậm rãi nói: “Đột phá Cổ Thánh, huyết mạch liền có thể tiếp tục kéo dài, tựa như chúng ta Trương gia! Huyết mạch chi lực khả năng đủ lưu giữ trong tộc, chính là bởi vì có một vị đạt tới loại cảnh giới này tiên tổ.? Bởi vậy, loại cảnh giới này, cũng được xưng chi vì huyết mạch kéo dài, là tấn cấp đến Cổ Thánh tiêu chí!”"),i("br"),i("br"),n._v("“Huyết mạch kéo dài?”"),i("br"),i("br"),n._v("Trương Huyền gật đầu."),i("br"),i("br"),n._v("Đạt tới loại cảnh giới này, huyết mạch liền có thể kéo dài."),i("br"),i("br"),n._v("Hiện tại xem ra, Viên Đào, Yêu Dị huyền đao, Ngoan Nhân, hầu như đều là loại thực lực này, lúc trước một sách đập chết Dị Linh tộc nhân Cổ Thánh, cũng gần như, loại tu vi này."),i("br"),i("br"),n._v("Thuộc về Cổ Thánh bên trong, yếu nhất gẩy ra."),i("br"),i("br"),n._v("“Cái thứ hai cảnh giới, là lúc trước Khổng sư đệ tử, bảy mươi hai Thánh bên trong, loại trừ thập hiền bên ngoài, sáu mươi hai người đi đến, bởi vậy, bị hậu nhân xưng là Chư Tử bách gia.” Trương Hoằng Thiên nói tiếp."),i("br"),i("br"),n._v("“Chư Tử bách gia? Đây là một loại cảnh giới?” Trương Huyền ngẩn ngơ."),i("br"),i("br"),n._v("“Chỉ là một loại xưng hô, đại biểu cảnh giới, còn tên gì, không quan trọng.” Trương Hoằng Thiên tiếp tục nói: “Cái thứ ba cảnh giới, gọi tích huyết trùng sinh, nói cách khác, đạt tới loại cảnh giới này, chỉ cần tuổi thọ chưa tới, dù là bị người chém xuống đầu lâu, cũng có thể lần nữa phục sinh! Ta chính là loại cảnh giới này, vì lẽ đó, máu của ta, xa so với cái khác Cổ Thánh chi huyết, càng thêm cường đại!”"),i("br"),i("br"),n._v("Trương Huyền chấn động."),i("br"),i("br"),n._v("Còn tưởng rằng đạt tới Cổ Thánh, liền có thể làm được tích huyết trùng sinh, hiện tại xem ra, cũng không phải là như vậy."),i("br"),i("br"),n._v("Có thể làm được điểm này, coi như tại Cổ Thánh bên trong, chỉ sợ cũng là đỉnh phong tồn tại."),i("br"),i("br"),n._v("“Cảnh giới cuối cùng đâu?”"),i("br"),i("br"),n._v("Nhịn không được hỏi."),i("br"),i("br"),n._v("“Cảnh giới cuối cùng, mặc kệ danh sư đại lục, còn là Chư Tử bách gia, loại trừ Khổng sư bên ngoài, hầu như không có người có thể đạt tới, nghe nói, đến loại cảnh giới này, có thể phá toái hư không, cùng Tiên Sứ so sánh! Bởi vậy, cũng được xưng là phá toái hư không, hoặc là Tiên Sứ cảnh! Đương nhiên, ta khoảng cách loại cảnh giới này, còn rất xa, cụ thể như thế nào, cũng không rõ ràng.”"),i("br"),i("br"),n._v("Trương Hoằng Thiên nói."),i("br"),i("br"),n._v("“Tiên Sứ cảnh?” Trương Huyền nội tâm nổi lên gợn sóng."),i("br"),i("br"),n._v("Hắn nghe qua!"),i("br"),i("br"),n._v("Ở trong hang lần đầu tiên gặp được Ngoan Nhân thời điểm, cái tên này, từng nói qua, bản thân là Tiên Sứ cảnh giới cường giả, lúc ấy không hiểu rõ cảnh giới, cũng không thế nào để ý, nằm mơ đều không có nghĩ đến, là Cổ Thánh cao nhất!"),i("br"),i("br"),n._v("Cũng liền nói, Ngoan Nhân, bây giờ nhìn lại chẳng ra sao cả, trên thực tế, khi còn sống quả thực là đại lục cao nhất siêu tuyệt nhân vật."),i("br"),i("br"),n._v("Khó trách, Khổng sư đều bị nhốt lại, không cách nào thoát thân."),i("br"),i("br"),n._v("“Cái kia... Vị này Nhan Thanh Cổ Thánh, thực lực gì?”"),i("br"),i("br"),n._v("Tiếp tục hỏi."),i("br"),i("br"),n._v("Nhan Thanh Cổ Thánh, hẳn là này một đám Cổ Thánh bên trong, tu vi cao nhất, đến cùng đạt đến loại cảnh giới nào?"),i("br"),i("br"),n._v("“Hắn đồng dạng là tích huyết trùng sinh, chỉ bất quá cao hơn ta một cái tiểu cảnh giới thôi!” Trương Hoằng Thiên nói: “Bất quá, vẫn như cũ không có đạt tới tích huyết trùng sinh cao nhất! Loại cảnh giới này, cũng không đủ Cổ Thánh lực lượng duy trì, đã rất khó tinh tiến!”"),i("br"),i("br"),n._v("Trương Huyền giật mình, trong mắt mang theo nghi ngờ: “Nói như vậy, ta có phải hay không hẳn là nhiều muốn một ít, tích huyết trùng sinh cấp bậc Cổ Thánh chi huyết?”"),i("br"),i("br"),n._v("“Không sai, loại cấp bậc này Cổ Thánh chi huyết, uy lực lớn nhất, hiệu quả cũng lớn nhất!” Trương Hoằng Thiên gật đầu."),i("br"),i("br"),n._v("“Tốt!” Trương Huyền hiểu được."),i("br"),i("br"),n._v("Đối thoại của bọn họ, đều là truyền âm, tốc độ cực nhanh, trước sau chẳng qua hơn mười hô hấp công phu, bên này mới vừa nói xong, liền nghe đến Bạch tôn giả thanh âm vang lên: “50 giọt Cổ Thánh chi huyết, tốt, ta Thánh thú nhất tộc mua!”"),i("br"),i("br"),n._v("Thanh âm thô cuồng, mang theo bễ nghễ thiên hạ khí thế."),i("br"),i("br"),n._v("“Bạch tôn giả trước đừng có gấp, ta nói 50 giọt Cổ Thánh chi huyết, là đạt tới tích huyết trùng sinh cấp bậc, mà không phải bình thường huyết dịch!” Trương Huyền nhẹ nhàng cười một tiếng."),i("br"),i("br"),n._v("“Tích huyết trùng sinh cấp bậc? Ngươi tại sao không đi cướp...”"),i("br"),i("br"),n._v("Chòm râu thổi lên, Bạch tôn giả kém chút nổ tung."),i("br"),i("br"),n._v("Xung quanh cũng một chút xôn xao, từng cái an tĩnh lại."),i("br"),i("br"),n._v("Bình thường 50 giọt Cổ Thánh chi huyết, chính là giá trên trời, cái tên này, lại muốn 50 giọt đạt tới tích huyết trùng sinh cảnh giới..."),i("br"),n._v(" “Quả thực chính là ăn cướp!”"),i("br"),i("br"),n._v("“Giá tiền này quá cao, hẳn là không người sẽ ra đi!”"),i("br"),i("br"),n._v('"Vì một cái danh ngạch,'),i("br"),i("br"),n._v('Tiêu phí như vậy lớn, có chút không đáng..."'),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("Cổ Thánh đè thấp thanh âm vang lên."),i("br"),i("br"),n._v("Một cái danh ngạch, mặc dù có thể để cho một phương thế lực, đạt được Xuân Thu đại điển tỷ lệ tăng thêm, nhưng... Cũng chỉ là tăng thêm cơ hội, vì một cái cơ hội, tiêu hao như vậy lớn, nghiêm chỉnh mà nói, quả thực có chút không đáng."),i("br"),i("br"),n._v("“Đáng cùng không đáng, chính các ngươi nhìn, Xuân Thu đại điển, là Khổng sư mạnh nhất pháp bảo, một khi đạt được, tích huyết trùng sinh cảnh giới cường giả mà thôi, chẳng lẽ còn sợ thành tựu không ra?”"),i("br"),i("br"),n._v("Dường như nhìn ra đám người do dự, Trương Huyền mỉm cười: “Mà một khi mất đi cơ hội này, đem triệt để cùng loại cơ duyên này ngăn cách... Lựa chọn ra sao, nhìn chính các ngươi, không mua cũng được, cùng lắm thì ta liền nhiều an bài mấy người, chúng ta được tỷ lệ cũng sẽ lớn hơn nhiều!”"),i("br"),i("br"),n._v("Đấu giá, giá cả đã xác định, coi như lưu phách, cũng không thể hạ thấp, nếu không, liền mất đi ý nghĩa."),i("br"),i("br"),n._v("“Cái này...”"),i("br"),i("br"),n._v("Đám người lặng im."),i("br"),i("br"),n._v("Một cái danh ngạch, liền đại biểu, nhiều một phần cơ duyên, lựa chọn ra sao, liền nhìn bọn họ nghĩ như thế nào."),i("br"),i("br"),n._v("“Cái này danh ngạch, ta Chư Tử bách gia mua!”"),i("br"),i("br"),n._v("Ngay tại bốn phía lặng im thời điểm, Nhan Thanh Cổ Thánh thanh âm, vang lên."),i("br"),i("br"),n._v("“Mua?”"),i("br"),i("br"),n._v("Đồng loạt nhìn tới, đám người tràn đầy khiếp sợ."),i("br"),i("br"),n._v("Cũng không nói nhảm, Nhan Thanh Cổ Thánh ngón tay búng một cái, một cái bình ngọc hướng về phía Trương Huyền liền bay tới."),i("br"),i("br"),n._v("Tiện tay nhận lấy, Trương Huyền mở ra nắp bình liếc mắt nhìn, huyết dịch khí tức nồng đậm, không nhiều không ít, vừa vặn 50 tích."),i("br"),i("br"),n._v("Lặng lẽ liếc mắt nhìn Trương Hoằng Thiên, thấy hắn âm thầm gật đầu, biết đây đều là đạt tới tích huyết trùng sinh cảnh giới, trong lòng vui mừng, thu vào nhẫn: “Tốt, cái thứ nhất danh ngạch, cấp cho Chư Tử bách gia!”"),i("br"),i("br"),n._v("“Hiện tại đấu giá cái thứ hai danh ngạch, cái này danh ngạch, 60 giọt tích huyết trùng sinh cảnh giới Cổ Thánh chi huyết!” Mí mắt vừa nhấc, Trương Huyền nói."),i("br"),i("br"),n._v("“Làm sao 60 giọt?”"),i("br"),i("br"),n._v("“Vừa rồi không phải là 50 giọt ư?”"),i("br"),i("br"),n._v("“Đồng dạng danh ngạch, vì sao cái này muốn tăng giá?”"),i("br"),i("br"),n._v("Bốn phía kém chút nổ tung."),i("br"),i("br"),n._v("Còn tưởng rằng, cái thứ hai danh ngạch, cũng là tương đồng giá cả, không nghĩ tới cái tên này, vừa mở miệng tăng trưởng hai mươi phần trăm!"),i("br"),i("br"),n._v("“Các ngươi có thể lựa chọn không mua, có điều, hiện tại ta danh sư đại lục bên này, bảy cái danh ngạch, Chư Tử bách gia ba cái, các ngươi riêng phần mình chỉ có một cái, lựa chọn ra sao, bản thân quyết định...”"),i("br"),i("br"),n._v("Mỉm cười, Trương Huyền nhìn về phía Dị Linh tộc nhân cùng Thánh thú nhất tộc: “Hơn nữa, phía sau hai cái danh ngạch, sẽ quý hơn, dù sao, càng đấu giá càng ít...”"),i("br"),i("br"),n._v("“Sáu mươi liền sáu mươi, ta mua...”"),i("br"),i("br"),n._v("Bạch tôn giả hàm răng cắn “Cục cục!” Vang vọng."),i("br"),i("br"),n._v("Tu vi đạt tới bọn họ loại cảnh giới này, ai không phải gắng sức nghiên cứu, một lòng chỉ muốn tu luyện, một cái đạt được mẫu phù, năm đại trận tâm siêu cấp thiên tài, thế mà còn là một vị gian thương..."),i("br"),i("br"),n._v("Suy nghĩ một chút đều để người cảm thấy buồn bực."),i("br"),i("br"),n._v("“Sáu mươi lời nói, ta cũng muốn!”"),i("br"),i("br"),n._v("Bạch tôn giả tiếng nói không lạc, một bên Dục lão quỷ hừ một tiếng, cũng mở miệng nói."),i("br"),i("br"),n._v("Người đăng: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-1776"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-1778"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);