(window.webpackJsonp=window.webpackJsonp||[]).push([[881],{1236:function(n,h,t){"use strict";t.r(h);var g=t(44),c=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-169-thien-đao-than-cong-đe-luc-trong"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-169-thien-đao-than-cong-đe-luc-trong"}},[n._v("#")]),n._v(" Chương 169: Thiên Đạo Thần Công đệ lục trọng")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v(" Ba sư hội tụ, cùng nhau bái phỏng, Trương Huyền không hề biết chuyện, hắn giờ phút này đang đứng tại Vương quốc tàng thư khố bên trong, hưng phấn con mắt tỏa ánh sáng."),t("br"),t("br"),n._v(" “Xong rồi...” "),t("br"),t("br"),n._v(" Nhìn vừa giữa trưa thư, rốt cục đem tàng thư trong kho liên quan với Ích Huyệt cảnh thư tịch tất cả lật một lần, thư viện cũng thành công hình thành hoàn chỉnh bản tầng thứ sáu Đạo Thần công."),t("br"),t("br"),n._v(" Phía trước phương pháp cùng trước hắn suy đoán như thế, hiện tại chỉ cần sát bên về phía sau tu luyện liền có thể."),t("br"),t("br"),n._v(" “Tu luyện!” "),t("br"),t("br"),n._v(" Ngược lại nơi này chỉ có một mình hắn, cũng không sợ kinh thế hãi tục, không chút do dự nào, Trương Huyền ngồi dưới đất, bắt đầu tu luyện."),t("br"),t("br"),n._v(" Đùng đùng! Đùng đùng! Đùng đùng!"),t("br"),t("br"),n._v(" Vừa bắt đầu tu luyện, hùng hậu linh khí lập tức từ trên trời giáng xuống, tràn vào thân thể, từng cái từng cái huyệt đạo ở trong người nổ tung, khác nào rang đậu giống như vậy, trong suốt vang lên."),t("br"),t("br"),n._v(" Không biết qua bao lâu, Trương Huyền mở mắt ra, một mặt kinh ngạc, tựa hồ có hơi không thể tin được."),t("br"),t("br"),n._v(" “Ta dĩ nhiên đem... 108 nơi huyệt đạo tất cả mở ra?” "),t("br"),t("br"),n._v(" Truyền thuyết, thân thể tổng cộng 108 nơi huyệt đạo, có thể mở ra, bất quá 72 viên, hơn nữa những cái này huyệt đạo thế nào mở ra là có trình tự, mỗi người thể chất không giống, cần không giống trình tự mới có thể đạt đến hiệu quả tốt nhất, không tìm được phương pháp, cả một đời, e sợ cũng chỉ có thể mở mang ba, bốn mươi cái huyệt đạo, liền lại không cách nào tiến bộ."),t("br"),t("br"),n._v(" Vốn tưởng rằng coi như có Thiên Đạo Thần Công, có thể mở mang 72 cái là tốt lắm rồi, nằm mơ đều không nghĩ tới, 108 nơi tất cả mở ra."),t("br"),t("br"),n._v(" Hắn giờ phút này, toàn thân huyệt đạo lúc sáng lúc tối, phảng phất hội tụ khắp trời đầy sao, tràn ngập sức mạnh."),t("br"),t("br"),n._v(" Toàn thân huyệt đạo tất cả mở ra, tin tức truyền đi, tuyệt đối có thể đem người dọa chết tươi."),t("br"),t("br"),n._v(" “108 nơi huyệt đạo, tăng cường 108 đỉnh, không tiến vào Ích Huyệt cảnh thời điểm thì có 20 đỉnh, thân thể 90 đỉnh, tất cả sức mạnh tính gộp lại, sức mạnh của ta dĩ nhiên đạt đến 218 đỉnh, có thể so với Thông Huyền cảnh trung kỳ cường giả...” "),t("br"),t("br"),n._v(" Tính toán một chút sức mạnh, Trương Huyền hai mắt tỏa ánh sáng."),t("br"),t("br"),n._v(" Thông Huyền cảnh sơ kỳ 100 đỉnh, trung kỳ 200 đỉnh, hậu kỳ 300 đỉnh, đỉnh phong 400 đỉnh."),t("br"),t("br"),n._v(" Ích Huyệt cảnh đỉnh phong, liền nắm giữ vượt qua Thông Huyền cảnh trung kỳ sức mạnh, toàn bộ Thiên Huyền Vương quốc thậm chí càng rộng hơn phạm vi, e sợ đều là tuyệt không thể nào."),t("br"),t("br"),n._v(" Lại làm cho hắn làm được dễ dàng."),t("br"),t("br"),n._v(" Coi như là hắn, đều cảm thấy có phải là đang nằm mơ."),t("br"),t("br"),n._v(" Thực lực như vậy, Thiên Huyền Vương quốc có thể vượt qua, cũng sẽ không quá nhiều."),t("br"),t("br"),n._v(" Chung quy, Thiên Huyền Vương quốc tối cường giả, cũng bất quá Thông Huyền cảnh đỉnh phong."),t("br"),t("br"),n._v(" “Tiếp tục nhìn có hay không võ giả thất trọng công pháp...” "),t("br"),t("br"),n._v(" Trương Huyền đứng dậy, hai mắt tỏa ánh sáng, tiếp tục hướng tàng thư khố nơi sâu xa đi đến."),t("br"),t("br"),n._v(" “Đáng tiếc...” "),t("br"),t("br"),n._v(" Đi rồi một vòng, đem toàn bộ tàng thư khố tìm một lần, Trương Huyền không nhịn được lắc đầu."),t("br"),t("br"),n._v(" Cái này tàng thư khố, cũng không có võ giả thất trọng công pháp."),t("br"),t("br"),n._v(" Ngẫm lại cũng là bừng tỉnh."),t("br"),t("br"),n._v(" Võ giả thất trọng Thông Huyền cảnh, tại Thiên Huyền Vương quốc được xem là nhất cường giả đỉnh cao, công pháp càng là quý giá không gì sánh được, làm sao có khả năng tùy tiện xếp đặt ở đây?"),t("br"),t("br"),n._v(" E là cho dù có, cũng thu thập tại chỉ có quốc vương bệ hạ mới biết địa phương, cung cấp hắn nghiên cứu, khống chế."),t("br"),t("br"),n._v(" “Không có công pháp, nhìn võ kỹ...” "),t("br"),t("br"),n._v(" Nghĩ đến điểm này, Trương Huyền cũng không có quá nhiều thất lạc, nhanh chân đi hướng võ kỹ khu."),t("br"),t("br"),n._v(" Vương quốc tàng thư kho tàng thư đo vô cùng phong phú, tuy rằng để ở chỗ này võ kỹ không phải cái gì bất truyền thuật, chỗ tốt chính là số lượng lớn, tùy tiện một cái chiêu số, liền có thể tìm tới mấy ngàn không giống lưu phái."),t("br"),t("br"),n._v(" Ngừng lại, Trương Huyền nhìn về phía trước mắt giá sách."),t("br"),t("br"),n._v(" Là thoái pháp cùng thân pháp khu vực."),t("br"),t("br"),n._v(" 《 bảy mươi hai mạch đàm chân 》, 《 Toàn Phong Thối Công 》, 《 ba mươi bốn thức liên hoàn đá 》, 《 xuyên thân Du Long 》..."),t("br"),t("br"),n._v(" Đủ loại thân pháp, thoái pháp bí tịch đặt tại trên giá sách, lít nha lít nhít."),t("br"),t("br"),n._v(" “Vương Dĩnh chân nhận qua tổn thương, coi như có Dưỡng Thể dịch tẩm bổ, muốn chiến thắng chân công tốt, tốc độ nhanh Đỗ Lỗi, cũng không có hoàn toàn chắc chắn, bất quá, có tốt thân pháp cùng thoái pháp, vượt qua liền nên đơn giản không ít...” "),t("br"),t("br"),n._v(" t r u ｙ e n c u a t u i N "),t("br"),n._v("e t  Nghĩ tới đây, Trương Huyền bắt đầu lật sách."),t("br"),t("br"),n._v(" Ào ào ào, ào ào ào!"),t("br"),t("br"),n._v(" Sau một canh giờ, chỗ có liên quan với chân công cùng thân pháp thư tịch tất cả lật hết, đầu óc quả nhiên cũng hình thành hai bản thư."),t("br"),t("br"),n._v(" Thiên Đạo thân pháp cùng Thiên Đạo thoái pháp!"),t("br"),n._v("  Tiện tay mở ra, tinh thần chìm đắm trong đó, sau hai canh giờ, hai bộ võ kỹ hoàn toàn học được."),t("br"),t("br"),n._v(" Này hai bộ võ kỹ bởi vì thu dọn đều là trụ cột nhất, căn bản nhất đồ vật, cùng Thiên Đạo thương pháp như thế, đều chỉ hình thành một chiêu."),t("br"),t("br"),n._v(" Đặc biệt là thân pháp, có thể để cho hắn tại một phần mười hô hấp bên trong thoát ra hai mươi mét khoảng cách, tốc độ nhanh chóng, khác nào thuấn di."),t("br"),t("br"),n._v(" Đương nhiên, tốc độ nhanh như vậy, đối với thân thể có rất lớn hạn chế, nếu không là hắn tu luyện qua Thiên Đạo Kim thân, chỉ như vậy trong nháy mắt tăng tốc, sẽ mạch máu nổ tung, đi đời nhà ma, dù vậy, dựa vào hắn hiện tại sức chịu đựng, cũng nhiều nhất chỉ có thể triển khai một lần, hơn nhiều, sẽ tai mũi ra máu, không chịu nổi."),t("br"),t("br"),n._v(" Coi như như vậy, cũng rất đáng sợ, cùng người đối chiến lúc, đột nhiên sử dụng khác nào thuấn di bình thường thân pháp, e sợ Thông Huyền cảnh đỉnh phong không có phòng bị bên dưới, cũng có thể được tại chỗ đánh nổ."),t("br"),t("br"),n._v(" Học xong thân pháp, thoái pháp, đi tới quyền pháp khu, tương tự lật xem, hình thành một chiêu quyền pháp."),t("br"),t("br"),n._v(" Sau một canh giờ, ung dung học được."),t("br"),t("br"),n._v(" Quyền pháp này cùng thân pháp như thế, cứ việc cũng là một chiêu, nhưng có thể để sức mạnh của hắn trong nháy mắt gia tăng gấp đôi."),t("br"),t("br"),n._v(" Hiện tại hắn có thể sử dụng tới 218 đỉnh, tăng lên dữ dội gấp đôi, chính là 436 đỉnh, nói cách khác, sử dụng tới Thiên Đạo quyền pháp, có thể để cho lực công kích của hắn sánh ngang Thông Huyền cảnh đỉnh phong cường giả."),t("br"),t("br"),n._v(" Đương nhiên, loại này vượt qua thân thể cực hạn sức mạnh, cùng thân pháp tương đồng, không thể không ngừng sử dụng, lấy năng lực hiện tại của hắn, cũng là nhiều nhất triển khai ba quyền mà thôi."),t("br"),t("br"),n._v(" Một đường lật xem, không đình học tập, thời gian cũng đang thong thả trôi qua."),t("br"),t("br"),n._v(" May là Trương Huyền biết chắc tại Vương quốc tàng thư khố chờ thời gian rất dài, sớm tại trữ vật giới chỉ bên trong chuẩn bị đồ ăn, không phải vậy, không cần tu luyện, đói bụng đều chết đói."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v(" Vương cung, đèn đuốc sáng choang."),t("br"),t("br"),n._v(" “Trần Tiêu đan sư, ngươi sao lại ở đây?” "),t("br"),t("br"),n._v(" Đi vào đại sảnh, Đỗ Mạc Hiên tràn đầy nghi hoặc."),t("br"),t("br"),n._v(" Hắn chỉ là Đỗ gia một trưởng lão, những năm này nương theo tu vi hạ thấp, càng là suy thoái, ngày hôm nay lại đột nhiên nhận được Thẩm Truy bệ hạ triệu hoán, để hắn có chút như hòa thượng sờ mãi không thấy tóc."),t("br"),t("br"),n._v(" Tiến vào cung điện, lúc này mới phát giác Trần Tiêu đan sư, Lăng Thiên Vũ, La Trùng bọn người tại."),t("br"),t("br"),n._v(" “Ta cũng là nhận được bệ hạ mời, khẳng định cùng Dương sư có quan hệ đi, chung quy mấy người chúng ta đều chịu Dương sư ân huệ!” "),t("br"),t("br"),n._v(" Lúc này Trần Tiêu đan sư cái nào còn có nửa điểm phải chết dáng dấp, chuyện trò vui vẻ, khí như cầu vồng."),t("br"),t("br"),n._v(" “Nói như vậy trái lại có thể...” "),t("br"),t("br"),n._v(" Đỗ Mạc Hiên gật gù, Lăng Thiên Vũ, La Trùng hai người cũng bỗng nhiên tỉnh ngộ."),t("br"),t("br"),n._v(" Bất quá, nghi hoặc cũng không lâu lắm, liền biết đáp án, Thẩm Truy bệ hạ nhanh chân đi tiến đại điện, cười hướng mọi người giới thiệu."),t("br"),t("br"),n._v(" “Cũng không phải là ta mời mọi người lại đây, mà là ba vị danh sư.” "),t("br"),t("br"),n._v(" “Danh sư?” "),t("br"),t("br"),n._v(" Đỗ Mạc Hiên mấy người sợ hết hồn, sốt ruột vội vàng đứng dậy, quả nhiên thấy bệ hạ phía sau theo ba vị khí vũ hiên ngang ông lão."),t("br"),t("br"),n._v(" “Ngồi đi, ngươi gọi Lăng Thiên Vũ đúng không, nghe nói thê tử ngươi bệnh nặng, có thể không cùng ta nói một chút lúc đó trị liệu tình huống?” Khách sáo xong, Lưu sư nhìn sang."),t("br"),t("br"),n._v(" Một lát sau."),t("br"),t("br"),n._v(" “Ngươi nói... Vị này Dương Huyền, chỉ liếc mắt nhìn, nhận ra của ngươi sư tử đá là Đề Nam Huyết Ngọc, cũng nhờ vào đó phán đoán, người nhà ngươi khả năng trọng bệnh? Hơn nữa... Chỉ đâm mấy cái huyệt đạo, nàng không chỉ tỉnh táo còn xuống đi bộ?” "),t("br"),t("br"),n._v(" Lưu sư trợn mắt lên."),t("br"),t("br"),n._v(" “Đề Nam Huyết Ngọc hút người tinh khí thần, thê tử ngươi hôn mê bất tỉnh, nói rõ đã bệnh đến giai đoạn cuối, muốn cứu trị, không chỉ phải tìm được kinh mạch bế tắc chỗ, còn cần chí ít trung tam phẩm khá cao chân khí mở ra ràng buộc, một chút nhìn ra những vị trí này, liền chần chờ đều không có... Danh sư thật sự có như vậy nhãn lực?” "),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v(" “Liền đan đều không luyện tập, chỉ đi tới, liền nhìn ra ngươi dính dáng tới tử khí, bất cứ lúc nào cũng sẽ tử vong? Tử khí tại thân, mi tâm biến thành màu đen, xác thực có thể nhìn ra, luyện đan sư nhất tiếp xúc thân mật chính là lô đỉnh, cũng có thể suy đoán cái không rời mười... Nhưng không cần hỏi dò liền suy đoán ra những cái này, ta thế nào cảm giác không giống như là danh sư, mà như là xem bói...” "),t("br"),t("br"),n._v(" “Còn có biết ngươi ăn Long Lân Hà, dẫn đến tâm cảnh bất ổn, không hỏi dò, luyện khí có thể nhìn ra? Đùa giỡn đi...” "),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v(" Tam đại danh sư nghe xong mọi người trải qua, mỗi một người đều giống như choáng váng."),t("br"),t("br"),n._v(" Giời ạ, nghe Thiên thư sao?"),t("br"),t("br"),n._v(" Coi như danh sư nhãn lực rất tốt, cũng không khuếch đại như vậy chứ!"),t("br"),t("br"),n._v(" “Nhìn tới... Chỉ có hai loại khả năng, đệ nhất... Vị này Dương Huyền trình độ vượt qua chúng ta thực sự quá nhiều, đạt đến ngay cả chúng ta không thể nào hiểu được mức độ.” "),t("br"),t("br"),n._v(" Không biết qua bao lâu, Lưu sư sắc mặt nghiêm túc: “Thứ hai, vậy thì là... Sớm đã biết tình huống của mọi người, hơn nữa tìm tới phương pháp giải quyết, lại đúng bệnh hốt thuốc, cố ý để người chấn động, mà trên thực tế... Nhưng là một tên lừa gạt, nhân cơ hội vơ vét của cải.” "),t("br"),t("br"),n._v(" Trịnh sư, Trang sư đồng thời gật đầu, hiển nhiên đối với cái quan điểm này cũng rất tán thành."),t("br"),t("br"),n._v(" “Nếu như là loại thứ nhất, chúng ta tự mình bái phỏng, thỉnh giáo học tập không có gì, nhưng nếu như loại thứ hai...” Nói đến đây, Lưu sư trong mắt phát lạnh: “Làm danh sư cũng có trách nhiệm vạch trần bọn đạo chích, không nên để cho hắn dơ tên tuổi.” (Chưa xong còn tiếp.) "),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-177"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-179"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);