(window.webpackJsonp=window.webpackJsonp||[]).push([[883],{1239:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1769-tam-canh-đot-pha-co-thanh-【-canh-3-】"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1769-tam-canh-đot-pha-co-thanh-【-canh-3-】"}},[n._v("#")]),n._v(" Chương 1769: Tâm cảnh đột phá Cổ Thánh! 【 Canh [3] 】")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1769: Tâm cảnh đột phá Cổ Thánh! "),t("br"),t("br"),n._v("Đã không có nguy hiểm, cũng không cần phải chậm trễ thời gian, nhanh thông qua khảo hạch, tìm tới Xuân Thu đại điển lại nói."),t("br"),t("br"),n._v("Đám người đồng thời gật đầu, cùng một chỗ hướng thông đạo đi tới."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Đi tới trước mặt, Trương Huyền lập tức cảm thấy ôn nhuận quang mang, bao phủ ở trên người, ngay sau đó thấy hoa mắt, từ biến mất tại chỗ."),t("br"),t("br"),n._v("Sau một khắc, đã xuất hiện tại một tòa hùng vĩ sơn phong trước đó."),t("br"),t("br"),n._v("Cao vút trong mây, không nhìn thấy phần cuối, toàn bộ núi, dường như tùy thư tịch tích tụ mà thành, không có con đường, không có phần cuối, chỉ có liên tiếp văn tự, thỉnh thoảng ẩn hiện huyền phù, cho người ta một loại tựa như ảo mộng cảm giác."),t("br"),t("br"),n._v("“Đến đỉnh núi, tức là thông qua! Từ bỏ, sẽ cùng ngươi đi lại tiến độ, ban tặng khác biệt cơ duyên!”"),t("br"),t("br"),n._v("Khổng sư thanh âm nhàn nhạt tiếp tục vang lên."),t("br"),t("br"),n._v("“Ừm!” Trương Huyền lên tiếng, hít sâu một hơi."),t("br"),t("br"),n._v("Tuy là hết thảy trước mắt, thoạt nhìn vô cùng chân thật, nhưng từ vừa rồi Triệu Nhã đám người tình huống đến xem, hẳn là một loại huyễn cảnh."),t("br"),t("br"),n._v("Nói cách khác, hắn hiện tại người vẫn tại trong thông đạo, tinh thần lại đi tới cái này tùy thư tịch tích tụ mà thành núi lớn."),t("br"),t("br"),n._v("Ánh mắt ngưng trọng, Trương Huyền trong cơ thể lực lượng vận chuyển."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Cũng không rời đi tại chỗ."),t("br"),t("br"),n._v("“Nhìn tới, không cách nào phi hành... Cũng đúng, nếu như có thể phi hành, cũng quá đơn giản...”"),t("br"),t("br"),n._v("Âm thầm gật đầu."),t("br"),t("br"),n._v("Đạt tới đỉnh núi coi như thông qua, có thể bay lời nói, bay thẳng đi lên, chẳng phải quá mức dễ dàng?"),t("br"),t("br"),n._v("Lần nữa hướng trước mắt núi sách nhìn lại, chỉ thấy cả tòa núi lớn, trụi lủi, loại trừ thư tịch chính là thư tịch, không có thông đạo, cũng không có có thể bắt tay vào làm địa phương."),t("br"),t("br"),n._v("Bàn tay tại một quyển sách bên trên nhẹ nhàng nhấn một cái, vừa định hướng lên trên vọt tới, liền cảm thấy áp lực cực lớn nghiền ép mà đến, như bị sét đánh, toàn thân run lên, bàn tay dường như bị mũi gai nhọn tổn thương, toát ra máu tươi."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Sầm mặt lại."),t("br"),t("br"),n._v("Còn muốn lấy, chính là leo núi, lấy thực lực của hắn, dù là nhẵn bóng như gương, leo đến đỉnh phong hẳn là cũng không khó, không nghĩ tới, áp lực như vậy đại!"),t("br"),t("br"),n._v("Như là có Bất Hủ cảnh đại viên mãn cường giả, liên tục ở đối với hắn công kích, loại tình huống này, thế nào bên trên đi?"),t("br"),t("br"),n._v("“Chẳng lẽ... Không phải leo đi lên?”"),t("br"),t("br"),n._v("Đi vòng một hồi, liên tục thí nghiệm mấy chỗ địa phương, đều tương đồng kết quả, lên không được nửa mét, liền bị áp bách xuống, Trương Huyền đành phải dừng lại, mày nhíu lại thành cục."),t("br"),t("br"),n._v("Không phải leo đi lên, lại không có con đường, làm sao đi lên?"),t("br"),t("br"),n._v("Khó trách Triệu Nhã, Ngụy Như Yên bọn người không cách nào thông qua, quả nhiên không tưởng tượng đơn giản như vậy."),t("br"),t("br"),n._v("“Nếu là núi sách, trước nghiên cứu một chút, đều là thứ gì sách, có lẽ có thể tìm tới mấu chốt...”"),t("br"),t("br"),n._v("Dừng lại một chút, Trương Huyền hướng trước mắt rất nhiều thư tịch nhìn sang, tinh thần khẽ động, mấy trăm quyển sách, thu vào thư viện, ngón tay một điểm, vô số kiến thức chảy vào trong óc."),t("br"),t("br"),n._v("Những sách vở này, công pháp, võ kỹ, nhân văn, địa lý, tinh tượng, xem bói... Đủ loại đều có, đại lục ở bên trên rất nhiều chức nghiệp, tựa hồ cũng ẩn chứa ở bên trong."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Vừa đem kiến thức cảm thụ một lần, chỉ thấy trước mắt nhẵn bóng như gương núi sách, bất ngờ xuất hiện một hàng bậc thang, đại khái mấy tầng bộ dạng."),t("br"),t("br"),n._v("Trương Huyền sững sờ."),t("br"),t("br"),n._v("Vừa rồi xoay thật lâu, cũng không thấy thứ này, làm sao chỉ nhìn mấy trăm quyển sách, liền xuất hiện?"),t("br"),t("br"),n._v("Chẳng lẽ... Đọc sách đó có thể thấy được một con đường?"),t("br"),t("br"),n._v("“Thử một chút!”"),t("br"),t("br"),n._v("Nghĩ đến điểm này, lại không do dự, con mắt thẳng tắp hướng núi sách bốn phía nhìn quanh."),t("br"),t("br"),n._v("Hoa lạp lạp lạp!"),t("br"),t("br"),n._v("Vô số thư tịch, xuất hiện tại trong thư viện, không đến mười phút đồng hồ, liền thu thập mấy vạn bản nhiều."),t("br"),t("br"),n._v("Nhẹ nhàng điểm một cái, kiến thức chảy xuôi trong óc, hai mắt nhắm nghiền, vừa đem hắn tiêu hóa, liền cảm thấy trước mắt sơn phong một hồi lay động, một hàng bậc thang, đột nhiên xuất hiện, chậm rãi lan tràn lên phía trên."),t("br"),n._v("“Quả nhiên...”"),t("br"),t("br"),n._v("Gặp quả nhiên cùng đọc sách, tiêu hóa kiến thức liên quan đến, Trương Huyền mắt sáng lên."),t("br"),t("br"),n._v("Người khác nhìn nhiều như vậy thư tịch, dù là có thể dùng thần thức liếc nhìn, khẳng định cũng sẽ phát điên, tinh thần hỗn loạn, hắn không giống, liếc mắt nhìn qua, thư viện tự động thu nhận, cũng đem hắn sửa sang lại, chuyển hóa thành ký ức..."),t("br"),t("br"),n._v("Về thời gian, mảy may đều không lãng phí, cũng sẽ không để người trong óc hỗn loạn."),t("br"),t("br"),n._v("Hắn hiện tại, liền giống như một cái mạnh mẽ máy tính, trước mắt to lớn núi sách, đối với người khác tới nói, cần ký ức, trầm tư suy nghĩ, không ngừng đọc thuộc lòng, mà đối với hắn, phục chế dán là có thể."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Bước lên cầu thang, tiếp tục hướng bốn phía thư tịch nhìn lại, kiến thức chảy vào trong óc."),t("br"),t("br"),n._v("Kèm theo nhìn sách càng nhiều, cầu thang từng tầng từng tầng hiện lên, Trương Huyền từng bước đi lên đi."),t("br"),t("br"),n._v("Thông qua tiến lên, hắn cũng phát hiện, bình thường nhìn một trăm bản thư tịch, sẽ xuất hiện một tầng lầu bậc thang."),t("br"),t("br"),n._v("Cũng chính là hắn, có thể nhìn như vậy sách, đổi lại những người khác, một mực đọc sách ký ức... Cho dù là Cổ Thánh, khẳng định cũng đã sớm điên rồi."),t("br"),t("br"),n._v("Đi không biết bao lâu, cúi đầu nhìn lại, phía dưới mây mù vây quanh, chỉ thấy một đạo cầu thang, nghiêng cắm ở mây trắng ở giữa, đất trời bốn phía mênh mông, dường như chỉ còn lại có hắn một cái thân ảnh cô độc."),t("br"),t("br"),n._v("“Nhìn nhiều như vậy sách, vẫn như cũ không có đến đỉnh núi, bất kỳ người nào tâm lý đều sẽ không chịu nổi đi!”"),t("br"),t("br"),n._v("Cảm xúc một tiếng."),t("br"),t("br"),n._v("Không có gì bất ngờ xảy ra, nơi này tốc độ thời gian trôi qua, cùng ngoại giới là hoàn toàn khác biệt."),t("br"),t("br"),n._v("Hắn có Thiên đạo thư viện cường đại như vậy máy gian lận, đi đến nơi này, đều nhìn hơn nửa ngày, những người khác đi đến nơi này, không có mấy trăm năm, thậm chí ngàn năm, tuyệt đối không làm được."),t("br"),t("br"),n._v("Mấy trăm năm thời gian, một mực đọc sách, không có cái gì, đơn phần này cô độc, liền sẽ để người tâm tính mất cân bằng, Cổ Thánh cũng chưa chắc có thể kiên trì nổi."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Tiếp tục hướng phía trước đi vài bước, một lát sau, Trương Huyền mặt mũi bất đắc dĩ."),t("br"),t("br"),n._v("Đi đến nơi này, cần nhìn một ngàn bản, mới có thể xuất hiện một tầng lầu bậc thang."),t("br"),t("br"),n._v("Trước đó cũng có chút khó mà kiên trì, độ khó lần nữa tăng thêm gấp mười lần, tương đương với vượt qua lạc đà cuối cùng một cái rơm rạ."),t("br"),t("br"),n._v("Bất quá, độ khó đối với người khác tới nói, là tăng lên, đối với hắn mà nói, vẫn như cũ là phỏng chế quá trình, không có cái gì."),t("br"),t("br"),n._v("Tiếp tục hướng phía trước, lại đi hồi lâu, giờ phút này đã đạt tới nhìn một vạn bản thư tịch, mới có thể xuất hiện một bậc thang."),t("br"),t("br"),n._v("Biết cũng nhanh đến phần cuối, Trương Huyền không có tiếp tục từ bỏ, tăng thêm tốc độ."),t("br"),t("br"),n._v("Cũng không biết qua bao lâu, rốt cục nhìn thấy sơn phong xuất hiện tại tầm mắt."),t("br"),t("br"),n._v("“Núi sách có đường chuyên cần vì đường... Nhìn tới Khổng sư khảo nghiệm là, lĩnh ngộ kiến thức, cùng chăm chỉ...”"),t("br"),t("br"),n._v("Cảm xúc một tiếng, Trương Huyền than thở một tiếng."),t("br"),t("br"),n._v("Đọc sách mới có thể lên núi, nhìn càng nhiều, bò càng cao, không riêng gì đối tinh thần rèn luyện, càng là Khổng sư đối hậu bối cảnh cáo."),t("br"),t("br"),n._v("Là muốn nói cho hậu nhân... Tu luyện, đánh đến cuối cùng, không phải thiên tư, mà là văn hóa!"),t("br"),t("br"),n._v("Văn hóa nội tình đầy đủ, mới có thể đối tu luyện, đối thiên đạo, có lĩnh ngộ sâu hơn cùng lý giải, mới có thể nắm giữ càng thêm bao la lòng dạ tiếp nhận chuyện mới mẻ vật, mới có thể càng đi càng xa, thành tựu càng ngày càng cao."),t("br"),t("br"),n._v("Liền giống như bảy mươi hai Thánh, Khổng môn thập hiền."),t("br"),t("br"),n._v("Thiên tư của bọn hắn coi như không bằng Khổng sư, có Khổng sư tự tay bồi dưỡng, tay nắm tay truyền thụ phương pháp tu luyện... Điều kiện nhất định so người sau, hậu đãi hơn nhiều."),t("br"),t("br"),n._v("Có thể thành liền nhưng còn xa không thể cùng Khổng sư so sánh..."),t("br"),t("br"),n._v("Chỉ có một khả năng, vậy chính là, tại kiến thức bên trên, bọn họ kém xa Khổng sư, văn hóa hạn chế tiến bộ."),t("br"),t("br"),n._v("Khổng sư, năm đó định thượng, trung, hạ, rất nhiều chức nghiệp, thoạt nhìn đối tu vi, không có cái gì chỗ khác biệt, trên thực tế, chính là loại này rèn luyện, mới để cho hắn lòng dạ thiên hạ, tiến lên càng xa."),t("br"),t("br"),n._v("Răng rắc!"),t("br"),t("br"),n._v("Những này ý niệm ở trong lòng chảy xuôi, đột nhiên, trong óc vang lên một quả trứng gà phá vỡ thanh âm, lại hình như mạ phá vỡ bùn đất, nghênh đón ánh nắng."),t("br"),t("br"),n._v("“Tâm cảnh... Đột phá 29. 9?”"),t("br"),t("br"),n._v("Nắm đấm xiết chặt, Trương Huyền con mắt thả ra hào quang chói sáng."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1780"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1782"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);