(window.webpackJsonp=window.webpackJsonp||[]).push([[887],{1242:function(n,h,g){"use strict";g.r(h);var t=g(44),i=Object(t.a)({},(function(){var n=this,h=n.$createElement,g=n._self._c||h;return g("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[g("h1",{attrs:{id:"chuong-1773-di-linh-toc-đe-nhat-hoang-gia"}},[g("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1773-di-linh-toc-đe-nhat-hoang-gia"}},[n._v("#")]),n._v(" Chương 1773: Dị Linh tộc đệ nhất Hoàng giả")]),n._v(" "),g("hr"),n._v(" "),g("p",[n._v("Chương 1773: Dị Linh tộc đệ nhất Hoàng giả"),g("br"),g("br"),n._v("Thần Dung Hoàng, Dị Linh tộc ba đại Hoàng giả một trong, hơn nữa là nhất thực quyền Đế Hoàng, trong tộc tất cả mọi chuyện, đều thuộc về hắn quản lý, mệnh danh Dị Linh tộc đệ nhất nhân, cũng không quá đáng... Thế nào lại là cái này theo sau lưng, một mực xưng hô bản thân “Thiếu gia” đồng tử?"),g("br"),g("br"),n._v("Nếu như là hắn, cái kia... Được hắn coi làm tiểu thư Lạc Nhược Hi, lại sẽ là thân phận gì?"),g("br"),g("br"),n._v("Lạnh cả người, Trương Huyền đã không dám suy nghĩ."),g("br"),g("br"),n._v("Trước đó, liền nghe nói, Dị Linh tộc mạnh nhất Hoàng giả, đến danh sư đại lục, làm thế nào cũng không tìm tới, nằm mơ đều không có nghĩ đến, vậy mà liền ở bên cạnh hắn, hóa thân một vị hạ nhân!"),g("br"),g("br"),n._v("“Linh Hoàng...”"),g("br"),g("br"),n._v("Tuy là trong lòng đã có đáp án, chân chính nghe được, Thần Dung Hoàng Ngột Thần còn là ánh mắt khó coi, nắm đấm xiết chặt, mang theo tức giận."),g("br"),g("br"),n._v("Ba đại Hoàng giả, ở chung không biết bao nhiêu năm, xưa nay, nội đấu ngược lại cũng thôi, thông đồng người khác, đối phó bản thân, đã phản bội chủng tộc."),g("br"),g("br"),n._v("Nghĩ đến cái này, cũng nhịn không được nữa, ngẩng đầu nhìn về phía cách đó không xa Nhan Thanh Cổ Thánh: “Nhìn tới, Chư Tử bách gia cùng bọn hắn đã sớm liên hợp đi!”"),g("br"),g("br"),n._v("“Đồng ý Thần Linh Hoàng, không chỉ có thể diệt trừ Dị Linh nhất mạch mạnh nhất Hoàng giả, càng có thể đổi lấy ba trăm năm bình yên, sao lại không làm?” Nhan Thanh Cổ Thánh thản nhiên nói, trên mặt không có chút nào sóng lớn."),g("br"),g("br"),n._v("“Ba trăm năm bình yên?”"),g("br"),g("br"),n._v("“Thần Linh Hoàng đã đồng ý Chư Tử bách gia, chỉ cần phối hợp đưa ngươi diệt trừ, trong vòng ba trăm năm, Dị Linh tộc sẽ không đi chủ động công kích!” Nhan Thanh Cổ Thánh nói."),g("br"),g("br"),n._v("“Thì ra là thế...” Thần Dung Hoàng cắn răng."),g("br"),g("br"),n._v("Khó trách Chư Tử bách gia cùng Dị Linh tộc, không đội trời chung, còn có thể bình yên ở chung, liên thủ đối phó bản thân, náo loạn hồi lâu, có điều kiện trao đổi."),g("br"),g("br"),n._v("Hắn là Linh tộc mạnh nhất Hoàng giả, một khi tử vong, toàn bộ Linh tộc, đều sẽ thực lực tổn thất lớn, ba trăm năm khôi phục không được, đồng ý không tấn công, thật ra thì cũng là đối với mình ta một loại bảo vệ."),g("br"),g("br"),n._v("Chỉ bất quá... Không tấn công người khác, chẳng lẽ có thể bảo đảm Chư Tử bách gia, Danh Sư đường, sẽ không nhân cơ hội tiêu diệt Linh tộc?"),g("br"),g("br"),n._v("“Nói như vậy lên, ban đầu ở Phong Cốc thành Thiên Cơ đường, các ngươi cũng đã chuẩn bị kỹ càng, đối ta mai phục a?”"),g("br"),g("br"),n._v("Thần Dung Hoàng lần nữa nói."),g("br"),g("br"),n._v("“Lúc trước chúng ta lấy Ngoan Nhân xương ngực làm mồi nhử, dẫn dụ ngươi ra tay, sau đó mượn Thiên Cơ đường che đậy Thiên Cơ, che lấp rất nhiều Cổ Thánh khí tức, vốn cho rằng, có thể tuỳ tiện chém giết, lại không nghĩ rằng, bị người phá hủy...”"),g("br"),g("br"),n._v("Bặc Dục hừ lạnh một tiếng, nhìn cách đó không xa Trương Huyền liếc mắt."),g("br"),g("br"),n._v("Lúc trước tại Thiên Cơ đường, mai phục mấy vị Cổ Thánh, liền đợi đến vị này Thần Dung Hoàng tự chui đầu vào lưới, kết quả, cái tên này không biết làm sao xông ra, mấu chốt nhất là... Vừa ra tay liền chém giết một vị Cổ Thánh cường giả, ngay cả chạy trốn đi đều không có làm được, trực tiếp sợ hãi đến tất cả mọi người không dám tiếp tục ra tay."),g("br"),g("br"),n._v("“Cái này...”"),g("br"),g("br"),n._v("Nghe được những này, Trương Huyền rốt cuộc minh bạch tới."),g("br"),g("br"),n._v("Khó trách, Chư Tử bách gia người, muốn ngang nhiên cướp đoạt Ngoan Nhân xương ngực, náo loạn hồi lâu, là vì thu hút Ngột Thần đi qua!"),g("br"),g("br"),n._v("Hắn nắm giữ Ngoan Nhân cánh tay, đối xương ngực nhất định có đặc thù cảm ứng, dùng cái này tới thu hút, quả thực không cách nào kháng cự."),g("br"),g("br"),n._v("t r ｕ y e n c u a t u i n e t"),g("br"),n._v("Còn về sau, khung xương bị bản thân Thiên đạo chi sách thôn phệ, hắn lại không nói thêm cái gì, có lẽ cùng Lạc Nhược Hi liên quan đến."),g("br"),g("br"),n._v("Nếu không, đường đường Hoàng giả, cũng không có khả năng, xưng hô bản thân một tiểu nhân vật vì thiếu gia!"),g("br"),g("br"),n._v("“Nói như vậy lên, đi Bạch Khê sơn, tìm kiếm truyền thế mẫu phù thời điểm, các ngươi cũng đã liên thủ...” Thần Dung Hoàng nói tiếp."),g("br"),g("br"),n._v("Bạch Khê sơn, đi theo Nhan Tiết mà đi ba người, trong đó một vị chính là Dị Linh tộc nhân, lúc ấy liền cảm thấy kỳ lạ, mới cáo từ tiểu thư, trở về điều tra, cũng báo tin người tấn công tộc đội ngũ, đình chỉ công kích."),g("br"),g("br"),n._v("Chỉ bất quá, không có tra ra cái gì, liền phát giác được Ngoan Nhân khung xương xuất hiện, đuổi tới."),g("br"),g("br"),n._v("“Không sai!”"),g("br"),n._v(" Cũng không phủ nhận, Nhan Thanh Cổ Thánh gật đầu nói."),g("br"),g("br"),n._v("“Rất tốt!” Đem muốn hỏi thăm lời nói hỏi xong, Thần Dung Hoàng con mắt đóng lại, ngay sau đó bỗng nhiên mở ra, một vòng xoáy khổng lồ, từ trong con mắt xoay tròn, như là vực sâu: “Nhìn tới Linh Hoàng muốn giết ta lâu, có điều, chỉ bằng các ngươi... Giết ta, không dễ dàng như vậy!”"),g("br"),g("br"),n._v("Ầm ầm!"),g("br"),g("br"),n._v("Kèm theo thanh âm kết thúc, đồng tử giống như thân ảnh, chậm rãi tăng cao, trên người sát lục chi khí càng lúc càng nồng nặc, mấy hơi thở công phu, liền khôi phục Dị Linh người bộ dáng, đầu đội vương miện, người mặc Kim Long trường bào, nhất cử nhất động có Hoàng giả phong thái cùng uy nghiêm."),g("br"),g("br"),n._v("“Quả nhiên là Thần Dung Hoàng...”"),g("br"),g("br"),n._v("Trương Hoằng Thiên trường kiếm bên hông không ngừng nhảy, tựa như lúc nào cũng sẽ không bị khống chế lao ra."),g("br"),g("br"),n._v("Vị này Dị Linh tộc mạnh nhất Hoàng giả, hắn tuy là chưa thấy qua, nhưng lại đã sớm nghe qua tin đồn, tại cái này, giống nhau như đúc."),g("br"),g("br"),n._v("Không chỉ hắn khiếp sợ, cái khác Cổ Thánh, cũng từng cái vẻ mặt nghiêm túc, mang theo cảnh giác."),g("br"),g("br"),n._v("Người có tên, cây có bóng."),g("br"),g("br"),n._v("Dị Linh tộc đệ nhất Hoàng giả lực uy hiếp thực tế quá lớn, cho dù thực lực của bọn hắn, đạt đến đại lục cao nhất, vẫn như cũ rõ ràng, người trước mắt này, tuyệt không phải người đông thế mạnh liền có thể đối phó."),g("br"),g("br"),n._v("Tận mắt thấy, Ngột Thần khôi phục chân thân, biến thành Dị Linh Hoàng giả, Trương Huyền nói không ra lời."),g("br"),g("br"),n._v("Cái khác Dị Linh tộc cường giả, lấy Cổ Thánh thực lực, ngụy trang thành nhân loại, sẽ bị Danh Sư đường phát giác, nhưng... Hắn tự mình cảm thụ qua Lạc Nhược Hi, ngụy trang phù lục thần kỳ, biết thứ này năng lực."),g("br"),g("br"),n._v("Nếu quả thật cho vị này Thần Dung Hoàng một cái, đừng nói ngụy trang thành đồng tử, coi như ngụy trang thành Dương sư, Danh Sư đường chỉ sợ đều không thể phân biệt ra được thật giả."),g("br"),g("br"),n._v("Loại bùa chú này thật là đáng sợ, ngụy trang về sau, huyết mạch đều điều tra không ra, huống chi cái khác!"),g("br"),g("br"),n._v("Cái này cũng liền giải thích, đường đường Dị Linh nhất tộc mạnh nhất Hoàng giả, vì sao đến nhân tộc, nhưng thủy chung tìm không tới!"),g("br"),g("br"),n._v("Còn nhân tộc, Chư Tử bách gia, vì sao biết hắn đã rời đi phong ấn, tiến hành bố cục, mai phục, chỉ sợ cùng vu hồn sư liên quan đến."),g("br"),g("br"),n._v("Những tin tức này nghe vào trong tai, Trương Huyền cũng coi như triệt để đem sự tình vuốt thuận."),g("br"),g("br"),n._v("Hẳn là, ẩn giấu ở Dị Linh nhất tộc vu hồn sư, phát giác Thần Dung Hoàng rời đi, lặng lẽ đưa tin cho Tưởng gia, Tưởng gia đem tin tức cho Chư Tử bách gia, sau đó mới có hiện tại bố cục."),g("br"),g("br"),n._v("Hiện tại duy nhất không hiểu nổi là được, Lạc Nhược Hi đến cùng thân phận gì! Lại tại trong đó giữ chức dạng gì nhân vật."),g("br"),g("br"),n._v("“Chúng ta biết không dễ dàng như vậy, có điều, ngày hôm nay ngươi muốn đi, gần như không có khả năng...”"),g("br"),g("br"),n._v("Thấy hắn khôi phục chân thân, Nhan Thanh Cổ Thánh biết lập tức sẽ đối mặt một hồi đại chiến, nuốt hai cái đan dược, khôi phục vừa rồi trong cơ thể nhận tổn thương, trong tay phương thiên họa kích giơ lên, trong mắt mang theo sát khí."),g("br"),g("br"),n._v("Chư Tử bách gia cùng Dị Linh tộc Cổ Thánh, cũng đồng thời giơ lên vũ khí."),g("br"),g("br"),n._v("Bố cục rất lâu, chỉ vì giờ khắc này."),g("br"),g("br"),n._v("“Tộc nhân muốn giết ta, Chư Tử bách gia cũng muốn ta chết... Ha ha!”"),g("br"),g("br"),n._v("Hét dài một tiếng, Thần Dung Hoàng quay đầu nhìn về phía Trương Hoằng Thiên cùng Danh Sư đường Cổ Thánh: “Các ngươi đâu?”"),g("br"),g("br"),n._v("“Thần Dung Hoàng, cha ta, huynh trưởng đều bị Dị Linh tộc nhân giết chết, ta sớm đã cùng các ngươi, không đội trời chung, nếu ngươi là mạnh nhất Hoàng giả, vậy thì lấy mạng đổi mạng đi!”"),g("br"),g("br"),n._v("“Chúng ta người thân hảo hữu, cũng đều chết tại trên tay các ngươi, ông trời có mắt, ngày hôm nay, coi như vẫn lạc ở đây, cũng muốn giết ngươi, lấy an ủi bọn họ trên trời có linh thiêng!”"),g("br"),g("br"),n._v("“Ta đạo lữ., bị ngươi khi đó một đạo đao khí chém giết biến thành tro bụi, ngày hôm nay có thể tự tay báo thù, chết cũng đáng!”"),g("br"),g("br"),n._v("..."),g("br"),g("br"),n._v("Danh Sư đường mấy vị Cổ Thánh, đồng thời hướng về phía trước, trên người sát khí đằng đằng."),g("br"),g("br"),n._v("Người đăng: ThấtDạ"),g("br"),g("br"),g("br"),g("br")]),n._v(" "),g("hr"),n._v(" "),g("p",[g("a",{attrs:{href:"./chap-1784"}},[n._v("Chương trước")]),n._v("  |  "),g("a",{attrs:{href:"./chap-1786"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);