(window.webpackJsonp=window.webpackJsonp||[]).push([[89],{443:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1057-huyen-sat-tran"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1057-huyen-sat-tran"}},[n._v("#")]),n._v(" Chương 1057: Huyễn sát trận")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1057: Huyễn sát trận"),t("br"),t("br"),n._v("“Xếp hạng thứ hai Hồng Diệp Vương?” Trương Huyền con ngươi co rụt lại."),t("br"),t("br"),n._v("Từng trải qua Ngọc Diệp Vương, Thạch Diệp Vương mạnh mẽ, đối với vị này bài danh cao hơn cường giả, một mực trong lòng có e dè, chỉ là không nghĩ tới sẽ ở loại tình huống này gặp gỡ!"),t("br"),t("br"),n._v("“Người nào? Giả thần giả quỷ, cút đi cho ta đi ra!”"),t("br"),t("br"),n._v("Hai người truyền âm đối thoại, Ngô sư thì mắt ưng nhìn chung quanh, giống như là xác nhận vị trí, lông mày nâng lên, hét lớn một tiếng, trường kiếm trong tay, bỗng nhiên đâm ra ngoài."),t("br"),t("br"),n._v("Soạt!"),t("br"),t("br"),n._v("Rộng lớn kiếm khí, tựa như sao băng, đem không khí xé rách, trực tiếp bắn ra ngoài, cùng phía trước bức tường tiếp xúc, lập tức cắt chém ra một cái cực lớn lỗ thủng."),t("br"),t("br"),n._v("“Thật mạnh...”"),t("br"),t("br"),n._v("Lần thứ nhất thấy Ngô sư xuất chiêu, Trương Huyền âm thầm gật đầu."),t("br"),t("br"),n._v("Một đường đến nay, vị này Thất Tinh thượng phẩm danh sư, không phải bị thương chính là đi tại bị thương trên đường, để hắn cảm thấy có chút hữu danh vô thực, thực lực cũng không thế nào, hiện tại toàn lực ra tay, mới biết được đáng sợ."),t("br"),t("br"),n._v("Mặc dù so ra kém thời kỳ toàn thịnh Ngọc Diệp Vương, nhưng là cùng Thạch Diệp Vương so, cũng không hề yếu."),t("br"),t("br"),n._v("Chân chính chiến đấu, ai sống ai chết cũng còn không biết."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Đánh xuyên bức tường, kiếm khí đột nhiên nổ tung, trước mắt kiến trúc lập tức đổ sụp một mảnh, khói bụi lượn lờ, nào có nửa cái bóng người."),t("br"),t("br"),n._v("“Không có người?”"),t("br"),t("br"),n._v("Không nghĩ tới bản thân phán đoán sai, Ngô sư sầm mặt lại, vội vàng lại hướng nhìn bốn phía."),t("br"),t("br"),n._v("“Ha ha, muốn động thủ với ta? Ngươi còn non lắm.”"),t("br"),t("br"),n._v("Thanh âm vang lên lần nữa."),t("br"),t("br"),n._v("Soạt!"),t("br"),t("br"),n._v("Ngô sư lần nữa đâm ra."),t("br"),t("br"),n._v("Lần này tốc độ nhanh hơn, trường kiếm hóa thành huyễn ảnh, lan tràn ra khoảng cách mấy trăm mét, trực tiếp đánh xuống, trước mắt kiến trúc giống như là cắt ra đậu phụ, mắt trần có thể thấy hướng hai bên tách ra, mặt đất đều xuất hiện một cái lỗ to lớn."),t("br"),t("br"),n._v("Bất quá, cùng vừa rồi đồng dạng, một bóng người đều không có, giống như lời mới vừa nói người, không tồn tại đồng dạng."),t("br"),t("br"),n._v("“Không cần uổng phí tâm cơ, ngươi cho dù đem nơi này toàn phá hủy, cũng tìm không thấy ta, cố gắng hưởng thụ đi!”"),t("br"),t("br"),n._v("Dường như biết cử động của hắn vô dụng, Hồng Diệp Vương lần nữa cười nhạo."),t("br"),t("br"),n._v("Soạt!"),t("br"),t("br"),n._v("Nương theo lời của hắn kết thúc, trước đó sụp đổ kiến trúc, “Vù vù!” Thoáng cái, lần nữa khôi phục, hình như vừa rồi công kích, toàn bộ đều là giả."),t("br"),t("br"),n._v("“Cái này, cái này... Cái này sao có thể?”"),t("br"),t("br"),n._v("Con ngươi co rụt lại, tất cả mọi người cũng không dám tin tưởng."),t("br"),t("br"),n._v("Vừa rồi Ngô sư một kiếm, khí tức cuồng bạo, lực lượng mười phần, trước mắt lầu các, rõ ràng toàn bộ sụp đổ, làm sao lại trong nháy mắt khôi phục?"),t("br"),t("br"),n._v("Hơn nữa, xem ra cùng vừa mới tiến tới thời điểm, giống nhau như đúc, không có bất kỳ cái gì khác biệt!"),t("br"),t("br"),n._v("“Chúng ta nhất định là rơi vào cái nào đó lợi hại trong đại trận...”"),t("br"),t("br"),n._v("Ánh mắt lộ ra vẻ cảnh giác, Ngô sư nhịn không được nói."),t("br"),t("br"),n._v("Mọi người gật đầu."),t("br"),t("br"),n._v("“Chẳng lẽ... Là huyễn trận?”"),t("br"),t("br"),n._v("Ô Thiên Khung nói."),t("br"),t("br"),n._v("Hắn mặc dù cũng phụ tu trận pháp sư chức nghiệp, nhưng cảnh tượng trước mắt thực sự quá kì quái, liền xem như hắn, cũng không dám xác nhận."),t("br"),t("br"),n._v("“Không sai, hẳn là... Huyễn trận!”"),t("br"),t("br"),n._v("t r u y e n c u a t"),t("br"),n._v("u i . v n  Hàn hội trưởng nhẹ gật đầu: “Đoán không sai, chúng ta vừa tiến đến, liền lâm vào trong đó, nói cách khác... Trước mắt nhìn thấy đều là giả! Vô luận như thế nào công kích, đều không có bất kỳ chỗ dùng nào!”"),t("br"),t("br"),n._v("“Vô dụng?”"),t("br"),t("br"),n._v("“Ừm, thật giống như những kiến trúc này, bất kể đổ sụp, vẫn là đứng sừng sững, đều là giả, không tìm bản chất cùng chân tướng, liền sẽ vĩnh viễn vây ở chỗ này, sinh hoạt tại giả tạo bên trong, không cách nào chạy trốn...”"),t("br"),t("br"),n._v("Hàn hội trưởng nắm đấm xiết chặt."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Mọi người cứng ngắc."),t("br"),t("br"),n._v("Nếu thật là huyễn trận lời nói, hết thảy trước mắt, cũng không tránh khỏi quá chân thực."),t("br"),t("br"),n._v("Bọn họ đại bộ phận đều là danh sư, phân biệt thật giả, xem thấu bản chất, là sở trường nhất, nhưng đến nơi này, không chút nào không phát giác, đây rốt cuộc là cái cao bao nhiêu cấp bậc trận pháp?"),t("br"),t("br"),n._v("Chân thật như vậy huyễn cảnh, hơn nữa có nhân chủ nắm... Muốn chạy đi, gần như không có khả năng!"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Trương Huyền cũng biến sắc."),t("br"),t("br"),n._v("Hắn vừa đến liền cảm thấy không được bình thường, thế nhưng là vẫn như cũ không phát hiện, nơi này lại là huyễn cảnh!"),t("br"),t("br"),n._v("Tinh thần khẽ động, vô số hoa văn tại trong mắt hiện lên, trước mắt lâu đài còn cùng trước đó đồng dạng, không có biến hóa chút nào."),t("br"),t("br"),n._v("“Chẳng lẽ không phải huyễn cảnh, đây đều là thật?”"),t("br"),t("br"),n._v("Nhịn không được sững sờ."),t("br"),t("br"),n._v("Minh Lý Chi Nhãn có thể xem thấu thật giả, hư ảo, nếu thật là ảo cảnh lời nói, hẳn là dễ dàng xem thấu, làm sao lại một điểm biến hóa đều không có?"),t("br"),t("br"),n._v("“Không đúng, hẳn là trận pháp này, cấp bậc quá cao, thực lực của ta quá thấp, Minh Lý Chi Nhãn vẫn không cách nào xem thấu...”"),t("br"),t("br"),n._v("Rất nhanh hiểu được."),t("br"),t("br"),n._v("Minh Lý Chi Nhãn mặc dù rất cường đại, đáng tiếc thực lực của hắn quá thấp, vượt qua quá nhiều, cũng liền vô dụng, thật giống như cho dù bát tinh danh sư đứng ở trước mặt hắn ngụy trang, nếu như đối phương không muốn để cho hắn nhìn thấy, đồng dạng nhìn không ra cái gì đồng dạng."),t("br"),t("br"),n._v("Trước mắt cái này huyễn trận, rõ ràng chính là như vậy."),t("br"),t("br"),n._v("“Đoán chừng cùng lôi đình chi hải đồng dạng... Ít nhất là cái cấp tám đại trận!”"),t("br"),t("br"),n._v("Trong lòng khiếp sợ."),t("br"),t("br"),n._v("Cũng chỉ có loại cấp bậc này đại trận, mới có thể để cho tất cả mọi người rơi vào trong đó, không có chút nào phát giác."),t("br"),t("br"),n._v("“Thiếu hụt!”"),t("br"),t("br"),n._v("Biết trận pháp đẳng cấp cực cao, tán đi Minh Lý Chi Nhãn, Trương Huyền nhìn quanh một vòng, trong lòng hô khẽ."),t("br"),t("br"),n._v("Minh Lý Chi Nhãn nhìn không ra cao cấp như vậy trận pháp, nhưng Thiên Đạo thư viện, cũng không thành vấn đề."),t("br"),t("br"),n._v("Vù vù!"),t("br"),n._v(" Một quyển sách xuất hiện."),t("br"),t("br"),n._v("Vội vàng mở ra."),t("br"),t("br"),n._v("“Thứ một trăm bảy mươi hai cửa, đại trận một bộ phận, thiếu hụt có tám, thứ nhất...”"),t("br"),t("br"),n._v("Rất mau nhìn xong, Trương Huyền vẻ mặt khó coi."),t("br"),t("br"),n._v("Thư viện là không bị hạn trận pháp cấp bậc, nhưng mà... Trận pháp này quá lớn, bọn họ rơi vào trong đó, nhìn bằng mắt thường đến, chỉ là một bộ phận."),t("br"),t("br"),n._v("Thật giống như thư tịch bên trên biểu hiện chính là một trăm bảy mươi hai cửa, cũng liền cho thấy, phía trước ít nhất còn có một trăm bảy mươi mốt cái tương đồng cửa... Thậm chí nhiều hơn."),t("br"),t("br"),n._v("Nhiều như vậy cửa, là sinh môn vẫn còn tốt, là tử môn, tùy tiện tiến vào lời nói, chết cũng không biết chết như thế nào."),t("br"),t("br"),n._v("“Trước đó gặp phải trận pháp, đều là một cái chỉnh thể, kéo một sợi tóc động đến cả người, mà cái này, thì là do vô số tiểu trận pháp tạo thành, bị nhốt chỗ đó, chỗ đó vận chuyển, cho dù thư viện lợi hại, cũng không cách nào biết toàn thể tình huống...”"),t("br"),t("br"),n._v("Hiểu được, nắm đấm xiết chặt."),t("br"),t("br"),n._v("Trước kia gặp phải trận pháp, chỉ cần vận chuyển, liền toàn bộ bắt đầu, mà đại trận này, chia làm không biết bao nhiêu cửa hộ, cho dù thư viện mạnh mẽ, chỉ cần không rời đi, liền không cách nào xem thấu bản chất, tìm tới thích hợp nhất lộ tuyến."),t("br"),t("br"),n._v("Nói cách khác... Thư viện không có khả năng để hắn giống như trước đồng dạng, trong nháy mắt tìm tới thiếu hụt, dễ dàng rời khỏi!"),t("br"),t("br"),n._v("Rầm rầm!"),t("br"),t("br"),n._v("Hắn những này suy nghĩ, trong đầu lóe lên liền biến mất, đang không biết như thế nào cho phải, liền nghe đến cách đó không xa, lần nữa phát ra gió kêu thanh âm, vô số đạo kiếm khí, chung quanh bổ tới."),t("br"),t("br"),n._v("Như là hơn vạn binh sĩ phóng tới mưa tên, bốn phương tám hướng, lít nha lít nhít, không có bỏ sót, hơi không cẩn thận, liền có thể sẽ bị tại chỗ mặc thành con nhím."),t("br"),t("br"),n._v("“Mọi người cẩn thận, cái này không riêng gì huyễn trận, mà là cái huyễn sát trận!”"),t("br"),t("br"),n._v("Hàn hội trưởng một tiếng la lên."),t("br"),t("br"),n._v("Huyễn trận chỉ có thể để cho người ta mê huyễn, không cách nào tìm tới chạy trốn phương hướng, đối với sinh mệnh, là không quá lớn nguy hiểm, mà huyễn sát trận, là sát trận cùng huyễn trận dung hợp, để cho người ta không phân rõ ngược lại cũng thôi, còn sẽ có đủ loại công kích, làm không cẩn thận liền sẽ tại chỗ chết ở trong đó."),t("br"),t("br"),n._v("“Huyễn sát trận?”"),t("br"),t("br"),n._v("Mọi người sắc mặt tất cả đều thay đổi, nguyên một đám chân khí tuôn ra, chuẩn bị nghênh đón chiến đấu."),t("br"),t("br"),n._v("Trương Huyền cũng đang muốn động thủ, liền nghe đến một bên Phùng Huân hét dài một tiếng: “Chiến sư đường, Huyền Vũ kiếm trận!”"),t("br"),t("br"),n._v("“Đúng!”"),t("br"),t("br"),n._v("Chiến sư đường còn thừa lại chín người, đồng thời hướng về phía trước, ngăn tại bốn phía, trường kiếm trong tay đồng loạt ra tay."),t("br"),t("br"),n._v("Rầm rầm!"),t("br"),t("br"),n._v("Kiếm trận thi triển đi ra, tạo thành một cái hình tròn vòng sáng, tựa như mai rùa."),t("br"),t("br"),n._v("Đinh đinh đinh đinh!"),t("br"),t("br"),n._v("Kiếm mang rơi vào phía trên, mưa rơi trường đình, mặc dù phát ra kịch liệt thanh âm rung động, lại không cách nào đánh xuyên."),t("br"),t("br"),n._v("“Thật mạnh phòng ngự!”"),t("br"),t("br"),n._v("Thấy cảnh này, Trương Huyền ánh mắt sáng lên."),t("br"),t("br"),n._v("Mặc dù những này Chiến sư thực lực, không tính là quá mạnh, nhưng phối hợp lại hết sức ăn ý, cái này Huyền Vũ kiếm trận, đem kiếm mang chặn lại, không có một tia lọt mất, liền xem như hắn đều làm không được."),t("br"),t("br"),n._v("Soạt!"),t("br"),t("br"),n._v("Kiếm mang phát một lớp, ngừng lại."),t("br"),t("br"),n._v("Rất nhiều Chiến sư thu hồi vũ khí, nguyên một đám cảnh giác nhìn bốn phía."),t("br"),t("br"),n._v("“Loại công kích này, không có khả năng một mực tiến hành, hẳn là ẩn chứa một loại nào đó quy luật...”"),t("br"),t("br"),n._v("Hàn hội trưởng nói."),t("br"),t("br"),n._v("Bất luận cái gì trận pháp, đều khó có khả năng một mực công kích, thật muốn lời như vậy, cần năng lượng cũng đem không cách nào cung cấp."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Tiếng nói kết thúc, lại một lớp kiếm khí bắn tới, rất nhiều Chiến sư kết thành trận pháp, tiếp tục đem hắn ngăn tại bên ngoài."),t("br"),t("br"),n._v("Đem một màn này nhìn ở trong mắt, Ngô sư nhíu mày, ngẩng đầu nhìn về phía bốn phía, thanh âm vang vang vang lên: “Vừa rồi vị kia, ngươi không phải rất mạnh ư? Có dám cùng ta quang minh chính đại đánh một trận?”"),t("br"),t("br"),n._v("Hiện tại bọn hắn một mực nằm ở địch tối ta sáng trạng thái bị động, nếu như có thể để cho đối phương đi ra, mặc kệ có thể hay không chém giết, đều sẽ an toàn rất nhiều."),t("br"),t("br"),n._v("“Quang minh chính đại? Ha ha, ngươi có thể đi ra rồi nói sau...”"),t("br"),t("br"),n._v("Đối phương căn bản không mắc mưu, lần nữa cười một tiếng."),t("br"),t("br"),n._v("Vù vù!"),t("br"),t("br"),n._v("Không khí xiết chặt, lại có vô số công kích đánh tới, lần này không còn là kiếm mang, mà là đao khí."),t("br"),t("br"),n._v("Quyết đoán, chấn nhân cánh tay run lên."),t("br"),t("br"),n._v("Bất quá, rất nhiều Chiến sư hình thành kiếm trận cực mạnh, đồng dạng đem những vật này ngăn tại bên ngoài."),t("br"),t("br"),n._v("Đao mang liên tục công kích ba lần, ngừng lại, chỉ chốc lát thong thả tiếng đàn vang lên, quanh quẩn bên tai, xâm nhập linh hồn."),t("br"),t("br"),n._v("“Là... Ma âm công kích!”"),t("br"),t("br"),n._v("Mọi người sắc mặt thay đổi."),t("br"),t("br"),n._v("Trước đó tại Vô Cương giới, bọn họ bị ma âm công kích, suýt chút nữa dẫn đến toàn quân bị diệt, nơi này càng thêm nguy hiểm, một khi lại đến, làm không cẩn thận thực biết chết ở chỗ này."),t("br"),t("br"),n._v("“Làm sao bây giờ?”"),t("br"),t("br"),n._v("Phát hiện đóng lại lục thức cùng trước đó đồng dạng, căn bản ngăn chặn không được thanh âm, mọi người tất cả đều tràn đầy khẩn trương, nguyên một đám khuôn mặt trắng bệch."),t("br"),t("br"),n._v("Nói thật, loại tình huống này, ma âm công kích, so vật lý công kích càng thêm có hiệu."),t("br"),t("br"),n._v("“Ah... Ta không chịu nổi...”"),t("br"),t("br"),n._v("Đột nhiên, một vị Chiến sư một tiếng hí dài, bỗng nhiên vọt lên phía trước ra ngoài, trường kiếm trong tay khắp nơi chém loạn, giống như là lâm vào điên."),t("br"),t("br"),n._v("“Hồ Thần...”"),t("br"),t("br"),n._v("Nhìn thấy hắn lao ra, Phùng Huân vội vàng hô lên, đang định đem hắn kéo về, lần nữa nghe được gió tiếng hót vang lên."),t("br"),t("br"),n._v("Rầm rầm!"),t("br"),t("br"),n._v("Lần nữa vô số kiếm mang bắn tới, đứng tại trước mọi người phương Hồ Thần còn không có kịp phản ứng, liền bị trong nháy mắt đâm thủng, thi thể nặng nề ném xuống đất."),t("br"),t("br"),n._v("Phốc! Phốc!"),t("br"),t("br"),n._v("Huyền Vũ kiếm trận thiếu một người, trở nên có sơ hở, cũng lập tức có hai vị Chiến sư bị thương, bị đâm xuyên đùi, máu tươi ục ục chảy ra."),t("br"),t("br"),n._v("“Chư vị lui về phía sau...”"),t("br"),t("br"),n._v("La lên một tiếng, Ngô sư bỗng nhiên hướng về phía trước, kiếm trong tay mang lấp lánh, bổ xuống, chung quanh kiếm quang tại một kiếm phía dưới, toàn bộ chém chết, làm xong những này, lúc này mới về phía sau nhìn một cái: “Nắm chặt thời gian bôi thuốc... Cứu người quan trọng!”"),t("br"),t("br"),n._v("“Tốt...”"),t("br"),t("br"),n._v("Phùng Huân liền vội vàng gật đầu, gấp gáp cho bị thương thuộc hạ băng bó."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1065"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1067"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);