(window.webpackJsonp=window.webpackJsonp||[]).push([[891],{1247:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1777-dung-hoang-thu-đoan"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1777-dung-hoang-thu-đoan"}},[n._v("#")]),n._v(" Chương 1777: Dung Hoàng thủ đoạn")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("“Muốn giết ta, cũng không có dễ dàng như vậy!”"),t("br"),t("br"),n._v("Thấy đối phương xé rách sau cùng da mặt, trực tiếp động thủ, Thần Dung Hoàng giãy dụa lấy nâng lên trường đao, lưỡi đao phá không mà lên, thời gian nháy mắt thiêu đốt ra ngọn lửa đen kịt, giống như là thiên hỏa kiếp đến."),t("br"),t("br"),n._v("Dị Linh tộc Thần Dung Hoàng, mạnh nhất võ kỹ, thiên hỏa liệu nguyên!"),t("br"),t("br"),n._v("Một đao ra, thiên hỏa đến, sắc bén vô song, không có gì không phá!"),t("br"),t("br"),n._v("“Hắc!”"),t("br"),t("br"),n._v("Dường như biết hắn xảy ra chiêu này, Thần Linh Hoàng nhẹ nhàng cười một tiếng, lật đổ bàn tay, toàn lực ép xuống, không khí bốn phía liền bị ngưng kết thành thực chất."),t("br"),t("br"),n._v("Thần Dung Hoàng, thời kỳ toàn thịnh, có thể xưng Dị Linh tộc đệ nhất nhân, Linh Hoàng, Tinh Hoàng hai người tính gộp lại, cũng chưa chắc có thể là đối thủ."),t("br"),t("br"),n._v("Nhưng giờ phút này, bản thân bị trọng thương, nguyên khí tổn hao nhiều, thực lực mười không còn một, thiên hỏa liệu nguyên chiêu số mặc dù mạnh mẽ, nhưng uy lực đã kém xa lúc trước."),t("br"),t("br"),n._v("Cùng chưởng lực của đối phương vừa tiếp xúc, tại không có không có gì không phá lực lượng, thiên hỏa hủy diệt, trường đao bay ngược mà ra, không biết rơi xuống nơi nào."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Cả người cũng giống đạn pháo đồng dạng, trùng trùng điệp điệp ngã xuống đất, vẻ mặt trắng bệch, máu tươi phun mạnh."),t("br"),t("br"),n._v("Bị Chư Tử bách gia, Danh Sư đường, tộc nhân, hơn hai mươi vị Cổ Thánh vây công, thương thế trên người, thực tế quá nghiêm trọng, có thể sống, đã là may mắn, như thế nào còn có thể chặn lại, cùng hắn thực lực tương đương siêu cấp cao thủ!"),t("br"),t("br"),n._v("“Ha ha!”"),t("br"),t("br"),n._v("//ngantruyen.com/"),t("br"),n._v("Một chiêu đắc thủ, Thần Linh Hoàng con mắt sáng lên: “Dung Hoàng, ngươi tung hoành thiên hạ, có thể từng nghĩ tới, cũng có ngày hôm nay!”"),t("br"),t("br"),n._v("Tiến về phía trước một bước, đi tới phía trước, bàn chân dẫm ở lồng ngực của hắn, ngửa mặt lên trời cười to."),t("br"),t("br"),n._v("Trước mắt vị này, vô địch thiên hạ, cỡ nào phong quang, hiện tại cũng giống vậy nằm tại dưới chân, sống không bằng chết."),t("br"),t("br"),n._v("“Tinh Hoàng, ta thường ngày như thế nào đợi ngươi, thật chẳng lẽ muốn cùng hắn thông đồng làm bậy, cùng một chỗ giết ta? Chỉ cần ngươi giúp ta vượt qua hôm nay cửa ải khó, trở về sẽ làm làm chuyện gì đều không có phát sinh, giống như quá khứ...”"),t("br"),t("br"),n._v("Nằm trên mặt đất, bị đối phương áp chế, động một cái cũng không thể động, Thần Dung Hoàng mặt xám như tro, quay đầu nhìn về phía cách đó không xa một vị khác Hoàng giả."),t("br"),t("br"),n._v("“Ta...” Thần Tinh Hoàng do dự."),t("br"),t("br"),n._v("Hắn từ trước tới nay trung lập, lần này phản bội Dung Hoàng, cũng là đúng là bất đắc dĩ."),t("br"),t("br"),n._v("“Tinh Hoàng, từ chúng ta liên minh bắt đầu, ngươi cũng đã không còn đường lui... Một khi để hắn khôi phục, chúng ta đều sẽ chết!”"),t("br"),t("br"),n._v("Gặp hắn thế mà do dự, Thần Linh Hoàng lông mày giương lên."),t("br"),t("br"),n._v("Cái tên này, cái gì cũng tốt, thực lực cũng mạnh, có thể lớn nhất thiếu hụt chính là quá không quả quyết."),t("br"),t("br"),n._v("Đều loại tình huống này, tại sao có thể có đường lui?"),t("br"),t("br"),n._v("“Thật xin lỗi, Dung Hoàng! Linh Hoàng cho ta điều kiện, thực tế không cách nào cự tuyệt...”"),t("br"),t("br"),n._v("Nhớ tới trước mắt vị này thủ đoạn, cùng đối phương đáp ứng hắn chỗ tốt, Thần Tinh Hoàng dừng lại phút chốc, hàm răng cắn chặt, bàn tay nâng lên, thẳng tắp đối nằm dưới đất Dung Hoàng vỗ xuống đi."),t("br"),t("br"),n._v("Lần này, lực lượng mười phần, người sau tránh né không xong lời nói, đầu sẽ trực tiếp nổ tung, tại chỗ tử vong."),t("br"),t("br"),n._v("“Tuy là không muốn thừa nhận các ngươi phản bội, nhưng... Linh Thần chuyên môn nhắc nhở qua ta, ta như thế nào lại bất cẩn như vậy?”"),t("br"),t("br"),n._v("Đối phương bàn tay ngay lúc sắp rơi vào đỉnh đầu, Thần Dung Hoàng chẳng những không có khẩn trương, ngược lại trở nên càng ngày càng yên bình."),t("br"),t("br"),n._v("“Ừm?”"),t("br"),t("br"),n._v("Nhìn thấy hắn bộ dáng này, hai vị Dị Linh tộc Hoàng giả, đồng thời sinh ra một tia không ổn cảm xúc, Thần Linh Hoàng cũng nhịn không được nữa: “Giết!”"),t("br"),t("br"),n._v("Biết chần chờ thời gian càng dài, tỷ lệ thành công càng nhỏ, lại không để ý tới nói nhiều, cũng đập xuống."),t("br"),t("br"),n._v("Cùng Tinh Hoàng đồng dạng, bàn tay còn chưa tới đến đối phương đầu trước mặt, ngực đột nhiên một hồi đau đớn kịch liệt, ngay sau đó liền thấy hai cái xương cốt bàn tay, xuyên thủng hậu tâm của bọn hắn, chui ra."),t("br"),t("br"),n._v("Ngoan Nhân xương tay!"),t("br"),t("br"),n._v("Thứ này trước đó bị tinh đồ cùng áo tơi vây khốn, không cách nào sử dụng, chẳng biết lúc nào lần nữa nhận khống chế, đi tới trước mặt, một lần xuyên thủng hai người giữa lưng!"),t("br"),n._v(" “Ngươi...”"),t("br"),t("br"),n._v("Trái tim bị xương tay bóp nát, hai đại Hoàng giả lập tức cảm thấy lực lượng toàn thân, như thủy triều lui đi, thân thể vụt qua, đồng thời quỳ rạp xuống đất."),t("br"),t("br"),n._v("Tích huyết trùng sinh cảnh giới, tuy là một giọt máu liền có thể phục sinh, nhưng cũng cần thời gian tích tụ, cùng lực lượng tích lũy, cái này khung xương, trực tiếp phá hủy bọn họ sinh cơ, coi như may mắn không chết, thực lực cùng tuổi thọ cũng nhất định bị tổn thương lớn."),t("br"),t("br"),n._v("“Áo tơi là Ngoan Nhân tiên tổ khi còn sống pháp bảo, có thể khống chế khung xương, sao lại thế...”"),t("br"),t("br"),n._v("Cảm thấy sinh mệnh, nhanh chóng toả ra, Thần Linh Hoàng khó tin."),t("br"),t("br"),n._v("Bặc Dục thả ra áo tơi, là Ngoan Nhân khi còn sống pháp bảo, có thứ này, có thể khống chế khung xương sinh ra ý niệm, nghe theo dặn dò, ngoan ngoãn dung hợp, cuối cùng khôi phục chân thân..."),t("br"),t("br"),n._v("Đem hắn thả ra, dựa theo tình huống bình thường, khung xương lại không có thể nhúc nhích, làm sao sẽ còn nhận Thần Dung Hoàng khống chế?"),t("br"),t("br"),n._v("“Khung xương bên trong đản sinh ý niệm, đã bị Linh Thần loại bỏ sạch sẽ, đổi thành ta... Vừa rồi cố ý để hắn nhận khống chế, chính là chờ các ngươi ra tay...”"),t("br"),t("br"),n._v("Miễn cưỡng đứng dậy, miệng lớn thở hổn hển, Thần Dung Hoàng nói."),t("br"),t("br"),n._v("Khung xương là sẽ sinh ra thuộc về mình ý niệm, nhưng đã sớm bị Linh Thần ra tay xóa đi, đổi thành hắn."),t("br"),t("br"),n._v("Bởi vậy, áo tơi tuy là lợi hại, nhưng cũng không khống chế được."),t("br"),t("br"),n._v("Nghe được Chư Tử bách gia người, liên hợp hai vị này đối với hắn hạ sát thủ, liền đoán ra bọn họ sẽ đích thân đến, cố ý để xương tay nghe theo mệnh lệnh, trên thực tế, là tại lơ là đối phương!"),t("br"),t("br"),n._v("Vừa rồi hỏi thăm Thần Tinh Hoàng, thật ra thì chính là tại cho đối phương một lần cơ hội lựa chọn lần nữa, đã đối phương vẫn như cũ lựa chọn phản bội... Vậy thì lại không có gì có thể khách khí."),t("br"),t("br"),n._v("“Ta và ngươi liều mạng...”"),t("br"),t("br"),n._v("Không nghĩ tới, thiết kế lâu như vậy, không những không giết chết đối phương, còn bị đối phương phản thiết kế, Thần Linh Hoàng cắn răng, trong cơ thể lực lượng hội tụ."),t("br"),t("br"),n._v("Ầm!"),t("br"),t("br"),n._v("Từ phía sau chơi qua tới xương tay bị chấn bay rớt ra ngoài, bàn tay giơ lên, hướng Thần Dung Hoàng lần nữa vỗ xuống."),t("br"),t("br"),n._v("Bất quá, còn chưa tới đến đối phương trước mặt, thân ảnh bất ngờ biến mất, giống như là nhảy vào một cái không gian thông đạo."),t("br"),t("br"),n._v("“Dung Hoàng, coi như ngươi lợi hại, lần này không giết được ngươi, đằng sau có rất nhiều cơ hội, hãy đợi đấy...”"),t("br"),t("br"),n._v("Thanh âm càng ngày càng nhỏ, cho đến biến mất không thấy gì nữa."),t("br"),t("br"),n._v("Vốn cho là hắn muốn động thủ, không nghĩ tới, thời khắc mấu chốt, vậy mà trốn."),t("br"),t("br"),n._v("“Ta... Chờ ta một chút...”"),t("br"),t("br"),n._v("Gặp cái này đồng bạn như vậy không đáng tin cậy, Thần Tinh Hoàng cái kia còn dám tiếp tục lưu lại, cũng thân thể xoay một cái, vội vã hướng nơi xa bỏ chạy."),t("br"),t("br"),n._v("Hai người rời đi, Thần Dung Hoàng vẻ mặt lần nữa một trắng, cũng lại đứng thẳng không được, đặt mông ngồi dưới đất."),t("br"),t("br"),n._v("Vừa rồi đánh lén hai đại Hoàng giả khung xương, giờ phút này cũng giống là mất đi tất cả lực lượng, rớt xuống, như là một bãi bùn nhão, lại không còn trước đó khí lực cùng nội tình."),t("br"),t("br"),n._v("Hiển nhiên, Thần Dung Hoàng mới vừa nói hung mãnh, trên thực tế, đã là dầu hết đèn tắt, chỉ cần hai người này, lá gan hơi lớn một ít, chết khẳng định là hắn!"),t("br"),t("br"),n._v("“Dung Hoàng, niệm tình ngươi một đời hào kiệt, cho ngươi lưu cái tôn nghiêm... Tự sát đi!”"),t("br"),t("br"),n._v("Bốn phía lặng ngắt như tờ, một lát sau, Nhan Thanh Cổ Thánh cùng những người khác tộc Cổ Thánh bay tới."),t("br"),t("br"),n._v("Hai đại Hoàng giả chạy trốn, bọn họ vẫn còn ở đó."),t("br"),t("br"),n._v("“Tự sát?”"),t("br"),t("br"),n._v("Nhìn thấy nhiều người như vậy tộc Cổ Thánh xuất hiện ở trước mắt, Thần Dung Hoàng rõ ràng, lại không cách nào chạy trốn, than thở một tiếng, trong mắt tràn đầy cô đơn: “Không nghĩ tới, ta Thần Dung một thân tung hoành, cuối cùng sẽ chết ở đây!”"),t("br"),t("br"),n._v("Bàn tay nâng lên, liền muốn chụp về phía cái trán."),t("br"),t("br"),n._v("“Chậm!”"),t("br"),t("br"),n._v("Đúng lúc này, Trương Huyền bay tới."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1788"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1790"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);