(window.webpackJsonp=window.webpackJsonp||[]).push([[892],{1245:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-170-dan-đong-phong-van"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-170-dan-đong-phong-van"}},[n._v("#")]),n._v(" Chương 170: Dẫn động phong vân")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v(" Thiên Huyền Vương thành."),t("br"),t("br"),n._v(" “Bắc Vũ nhị đẳng Vương quốc Lưu sư, Trang sư cùng với Hàn Vũ Vương quốc Trịnh sư, đến rồi Vương thành, còn cùng đi bái phỏng vị này Dương Huyền?” "),t("br"),t("br"),n._v(" “Không sai, bái phỏng sau đó, nghe nói suốt đêm đem Lăng Thiên Vũ, Đỗ Mạc Hiên mấy người chiêu tiến Vương cung, xảy ra chuyện gì không biết, nhưng ngày thứ hai, ba vị danh sư lần thứ hai đích thân tới phủ đệ bái phỏng... Hơn nữa... Bọn họ liên tục bái phỏng năm ngày, thậm chí ngay cả một bóng người đều không thấy?” "),t("br"),t("br"),n._v(" “Mịa nó, quá mạnh đi, liền danh sư cũng không thấy? Lưu sư ta biết, tại nhất tinh bên trong đều được xem là khá cao, người như thế dĩ nhiên cũng bị chặn ở ngoài cửa... Vị này Dương sư đến cùng cái gì trình độ?” "),t("br"),t("br"),n._v(" “E sợ không chỉ là nhị tinh danh sư như thế đơn giản đi!” "),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v(" Dương Huyền danh tự này, không ai nghe qua, thậm chí giáo sư công hội cũng tuần tra không tới, thế cho nên không ít người mang theo bán tín bán nghi thái độ, cảm thấy hắn cũng không phải là chân chính danh sư."),t("br"),t("br"),n._v(" Hiện tại không giống."),t("br"),t("br"),n._v(" Lưu sư, Trịnh sư, Trang sư ba vị chân chính danh sư, thế nhưng người người đều nghe qua, coi như tại chung quanh mấy đại vương quốc, cũng đều tiếng tăm lừng lẫy, ba người dắt tay nhau bái phỏng, hơn nữa liên tiếp năm ngày, cửa đều chưa tiến vào, có ngốc cũng biết cái này Dương Huyền, khẳng định không đơn giản."),t("br"),t("br"),n._v(" “Là danh sư, lại không tra được... E sợ Dương Huyền danh tự này, cũng là giả...” "),t("br"),t("br"),n._v(" “Danh sư yêu thích du lịch, tôi luyện, trải nghiệm người bình thường sinh hoạt, do đó khai thác tâm cảnh, tăng lên linh hồn, có thể hay không vị này chính là nơi nào du lịch đến cao cấp danh sư?” "),t("br"),t("br"),n._v(" “Này liền không biết... Thật muốn là như vậy, Thiên Huyền Vương quốc chẳng phải muốn phát triển?” "),t("br"),t("br"),n._v(" “Đúng đấy, danh sư là quốc lực tượng trưng, nhất tinh trấn thủ, có thể xin nhị đẳng Vương quốc, nhị tinh trấn thủ, có thể xin nhất đẳng Vương quốc, nếu như nắm giữ tam tinh danh sư, cái kia là có thể xin phong hào!” "),t("br"),t("br"),n._v(" “Vương quốc một khi đạt đến phong hào, liền có tư cách thành lập to lớn truyền tống trận, mua bán ra đủ loại vật tư, vạn quốc đến bái, chỗ tốt lớn, khó có thể tưởng tượng...” "),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v(" Trong lúc nhất thời đủ loại thuyết pháp đều có, vang vọng toàn bộ Vương thành."),t("br"),t("br"),n._v(" “Bệ hạ, ba sư tuy không xác nhận, nhưng nằm ở hoài nghi giai đoạn, nhưng lấy lão nô xem, vị này Dương Huyền, khẳng định là có đại bản lĩnh, bằng không, cũng không thể đơn giản chữa khỏi nhiều người như vậy, dù cho không phải danh sư, e sợ cũng là một vị y đạo đại sư, lão tổ có lẽ... Có cứu.” "),t("br"),t("br"),n._v(" Vương cung, một cái lão thái giám phân tích hiện nay đạt được tin tức."),t("br"),t("br"),n._v(" “Ân, phái người chặt chẽ bảo vệ, một khi vị này Dương Huyền trở về, lập tức hướng ta bẩm báo, ta muốn đích thân nhìn.” Thẩm Truy bệ hạ ánh mắt nghiêm nghị."),t("br"),t("br"),n._v(" “Vâng!” "),t("br"),t("br"),n._v(" Lão thái giám liền vội vàng gật đầu."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v(" “Đây chính là cơ hội ngàn năm một thuở, Vương Siêu, nếu như vị này Dương sư một khi vừa ý chúng ta, trở thành học đồ, sau đó trở thành danh sư ngay trong tầm tay!” "),t("br"),t("br"),n._v(" Hồng Thiên học viện, rộng rãi lớp học, Lục Tầm đầy mặt hưng phấn mà nhìn trước mắt thanh niên."),t("br"),t("br"),n._v(" “Đúng đấy, bất luận thế nào cũng phải đi vào bái kiến, tranh thủ tại Dương sư trước mặt lưu cái ấn tượng tốt!” Vương Siêu cũng gật gù, hai mắt tỏa ánh sáng: “Dù cho không thể thành học đồ, đạt được chỉ điểm hai câu, cũng nhất định có thể lập tức đột phá.” "),t("br"),t("br"),n._v(" “Ừm!” Lục Tầm gật đầu."),t("br"),t("br"),n._v(" “Đúng rồi, Trương Huyền bên kia làm sao bây giờ?” Vương Siêu nhìn sang."),t("br"),t("br"),n._v(" “Hắn?” Lục Tầm lão sư ánh mắt nhìn về phía phía trước, mang theo cao cao tại thượng khí tức, hai tay một lưng: “Hắn chỉ là ta đi về danh sư trên đường đá đạp chân mà thôi, không tính là gì, yên tâm đi, ta tuyển lựa cùng đối chiến học viên, đều đang bí mật tu luyện, dùng không được mấy ngày, cũng có thể có đại cảnh giới đột phá, nhất định sẽ lấy to lớn nhất thắng lợi thắng lần này sư giả bình trắc, do đó dẫn tới rất nhiều danh sư chú ý!” "),t("br"),t("br"),n._v(" “Vậy thì tốt, ta liền sớm chúc mừng ngươi...” Vương Siêu hưng phấn ôm quyền."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v(" Tương tự cảnh tượng phát sinh tại Vương thành các nơi, danh sư xuất hiện, để hết thảy thế lực đều rung chuyển lên, từng cái từng cái nhìn cho phép Trương Huyền vị trí phủ đệ, trong nháy mắt, cái này không đáng chú ý tiểu viện phảng phất đã biến thành toàn bộ Vương thành trung tâm..."),t("br"),t("br"),n._v(" Dẫn động phong vân."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v(" Giờ khắc này Trương Huyền đã đi tới Vương quốc tàng thư khố tận cùng bên trong một loạt giá sách trước mặt."),t("br"),t("br"),n._v(" Năm ngày không ngớt không ngủ, liên tục lật sách, đã để hắn mệt bở hơi tai, đừng nói tu luyện, nói liên tục tâm tình đều không có."),t("br"),t("br"),n._v(" “Còn có hàng cuối cùng...” "),t("br"),t("br"),n._v(" Biết tới một lần Vương quốc tàng thư khố không dễ dàng, hắn dự định đem những sách này trước ấn đến Thiên Đạo thư viện lại nói, vẫn chưa để cho hình thành đối ứng Thiên Đạo thư tịch, dù vậy, cũng mệt đến ngất ngư, bất cứ lúc nào cũng sẽ ngã xuống."),t("br"),t("br"),n._v(" Nhìn thấy chỉ còn dư lại hàng cuối cùng, nhất thời hưng phấn hai mắt tỏa ánh sáng, nước mắt ào ào."),t("br"),t("br"),n._v(" Cực khổ rồi nhiều ngày như vậy, rốt cục nhìn thấy phần cuối."),t("br"),t("br"),n._v(" “Hả? Đây là liên quan với độc thư tịch...” "),t("br"),t("br"),n._v(" Đây là một loạt liên quan với thế nào dùng độc, chế độc, dùng độc, phân biệt độc thư tịch, cùng cái khác loại hình như thế, chỉ là cơ sở mà thôi, không tính là cao minh."),t("br"),t("br"),n._v(" “Lật xem!” "),t("br"),t("br"),n._v(" Ngón tay xẹt qua, một trận rào rào vang rền, từng quyển từng quyển thư tịch ấn vào đầu óc."),t("br"),t("br"),n._v(" Rất nhiều nghề nghiệp bên trong, có độc sư nghề nghiệp này, dựa theo uy lực, tuyệt đối có thể xếp tới bên trên cửu lưu, chỉ có điều cùng sát thủ tương tự, chỉ có thể ẩn giấu ở u ám nơi, người không nhận ra, rất nhiều người không muốn đi tuyển, cuối cùng bị trở thành hạ cửu lưu."),t("br"),t("br"),n._v(" Trương Huyền tự nhiên không muốn học tập độc sư, bất quá nhìn cũng không sao, có thể để phòng ngừa bị người hãm hại."),t("br"),t("br"),n._v(" Lòng hại người không thể có, nhưng nên có tâm phòng bị người."),t("br"),n._v(" Sau một canh giờ, liên quan với độc thư tịch tất cả xem xong."),t("br"),t("br"),n._v(" “Trở về trước ngủ một giấc...” "),t("br"),t("br"),n._v(" Đem tàng thư trong kho thư tịch tất cả ấn vào đầu óc, Trương Huyền lúc này mới cảm thấy mệt mỏi che ngợp bầu trời đè xuống, cả người như là bị đào trống không giống như vậy, liền muốn tìm một chỗ trực tiếp nằm xuống ngủ."),t("br"),t("br"),n._v(" Hắn tuy rằng đạt đến Ích Huyệt cảnh đỉnh phong, so người bình thường muốn mạnh hơn nhiều, có thể liên tục năm ngày năm đêm, không ngớt không ngủ, lại thêm lên tinh thần độ cao vận chuyển, mạnh hơn cũng không kiên trì được."),t("br"),t("br"),n._v(" Bất quá, tuy rằng mệt mỏi, nhưng cũng đáng giá."),t("br"),t("br"),n._v(" Thiên Huyền Vương quốc thành lập không dưới ngàn năm, trong đó tàng thư, chất lượng tuy rằng đều không cao, không tính là cái gì bí tịch, lại bao dung mỗi một cái nghề nghiệp, hơn nữa số lượng lớn."),t("br"),t("br"),n._v(" Tình huống như thế đối với những người khác tới nói, rất khó phân phân biệt thật giả, xem càng nhiều vượt hồ đồ, chơi không ít còn có thể tinh thần thác loạn, nhưng đối với nắm giữ Thiên Đạo thư viện Trương Huyền tới nói, căn bản không gọi sự tình."),t("br"),t("br"),n._v(" Chỉ cần hắn mong muốn, có thể bất cứ lúc nào hình thành Thiên Đạo thư tịch, đem hắn bên trong chính xác nhất tri thức hút ra đi ra, hóa thành thứ thuộc về chính mình."),t("br"),t("br"),n._v(" Đi ra Vương cung, nhìn một chút mặt trời, này mới phản ứng được, tại tàng thư khố ròng rã lưu lại năm ngày."),t("br"),t("br"),n._v(" “Vẫn chưa thể nghỉ ngơi, khoảng cách mua Phá Âm đan cùng Cự Tê thú huyết dịch gần như mười ngày, đồ vật nên đến...” "),t("br"),t("br"),n._v(" Đi trên đường lúc này mới nhớ tới, không thể trực tiếp đi ngủ, quãng thời gian này, vội vàng kiếm tiền, đọc sách, mười ngày kỳ hạn gần như đến, xin nhờ Âu Dương hội trưởng mua dược liệu, nên gần như đến."),t("br"),t("br"),n._v(" “Trước về một chuyến phủ đệ đi, chỉ mong mấy ngày nay, có người đến tìm, như vậy tiền liền có thể tập hợp...” "),t("br"),t("br"),n._v(" Suy nghĩ một chút, trên người tiền còn kém hai trăm vạn, cũng không do dự, tìm cái chỗ không người đổi đi y phục ngụy trang thành “Dương Huyền” dáng dấp, lúc này mới hướng phủ đệ phương hướng đi đến."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v(" Trương Liêu, mở ra im lặng là Thiên Huyền Vương quốc nổi tiếng phi tặc, chuyên môn đánh cắp nhà giàu, cướp của người giàu giúp người nghèo khó, được xem là nghĩa trộm."),t("br"),t("br"),n._v(" Bởi vì thân pháp linh xảo, thủ đoạn sạch sẽ, nhiều năm qua chưa bao giờ thất thủ, Thiên Huyền Vương quốc từng ra giá cao treo giải thưởng bắt lấy hai người, nhưng buồn cười chính là, lại liền tướng mạo của bọn họ cũng không biết."),t("br"),t("br"),n._v(" “Phía trước đây là Đỗ Kiều phủ đệ, cái tên này làm giàu bất nhân, làm nhiều việc ác, tuy rằng gặp phải đạo phỉ, chuyện làm ăn đại gặp khó gãy, nhưng gầy chết lạc đà so ngựa lớn, đi trộm bên trên một bút, liền đủ tốn không ít tháng ngày...” "),t("br"),t("br"),n._v(" Trương Liêu cười hì hì, nhanh chân về phía trước."),t("br"),t("br"),n._v(" Hai người bọn họ bởi vì bị đuổi bắt lợi hại, rời đi Thiên Huyền Vương thành nửa năm, sắp tới liền đem chủ ý đánh tới thương nhân Đỗ Kiều trên người."),t("br"),t("br"),n._v(" “Ân, bất quá, muốn giẫm tốt một chút, xác nhận hộ vệ cùng thói quen, mới năng động tay.” Mở ra im lặng gật gù."),t("br"),t("br"),n._v(" Hai người làm trộm, cũng không phải là nhìn thấy ai liền đi trộm ai, bình thường muốn quan sát rất lâu, xác định rõ phòng ngự bố cục, tìm đúng nhược điểm, lại một đòn chắc chắn bên trong."),t("br"),t("br"),n._v(" Chính vì như thế, làm trộm không dưới năm mươi lên, chưa bao giờ thất thủ."),t("br"),t("br"),n._v(" “Phía trước tòa phủ đệ này chính là, ta đã tới một lần... A?” "),t("br"),t("br"),n._v(" Trương Liêu nhanh chân về phía trước, chuyển qua một lối đi, đang muốn chạy tới muốn trộm trước phủ đệ, cẩn thận quan sát, lập tức sợ hết hồn."),t("br"),t("br"),n._v(" Mở ra im lặng cũng sắc mặt trắng nhợt, nhìn trước mắt, con mắt trừng lớn."),t("br"),t("br"),n._v(" Chỉ thấy tòa phủ đệ này trước cửa, xếp hàng đầy hàng dài, đủ có mấy trăm người đứng ở phía trước, nếu như chỉ là nhiều người ngược lại cũng thôi, mấu chốt nhất chính là... Phủ đệ đại cửa đóng chặt, tựa hồ căn bản không mở ra dự định."),t("br"),t("br"),n._v(" “Mau nhìn, cái kia... Không phải Bát Tí Thần Long, Lưu Nguyên Khải sao?” "),t("br"),t("br"),n._v(" Đột nhiên Trương Liêu nhận ra một người, sắc mặt trắng nhợt."),t("br"),t("br"),n._v(" “Là hắn... Bát Tí Thần Long Lưu Nguyên Khải, tán tu bên trong nổi tiếng nhân vật tuyệt thế, Thông Huyền cảnh hậu kỳ vô thượng cường giả, bởi vì một lần vây bắt man thú bị thương, biến mất ở tầm mắt mọi người, chạy thế nào nơi này đến rồi? Hơn nữa, đứng ở trước cửa?” Mở ra im lặng cũng chấn động toàn thân."),t("br"),t("br"),n._v(" “Cái kia là... Long Nham Thiết Thủ Phong Tuấn? Hàn Tuyết thành đệ nhất cao thủ, một đôi thiết thủ khai bia liệt thạch, tiến vào Vương cung, Thẩm Truy bệ hạ đều ngang hàng xưng hô siêu cường tồn tại, tu vi càng là Thông Huyền cảnh trung kỳ, làm sao cũng ở nơi đây... Xếp hàng?” "),t("br"),t("br"),n._v(" “Trác Ưng Thần Nhãn Đoàn Cửu Giang? Chông sắt Long Hiên Hải? Bạch diện thư sinh, Hồ Hiếu Bạch...” "),t("br"),t("br"),n._v(" Vốn tưởng rằng bên ngoài xếp hàng, đều là chút người bình thường, nhận ra một cái, cẩn thận về phía sau nhìn lại, Trương Liêu toàn thân run lên, suýt chút nữa không tại chỗ hù chết..."),t("br"),t("br"),n._v(" Những người này, mỗi một cái đều là Thiên Huyền Vương quốc cao thủ nổi danh, kém cỏi nhất cũng đều đạt đến Thông Huyền cảnh sơ kỳ."),t("br"),t("br"),n._v(" Bọn họ mặc dù có chút thủ đoạn, nhưng thật muốn gặp phải những người này, e sợ chạy đều chạy không thoát, sẽ bị tại chỗ đánh chết."),t("br"),t("br"),n._v(" Chỉ là..."),t("br"),t("br"),n._v(" Thực lực như vậy cao thủ, tất cả đều kiêu căng khó thuần, coi trời bằng vung, lúc trước Thẩm Truy bệ hạ chuyên môn mời, đều không mời tới mấy vị, làm sao tất cả đều chạy cái cửa này đứng yên xếp hàng?"),t("br"),t("br"),n._v(" Ta sẽ không nhìn lầm đi..."),t("br"),t("br"),n._v(" Trương Liêu, mở ra im lặng hai người hai mặt nhìn nhau, từng cái từng cái liên tục nắm tóc, sắp điên rồi."),t("br"),t("br"),n._v(" Nếu như những người này chỉ là xếp hàng đứng yên ngược lại cũng thôi, then chốt từng cái từng cái không dám thở mạnh, chỉ lo đắc tội trong sân người, thậm chí bị người quan ở bên ngoài... Liền cửa cũng không dám gõ..."),t("br"),t("br"),n._v(" Giời ạ, trời xanh, đại địa, ai có thể nói cho ta, là đám người kia điên rồi, vẫn là ta điên rồi?"),t("br"),t("br"),n._v(" Này không phải Đỗ Kiều phủ đệ sao?"),t("br"),t("br"),n._v(" Một cái nho nhỏ thương nhân, tại sao có thể có lớn như vậy lực uy hiếp?"),t("br"),t("br"),n._v(" Để như thế một đám người, cam tâm tình nguyện cùng ở ngoài cửa, phí lời cũng không dám nhiều lời?"),t("br"),t("br"),n._v(" (Chưa xong còn tiếp.) "),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-178"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-180"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);