(window.webpackJsonp=window.webpackJsonp||[]).push([[894],{1249:function(n,h,t){"use strict";t.r(h);var c=t(44),i=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1779-cho-danh-su-đuong-mot-cau-tra-loi"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1779-cho-danh-su-đuong-mot-cau-tra-loi"}},[n._v("#")]),n._v(" Chương 1779: Cho Danh Sư đường một câu trả lời")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1779: Cho Danh Sư đường một câu trả lời"),t("br"),t("br"),n._v("“Vậy ngươi nói làm sao bây giờ?”"),t("br"),t("br"),n._v("Buồn bực phút chốc, Nhan Thanh Cổ Thánh lần nữa nhìn qua."),t("br"),t("br"),n._v("“Rất đơn giản, Thần Dung Hoàng không những không thể chết, còn muốn giúp hắn khôi phục thương thế, sau đó... Để hắn trở lại trong tộc báo thù! Thần Linh Hoàng, Thần Tinh Hoàng tuy là yếu đi chút, nhưng có thể cùng đối phương tranh đấu nhiều năm, tuyệt không phải đơn giản như vậy liền có thể đối phó... Một khi tam phương sinh ra hỗn chiến, nhân tộc liền có thể nghỉ ngơi lấy lại sức, thậm chí có thể thừa dịp!”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("Đối thoại của bọn họ, đều là truyền âm, Nhan Thanh Cổ Thánh càng là trước thời hạn còn ngăn cách không gian, bởi vậy, Thần Dung Hoàng cũng không nghe trò chuyện nội dung."),t("br"),t("br"),n._v("“Hơn nữa, Thần Dung Hoàng hiện tại sinh tử đều tại chúng ta một ý niệm, hiện nay thống hận nhất, khẳng định là hai vị khác, nằm mộng cũng muốn lấy để hắn chết đi! Lúc này tới ký kết không xâm phạm lẫn nhau hiệp ước, một khi hắn lần nữa khống chế Dị Linh nhất tộc, đối với chúng ta sẽ có lợi ích rất lớn!”"),t("br"),t("br"),n._v("Trương Huyền tiếp tục nói."),t("br"),t("br"),n._v("“Không sai!” Trương Hoằng Thiên mắt sáng lên."),t("br"),t("br"),n._v("Đã có thể cùng Thần Linh Hoàng đám người ký hiệp ước, tự nhiên cũng có thể cùng trước mắt vị này ký."),t("br"),t("br"),n._v("Lấy Dung Hoàng tại trong tộc uy tín, thật muốn đồng ý, hiệu quả chỉ sợ so cái trước tốt hơn nhiều."),t("br"),t("br"),n._v("“Ngươi nói không sai, nói như vậy lên, Thần Dung Hoàng quả thực tạm thời không thể chết! Bất quá... Có vài điểm, muốn trước thời hạn cùng ngươi nói, để ngươi biết!”"),t("br"),t("br"),n._v("Suy tư phút chốc, cũng biết hắn nói hết sức chính xác, Nhan Thanh Cổ Thánh lắc đầu, nhìn qua: “Thứ nhất, Thần Dung Hoàng một khi khôi phục, thực lực vượt xa quá chúng ta, thật muốn cường công, Danh Sư đường cùng Chư Tử bách gia loại trừ cố thủ đại bản doanh, hầu như không cách nào ngăn cản! Thứ hai, hắn túc trí đa mưu, dụng binh như thần, lại thêm cùng nhân tộc đối chiến nhiều năm, thậm chí tới qua nhân tộc, tiến vào Danh Sư đường, đối với chúng ta như lòng bàn tay... Sống sót, dù là có thể trong thời gian ngắn không tấn công, chỉ khi nào tấn công, nhất định là lớn nhất tai nạn!”"),t("br"),t("br"),n._v("“Thứ ba... Lần này chúng ta đều bị thương rất nặng, cũng không đủ Cổ Thánh lực lượng khôi phục, tuổi thọ toàn bộ tiếp cận đại nạn... Lại không bảo vệ được nhân tộc mấy lần.”"),t("br"),t("br"),n._v("Thật ra thì bọn họ cũng biết, phục kích Thần Dung Hoàng, cũng không thể một lần vất vả suốt đời nhàn nhã, làm không cẩn thận, sẽ còn xuất hiện càng ma túy hơn phiền."),t("br"),t("br"),n._v("Nhưng không có thứ hai con đường có thể đi."),t("br"),t("br"),n._v("Một vạn năm trước, Cổ Thánh lực lượng biến mất, nhân tộc Cổ Thánh chỉ có thể dựa vào rơi vào trạng thái ngủ say, kéo dài hơi tàn."),t("br"),t("br"),n._v("Nói cách khác... Bọn họ đám này Cổ Thánh, nhỏ tuổi nhất Trương Hoằng Thiên, cũng đều một vạn hơn năm trăm tuổi... Không có trọng thương lời nói, còn có thể thức tỉnh mấy lần, mà bây giờ, không dùng đến hai lần, liền sẽ hoàn toàn chết đi."),t("br"),t("br"),n._v("Hiện tại Xuân Thu đại điển lại không đạt được... Nhân tộc đã là vài vạn năm đến, yếu nhất thời điểm."),t("br"),t("br"),n._v("Vị này Thần Dung Hoàng nếu là chết rồi, ngược lại cũng thôi, còn lại hai vị Hoàng giả, bất luận thực lực còn là mưu kế, đều hơi yếu, nhân tộc lại kém, cũng có thể chống lại một hai... Không chết, đằng sau liền thật không biết như thế nào."),t("br"),t("br"),n._v("Ở đây rất nhiều Cổ Thánh, tất cả đều ảm đạm xuống."),t("br"),t("br"),n._v("Nhân tộc trời sinh tuổi thọ không bằng Dị Linh tộc nhân, đây là chuyện không có cách giải quyết."),t("br"),t("br"),n._v("Tuổi thọ hạn chế bên dưới, không còn Cổ Thánh lực lượng... Không cách nào sinh ra mới Cổ Thánh, sớm muộn đều sẽ bị người sau mài chết."),t("br"),t("br"),n._v("“Cái này đơn giản... Ta có thể cho hắn hạ một loại không cách nào hóa giải kịch độc, nếu như hắn về sau dám vi phạm ước định, đối nhân tộc tấn công, cái này độc, cũng đủ để cho hắn tử vong! Đến lúc đó, ba đại Hoàng giả, tất cả đều không còn, nhân tộc coi như không có Cổ Thánh, cũng không có nguy hiểm như vậy! Huống chi, Dương sư mới vừa vặn đột phá, không có gì bất ngờ xảy ra, lần này Khổng miếu cơ duyên bên dưới, sẽ còn có mới Cổ Thánh xuất hiện, an toàn bên trên, không dùng qua chia sẻ tâm!”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("Tuy là hắn xuân thu đồ phía trong, nắm giữ Cổ Thánh lực lượng, nhưng số lượng cũng có hạn, hắn bên này, cha mẹ, bản thân, rất nhiều đệ tử đột phá, cũng chưa chắc có thể, cũng liền không tiện phân cho những người khác."),t("br"),t("br"),n._v("“Cái gì độc, đối tích huyết trùng sinh cường giả đều hữu dụng?”"),t("br"),t("br"),n._v("Nhan Thanh Cổ Thánh sững sờ."),t("br"),t("br"),n._v("Tích huyết trùng sinh cường giả, mỗi một giọt máu, cũng có thể lần nữa diễn hóa sinh mệnh, bất luận thân thể còn là linh hồn, sớm đã trong sáng không một hạt bụi, bách độc bất xâm."),t("br"),t("br"),n._v("Chẳng lẽ trên thế giới này còn có kịch độc, có thể đối loại này cường giả, tạo thành ảnh hưởng?"),t("br"),t("br"),n._v("“Cái này ngươi không cần phải để ý đến, ta đã dám như thế nói, tự nhiên có!”"),t("br"),t("br"),n._v("Trương Huyền cười cười."),t("br"),n._v("Bình thường độc, quả thực đối loại này cường giả vô dụng, nhưng... Thiên đạo chân khí, hết sức nhỏ vô cùng, có thể chui vào thân thể bất kỳ một chỗ, coi như tích huyết trùng sinh, đồng dạng có thể phân tán đến mỗi một giọt máu bên trong che giấu, khó lòng phòng bị."),t("br"),t("br"),n._v("Khôi phục thực lực ở dưới tích huyết trùng sinh, có thể không e ngại Thiên đạo trừng phạt, nhưng đối loại này có thể phá hoại sinh cơ chân khí, khẳng định đồng dạng sẽ nhức đầu không thôi, khó mà loại trừ."),t("br"),t("br"),n._v("Chỉ cần loại trừ không được, liền sẽ vĩnh viễn bị quản chế."),t("br"),t("br"),n._v("Nghe được hắn đều chuẩn bị xong, đám người thở phào nhẹ nhõm, Nhan Thanh Cổ Thánh lần nữa nhìn lại, mang theo áp bức: “Chuyện của hắn, chúng ta có thể dựa theo ngươi nói xử lý, Thần Dung Hoàng cũng có thể tạm thời không chết, nhưng... Chuyện của ngươi, có phải hay không cũng nên cho nhân tộc, một câu trả lời!”"),t("br"),t("br"),n._v("“Ta?”"),t("br"),t("br"),n._v("“Ngươi dẫn dắt Dị Linh tộc Linh Thần đi vào chủ điện, để hắn thành công luyện hóa Xuân Thu đại điển, mà thờ ơ, khiến nhân tộc từ đó mất đi chí bảo... Chuyện này, coi như muốn che lấp, cũng không che giấu được, thậm chí, chạy trốn Thần Linh Hoàng đám người, đều đã tuyên truyền đi ra ngoài. Không cho cái bàn giao, ta sợ thiên hạ danh sư, tất cả Nhân tộc, đều sẽ đem ngươi trở thành dị loại mà đối đãi, Danh Sư đường, cũng lại không dung thân chỗ!”"),t("br"),t("br"),n._v("Nhan Thanh Cổ Thánh nói."),t("br"),t("br"),n._v("Trương Huyền lặng im."),t("br"),t("br"),n._v("Hắn không biết Lạc Nhược Hi chân thực thân phận là Linh Thần."),t("br"),t("br"),n._v("Bất quá, giải thích như vậy, người trong thiên hạ, chắc chắn sẽ không tin tưởng."),t("br"),t("br"),n._v("Xuân Thu đại điển, là Khổng sư lưu lại chí bảo, nhân tộc mạnh nhất tín niệm, giờ phút này lại bị Dị Linh tộc nhân đạt được, chỉ điều này, liền không có cách nào để cho người ta tha thứ."),t("br"),t("br"),n._v("Tin tức một khi truyền ra, có thể đoán được, thiên hạ danh sư, tất cả Nhân tộc, đều sẽ coi hắn là thành dị loại, hết đường chối cãi."),t("br"),t("br"),n._v("Tìm Linh Thần làm bạn gái... Không phải phản bội là cái gì?"),t("br"),t("br"),n._v("Lúc đó đều bắt lại tay của đối phương, vì sao không đem Xuân Thu đại điển mang về?"),t("br"),t("br"),n._v("Mặc kệ trước ngươi làm bao nhiêu cống hiến, chỉ điều này, đạo đức bắt cóc, liền có thể trở thành chuột chạy qua đường, lại không xoay người đường sống."),t("br"),t("br"),n._v("“Ta... Sẽ cho người trong thiên hạ một câu trả lời, cho Danh Sư đường một câu trả lời!”"),t("br"),t("br"),n._v("Hít sâu một hơi, Trương Huyền ánh mắt kiên định."),t("br"),t("br"),n._v("“Vậy là tốt rồi...”"),t("br"),t("br"),n._v("Gặp hắn đã suy nghĩ ra, Nhan Thanh Cổ Thánh gật gật đầu, không nói thêm lời."),t("br"),t("br"),n._v("Đối phương chẳng qua một cái Kim Thân cảnh, hắn còn khinh thường tự thân ra tay."),t("br"),t("br"),n._v("Thảo luận xong xuôi, còn lại liền đơn giản."),t("br"),t("br"),n._v("Trương Huyền đem Thiên đạo chân khí, tràn vào Thần Dung Hoàng trong cơ thể, ẩn núp, đồng thời lấy ra một ít đan dược, linh lực, hỗ trợ khôi phục thương thế."),t("br"),t("br"),n._v("Bất quá, cái tên này thương thế thực tế quá nặng, đã thương tổn tới cơ sở cùng tuổi thọ, bình thường đan dược, đã không có quá lớn hiệu quả."),t("br"),t("br"),n._v("“Đa tạ thiếu gia cứu ta tính mạng... Ta chết không có gì đáng tiếc, nhưng cho dù chết, cũng muốn báo thù rửa hận!”"),t("br"),t("br"),n._v("Mặc dù không biết hai bên nói thứ gì, nhưng biết là trước mắt vị này van xin hộ cứu tính mạng của hắn, Thần Dung Hoàng đứng dậy ôm quyền."),t("br"),t("br"),n._v("Thần Linh Hoàng, Thần Tinh Hoàng, hắn trước kia cũng không thèm để ý, không nghĩ tới, lần này trồng như vậy lớn té ngã."),t("br"),t("br"),n._v("Đã lần này có thể còn sống trở về, chắc chắn sẽ để cho bọn họ thừa nhận lửa giận của mình, trả giá thê thảm đau đớn đánh đổi."),t("br"),t("br"),n._v("“Không cần khách khí!”"),t("br"),t("br"),n._v('Cứu hắn là vì nhân tộc, cũng không phải là người tư lợi, Trương Huyền lắc đầu, nhìn lại, trong mắt mang theo hỏi thăm: "Ngươi cùng Ngoan Nhân, là quan hệ như thế nào?'),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1790"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1792"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);