(window.webpackJsonp=window.webpackJsonp||[]).push([[896],{1251:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1781-truong-hoang-thien-van-lac-【-canh-thu-nhat-】"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1781-truong-hoang-thien-van-lac-【-canh-thu-nhat-】"}},[n._v("#")]),n._v(" Chương 1781: Trương Hoằng Thiên vẫn lạc 【 canh thứ nhất 】")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1781: Trương Hoằng Thiên vẫn lạc "),t("br"),t("br"),n._v("Dương sư đoán không sai, Trương Hoằng Thiên sở dĩ muốn ở thời điểm này khiêu chiến Dục lão quỷ, lớn nhất lực lượng chính là biết hắn công pháp bên trong chiêu số cùng thiếu hụt."),t("br"),t("br"),n._v("Lúc này thoạt nhìn, nằm ở bị động, trên thực tế cùng đối phương đồng dạng, ngay tại tìm cơ hội, tranh thủ một kích tất trúng, tại chỗ chém giết!"),t("br"),t("br"),n._v("Vô số đạo kiếm khí hóa thành từng đầu cự long, hướng lên bầu trời bao phủ, Dục lão quỷ quỷ trảo vụt qua, đi ngược dòng nước."),t("br"),t("br"),n._v("“Ngay vào lúc này!”"),t("br"),t("br"),n._v("Thấy đối phương thiếu hụt, cùng Trương Huyền nói tới giống nhau như đúc, Trương Hoằng Thiên cũng lại kìm nén không được, trường kiếm chợt lóe, lập tức ẩn giấu ở trường long bên trong, đâm tới."),t("br"),t("br"),n._v("Lưu Thủy Tàng Kiếm!"),t("br"),t("br"),n._v("Nghe nói là Trương gia một vị si tình thiên tài sáng tạo, vợ hắn bị kẻ thù giết chết, người sau am hiểu dùng nước, thậm chí mượn nước ẩn trốn, khó mà bắt, vì báo thù, sáng chế bộ này kiếm quyết, đem kiếm che giấu tại dòng nước bên trong, đối đãi đối phương mượn thủy chi lúc, đâm xuyên qua cổ họng."),t("br"),t("br"),n._v("Trương Hoằng Thiên giờ phút này mượn kiếm khí trường long, che giấu trường kiếm, hiệu quả như nhau cung."),t("br"),t("br"),n._v("“Phá!”"),t("br"),t("br"),n._v("Dục lão quỷ quả nhiên không có phát hiện trường kiếm, quỷ trảo trên không trung không ngừng lay động, ngăn cản kiếm khí."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Trường long run lên, trường kiếm chui ra, tại trong con mắt hắn càng lúc càng lớn."),t("br"),t("br"),n._v("Bất quá, giờ phút này lực lượng của hắn, đang ở tại lực cũ suy kiệt, lực mới không tế thời điểm, cũng lại trốn tránh có điều, “Phốc!” Một tiếng, liền xuyên qua cổ họng, trong đó mênh mông lực lượng, không ngừng hủy diệt lấy trong cơ thể hắn sinh cơ."),t("br"),t("br"),n._v("“Ngươi... Ngươi... Làm sao lại biết, ta chiêu này kết thúc, có một phần trăm cái hô hấp chân khí dừng lại?”"),t("br"),t("br"),n._v("Hai mắt trợn tròn xoe, Dục lão quỷ đến chết vẫn không tin nổi, liền như thế bị giết!"),t("br"),t("br"),n._v("Một vạn năm đến, hai người không biết chiến đấu qua bao nhiêu tràng, đối với đối phương thủ đoạn cùng năng lực sớm đã rõ như lòng bàn tay, cái tên này, là mạnh hơn hắn một ít, thế nhưng mạnh có hạn, thắng không nổi, bảo vệ tính mạng không khó..."),t("br"),t("br"),n._v("Nhưng bây giờ..."),t("br"),t("br"),n._v("Hắn quỷ trảo liên hoàn, liên tục thi triển, thứ mười bảy chiêu thời điểm, có một phần trăm hô hấp dừng lại, chỗ thiếu hụt này, bởi vì tốc độ quá nhanh, cùng nhau tu luyện bằng hữu, đều không biết, thậm chí Thần Linh Hoàng, đều không có phát giác."),t("br"),t("br"),n._v("Kẻ trước mắt này, đến cùng như thế nào biết được, hơn nữa vừa vặn bắt lại cái này thời gian ngắn ngủi, một lần phá vỡ phòng ngự của mình?"),t("br"),t("br"),n._v("“Ngươi thua!”"),t("br"),t("br"),n._v("  Chẳng muốn trả lời đối phương, Trương Hoằng Thiên thở phào nhẹ nhõm, hai hàng lông mày bỗng nhiên giương lên."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Trên thân kiếm ẩn chứa vô số kiếm khí, lập tức nổ tung, Dục lão quỷ cũng lại không chịu nổi, thân thể đột nhiên nổ tung."),t("br"),t("br"),n._v("Xì xì xì!"),t("br"),t("br"),n._v("Đúng lúc này, một quyển sách bay tới, đem nổ tung lên huyết nhục, lắc lư một cái, thôn phệ sạch sẽ."),t("br"),t("br"),n._v("Ngẩng đầu nhìn lại, ngay sau đó nhìn thấy Trương Huyền chẳng biết lúc nào đã đem áo tơi luyện hóa, ôm quyền nhìn lại: “Chúc mừng tiên tổ!”"),t("br"),t("br"),n._v("“Có thể đem chém giết, còn muốn làm phiền ngươi!”"),t("br"),t("br"),n._v("Cười ha ha một tiếng, Trương Hoằng Thiên tâm cảnh thả lỏng phía dưới, không thể kiên trì được nữa, té ngã trên đất."),t("br"),t("br"),n._v("Mặc dù đem Dục lão quỷ chém giết, hắn cũng đã dầu hết đèn tắt, lực lượng triệt để hao hết."),t("br"),t("br"),n._v("“Lão tổ...”"),t("br"),t("br"),n._v("Vội vàng đi tới trước mặt, Trương Huyền Thiên đạo chân khí, rót tràn vào đi, lại phát hiện, trước mắt vị này mạnh mẽ Cổ Thánh, thân thể đã cùng phá bao tải đồng dạng, cũng không còn cách nào gánh chịu chân khí lực lượng."),t("br"),n._v("“Điều đó không có khả năng...”"),t("br"),t("br"),n._v("Một cái luống cuống, vội vàng lấy ra không thiếu bảo mệnh cấp chín đan dược, không cần tiền đồng dạng đưa vào đối phương trong miệng."),t("br"),t("br"),n._v("Hắn Thiên đạo chân khí, mọi việc đều thuận lợi, thậm chí hoàn toàn có thể khởi tử hồi sinh, không có tác dụng gì, còn là lần đầu tiên gặp phải."),t("br"),t("br"),n._v("Vị này Trương Hoằng Thiên, là Trương gia vị cuối cùng Cổ Thánh lão tổ, bất luận tính tình bản tính, đều mười phần ưa thích, ngàn vạn không thể có sự tình."),t("br"),t("br"),n._v("“Ta đến xem...”"),t("br"),t("br"),n._v("Nhan Thanh Cổ Thánh bay tới, đem Trương Hoằng Thiên vịn ngồi dậy, ngón tay khoác lên trên mạch môn, nhìn phút chốc, trên mặt lộ ra vẻ ảm đạm, lắc đầu: “Hắn... Tuổi thọ đã tới! Hết cách xoay chuyển...”"),t("br"),t("br"),n._v("“Cái này sao có thể? Hắn tại một đám người bọn ngươi bên trong, tuổi tác rất nhỏ, làm sao lại tuổi thọ trước hết nhất đạt tới...”"),t("br"),t("br"),n._v("Trương Huyền không tin."),t("br"),t("br"),n._v("Vị này tiên tổ, thiên tư trác tuyệt, chừng năm trăm tuổi liền tu đến Cổ Thánh cấp bậc, mệnh danh lúc ấy thiên phú cao nhất siêu cấp cường giả, tuy là sống một vạn năm, nhưng hầu như đều đang ngủ say, dựa theo tình huống bình thường, coi như tuổi thọ đến, cũng muốn so những người khác sống lâu thêm rất lâu."),t("br"),t("br"),n._v("Làm sao có thể người khác không có việc gì, không chịu nổi trước?"),t("br"),t("br"),n._v("“Cùng Thần Dung Hoàng đánh một trận, hắn liền đem tinh huyết trong cơ thể, tuổi thọ tiêu hao tám chín phần mười, vì chém giết Dục lão quỷ, càng là không giữ lại chút nào... Ta cũng không có phương pháp!”"),t("br"),t("br"),n._v("Tuy là đã sớm biết, bọn họ sớm muộn đều sẽ vẫn lạc, Nhan Thanh Cổ Thánh vẫn như cũ lộ ra vẻ cô đơn, nhịn không được lắc đầu."),t("br"),t("br"),n._v("Cổ Thánh có thể đoạn chi lại nối tiếp, tích huyết trùng sinh, vẫn như trước ngăn cản không nổi tuổi thọ đại nạn."),t("br"),t("br"),n._v("Kỳ hạn đi tới, mạnh hơn thực lực, cũng chỉ có thể hóa thành một nắm bụi bặm, mặc ngươi vương công tướng tướng, mặc ngươi thăng đấu tiểu dân, đều tránh không khỏi."),t("br"),t("br"),n._v("Liền giống như vị này Trương Hoằng Thiên, tuổi tác tuy là tại rất nhiều Cổ Thánh bên trong tương đối nhỏ, có thể mỗi lần chiến đấu, đều không giữ lại chút nào, trong cơ thể sinh cơ, đã sớm phá hủy."),t("br"),t("br"),n._v("Nếu không phải hai mươi năm trước, thay đổi huyết dịch, khả năng sớm đã tử vong."),t("br"),t("br"),n._v("Có thể lại kiên trì lâu như vậy, thậm chí trải qua hai trận chiến đấu, đã vượt qua tưởng tượng."),t("br"),t("br"),n._v("“Dương sư, ngươi không phải năng thay đổi huyết dịch ư? Máu của ta, còn có thể dùng...” Trương Huyền vội vàng nhìn về phía Dương Huyền."),t("br"),t("br"),n._v("Gặp hắn bộ dáng này, Dương Huyền cũng đầy là bất đắc dĩ lắc đầu: “Sư huynh... Máu của ngươi đã thay đổi qua một lần, đối với hắn mà nói, lại không tác dụng...”"),t("br"),t("br"),n._v("“Nhưng...” Trương Huyền còn muốn nói nhiều cái gì, bị Dương sư cắt đứt: “Sinh lão bệnh tử, đây là quy luật, Cổ Thánh cũng không cách nào tránh khỏi, năm đó Trương gia chín đại Cổ Thánh, tung hoành thiên địa, mạnh mẽ biết bao, mà bây giờ, đồng dạng toàn bộ biến thành từng cái bài vị, chỉ có thể hưởng thụ hậu nhân ngưỡng mộ!”"),t("br"),t("br"),n._v("Trương Huyền nói không ra lời."),t("br"),t("br"),n._v("“Không cần bi thương!”"),t("br"),t("br"),n._v("Trương Hoằng Thiên khôi phục chút tinh thần, nhìn thấy hắn cái bộ dáng này, mỉm cười, trong mắt mang theo yêu thương: “Ta vẫn muốn kiên trì, là bởi vì, Trương gia chỉ còn lại có ta một vị Cổ Thánh, một khi tử vong, toàn cả gia tộc đều sẽ suy yếu... Hiện tại, ngươi xuất hiện, nắm giữ Cổ Thánh cấp bậc trường đao, cùng đủ loại thủ đoạn, chân chính chém giết, so với ta, cũng không yếu! Lại không có gì có thể lo lắng!”"),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Trương Huyền hốc mắt đỏ lên."),t("br"),t("br"),n._v("“Có sinh thì có tử, sinh mệnh vô thường, điểm ấy ta đã sớm biết! Kể từ khi biết giữa thiên địa lại không Cổ Thánh lực lượng, ta quyết định ngủ say một khắc kia trở đi... Trương Hoằng Thiên cũng đã chết! Kéo dài hơi tàn, chỉ là vì để tộc nhân, để nhân tộc, đi càng xa mà thôi.”"),t("br"),t("br"),n._v("Trương Hoằng Thiên nhìn lại: “Hiện tại, ta rốt cục có thể bỏ xuống trọng trách, không tại lo lắng, xem như giải thoát rồi, còn có cái gì có thể thương tâm? Ngược lại là ngươi, chẳng qua Kim Thân cảnh đỉnh phong, liền gánh vác lên thủ hộ gia tộc trách nhiệm... So ta càng thêm gian nan!”"),t("br"),t("br"),n._v("Nói xong những này, cảm nhận được trong cơ thể sinh cơ trôi đi mất, biết lại khó vãn hồi, Trương Hoằng Thiên quay đầu ngắm nhìn bốn phía, cười ha ha một tiếng."),t("br"),t("br"),n._v("“Tốt, chư vị bằng hữu, chỉ mong đời sau còn có thể cùng một chỗ kề vai chiến đấu! Ta Trương Hoằng Thiên, có chút muốn lười biếng, trước hết đi cáo từ...”"),t("br"),t("br"),n._v("Lời còn chưa dứt, con mắt đóng lại, hạp nhiên mà qua."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1792"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1794"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);