(window.webpackJsonp=window.webpackJsonp||[]).push([[897],{1253:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1782-truong-huyen-hon-me-【-canh-thu-hai-】"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1782-truong-huyen-hon-me-【-canh-thu-hai-】"}},[n._v("#")]),n._v(" Chương 1782: Trương Huyền hôn mê 【 canh thứ hai 】")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1782: Trương Huyền hôn mê "),t("br"),t("br"),n._v("“Hoằng Thiên Cổ Thánh...”"),t("br"),t("br"),n._v("Nhìn trước mắt thi thể, tất cả mọi người nói không ra lời, từng cái vẻ mặt ngưng trọng."),t("br"),t("br"),n._v("Đặc biệt là Danh Sư đường đám người, càng là toàn bộ lặng im."),t("br"),t("br"),n._v("Bọn họ cùng một chỗ chiến đấu, duy trì một vạn năm, đã sớm biết sẽ có ngày này, nhưng chân chính đến, vẫn như cũ cảm thấy trong lòng có sự cảm thông."),t("br"),t("br"),n._v("“Cho dù cường đại như Cổ Thánh, vẫn như cũ chạy không khỏi thời gian pháp tắc!”"),t("br"),t("br"),n._v("Lắc đầu, Nhan Thanh Cổ Thánh than thở."),t("br"),t("br"),n._v("“Cổ Thánh không phải vượt qua Thiên đạo sao? Làm sao sẽ còn bị thời gian ngăn cấm?” Trương Huyền quay đầu."),t("br"),t("br"),n._v("Cổ Thánh là vượt qua Thiên đạo tồn tại, đã như vậy, như thế nào lại bị thời gian chế tạo khống chế, tuổi thọ cũng không khỏi bản thân?"),t("br"),t("br"),n._v("“Cổ Thánh là vượt qua Thiên đạo, nhưng... Vượt qua chỉ là bản thân lĩnh ngộ pháp tắc! Như Trương Hoằng Thiên, hắn lĩnh ngộ kiếm đạo, vượt qua Thiên đạo tồn tại, tuổi thọ bên trên, vẫn như cũ không làm được!”"),t("br"),t("br"),n._v("Ngạn thanh Cổ Thánh nói."),t("br"),t("br"),n._v("“Kiếm đạo? Ý của ngươi là... Chỉ cần lĩnh ngộ pháp tắc, có một bộ phận vượt qua Thiên đạo, chẳng khác nào có thể đặt chân Cổ Thánh?” Trương Huyền sửng sốt."),t("br"),t("br"),n._v("Như thế nào đột phá Cổ Thánh, hắn nghe cha Hưng kiếm thánh nói qua, chỉ bất quá, đối phương chỉ là Đại Thánh cấp bậc, biết, đều là tin đồn, Nhan Thanh Cổ Thánh, là thật Chư Tử bách gia đỉnh cao cường giả, biết khẳng định càng thêm chuẩn xác."),t("br"),t("br"),n._v("“Không sai, lĩnh ngộ pháp tắc đột phá Thiên đạo, mới có thể thành tựu Cổ Thánh, nếu không, Cổ Thánh làm sao có thể nắm giữ loại lực lượng này?” Nhan Thanh Cổ Thánh gật đầu."),t("br"),t("br"),n._v("“Trương gia không phải am hiểu thời gian ư? Vì sao không dùng thời gian pháp tắc đột phá?” Trương Huyền hỏi lần nữa."),t("br"),t("br"),n._v("Trương gia huyết mạch bên trong, đều ẩn chứa thời gian pháp tắc."),t("br"),t("br"),n._v("Đã như vậy, thời gian khống chế bên trên, đột phá Thiên đạo, có thể hay không tuổi thọ thật to kéo dài? Nhảy ra thời gian ràng buộc?"),t("br"),t("br"),n._v("“Thời gian pháp tắc, không chỉ chảy xuôi tại danh sư đại lục thế giới, Dị Linh tộc vị trí cổ chiến trường, vị kia Linh Thần vị trí vị diện cao hơn, đồng dạng tồn tại... Ngươi nhiều nhất chỉ lĩnh ngộ một cái thế giới pháp tắc, muốn vượt qua... Gần như không có khả năng làm được! Năm đó Khổng sư, đều không có hoàn thành, huống chi hậu nhân?”"),t("br"),t("br"),n._v("Nhan Thanh Cổ Thánh cười khổ một tiếng."),t("br"),t("br"),n._v("Thời gian, là trong vũ trụ, cơ sở nhất pháp tắc, dù là thế giới sụp đổ, ngũ hành trầm luân, không gian đều không tồn tại, có thể nó vẫn tồn tại như cũ."),t("br"),t("br"),n._v("Cường đại như thế đồ vật, như thế nào người khác tùy tiện liền có thể lĩnh ngộ?"),t("br"),t("br"),n._v("Khổng sư có thể lĩnh ngộ, cũng không có khả năng biến mất tại dòng sông lịch sử, cuối cùng không thấy tung tích."),t("br"),t("br"),n._v("“Cũng đúng!”"),t("br"),t("br"),n._v("Trương Huyền giật mình."),t("br"),t("br"),n._v("Hắn mượn huyết mạch lực lượng, là có thể điều động thời gian, thậm chí khống chế tốc độ phát sinh biến hóa, nhưng đối với thời gian bản chất tới nói, không có bất kỳ biến hóa nào."),t("br"),t("br"),n._v("Liền giống như, muốn tại không gian gấp, bố trí tốc độ thời gian trôi qua cùng ngoại giới khác biệt, liền không làm được!"),t("br"),t("br"),n._v("“Thời gian pháp tắc quá khó, ta nếu là có thể lĩnh ngộ, có lẽ vừa rồi liền có thể cứu Hoằng Thiên tiên tổ...”"),t("br"),t("br"),n._v("Trong lòng than thở, suy tư thời gian chân giải bên trong lĩnh ngộ nội dung, đang trầm tư, đột nhiên trong đầu một hồi chấn động kịch liệt, thư viện giống như là động đất đồng dạng, phát ra nổ vang thanh âm."),t("br"),t("br"),n._v("“Nguy rồi...”"),t("br"),t("br"),n._v("Đồng tử co rụt lại, Trương Huyền liền cảm thấy mắt tối sầm lại, không dừng lại quá lâu, “Phù phù!” Ngã trên mặt đất, ngất đi."),t("br"),t("br"),n._v("“Sư huynh...”"),t("br"),t("br"),n._v("Dương Huyền vội vàng đi tới trước mặt."),t("br"),t("br"),n._v("“Hẳn là linh hồn nhận lấy một loại nào đó tổn thương...”"),t("br"),t("br"),n._v("Nhìn một hồi, Nhan Thanh Cổ Thánh cũng nhíu nhíu mày."),t("br"),t("br"),n._v("Đại Thánh cấp bậc cường giả, không có người đánh lén, không có công kích, vậy mà lại bản thân linh hồn bị tổn thương ngất đi, đến cùng chuyện gì xảy ra?"),t("br"),t("br"),n._v("“Ta am hiểu linh hồn, ta đến xem...”"),t("br"),t("br"),n._v("Tưởng gia Cổ Thánh đi tới trước mặt, linh hồn lực lan ra, còn không có đi vào Trương Huyền trong cơ thể, liền thân thể không tự chủ được run rẩy, nhanh chóng lui về phía sau mấy bước, khuôn mặt trắng bệch!"),t("br"),t("br"),n._v("“Làm sao vậy?”"),t("br"),t("br"),n._v("Nhìn thấy hắn cái dạng này, đám người đồng loạt nhìn tới."),t("br"),t("br"),n._v("“Linh hồn của hắn đang bị một loại đặc thù lực lượng bảo vệ, ta... Không cách nào dò xét!”"),t("br"),t("br"),n._v("Tưởng gia Cổ Thánh nắm đấm xiết chặt."),t("br"),t("br"),n._v("“Đặc thù lực lượng bảo vệ? Cái gì lực lượng có thể liền ngươi đều ngăn tại bên ngoài?” Nhan Thanh Cổ Thánh không hiểu."),t("br"),n._v(" Am hiểu linh hồn Cổ Thánh, cùng cấp bậc dưới tình huống, muốn dò xét, hắn đều rất khó chống cự, đối phương đều rơi vào hôn mê, cái gì lực lượng có thể ngăn cản, còn đem hắn chấn thương?"),t("br"),t("br"),n._v("“Là...” Tưởng gia Cổ Thánh thân thể cứng nhắc: “Thiên đạo!”"),t("br"),t("br"),n._v("“Thiên đạo bảo vệ?”"),t("br"),t("br"),n._v("Ngây người tại chỗ, tất cả mọi người lần nữa nhìn về phía Trương Huyền, khiếp sợ tất cả đều nói không ra lời."),t("br"),t("br"),n._v("Một cái Đại Thánh cấp bậc tiểu nhân vật, lại có Thiên đạo bảo vệ..."),t("br"),t("br"),n._v("Đến cùng chuyện gì xảy ra?"),t("br"),t("br"),n._v("“Dương sư, ngươi thủ hộ lấy hắn, có chuyện gì, trực tiếp cùng ta liên hệ, ta cùng những người khác đi lần nữa củng cố không gian bốn phía, mất đi Xuân Thu đại điển cùng Khổng sư lạc ấn trấn áp, toàn bộ Khổng miếu đã lâm vào thời không loạn lưu, không nhanh chóng mang về, tất cả mọi người không cách nào rời đi!”"),t("br"),t("br"),n._v("Liếc bầu trời một cái, phát hiện ngôi sao đã biến mất không thấy gì nữa, Nhan Thanh Cổ Thánh trên mặt thần sắc lo lắng, nói."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Dương sư gật gật đầu."),t("br"),t("br"),n._v("“Ừm...”"),t("br"),t("br"),n._v("Ngạn thanh Cổ Thánh không tại nhiều nói, mang theo còn lại cường giả, xoay người rời đi."),t("br"),t("br"),n._v("Tuy là Xuân Thu đại điển xuất thế, đem Khổng miếu hư hại không ít, lại liên tiếp đại chiến, nhưng bất kể nói thế nào, đây đều là Khổng sư tự thân lưu lại di tích, không thể có tổn hại."),t("br"),t("br"),n._v("“Sư huynh... Ngươi đến cùng làm sao vậy?”"),t("br"),t("br"),n._v("Đám người rời đi, Dương sư lần nữa nhìn về phía hôn mê Trương Huyền, lộ ra lo âu nồng đậm."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Không biết tình huống ngoại giới, thời khắc này Trương Huyền, mê man, hình như ý thức đều từ thân thể bị tước đoạt đi ra."),t("br"),t("br"),n._v("“Nhược Hi...”"),t("br"),t("br"),n._v("Trước mắt phảng phất xuất hiện lần nữa nữ hài thân ảnh, nhìn lấy hắn, mang theo nồng đậm lo lắng, chính là Lạc Nhược Hi."),t("br"),t("br"),n._v("“Yên tâm, ta không sao...”"),t("br"),t("br"),n._v("Trương Huyền vội vàng mở miệng."),t("br"),t("br"),n._v("“Chiếu cố thật tốt bản thân, nhất định phải đem ta đưa cho ngươi đồ vật đeo ở trên người, một khắc cũng không thể tách ra...”"),t("br"),t("br"),n._v("Nữ hài thanh âm vang lên."),t("br"),t("br"),n._v("“Ngươi nói cái kia mặt dây chuyền? Ta sẽ một mực mang theo...” Trương Huyền vội vàng gật đầu."),t("br"),t("br"),n._v("Đi vào Khổng miếu phía trước, Lạc Nhược Hi từng từng chuyên môn đưa cho hắn một cái mặt dây chuyền, nói là lúc trước đối phương cha ban cho, thời khắc mấu chốt, có thể bảo vệ hắn một mạng."),t("br"),t("br"),n._v("Tuy là cho rằng đối phương làm như vậy, có chút quái lạ, vẫn là cầm, hiện tại vẫn như cũ sát người đảm bảo."),t("br"),t("br"),n._v("“Vậy là tốt rồi, ta đi thật, trong thư viện nhiều ra đồ vật, là ta đưa cho ngươi quà tặng, cố gắng sử dụng, hi vọng chúng ta sớm ngày có thể gặp nhau...”"),t("br"),t("br"),n._v("Nghe hắn một mực mang theo, Lạc Nhược Hi thân thể mềm mại càng ngày càng mỏng manh, bất cứ lúc nào cũng sẽ biến mất."),t("br"),t("br"),n._v("“Thư viện... Ngươi biết ta có thư viện?”"),t("br"),t("br"),n._v("Trương Huyền thân thể cứng đờ."),t("br"),t("br"),n._v("Thân có thư viện, là hắn bí mật lớn nhất, ai cũng không có nói cho, vốn cho rằng Lạc Nhược Hi cũng không hiểu rõ tình hình, không nghĩ tới, vậy mà biết."),t("br"),t("br"),n._v("“Gặp lại, Trương Huyền!”"),t("br"),t("br"),n._v("Nhẹ nhàng cười một tiếng, Lạc Nhược Hi cũng không giải thích, ánh mắt lộ ra không bỏ, biến mất không thấy gì nữa."),t("br"),t("br"),n._v("“Đừng đi...”"),t("br"),t("br"),n._v("Một tiếng la hét, Trương Huyền bỗng nhiên ngồi dậy, lúc này mới phát hiện, lại là một giấc mộng."),t("br"),t("br"),n._v("“Lão sư, ngươi đã tỉnh...”"),t("br"),t("br"),n._v("Trương Huyền quay đầu, ngay sau đó nhìn thấy Triệu Nhã, Trịnh Dương đám người đang đứng tại cách đó không xa, ánh mắt lộ ra lo lắng."),t("br"),t("br"),n._v("Cha mẹ Hưng Mộng kiếm thánh vậy mà cũng ở bên cạnh."),t("br"),t("br"),n._v("“Chúng ta rời đi Khổng miếu?”"),t("br"),t("br"),n._v("Nhìn một chút hoàn cảnh bốn phía, Trương Huyền nhịn không được hỏi."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1793"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1795"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);