(window.webpackJsonp=window.webpackJsonp||[]).push([[898],{1252:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1783-đoan-tuyet-tai-danh-su-đuong-【-chuong-cuoi-quyen-nay-】"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1783-đoan-tuyet-tai-danh-su-đuong-【-chuong-cuoi-quyen-nay-】"}},[n._v("#")]),n._v(" Chương 1783: Đoạn tuyệt tại Danh Sư đường 【 chương cuối quyển này 】")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1783: Đoạn tuyệt tại Danh Sư đường "),t("br"),t("br"),n._v("(Canh [3])"),t("br"),t("br"),n._v("Triệu Nhã đám người cùng hắn cùng một chỗ từng tiến vào chủ điện, bởi vì không có thông qua khảo hạch, bị truyền tống ra ngoài, mặc dù không biết đi nơi nào, nhưng khẳng định không có nguy hiểm, cũng không thế nào đi quản."),t("br"),t("br"),n._v("Còn Trịnh Dương, đi vào Khổng miếu sau liền lại chưa thấy qua, nghe nói đạt được một loại nào đó gặp gỡ."),t("br"),t("br"),n._v("Hiện tại đồng thời xuất hiện ở đây, nói rõ đã rời đi Khổng miếu, đến mặt khác địa phương."),t("br"),t("br"),n._v("“Ừm, chúng ta cùng rất nhiều danh sư đồng thời trở về, đến Danh Sư đường, lão sư đã hôn mê trọn ba ngày...”"),t("br"),t("br"),n._v("Vương Dĩnh đi tới trước mặt, tràn đầy lo lắng."),t("br"),t("br"),n._v("“Hôn mê ba ngày?”"),t("br"),t("br"),n._v("Trương Huyền sửng sốt một chút, vội vàng hướng thư viện nhìn lại, lại phát hiện, cái này chỗ dựa lớn nhất, đã đóng lại cửa, ngay cả hắn, tạm thời còn không thể nào vào được."),t("br"),t("br"),n._v("“Hẳn là thăng cấp...”"),t("br"),t("br"),n._v("Trước đó từng có trải qua, cũng không cho rằng khẩn trương."),t("br"),t("br"),n._v("Lúc hôn mê, là hắn biết nguyên nhân, là bởi vì thư viện thăng cấp, hiện tại hắn tỉnh lại, người sau dường như còn không có thăng cấp xong, muốn vào cũng vào không được."),t("br"),t("br"),n._v("Biết tạm thời vẫn chưa thể dùng, Trương Huyền cũng liền lười đi quản, quay đầu nhìn về phía đám người."),t("br"),t("br"),n._v("“Nhìn tới, lần này Khổng miếu chuyến đi, tất cả đều cơ duyên không nhỏ...”"),t("br"),t("br"),n._v("Không khỏi khen ngợi."),t("br"),t("br"),n._v("Lúc này Trịnh Dương, cùng Triệu Nhã đám người tương đồng, đạt đến Đại Thánh Bất Hủ cảnh cảnh giới đại viên mãn, trong cơ thể lực lượng dâng trào như sông, tựa như lúc nào cũng có thể đột phá."),t("br"),t("br"),n._v("Lộ Xung cũng giống vậy, không chỉ hồn lực vô biên vô tận, bản thân tu vi, vậy mà cũng cùng cái trước tương đồng."),t("br"),t("br"),n._v("Vương Dĩnh cũng đạt tới cảnh giới này."),t("br"),t("br"),n._v("Náo loạn hồi lâu, chính mình cái này làm sư phụ, ngược lại tu vi thấp nhất."),t("br"),t("br"),n._v("Suy nghĩ một chút lần nữa tâm tắc."),t("br"),t("br"),n._v("Quay đầu nhìn về phía cha mẹ đám người, con mắt rất nhanh sáng lên."),t("br"),t("br"),n._v("Vốn lưu lại hai cái Bồ Đề quả, định cho bọn họ sử dụng, để hắn tăng lên tâm cảnh, vì đột phá Cổ Thánh, tăng lên càng quan trọng hơn quả cân, không nghĩ tới hai cái vị này, cũng không biết thu được cơ duyên gì, tâm cảnh đã đột phá!"),t("br"),t("br"),n._v("Nói cách khác, khoảng cách Cổ Thánh, càng gần."),t("br"),t("br"),n._v("“Ta chỗ này có một bộ hợp kích kiếm pháp, các ngươi thử một chút tu luyện một cái, có lẽ, đối với các ngươi đột phá, có chỗ trợ giúp!”"),t("br"),t("br"),n._v("Chần chờ một chút, ngón tay một điểm, một đạo pháp quyết tu luyện, truyền vào hai người trong óc."),t("br"),t("br"),n._v("Núi sách nhìn qua vô số thư tịch, đối Hưng, Mộng kiếm thánh hợp kích kiếm pháp, lần nữa có thay đổi, tuy là khoảng cách vượt qua Thiên đạo, còn cách một đoạn, nhưng đã vô tận tới gần."),t("br"),t("br"),n._v("Chỉ cần cố gắng tu luyện, nhất định có thể càng ngày càng mạnh, cuối cùng sẽ có một ngày, bước vào cảnh giới kia, tung hoành thiên hạ."),t("br"),t("br"),n._v("“Trịnh Dương, Vương Dĩnh, cái này hai cái Bồ Đề quả, nghĩ biện pháp luyện hóa, nơi này còn có một bộ tu luyện tâm cảnh pháp quyết, cùng một chỗ cầm lấy đi, cố gắng tu luyện!”"),t("br"),t("br"),n._v("Giao phó xong cha mẹ, đem cuối cùng hai cái Bồ Đề quả, đưa ra ngoài."),t("br"),t("br"),n._v("Tâm cảnh của hắn đã đột phá Cổ Thánh cấp bậc, thứ này cũng liền không còn tác dụng, cùng hắn giữ lại lãng phí, còn không bằng bảo bọn họ mau chóng nghĩ biện pháp tấn cấp."),t("br"),t("br"),n._v("“Cái này rễ cây, là Khổng miếu sáu đại trong thế giới một cái trận tâm, nắm giữ cực mạnh khải linh năng lực! Vương Dĩnh, ngươi nghĩ biện pháp luyện hóa, đối khải linh cảm ngộ sẽ càng sâu... Chuôi này trường thương, là ta chém giết Dị Linh tộc một vị cường giả tâm đắc, mặc dù không bằng Long Cốt thần thương, nhưng cũng là Đại Thánh cao nhất bảo vật, đối ngươi sẽ có trợ giúp thật lớn!”"),t("br"),t("br"),n._v("Mở ra chủ điện rất nhiều trận tâm, bởi vì sớm đã luyện hóa, người khác cũng không cách nào lấy đi, vừa rời đi chủ điện, liền toàn bộ bay tới, bị lần nữa thu vào nhẫn."),t("br"),t("br"),n._v("“Lộ Xung, đừng cho rằng vi sư bất công, linh hồn ngươi mạnh mẽ, tâm cảnh dễ dàng tăng lên, Bồ Đề quả đối với ngươi mà nói, hiệu quả không lớn, hiện tại ban tặng ngươi một cái hồn ấn, thứ này, có thể thôn phệ linh hồn, đối ngươi lực lượng tiến hành bổ dưỡng, trong mọi người, Viên Đào tuy là trải qua một lần Cổ Thánh kiếp, nhưng muốn nói ai có thể cái thứ nhất đột phá loại cảnh giới này, chỉ sợ vẫn là ngươi!”"),t("br"),t("br"),n._v("Đem đại biểu vu hồn nhất tộc, cao nhất thân phận hồn ấn, cho đối phương đưa tới."),t("br"),t("br"),n._v("Thứ này, có thể thôn phệ linh hồn, hóa thành tinh thuần nhất lực lượng, đối với vu hồn sư tới nói, tuyệt đối là tốt nhất bảo vật."),t("br"),t("br"),n._v("So Bồ Đề quả quan trọng không biết gấp bao nhiêu lần."),t("br"),n._v(" Băng hàn tinh thạch, mặc dù bây giờ đã không còn băng hàn năng lực, nhưng cùng Triệu Nhã cũng có thể hoàn mỹ dung hợp, Trương Huyền đồng dạng ban cho nàng."),t("br"),t("br"),n._v("Còn cá chép, ban cho Ngụy Như Yên."),t("br"),t("br"),n._v("Năm đại vương giả ban cho Viên Đào."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Còn có, đây là Cổ Thánh lực lượng, ta vừa rồi dùng không gian gấp bí pháp, mỗi người phong ấn một ít, chia làm tám phần, các ngươi một người một phần.”"),t("br"),t("br"),n._v("Đem bảo vật toàn bộ chia xong, Trương Huyền đi vào xuân thu đồ, quá rồi đoạn thời gian, vẻ mặt trở nên trắng đi ra, cho mỗi người một bức tranh."),t("br"),t("br"),n._v("Khổng sư có thể cắt chém một bộ phận thiên địa, bỏ vào thư hoạ, treo trên tường vạn năm không hủy, hắn hiện tại liên quan tới thư họa sư thư tịch, toàn bộ xem xong, đối thư hoạ lĩnh ngộ, cũng đạt tới không kém gì đối phương tình trạng, đem xuân thu trong bức tranh không gian cắt chém một cái, cũng không khó."),t("br"),t("br"),n._v("Có những bức họa này cuốn, mình coi như không ở bên người, bọn họ cũng có thể có cơ hội đột phá."),t("br"),t("br"),n._v("“Huyền nhi, ngươi có phải hay không có tâm sự gì?”"),t("br"),t("br"),n._v("Gặp nhi tử đem vất vả tại Khổng miếu được bảo vật, toàn bộ phân ra đến, cho đám người, Mộng kiếm thánh nhịn không được nhìn qua, ánh mắt bên trong tràn đầy lo lắng."),t("br"),t("br"),n._v("Làm sao cảm giác cùng bàn giao hậu sự đồng dạng?"),t("br"),t("br"),n._v("Trương gia, tuy là không còn lão tổ, Dương sư nhưng đã bắn tiếng, ai dám làm khó hắn bọn họ, chính là cùng hắn đối nghịch."),t("br"),t("br"),n._v("Lại thêm, nhi tử vũ khí cũng đột phá Cổ Thánh cảnh giới, Viên Đào thực lực cũng không yếu, lúc này Trương gia, trên thực tế so Trương Hoằng Thiên còn tại thời điểm, càng thêm cường đại."),t("br"),t("br"),n._v("Cũng không phải nói bọn họ tu vi cao, mà là bọn họ không cần ngủ say, có thể cuộc sống tự do, tự do hoạt động Cổ Thánh... Mới có càng lớn lực uy hiếp."),t("br"),t("br"),n._v("Trương gia vững như thành đồng, lại là lần này Khổng miếu di tích lớn nhất người thắng, hoàn toàn có thể chậm rãi củng cố tu vi, mau chóng đột phá, đến cùng chuyện gì, để hắn nhanh như vậy đem bảo vật phân cho đám người?"),t("br"),t("br"),n._v("“Không có gì...” Trương Huyền cười cười, đứng dậy, nhìn về phía mẹ: “Danh Sư đường hai ngày này, nhưng có người tìm ta?”"),t("br"),t("br"),n._v("“Danh Sư đường Nhậm đường chủ, nói ngươi tỉnh lại, liền đi tìm hắn...” Hưng kiếm thánh nói."),t("br"),t("br"),n._v("“Ừm, các ngươi đều ở lại chỗ này tu luyện, tranh thủ sớm ngày đột phá Cổ Thánh, ta đi qua một chuyến...”"),t("br"),t("br"),n._v("Khoát tay áo, Trương Huyền ra khỏi phòng."),t("br"),t("br"),n._v("“Lão sư, ta và ngươi cùng một chỗ...”"),t("br"),t("br"),n._v("Cũng nhìn ra lão sư thần thái không đúng, Triệu Nhã nhịn không được đi theo tới."),t("br"),t("br"),n._v("“Ai cũng đừng theo tới!” Trương Huyền bàn giao một câu: “Hơn nữa, bất luận xảy ra chuyện gì, đều tuân theo Danh Sư đường quy định, ngàn vạn không thể gây rối!”"),t("br"),t("br"),n._v("“Chuyện gì xảy ra?”"),t("br"),t("br"),n._v("Đám người hai mặt nhìn nhau, không biết chuyện gì xảy ra, chỉ có thể mặc cho người sau xoay người rời đi."),t("br"),t("br"),n._v("Lạc Nhược Hi hóa thân Linh Thần, cướp đi Xuân Thu đại điển thời điểm, bọn họ đều mỗi người đang tìm kiếm cơ duyên của mình, về sau liền toàn bộ lo lắng Trương Huyền tới nơi này, chuyện ngoại giới phát sinh, biết cũng không nhiều."),t("br"),t("br"),n._v("Ngay tại kỳ lạ, đến cùng xảy ra chuyện gì, một cái âm thanh vang dội, vang dội đến, dường như truyền thấu toàn bộ đại lục."),t("br"),t("br"),n._v("“Trương gia Trương Huyền tộc trưởng, mang Dị Linh tộc Linh Thần đi vào chủ điện, cướp đi Xuân Thu đại điển, tuy là vô tâm chi thất, nhưng cũng hủy nhân tộc cơ sở, thẹn với nhân tộc, đã đoạn tuyệt tại Danh Sư đường, nhìn hậu nhân có thể coi đây là dạy bảo, nhớ kỹ danh sư trách nhiệm, làm việc nhìn rõ đính chính, chớ nên một sẩy chân thiên cổ hận...”"),t("br"),t("br"),n._v("“Lão sư...”"),t("br"),t("br"),n._v("“Huyền nhi...”"),t("br"),t("br"),n._v("Mọi người mới biết chuyện gì xảy ra, cũng lại kìm nén không được, bỗng nhiên liền xông ra ngoài."),t("br"),t("br"),n._v("PS: Quyển thứ mười hai đoạn tuyệt tại Danh Sư đường, hoàn tất! Vốn thiết kế tốt giai đoạn kết thúc, so cái này muốn ầm ầm sóng dậy, Trương Huyền muốn thi triển ra Minh Lý Chi Nhãn đệ ngũ trọng, cùng Danh Sư đường mạnh mẽ chống đỡ, thậm chí đả thương một ít Cổ Thánh, cuối cùng dẫn đến hai mắt mù, như vậy càng thêm bi tình một ít... Phục bút đều lưu tốt, cuối cùng suy nghĩ một chút, còn là từ bỏ a, nếu không, Trương Huyền là thật trời không chứa xuống. Mọi người không thích ngược chủ, cũng bởi vì một cái Lạc Thất Thất, cùng đặt trước rơi mất mấy ngàn, đều sắp uống gió tây bắc, sắp bị mắng chết... Cảm giác như thế viết, sẽ có càng nhiều người muốn giết ta... Liền sửa đối lập có thể tiếp nhận chút."),t("br"),t("br"),n._v("Chẳng qua cũng không có như thế ầm ầm sóng dậy."),t("br"),t("br"),n._v("Một quyển này dính dáng đại thế giới kết nối cùng Khổng miếu xuất hiện, càng dính dáng mấy trăm vạn chữ trước phục bút, viết quả thực thật vất vả. Mới cuốn một cái,  sắp sửa mở ra. Ban ngày còn có chương mới."),t("br"),t("br"),n._v("Cầu nguyệt phiếu ủng hộ!"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1794"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1796"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);