(window.webpackJsonp=window.webpackJsonp||[]).push([[899],{1254:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1784-gac-chuong-loi-noi-trong-đem-【-canh-4-】"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1784-gac-chuong-loi-noi-trong-đem-【-canh-4-】"}},[n._v("#")]),n._v(" Chương 1784: Gác chuông lời nói trong đêm 【 Canh [4] 】")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1784: Gác chuông lời nói trong đêm "),t("br"),t("br"),n._v("Danh sư đại lục, Sóc Bắc cảnh, tuyết trắng tung bay."),t("br"),t("br"),n._v("Một cái cũ nát gác chuông bên trong, chất đầy đi đường khách nhân."),t("br"),t("br"),n._v("Nơi này là sáu đại Địa quật bên trong, Băng Hải Địa quật vị trí đất, bình thường hiếm có người đến, có điều, từ khi Dị Linh tộc đại quy mô rút quân về sau, thành giao thông cứ điểm, mỗi ngày đều sẽ có vô số cường giả đến đây."),t("br"),t("br"),n._v("Trong lòng đất tuy là có Huyết Nguyệt treo chếch, sát lục chi khí nồng đậm, nhưng cũng có thể sinh trưởng ra, danh sư đại lục không cách nào mọc ra cây cối, những vật này, bất luận là dược dụng còn là xem như vũ khí tài liệu, đều trân quý dị thường."),t("br"),t("br"),n._v("Trước đó chiến sự mãnh liệt, nổi danh sư, không ai dám tiến vào bên trong tìm kiếm, lại thêm Danh Sư đường phong tỏa nghiêm mật, không cho phép người bình thường ra vào, tự nhiên cũng liền không có gì cơ hội buôn bán."),t("br"),t("br"),n._v("Lúc này Dị Linh tộc nhân rút lui, không ít tu vi không kém thương nhân, đã có thể du lịch trong đó, kèm theo những vật phẩm này được mang đi ra càng nhiều, một lần vang dội, đưa tới càng nhiều đến đây tìm kiếm cường giả."),t("br"),t("br"),n._v("Lúc này, gác chuông bên trong đợi những này, chính là những này, muốn tìm kiếm cơ duyên võ giả."),t("br"),t("br"),n._v("“Phía ngoài gió tuyết càng lúc càng lớn, mỗi đến lúc này, băng tuyết Địa quật đều sẽ tự động phong bế, muốn vào cũng vào không được, chỉ có thể ở nơi này chờ đợi trời trong!”"),t("br"),t("br"),n._v("“Đúng vậy a, băng tuyết Địa quật, nằm ở tuyết sơn hầm băng, bông tuyết tung bay thời điểm, chính là một năm càng băng hàn thời khắc, quả thực không có cách nào đi vào!”"),t("br"),t("br"),n._v("“Đã đi không được, đợi vô sự, nhưng có cái gì tươi mới sự tình, nói nghe một chút, mọi người cũng tốt mở mắt một chút?”"),t("br"),t("br"),n._v("“Ngô huynh, ngươi mới từ Danh Sư đường tổng bộ bên kia tới, có thể đã nghe qua tin tức gì?”"),t("br"),t("br"),n._v("Nghe được xưng hô Ngô huynh, đám người đồng loạt đem ánh mắt tập trung ở ở giữa ngồi một người trung niên trên người."),t("br"),t("br"),n._v("Người trung niên này một thân danh sư trường bào, ngực bảy viên ngôi sao chói lóa mắt, lại là một vị thất tinh danh sư, nhìn tu vi, đạt tới Thánh vực tứ trọng, Nguyên Thần cảnh đỉnh phong, tại trong thất tinh, đều được cho khá cao."),t("br"),t("br"),n._v("Thấy mọi người ánh mắt tập trung đến, vị này Ngô huynh, dừng lại một chút, nhìn quanh một vòng, hỏi: “Các ngươi có biết hiện nay thế giới, thiên tài nhất danh sư, là ai?”"),t("br"),t("br"),n._v("“Thiên tài nhất danh sư? Hẳn là Phùng Tử Dật đi! Ta nghe nói lão sư hắn tấn cấp Cổ Thánh, tu vi của hắn cũng trực tiếp tăng vọt, sớm đã đạt đến khó mà ngước nhìn tình trạng!”"),t("br"),t("br"),n._v("“Ta cảm thấy là Triệu Mặc Hiên, người này ta gặp qua một lần, thiên tài như yêu, đối thứ gì, đều lĩnh ngộ cực nhanh, hơn nữa một học liền tinh, trí tuệ không tầm thường!”"),t("br"),t("br"),n._v("“Ta cho rằng là Tưỏng Phi Phi, lớn lên đẹp đẽ không nói, đối thiên công chức nghiệp càng là đạt tới hóa cảnh...”"),t("br"),t("br"),n._v("“Ta nghe nói là...”"),t("br"),t("br"),n._v("Nghị luận ầm ĩ."),t("br"),t("br"),n._v("Mỗi người đều có bản thân sùng bái thần tượng, đối hắn nắm giữ lớn nhất tôn sùng cùng khen ngợi."),t("br"),t("br"),n._v("Bọn họ nói tới mỗi một cái tên, đều vang vọng đại lục, vì thế nhân ngưỡng mộ."),t("br"),t("br"),n._v("“Các ngươi chẳng lẽ quên... Trương Huyền?”"),t("br"),t("br"),n._v("Đột nhiên, một thanh âm từ trong đám người vang lên."),t("br"),t("br"),n._v("“Trương Huyền?”"),t("br"),t("br"),n._v("Nghe được cái tên này, vừa rồi thảo luận thanh âm toàn bộ trầm mặc xuống."),t("br"),t("br"),n._v("“Thật sự là hắn là thiên tài nhất danh sư, vài vạn năm đến, cũng chỉ có Khổng sư có thể cùng so sánh, nhưng... Thông đồng Dị Linh tộc Linh Thần, cướp đi Xuân Thu đại điển, để nhân tộc nằm ở bị động, chỉ điều này... Liền không xứng làm danh sư!”"),t("br"),t("br"),n._v("“Danh sư bên trong, không thể có thứ bại hoại như vậy!”"),t("br"),t("br"),n._v("“Ta làm sao nghe nói, hắn cũng không phải là thông đồng, mà là bị lừa, trước đó cũng không biết thân phận đối phương!”"),t("br"),t("br"),n._v("“Làm người sư giả, cần chính mình, chính bản thân, chính hành, chính đức, coi như không biết thân phận, biết về sau, cũng hẳn là cắt bào đoạn nghĩa, mà hắn không có ngăn cản, càng không có đem đại điển đoạt lại, chỉ bằng vào điểm ấy, đã nói lên, không phải cái hợp lệ danh sư!”"),t("br"),t("br"),n._v("“Loại người này, không đề cập tới cũng được!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Nghe được cái tên này, đám người thảo luận càng nhiều."),t("br"),t("br"),n._v("“Ha ha!”"),t("br"),t("br"),n._v("Tiếng nghị luận càng lúc càng lớn, đột nhiên, một cái cười lạnh vang lên, chấn toàn bộ gác chuông đều không ngừng lay động, tựa như lúc nào cũng sẽ sụp đổ."),t("br"),t("br"),n._v("Nghe được thanh âm, trung khí mười phần, lực lượng hung mãnh, đám người không chịu được đồng loạt nhìn sang."),t("br"),t("br"),n._v("Ngay sau đó, nhìn thấy cười to chính là trong mọi người, thực lực mạnh nhất vị kia thất tinh danh sư “Ngô huynh!”."),t("br"),n._v("Lúc này Ngô huynh, ánh mắt bên trong mang theo lạnh lùng, lại không còn vừa rồi vui vẻ, đứng dậy, nhìn về phía đám người: “Các ngươi nói, Trương sư, không xứng làm danh sư? Ai cho các ngươi lá gan nói như vậy?”"),t("br"),t("br"),n._v("“Chính mình cũng cho rằng có lỗi với nhân tộc, xấu hổ tự sát tại Danh Sư đường phía trước, chẳng lẽ không phải bởi vì không xứng với danh sư xưng hào?”"),t("br"),t("br"),n._v("Một vị trên mặt đeo đao sẹo trung niên nhân khẽ nói."),t("br"),t("br"),n._v("Khoảng cách Trương Huyền, đoạn tuyệt tại Danh Sư đường, đã qua mười ngày, tin tức sớm đã truyền khắp toàn bộ đại lục."),t("br"),t("br"),n._v("“Không xứng?”"),t("br"),t("br"),n._v("Ngô huynh híp mắt lại: “Ta hỏi ngươi, ngươi cũng đã biết, chúng ta bây giờ có thể nhẹ nhõm đi vào băng tuyết Địa quật, là bởi vì cái gì?”"),t("br"),t("br"),n._v("“Cái này... Không phải là bởi vì, Dị Linh tộc nhân lui binh ư?”"),t("br"),t("br"),n._v("Người trung niên mặt sẹo nói."),t("br"),t("br"),n._v("Những người khác cũng đồng thời gật đầu."),t("br"),t("br"),n._v("Dị Linh tộc nhân lui binh, bọn họ mới có can đảm đi vào, nếu không, đi vào một cái chết một cái, ai cũng không phải ăn nhiều chết no, muốn đi tìm chết."),t("br"),t("br"),n._v("“Lui binh? Ha ha!”"),t("br"),t("br"),n._v("Ngô huynh cười nhạo: “Vài vạn năm đến, băng tuyết Địa quật, Dị Linh tộc lui binh không có hơn ngàn lần, cũng có hơn trăm lần, nhưng vì sao chưa từng để người bình thường tiếp cận qua?”"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Người trung niên mặt sẹo nói không ra lời."),t("br"),t("br"),n._v("Hai bên giao chiến, vật tư cung cấp, nhân viên thương vong, đều là lui quân mấu chốt, Dị Linh tộc nhân cũng rút lui qua binh, thế nhưng là Danh Sư đường nhưng lại chưa bao giờ để người bình thường từng tiến vào, chẳng lẽ, trong này còn có cái gì khác biệt ý nghĩa?"),t("br"),t("br"),n._v("“Ngô huynh, đến cùng chuyện gì xảy ra?”"),t("br"),t("br"),n._v("Một người cũng nhịn không được nữa, hỏi."),t("br"),t("br"),n._v("“Một tháng trước, ta vừa vặn vâng mệnh đi Danh Sư đường tổng bộ làm việc, nghe được tin tức này!” Ngô huynh rơi vào trong hồi ức: “Dị Linh tộc nhân là rút lui, nhưng tập kết hơn mười vạn binh lực, đang chuẩn bị phản công!”"),t("br"),t("br"),n._v("“Hơn mười vạn binh lực phản công?”"),t("br"),t("br"),n._v("Đám người líu lưỡi."),t("br"),t("br"),n._v("Dị Linh tộc nhân mạnh mẽ, bọn họ thấm sâu trong người, thấu hiểu rất rõ, nhiều người như vậy một khi phản công, vô cùng có khả năng tạo thành khó có thể tưởng tượng phá hoại, danh sư đại lục cũng có thể thủ không được."),t("br"),t("br"),n._v("“Không đúng... Đã tập kết nhiều lính như vậy lực, tại sao không có một điểm động tĩnh?”"),t("br"),t("br"),n._v("Đám người lần nữa đồng loạt nhìn tới."),t("br"),t("br"),n._v("Dị Linh tộc nhân gây ra động tĩnh lớn như vậy, bọn họ lại không nghe nói qua, chuyện gì xảy ra?"),t("br"),t("br"),n._v("Còn Khổng miếu, đó là Danh Sư đường tổng bộ cùng đại nhân vật ở giữa sự tình, không có quan hệ gì với bọn họ, quan tâm thật ra thì cũng không nhiều."),t("br"),t("br"),n._v("“Đương nhiên không có động tĩnh... Là bởi vì, có một vị danh sư, đơn thương độc mã, xông vào cái này hơn mười vạn binh sĩ bên trong, đem hắn toàn bộ chém giết!”"),t("br"),t("br"),n._v("Ngô huynh ánh mắt bên trong lộ ra kính trọng chi sắc: “Là hắn, phá hủy Dị Linh tộc âm mưu, ép bọn họ tạm thời không còn dám tới! Là hắn, chữa trị phong ấn, nhường đất quật biến thành chúng ta có thể tùy ý thăm dò tồn tại, mà không phải Tu La Địa Ngục!”"),t("br"),t("br"),n._v("“Một người, giết hơn mười vạn Dị Linh tộc nhân?”"),t("br"),t("br"),n._v("“Nghe nói những này Dị Linh tộc nhân, tu vi thấp nhất, đều đạt đến Thánh vực tam trọng...”"),t("br"),t("br"),n._v("“Cái này sao có thể làm được? Ngô huynh, người này đến cùng là ai?”"),t("br"),t("br"),n._v("Cũng lại kìm nén không được, đám người lần nữa nhìn lại."),t("br"),t("br"),n._v("“Không phải là các ngươi nói tới cái kia không xứng làm danh sư người...”"),t("br"),t("br"),n._v("Ngô huynh nắm đấm xiết chặt: “Trương Huyền!”"),t("br"),t("br"),n._v("PS: Mới một cuốn bắt đầu, tiếp tục cầu nguyệt phiếu!"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1795"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1797"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);