(window.webpackJsonp=window.webpackJsonp||[]).push([[90],{444:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1058-nguoi-la-thien-diep-vuong-thuong"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1058-nguoi-la-thien-diep-vuong-thuong"}},[n._v("#")]),n._v(" Chương 1058: Ngươi là Thiên Diệp Vương (thượng)")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1058: Ngươi là Thiên Diệp Vương (thượng)"),t("br"),t("br"),n._v("Bên này mới vừa băng bó kỹ, ma âm vang lên lần nữa, lần này tiếng đàn mang theo mê hoặc, để cho người ta kìm lòng không được rơi vào trong đó, tư duy đã không bị bản thân khống chế."),t("br"),t("br"),n._v("“Nguy rồi, là mê âm một loại...”"),t("br"),t("br"),n._v("Trương Huyền biến sắc."),t("br"),t("br"),n._v("Ma âm cùng trận pháp đồng dạng, cũng chia làm rất nhiều chủng loại, mê âm, huyễn âm, trầm âm, náo âm... Mê âm tuy chỉ là một cái trong đó phân loại, lại là đáng sợ nhất một trong, chỉ cần chìm đắm trong đó, cường giả đều sẽ trầm mê, không cách nào tự kềm chế."),t("br"),t("br"),n._v("Cùng sư ngôn thiên bẩm có cách làm khác nhau nhưng kết quả lại giống nhau đến kì diệu, một khi say mê, hoàn toàn có thể để cho hắn làm ra không bị bản thân khống chế cử động, thậm chí... Tự sát!"),t("br"),t("br"),n._v("Năm đó liền có Cầm Ma, dùng ma âm tàn sát một thành, dẫn đến loại nghề nghiệp này có “Ma” xưng hô."),t("br"),t("br"),n._v("Trương Huyền thân là Thất Tinh ma âm sư, biết đáng sợ, trước mắt cái này, mang theo triền miên chi ý, theo đáy lòng sinh ra, rất dễ dàng để cho người ta mê huyễn trong đó, đợi say mê sau thi triển sát chiêu công kích, làm không cẩn thận bọn họ sẽ toàn quân bị diệt ở đây."),t("br"),t("br"),n._v("Không kịp giải thích, cổ tay khẽ đảo, một cái đàn ngọc xuất hiện tại trước mặt, vận chuyển chân khí, năm ngón tay đột nhiên tìm tới."),t("br"),t("br"),n._v("Đông đông đông!"),t("br"),t("br"),n._v("Như là hoàng chung đại lữ, lại hình như kinh lôi vang lên, hùng hồn thanh âm tại mọi người bên tai trong nháy mắt nổ tung."),t("br"),t("br"),n._v("Mọi người tình huống trước mắt cùng Vô Cương giới khác biệt, lúc ấy không lưu tâm tiếng gió, đợi phát hiện thời điểm, đã xâm nhập cốt tủy, lấy thực lực của hắn, lấy âm phá âm, căn bản làm không được, cưỡng ép đi làm lời nói, làm không cẩn thận sẽ để cho tất cả mọi người trọng thương mà chết."),t("br"),t("br"),n._v("Hiện tại vừa mới trúng chiêu, xử lý liền sẽ đơn giản không ít."),t("br"),t("br"),n._v("Quả nhiên, bị thanh âm chấn động, tất cả mọi người đã kìm lòng không được lay động hai lần, miệng mũi phun ra máu tươi, mặc dù bị thương, nhưng thanh tỉnh lại."),t("br"),t("br"),n._v("“Ở đây tiếp tục chờ đợi, tất cả mọi người sẽ chết, đi theo ta...” Một tiếng hí dài, Trương Huyền tìm đúng một cái phương hướng đi thẳng về phía trước."),t("br"),t("br"),n._v("Mặc dù không biết cái này một trăm bảy mươi hai cửa, chỉ là có ý gì, nhưng mà tiếp tục đợi ở chỗ này, mọi người thương thế sẽ chỉ càng ngày càng nặng, làm không cẩn thận sẽ cùng trước đó vị kia Chiến sư đồng dạng, không khống chế được, trực tiếp làm chuyện điên rồ, cuối cùng bị hỗn loạn kiếm mang chém giết."),t("br"),t("br"),n._v("Cái khác cửa cho dù nguy hiểm, xấu nhất tình huống cũng chỉ như vậy, trước chạy đi lại nói."),t("br"),t("br"),n._v("Mọi người cũng ý thức được điểm ấy, theo sát tại Trương Huyền sau lưng, Ngô sư, Hàn hội trưởng đám người, tay cầm trường kiếm, cảnh giác nhìn xem bốn phía, một khi lại có công kích xuất hiện, sẽ lập tức phản kích."),t("br"),t("br"),n._v("Bọn họ mặc dù chỉ có hai người, nhưng đều là Thánh vực tứ trọng siêu cấp cường giả, lĩnh ngộ nguyên thần lực lượng, hoàn toàn có thể đem kiếm mang ngăn tại bên ngoài."),t("br"),t("br"),n._v("Đinh đinh đinh đinh!"),t("br"),t("br"),n._v("Đi về phía trước một hồi, kiếm mang lần nữa đánh tới, hai đại cao thủ đồng thời ngăn cản, mọi người trốn ở trong đó, nhanh chóng tiến lên."),t("br"),t("br"),n._v("Đi qua hai đạo rộng lớn hành lang, liếc mắt nhìn hai phía, Trương Huyền mày nhăn lại, thân thể đột nhiên nhoáng một cái, đối với một bức tường, đụng tới."),t("br"),t("br"),n._v("Mọi người đang tràn đầy không hiểu, liền thấy bức tường đổ sụp, một cái rộng rãi sân nhỏ xuất hiện ở trước mắt."),t("br"),t("br"),n._v("Vừa rồi hung mãnh vô cùng kiếm mang, hình như bị xác định giới hạn, mọi người vừa tiến vào sân nhỏ, lập tức đình chỉ công kích, hình như trước đó căn bản không tồn tại đồng dạng."),t("br"),t("br"),n._v("“Công kích biến mất, tiếng đàn cũng không thấy...”"),t("br"),t("br"),n._v("“Chúng ta trốn ra được?”"),t("br"),t("br"),n._v("Ô Thiên Khung đám người sau lưng hai tên danh sư, nhịn không được mở miệng."),t("br"),t("br"),n._v("“Không có, hiện tại vẫn tại trong trận pháp, chỉ bất quá đại trận này, bị ngăn cách thành vô số tiểu trận, chúng ta chỉ là theo một cái chạy trốn tới một cái khác...”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Đạt tới cấp tám đại trận, một cái so một cái đáng sợ! Nếu như đơn giản như vậy liền có thể chạy đi, cũng không trở thành để hắn đã thúc thủ vô sách."),t("br"),t("br"),n._v("Huống chi trận pháp bên ngoài, còn có một cái Hồng Diệp Vương, đang nhìn chằm chằm."),t("br"),t("br"),n._v("Nghe rõ giải thích, mọi người nguyên một đám nắm đấm xiết chặt."),t("br"),t("br"),n._v("Còn tưởng rằng đã thoát đi nguy hiểm, hiện tại xem ra, nguy hiểm vẫn tồn tại như cũ."),t("br"),t("br"),n._v("“Mọi người không cần khẩn trương, chí ít hiện nay chúng ta là an toàn...” Thấy mọi người vẻ mặt khó coi, Trương Huyền cười cười, không nói thêm lời, hướng bốn phía nhìn lại."),t("br"),t("br"),n._v("Cái nhà này so vừa rồi còn rộng rãi hơn một chút, đông, tây, nam, bắc đều có một cái cửa lớn."),t("br"),t("br"),n._v("Vừa rồi đi vào bị đụng nát bức tường, lúc này đã khôi phục như lúc ban đầu, cùng địa phương khác hoàn mỹ hòa làm một thể, không cẩn thận quan sát lại tìm tìm không được."),t("br"),t("br"),n._v("“Chúng ta đi như thế nào?”"),t("br"),t("br"),n._v("Vừa tới bức tường khẳng định là không thể trở về, nếu không lại muốn rơi vào nguy hiểm."),t("br"),t("br"),n._v("Bốn cái cửa nên lựa chọn như thế nào?"),t("br"),t("br"),n._v("“Trương sư...”"),t("br"),t("br"),n._v("Mọi người đồng loạt nhìn qua."),t("br"),t("br"),n._v("Đã trải qua nhiều lần nguy cơ, đều là trước mắt vị này hóa giải, tất cả mọi người đối với hắn sinh ra tin cậy."),t("br"),t("br"),n._v("“Ta cũng không rõ lắm...”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Hiện tại trận pháp không có khởi động, Thiên Đạo thư viện không cách nào hình thành đối ứng thư tịch, hắn đương nhiên cũng liền không biết thiếu hụt ở nơi nào."),t("br"),t("br"),n._v("“Các ngươi ở chỗ này chờ, ta tùy tiện vào một cái thử xem...”"),t("br"),t("br"),n._v(" Chần chờ một chút, Trương Huyền nói."),t("br"),t("br"),n._v("Hiện tại trọng yếu nhất chính là để trận pháp vận chuyển lại, mới tốt tìm ra thiếu hụt, một mực tại nơi này làm chờ lấy, khẳng định không có một chút tác dụng nào."),t("br"),t("br"),n._v("“Được...” Mọi người gật gật đầu."),t("br"),t("br"),n._v("Trương Huyền hít sâu một hơi, tuyển một cái phương hướng, đang định đi qua, liền thấy trước mắt cửa lớn, “Kẹt kẹt!” Một tiếng từ từ mở ra."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Cửa vừa mở ra, một đạo phong nhận liền hướng mọi người tập kích tới, không khí bốn phía trang giấy đồng dạng bị cắt chém thành hai nửa."),t("br"),t("br"),n._v("“Nhanh nằm xuống...”"),t("br"),n._v("Biến sắc, Ngô sư vội vàng hét lớn."),t("br"),t("br"),n._v("“Một cái phong nhận mà thôi, nhìn ta dốc hết sức chém chi...” Vân Hư học viện một vị trưởng lão không để ý lắm, nhẹ nhàng cười một tiếng, lòng bàn tay nhiều ra một cây búa to, bỗng nhiên bổ tới."),t("br"),t("br"),n._v("Soạt!"),t("br"),t("br"),n._v("Cự phủ lực lượng mười phần, cùng phong nhận tiếp xúc, không tạo thành mảy may ngăn cản, bị cắt chém thành hai đoạn, vị trưởng lão này còn không có kịp phản ứng, bàn tay cùng thân thể liền bị cắt thành hai nửa."),t("br"),t("br"),n._v("“Cái gì...”"),t("br"),t("br"),n._v("Mọi người mồ hôi lạnh thoáng cái chảy xuống."),t("br"),t("br"),n._v("Nhất là Ô Thiên Khung, thiếu chút nữa ngất đi."),t("br"),t("br"),n._v("Vị trưởng lão này, sử dụng vũ khí, người khác không biết được, hắn biết đến rất rõ ràng, tại tuyệt phẩm Linh khí bên trong đã được cho đỉnh phong... Lợi hại như thế, tại phong nhận trước mặt thế mà liền nửa cái hô hấp đều không chịu đựng nổi..."),t("br"),t("br"),n._v("Có phần quá kinh khủng!"),t("br"),t("br"),n._v("“Là đem không gian đã cắt ra...”"),t("br"),t("br"),n._v("Hàn hội trưởng khẽ quát một tiếng, không còn dám do dự, cũng vội vàng nằm rạp trên mặt đất."),t("br"),t("br"),n._v("Phong nhận cắt chém qua đi, mặc dù thoạt nhìn, cùng bình thường không có gì khác biệt, nhưng thần thức không cách nào xuyên thấu... Nói cách khác, phong nhận phía trên cùng phía dưới, đã là hai cái không gian."),t("br"),t("br"),n._v("Vù vù!"),t("br"),t("br"),n._v("Mọi người nằm xuống, phong nhận từ đỉnh đầu lướt qua, cho người ta một loại da lông nổ tung cảm giác, Trương Huyền toàn thân cứng ngắc, động cũng không dám động."),t("br"),t("br"),n._v("Thân thể của hắn cứ việc đạt đến Thánh khí cấp bậc, nhưng mà đối mặt quỷ dị như vậy phong nhận, chỉ sợ vẫn như cũ không đỡ nổi một đòn, vẫn là cẩn thận cho thỏa đáng."),t("br"),t("br"),n._v("Kẹt kẹt!"),t("br"),t("br"),n._v("Phong nhận từ trên đầu xẹt qua, mọi người mới vừa thở phào nhẹ nhõm, cảm thấy tránh thoát nguy hiểm, liền nghe đến sau lưng cửa lớn lần nữa mở ra, ngay sau đó một đạo nghiêng phong nhận quét tới."),t("br"),t("br"),n._v("“Nguy rồi!”"),t("br"),t("br"),n._v("Một đạo phong nhận liền để bọn họ suýt chút nữa dọa ngất, hai đạo đồng thời tới, còn thế nào ngăn cản?"),t("br"),t("br"),n._v("Bất quá, chuyện còn chưa kết thúc, còn lại hai cái cửa lớn, lần nữa mở ra, lại có hai đạo phong nhận, từ khác nhau góc độ, bắn tới, đem mọi người có thể tránh né chỗ, toàn bộ bao vây."),t("br"),t("br"),n._v("“Không được, tiếp tục như vậy, sớm muộn cũng sẽ bị giết chết...”"),t("br"),t("br"),n._v("Ngô sư nhịn không được mở miệng."),t("br"),t("br"),n._v("Mặc dù bọn họ phân tán ra đến, còn có thể trốn đi trước mắt phong nhận công kích, có thể... Nương theo số lượng càng ngày càng nhiều, uy lực càng lúc càng lớn, sớm muộn gì đều sẽ bị chém giết ở đây."),t("br"),t("br"),n._v("Nói cách khác... Trước mắt cái nhà này, so trước đó còn muốn đáng sợ!"),t("br"),t("br"),n._v("“Hướng bên này đi...”"),t("br"),t("br"),n._v("Hiểu nguy cơ, Trương Huyền vội vàng tìm ra thiếu hụt, hô một tiếng, đi đầu hướng một cái phương hướng phóng đi."),t("br"),t("br"),n._v("Mọi người theo sát phía sau."),t("br"),t("br"),n._v("Thời gian không dài, đi vào một cái khác sân nhỏ."),t("br"),t("br"),n._v("Cái viện này, đồng dạng nắm giữ kỳ lạ được trận pháp, có điều, không phải phong nhận, mà là bẫy rập, thật vất vả trốn đi, cùng vừa rồi đồng dạng, một vị danh sư trưởng lão, vĩnh viễn lưu tại nơi này."),t("br"),t("br"),n._v("Một đường tiến lên, lại xông qua hai cái sân nhỏ, lần nữa tổn thất hai người, mọi người dừng ở một cái không lớn sân nhỏ, sở đoản thời gian không có cơ quan, lúc này mới miệng lớn thở hổn hển, nguyên một đám khuôn mặt trắng bệch."),t("br"),t("br"),n._v("Từ khi đi vào Thiên cung đến bây giờ, chẳng qua vài phút thời gian, nhưng liên tiếp chết bốn vị Chiến sư, bốn vị trưởng lão... So trước đó tất cả cửa ải tính gộp lại thương vong đều lớn hơn, mọi người không chịu được một trận trầm mặc."),t("br"),t("br"),n._v("“Như vậy đi xuống, khẳng định là vô cùng vô tận tiểu trận pháp, chúng ta làm không cẩn thận đều sẽ chết ở chỗ này...”"),t("br"),t("br"),n._v("Hàn hội trưởng nhịn không được nói."),t("br"),t("br"),n._v("Mọi người gật đầu."),t("br"),t("br"),n._v("Hắn nói không sai, trước mắt rất nhiều tiểu trận pháp tầng tầng lớp lớp, căn bản không biết phần cuối ở đâu, tiếp tục đi tới đích, sẽ chỉ càng ngày càng nguy hiểm, một cửa ải chết một hai cái, bọn họ tổng cộng liền hai mươi mấy người, qua không được mấy đóng, đoán chừng đều sẽ khai báo ở đây."),t("br"),t("br"),n._v("“Vậy làm sao bây giờ?” Mọi người tất cả đều nhìn qua."),t("br"),t("br"),n._v("“Lợi hại hơn nữa trận pháp, cũng phải có người chủ trì mới được, trái lại, liền như là nước không nguồn, cây không rễ! Chúng ta hiện tại sở dĩ bị động như vậy, là bởi vì vừa rồi cái kia Dị Linh tộc nhân, đang chủ trì trận pháp, nếu như có thể tìm tới đối phương, đem hắn đánh giết, trận pháp liền sẽ tự động phá giải...”"),t("br"),t("br"),n._v("Hàn hội trưởng nói."),t("br"),t("br"),n._v("Không có người chủ trì trận pháp, tựa như trước đó Vô Cương giới, Hắc Yên Trần Sa, mặc dù nguy hiểm, chỉ cần tìm đúng phương pháp, có thể nhẹ nhõm rời khỏi... Mà trước mắt cái này, mỗi một cái sân nhỏ đã có kỳ lạ trận pháp, không có mục tiêu ngạnh sấm mà nói, chẳng khác nào không ngừng rơi vào bẫy rập, vĩnh viễn không có chạy ra khả năng."),t("br"),t("br"),n._v("“Hàn hội trưởng nói không sai, biện pháp tốt nhất là đem Hồng Diệp Vương chém giết!”"),t("br"),t("br"),n._v("Trương Huyền tán đồng nhẹ gật đầu."),t("br"),t("br"),n._v("Bọn họ bây giờ tại rõ ràng, đối phương ở trong tối, rất rõ ràng là nhìn xem tung tích của bọn hắn, khởi động trận pháp, một mực bị động như vậy, sớm muộn cũng sẽ bị giết sạch!"),t("br"),t("br"),n._v("“Hồng Diệp Vương?”"),t("br"),t("br"),n._v("Mọi người nhìn qua."),t("br"),t("br"),n._v("“Ừm, vừa rồi nói chuyện vị kia là Thanh Điền thập đại vương giả xếp hạng thứ hai Hồng Diệp Vương, so Ngọc Diệp Vương, Thạch Diệp Vương càng thêm đáng sợ!”"),t("br"),t("br"),n._v("Trương Huyền giải thích nói."),t("br"),t("br"),n._v("“Thanh Điền nhất mạch ta xem qua liên quan thư tịch, là Dị Linh tộc nhân một cái chi nhánh, trong đó thập đại vương giả, lấy trời, đỏ, ngọc, thạch, trên lửa ngũ vương vi tôn... Nếu như vây khốn chúng ta là Hồng Diệp Vương, chỉ sợ rất khó chạy ra... Hơn nữa cho dù trực tiếp gặp gỡ, đoán chừng cũng không là đối thủ!”"),t("br"),t("br"),n._v("Ngô sư da mặt nhảy một cái."),t("br"),t("br"),n._v("Thân là Thanh Nguyên Danh Sư đường danh sư, đối phạm vi bên trong Dị Linh tộc nhân biết đến rất rõ ràng, Thanh Điền thập đại vương giả, mỗi một cái đều đáng sợ đến cực điểm, đều không phải là kẻ vớ vẩn."),t("br"),t("br"),n._v("“Rất có tự mình biết mình nha... Hắc hắc!”"),t("br"),t("br"),n._v("Ngô sư tiếng nói vừa dứt, liền nghe đến trước đó cái kia âm trầm thanh âm, lần nữa vang lên."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1066"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1068"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);