(window.webpackJsonp=window.webpackJsonp||[]).push([[91],{445:function(n,h,g){"use strict";g.r(h);var i=g(44),t=Object(i.a)({},(function(){var n=this,h=n.$createElement,g=n._self._c||h;return g("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[g("h1",{attrs:{id:"chuong-1059-nguoi-la-thien-diep-vuong-ha"}},[g("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1059-nguoi-la-thien-diep-vuong-ha"}},[n._v("#")]),n._v(" Chương 1059: Ngươi là Thiên Diệp Vương (hạ)")]),n._v(" "),g("hr"),n._v(" "),g("p",[n._v("Chương 1059: Ngươi là Thiên Diệp Vương (hạ)"),g("br"),g("br"),n._v("“Hồng Diệp Vương?”"),g("br"),g("br"),n._v("Trương Huyền híp mắt lại."),g("br"),g("br"),n._v("“Không sai, là ta! Có thể theo thanh âm của ta, liền biết ta là ai... Ngọc Diệp Vương, Thạch Diệp Vương hai cái ngu xuẩn, hẳn là bị ngươi giết chết a?”"),g("br"),g("br"),n._v("Tiếng cười lạnh vang lên, Trương Huyền lập tức cảm thấy thân thể xiết chặt, giống như bị cái gì rắn độc tập trung vào, toàn thân cứng ngắc."),g("br"),g("br"),n._v("“Là nguyên thần áp bức...”"),g("br"),g("br"),n._v("Trên đầu mồ hôi lạnh ứa ra, Trương Huyền biết đối phương đây là vận dụng nguyên thần áp bức, mặc dù chỉ là đơn giản một tia, nhưng cũng để hắn không sinh ra bất luận cái gì ý niệm phản kháng."),g("br"),g("br"),n._v("“Không sai, là ta giết, ngươi chẳng lẽ muốn báo thù cho bọn họ?”"),g("br"),g("br"),n._v("Vận chuyển Thiên Đạo chân khí, lúc này mới cảm giác thoải mái một chút, Trương Huyền cắn răng hướng bốn phía nhìn lại."),g("br"),g("br"),n._v("Đối phương không biết giấu ở nơi nào, cho dù Minh Lý Chi Nhãn cũng nhìn không ra tung tích."),g("br"),g("br"),n._v("“Báo thù? Bị một cái Tòng Thánh giết chết, nói rõ bọn họ không xứng làm vương giả, chết chưa hết tội...” Hồng Diệp Vương cười lạnh."),g("br"),g("br"),n._v("Dị Linh tộc nhân chỉ thuần phục Hoàng giả, tính cách lương bạc, liền xem như đồng bạn chết rồi, cũng không quan tâm."),g("br"),g("br"),n._v("“Ngươi có thể giết chết bọn họ, để hắn liền đưa tin thời gian đều không có, cũng coi là cái nhân vật, như vậy đi, ta cũng không vội vã giết ngươi, cùng ngươi tổ chức một cái trò chơi, thế nào?” Hồng Diệp Vương tiếp tục cười nói."),g("br"),g("br"),n._v("“Tổ chức trò chơi?” Trương Huyền sững sờ."),g("br"),g("br"),n._v("Những người khác cũng đầy là kỳ quái."),g("br"),g("br"),n._v("“Không sai, quy tắc của trò chơi rất đơn giản, các ngươi không phải muốn chạy trốn ra nơi này ư? Ta chỗ này tổng cộng có năm đầu manh mối, chỉ cần toàn bộ nhận được, liền có thể nhẹ nhõm trốn đi trận pháp, nhìn thấy ta chân thân...”"),g("br"),g("br"),n._v("Hồng Diệp Vương nói."),g("br"),g("br"),n._v("“Năm đầu manh mối? Chỉ sợ cũng không phải rất dễ dàng lấy được đi!” Trương Huyền cau mày."),g("br"),g("br"),n._v("“Thông minh, ta cũng thích cùng người thông minh nói chuyện!”"),g("br"),g("br"),n._v("Hồng Diệp Vương cười cười: “Dự đoán được manh mối rất đơn giản, giết chết một vị danh sư, ta cho ngươi cái thứ nhất, lại giết chết hai cái, ta cho ngươi cái thứ hai, lại giết chết ba cái, cho ngươi cái thứ ba... Cứ thế mà suy ra, nói cách khác, chỉ cần ngươi giết chết mười lăm vị danh sư, liền có thể nhận được hoàn chỉnh manh mối, chạy ra nơi này...”"),g("br"),g("br"),n._v("“Giết chết danh sư?” Trương Huyền nắm đấm xiết chặt."),g("br"),g("br"),n._v("Bọn họ tổng cộng ba mươi người, giờ phút này đã chết tám cái, còn lại chỉ có hai mươi hai người, giết chết mười lăm cái... Hầu như liền không còn lại bao nhiêu."),g("br"),g("br"),n._v("Thật ác độc!"),g("br"),g("br"),n._v("Để cho mình tự giết lẫn nhau, tốt ngư ông đắc lợi, mặc dù còn không có nhìn thấy bản nhân, lại biết vị này Hồng Diệp Vương, kỳ lạ, tà ác, so Ngọc Diệp Vương đám người, mạnh mẽ không biết gấp bao nhiêu lần."),g("br"),g("br"),n._v("“Không sai, ngươi là người thông minh, hẳn phải biết lựa chọn thế nào, không giết, ngươi không có nhìn thấy ta cơ hội, đồng dạng sẽ bị trận pháp toàn bộ giết chết; Mà động tay giết người lời nói, chí ít còn có thể nhìn thấy ta, có lẽ có thể vì bọn họ báo thù...”"),g("br"),g("br"),n._v("Hồng Diệp Vương nói tiếp."),g("br"),g("br"),n._v("“Hèn hạ!”"),g("br"),g("br"),n._v("Mọi người tất cả đều khí sắc mặt trắng bệch."),g("br"),g("br"),n._v("Một cửa ải đóng xông, cùng đối phương nói đồng dạng, sớm muộn gì đều sẽ bị giết chết, thậm chí liền thấy cái này Hồng Diệp Vương cơ hội đều không có, chỉ có nhận được manh mối, mới có thể lao ra, cùng hắn liều mạng."),g("br"),g("br"),n._v("“Hắn là tại tru tâm... Thật muốn làm như vậy, cùng Dị Linh tộc nhân khác nhau ở chỗ nào? Còn có cái gì tư cách kêu danh sư?”"),g("br"),g("br"),n._v("Ngô sư cắn răng."),g("br"),g("br"),n._v("Đối phương hiện tại là ăn chắc bọn họ."),g("br"),g("br"),n._v("Hai con đường chỉ có thể lựa chọn một loại."),g("br"),g("br"),n._v("“A, đúng, ngươi không giết bọn họ cũng được, dù sao các ngươi trong nhóm người này, mặc kệ ai động thủ, chỉ cần có thể chết trước mười lăm cái, ta manh mối đồng dạng sẽ lấy ra...”"),g("br"),g("br"),n._v("Hồng Diệp Vương tiếp tục cười cười: “Nhanh quyết định đi, cho các ngươi một canh giờ cân nhắc... Hắc hắc! Hi vọng chúng ta lần thứ nhất hợp tác, có thể vô cùng vui vẻ...”"),g("br"),g("br"),n._v("Thanh âm chói tai sắc nhọn, dần dần biến mất, hình như cái tên này lại không nhìn trộm."),g("br"),g("br"),n._v("“Hẳn là đi...”"),g("br"),g("br"),n._v("Thần thức lan tràn, Ngô sư quay đầu nhìn qua."),g("br"),g("br"),n._v("“Làm sao bây giờ?”"),g("br"),g("br"),n._v("Ô Thiên Khung mở miệng."),g("br"),g("br"),n._v("Những người khác tất cả đều kìm lòng không được nhìn về phía cách đó không xa Trương Huyền."),g("br"),g("br"),n._v("Một mực xông ra đi, khẳng định không được, thật chẳng lẽ cùng đối phương nói đồng dạng, tự giết lẫn nhau?"),g("br"),g("br"),n._v("“Đây nhất định là Hồng Diệp Vương quỷ kế, mục đích đúng là để chúng ta tự giết lẫn nhau... Mọi người tuyệt đối không nên mắc lừa!” Trương Huyền nhìn quanh một vòng."),g("br"),g("br"),n._v("“Đúng vậy a, một khi trúng kế, tự giết lẫn nhau, cho dù sống sót, cũng sẽ trở thành tâm ma, bị đối phương lợi dụng...” Mộc sư cũng nhẹ gật đầu."),g("br"),g("br"),n._v("“Nói thật, chúng ta có thể đi ra cơ hội quả thực không lớn, chỉ cần có thể giết cái này Hồng Diệp Vương là gió trưởng lão bọn họ báo thù, cho dù chết, cũng không tính là gì!” Đánh gãy hắn, Ô Thiên Khung nói."),g("br"),g("br"),n._v("“Không sai, chúng ta chết không có gì đáng tiếc, nhưng nếu như là bị nhốt, bị trận pháp giết, cũng quá không đáng giá...” Ngũ Nhiên cũng nói."),g("br"),g("br"),n._v("“Ý kiến của ta giống như những người khác, chúng ta có thể chết, Trương sư cùng Ngô sư không thể chết, chỉ cần các ngươi vẫn còn, liền có cơ hội giết chết Hồng Diệp Vương, chạy ra nơi này...” Phùng Huân cũng gật đầu."),g("br"),g("br"),n._v("Những người khác nhao nhao hướng về phía trước, bày tỏ đồng ý."),g("br"),g("br"),n._v("Bọn họ ý tứ rất đơn giản, cùng lắm thì chết đến mười lăm cái, chỉ cần có thể giữ lại thực lực, dù sao cũng so toàn bộ chết ở chỗ này tốt."),g("br"),g("br"),n._v("“Ý nghĩ của các ngươi ta biết, nhưng mà... Các ngươi có thể bảo đảm Hồng Diệp Vương nói lời giữ lời, liền nhất định có thể cho manh mối? Hơn nữa cho dù biết manh mối, liền nhất định có thể chạy đi?”"),g("br"),g("br"),n._v("Thấy mọi người bộ dáng này, Trương Huyền lắc đầu."),g("br"),g("br"),n._v("Bọn hắn ý nghĩ đại công vô tư, thậm chí không tiếc hi sinh chính mình, có thể... Như vậy vô dụng!"),g("br"),g("br"),n._v("Không nói trước Dị Linh tộc nhân xảo trá vô cùng, cho dù thật đem manh mối cho bọn hắn, thậm chí đem hắn thả ra trận pháp, nhưng... Danh sư chính là danh sư, có làm việc nguyên tắc, không có khả năng bởi vì chính mình, hi sinh những người khác."),g("br"),g("br"),n._v("Nếu không, cùng Dị Linh tộc nhân, có cái gì khác nhau?"),g("br"),g("br"),n._v("“Vậy làm sao bây giờ?”"),g("br"),g("br"),n._v("Mọi người cũng biết là đạo lý này, chỉ là tận mắt thấy nhiều bằng hữu như vậy tử vong, lại bị đối phương đùa bỡn đang vỗ tay ở giữa, thực sự nhịn không được, mới toát ra loại kia ý nghĩ."),g("br"),g("br"),n._v("Giờ phút này nghe được Trương Huyền phân tích, nguyên một đám nhìn qua, hi vọng trước mắt vị này có thể tiếp tục sáng tạo kỳ tích."),g("br"),g("br"),n._v("“Ta cũng không biết...” Trương Huyền cười khổ."),g("br"),g("br"),n._v("Hắn hiện tại là thật hết cách rồi, không biết đại trận này, đến cùng như thế nào vận chuyển, không biết Hồng Diệp Vương người ở chỗ nào, nhiều hơn nữa phương pháp đều không có bất kỳ chỗ dùng nào!"),g("br"),n._v(" “Ngươi cũng không biết?”"),g("br"),g("br"),n._v("Thấy hắn đều không có phương pháp, tất cả mọi người giống như là quả cầu da xì hơi."),g("br"),g("br"),n._v("Trương sư một đường đến nay, chuyện gì đều không có làm khó qua, liền hắn đều không có phương pháp, vậy liền thật không biết nên làm gì bây giờ."),g("br"),g("br"),n._v("“Ta nhịn không được, Hồng Diệp Vương không phải nói chết mười lăm người là được sao? Sau khi ta chết, người sống, nhất định phải báo thù cho ta...”"),g("br"),g("br"),n._v("Buồn bực rất lâu, một cái tiếng gào thét vang lên, một vị Chiến sư cũng nhịn không được nữa, giơ lên trong tay trường kiếm liền muốn tự sát."),g("br"),g("br"),n._v("“Dừng tay!”"),g("br"),g("br"),n._v("Không nghĩ tới, đều nói rõ ràng như vậy, lại còn có người muốn tự sát, Trương Huyền lông mày giương lên, ngón tay bỗng nhiên bắn ra."),g("br"),g("br"),n._v("Đinh!"),g("br"),g("br"),n._v("Một đạo chân khí vọt tới, đem trường kiếm đánh bay, Ngô sư tiến về phía trước một bước, ngón tay chỉ ra, đem hắn tu vi phong ấn."),g("br"),g("br"),n._v("Hiện tại lúc này, không phải tự sát liền có thể giải quyết, mà là nếu muốn ra biện pháp tốt hơn."),g("br"),g("br"),n._v("“Đừng cản ta, để cho ta chết...”"),g("br"),g("br"),n._v("Bị phong bế tu vi, vị danh sư này không có đình chỉ, vẫn như cũ lên tiếng rống to."),g("br"),g("br"),n._v("“Không cho chúng ta tự sát, vậy ta động thủ...”"),g("br"),g("br"),n._v("Mới vừa giải quyết cái này, lại một tiếng gào thét, một cái danh sư nhảy ra ngoài, đối bên cạnh một vị Chiến sư đánh ra."),g("br"),g("br"),n._v("“A!”"),g("br"),g("br"),n._v("Sầm mặt lại, Phùng Huân thân thể nhoáng một cái, đi tới vị danh sư này trước mặt, một chân đạp tới."),g("br"),g("br"),n._v("Bành!"),g("br"),g("br"),n._v("Máu tươi phun ra, danh sư bay ngược ra ngoài, tầng tầng ngã xuống đất, bị trọng thương."),g("br"),g("br"),n._v("“Phùng Huân, ngươi làm gì?”"),g("br"),g("br"),n._v("Liền vội vàng đem bị thương danh sư đỡ dậy, Thẩm Bình Triều quay đầu trừng mắt nhìn."),g("br"),g("br"),n._v("Đúng là bọn họ La Thanh học viện một vị trưởng lão."),g("br"),g("br"),n._v("“Làm gì? Đánh lén người của ta, không giết hắn là tốt lắm rồi!”"),g("br"),g("br"),n._v("Phùng Huân trừng mắt nhìn."),g("br"),g("br"),n._v("Hắn Chiến sư, vẫn đứng tại nguyên chỗ không động, người danh sư này đánh lén, nếu không phải mình ra tay, chỉ sợ đã bị giết, lúc này, thế mà hỏi vì cái gì, nói đùa cái gì!"),g("br"),g("br"),n._v("“Ta nhìn ngươi là kiếm cớ động thủ đi, đã như vậy, không bằng trước đem ta giết...” Thẩm Bình Triều khẽ nói."),g("br"),g("br"),n._v("“Động thủ lại như thế nào? Các ngươi ba đại học viện tới những này, vẫn luôn là vướng víu, đã sớm nhìn các ngươi không vừa mắt...” Phùng Huân cười lạnh."),g("br"),g("br"),n._v("“Đủ rồi!”"),g("br"),g("br"),n._v("Không nghĩ tới trong nguy hiểm, mọi người không những không đoàn kết, còn cãi vã, Ngô sư khí sắp bùng nổ."),g("br"),g("br"),n._v("“Tất cả mọi người là danh sư, Chiến sư, lần này tới đến di tích, mặc dù đã ôm quyết tâm quyết tử, nhưng cũng không thể đang cùng Dị Linh tộc nhân tranh đấu quá trình bên trong tự giết lẫn nhau! Nếu thực như thế, như thế nào có khuôn mặt đối mặt Khổng sư? Đối mặt liệt tổ liệt tông?”"),g("br"),g("br"),n._v("“A!”"),g("br"),g("br"),n._v("Nghe được hắn trách mắng, Phùng Huân cùng Thẩm Bình Triều liếc mắt nhìn nhau, từng người hơi vung tay cánh tay, cưỡng ép đem lửa giận đè ép xuống."),g("br"),g("br"),n._v("“Được rồi, tất cả mọi người bớt giận, vẫn là nhanh suy nghĩ một chút, làm sao ra ngoài rồi nói sau!”"),g("br"),g("br"),n._v("Thấy hai người lại không cãi lộn, Hàn hội trưởng mở miệng."),g("br"),g("br"),n._v("“Làm sao ra ngoài? Không nói trước trận pháp này ba tầng trong ba tầng ngoài, đâu đâu cũng có bẫy rập, cho dù đi ra ngoài, gặp Hồng Diệp Vương, chúng ta ai có thể là đối thủ?”"),g("br"),g("br"),n._v("Phùng Huân khoát tay áo: “Ta thấy tận mắt Thạch Diệp Vương thực lực, Ngô sư cho dù dùng hết toàn lực, cũng nhiều nhất đánh cái ngang tay... Hồng Diệp Vương mạnh hơn, lại thêm trận pháp, chúng ta lần này khẳng định là không có cách nào rời đi!”"),g("br"),g("br"),n._v("“Đúng vậy a, ta cũng cảm thấy không có cách nào rời đi... Trừ phi, giết chết mười lăm cái, nhận được đối phương manh mối!” Chiến sư đường một vị khác thiên phu trưởng, Giang Nguyên nói."),g("br"),g("br"),n._v("“Thật muốn động thủ, chúng ta liền thật trúng Hồng Diệp Vương kế ly gián...” Ngô sư cau mày."),g("br"),g("br"),n._v("“Trúng kế? Ta không cảm thấy như vậy, ta ngược lại cảm thấy, chúng ta bên trong, khẳng định có nội gian, nếu không, Dị Linh tộc nhân tại sao lại đối với chúng ta hành tung như lòng bàn tay? Vì sao lại khắp nơi cướp được chúng ta phía trước? Không có khả năng trùng hợp như vậy đi!”"),g("br"),g("br"),n._v("Giang Nguyên nói."),g("br"),g("br"),n._v("“Nội gian? Cái này sao có thể! Chúng ta đều là danh sư, làm sao có thể...” Ngô sư lắc đầu."),g("br"),g("br"),n._v("“Vưu Hư cũng là danh sư, cũng không phản bội nhân tộc?” Giang Nguyên nói."),g("br"),g("br"),n._v("“Cái này...”"),g("br"),g("br"),n._v("Ngô sư ánh mắt lóe lên, không biết trả lời như thế nào, nhíu nhíu mày, nhìn lại: “Giang sư, có phải hay không phát hiện cái gì? Cứ nói đừng ngại...”"),g("br"),g("br"),n._v("“Ta là phát hiện một chút không đúng...” Hừ lạnh một tiếng, Giang Nguyên quay đầu nhìn về phía cách đó không xa Lạc Nhược Hi, ánh mắt như điện: “Lạc sư, ta có mấy cái vấn đề muốn hỏi ngươi...”"),g("br"),g("br"),n._v("Lạc Nhược Hi cau mày: “Mời nói!”"),g("br"),g("br"),n._v("“Đi vào di tích, Ngô sư, Hàn hội trưởng cũng không phát hiện trận pháp không gian, ngươi nhưng một cái nhìn ra; Vô Cương giới, chúng ta bị nhốt, chân khí tổn hao nhiều, chỉ có ngươi không bị đến quá lớn thương hại, hơn nữa ngươi vừa rời đi, Dị Linh tộc nhân liền đến công kích; Hắc Yên Trần Sa, ai cũng không biết linh hồn loại pháp bảo có thể ngăn cản, ngươi nhưng rõ rõ ràng ràng, thậm chí, để chúng ta dọc theo Dị Linh tộc nhân đi qua dấu vết tới... Kết quả, liền tiến vào đối phương bẫy rập!”"),g("br"),g("br"),n._v("Giang Nguyên cười lạnh: “Những thứ này... Ngươi giải thích thế nào?”"),g("br"),g("br"),n._v("“Hắn vừa nói như vậy, thật đúng là...”"),g("br"),g("br"),n._v("“Trước đó ta còn không có chú ý, Giang chiến sư nói chuyện, vị này Lạc sư quả nhiên có chút không đúng!”"),g("br"),g("br"),n._v("“Vừa mới bắt đầu không nói muốn tới di tích, đột nhiên gia nhập, ta liền cảm thấy có vấn đề, hiện tại xem ra quả nhiên có vấn đề...”"),g("br"),g("br"),n._v("“Nhìn nàng một cái trả lời thế nào đi...”"),g("br"),g("br"),n._v("Soạt!"),g("br"),g("br"),n._v("Nghe xong Giang Nguyên phân tích, mọi người sắc mặt biến đổi, đồng loạt lùi về phía sau mấy bước, Lạc Nhược Hi chung quanh lập tức trống ra một vòng tròn."),g("br"),g("br"),n._v("“Ngươi cảm thấy ta là gian tế?”"),g("br"),g("br"),n._v("Không nghĩ tới đối phương sẽ đem mũi nhọn chỉ hướng bản thân, Lạc Nhược Hi đôi mi thanh tú nhăn thoáng cái."),g("br"),g("br"),n._v("“Không sai, ngụy trang tại bên người chúng ta, từng bước một mang bọn ta đi vào bẫy rập, nhưng không có phát giác, Thanh Điền nhất mạch, nắm giữ loại năng lực này cùng thực lực, chỉ sợ cũng chỉ có trong truyền thuyết kia, thần long thấy đầu mà không thấy đuôi cường giả! Nếu ta đoán không lầm...”"),g("br"),g("br"),n._v("Giang Nguyên lạnh lùng chằm chằm tới, hai mắt như điện, nói: “Ngươi chính là Thiên Diệp Vương!”"),g("br"),g("br"),n._v("(Cuối cùng mấy canh giờ, nguyệt phiếu không tìm đến liền lãng phí, mặt khác, rạng sáng 12 điểm có một chương đổi mới, mời mọi người đem giữ gốc nguyệt phiếu lưu cho lão nhai ah! Đa tạ!)"),g("br"),g("br"),g("br"),g("br")]),n._v(" "),g("hr"),n._v(" "),g("p",[g("a",{attrs:{href:"./chap-1067"}},[n._v("Chương trước")]),n._v("  |  "),g("a",{attrs:{href:"./chap-1069"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=t.exports}}]);