(window.webpackJsonp=window.webpackJsonp||[]).push([[910],{1264:function(n,h,g){"use strict";g.r(h);var t=g(44),c=Object(t.a)({},(function(){var n=this,h=n.$createElement,g=n._self._c||h;return g("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[g("h1",{attrs:{id:"chuong-1793-bac-huan-co-thanh-【-canh-thu-nhat-】"}},[g("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1793-bac-huan-co-thanh-【-canh-thu-nhat-】"}},[n._v("#")]),n._v(" Chương 1793: Bặc Huân Cổ Thánh 【 canh thứ nhất 】")]),n._v(" "),g("hr"),n._v(" "),g("p",[n._v("Chương 1793: Bặc Huân Cổ Thánh "),g("br"),g("br"),n._v("“Đây là Nguyên Đãng sơn!”"),g("br"),g("br"),n._v("Thảo luận xong xuôi, Ngột Thần, Trương Huyền liền cùng Lưu Dương cáo từ, hướng vị kia Bặc Huân Cổ Thánh vị trí chạy tới, sợ bị Linh Hoàng đám người phát hiện, không dám dùng xé rách không gian năng lực, có điều, nơi này khoảng cách vương thành cũng không xa, chỉ dùng chưa tới một canh giờ liền đến đến trước mặt."),g("br"),g("br"),n._v("Nói là núi, không bằng nói là cái vực sâu, nhìn một cái, đen kịt không nhìn thấy cuối cùng, tản mát ra âm lãnh băng hàn khí tức."),g("br"),g("br"),n._v("“Ta thử một chút đi!”"),g("br"),g("br"),n._v("Ngột Thần tiến về phía trước một bước, huyền phù tại trên vực sâu không, hít sâu một hơi: “Bặc Huân Cổ Thánh, bạn già Thần Dung đến đây viếng thăm!”"),g("br"),g("br"),n._v("Thanh âm ngưng tụ thành chỉ, hướng đen kịt chỗ sâu truyền lại, không biết lan ra bao xa."),g("br"),g("br"),n._v("Liên tục hô ba lần, dưới vực sâu mặt dường như có mây đen cuồn cuộn, ngay sau đó, từng đạo hào quang năm màu, từ phía dưới bắn đi lên, tựa như cực quang."),g("br"),g("br"),n._v("Những ánh sáng này vừa xuất hiện, hai người lập tức cảm thấy phi hành không ổn định, thân thể không ngừng lay động, chân khí không khỏi bản thân khống chế, bất cứ lúc nào cũng sẽ từ không trung rơi xuống."),g("br"),g("br"),n._v("“Là nguyên từ lực!”"),g("br"),g("br"),n._v("Trương Huyền lông mày giương lên."),g("br"),g("br"),n._v("Loại lực lượng này, cùng nam châm tương đồng, chỉ bất quá, đối tu luyện giả chân khí trong cơ thể, có phá hoại tác dụng, làm cho không người nào có thể tự chủ khống chế."),g("br"),g("br"),n._v("“Hừ!”"),g("br"),g("br"),n._v("Lông mày giương lên, Ngột Thần không tại ngụy trang, biến trở về Dị Linh tộc nhân bộ dáng, tinh thuần sát lục chân khí sôi trào, rốt cục ổn định thân hình, chỉ bất quá, hắn bị thương không có hoàn toàn khôi phục, vận dụng lực lượng, vẻ mặt lần nữa trở nên trắng bệch."),g("br"),g("br"),n._v("Trương Huyền cổ tay khẽ đảo, Yêu Dị huyền đao bay ra, đứng tại dưới chân, khống chế được thân hình."),g("br"),g("br"),n._v("Loại này nguyên từ lực, có thể đối chân khí tạo thành phá hoại, nhưng Yêu Dị huyền đao chỉ là vũ khí, không có cái gọi là chân khí, cũng liền không bị ảnh hưởng."),g("br"),g("br"),n._v("Phía dưới ngũ thải quang mang, lần nữa cuồn cuộn, tựa như sóng nước dâng lên, ngay sau đó liền thấy một người mặc hắc bào bóng người, đứng tại quang mang trên phương, chậm rãi lơ lửng."),g("br"),g("br"),n._v("“Bặc Huân Cổ Thánh!”"),g("br"),g("br"),n._v("Thần Dung Hoàng ôm quyền."),g("br"),g("br"),n._v("Trương Huyền nhìn sang."),g("br"),g("br"),n._v("Trước mắt vị này toàn thân đều bao phủ tại dưới hắc bào, chỉ có hai con mắt, phát ra đỏ tươi chi sắc, cho người ta một loại quỷ dị cảm giác, khí tức trên thân nồng đậm mà mạnh mẽ, nhìn không ra rốt cuộc mạnh cỡ nào, có điều, cùng trước đó Bặc Dục Cổ Thánh so, còn hơn."),g("br"),g("br"),n._v("Thậm chí so với Trương Hoằng Thiên, đều không hề yếu."),g("br"),g("br"),n._v("Khó trách bị Ngột Thần tôn sùng vô cùng, chỉ sợ tại tích huyết trùng sinh cảnh giới, cũng được cho cường giả."),g("br"),g("br"),n._v("“Lão phu sớm đã không để ý tới thế sự, ngươi coi như tự thân tới, cũng vô dụng!” Hai tay ẩn giấu ở bóng tối bên trong, Bặc Huân Cổ Thánh mặt không hề cảm xúc."),g("br"),g("br"),n._v("“Tình huống hiện tại, ngươi thấy được, mong rằng Bặc Huân huynh có thể ra tay giúp ta!” Thần Dung Hoàng khom người đến cùng."),g("br"),g("br"),n._v("Hắn bản thân bị trọng thương, sinh mệnh bất cứ lúc nào cũng sẽ tiêu hao sạch sẽ, chỉ cần là tu luyện giả liền có thể nhìn ra, không cần tận lực giải thích."),g("br"),g("br"),n._v("“Người người đều sẽ vẫn lạc, hoàng đế bệ hạ tương đồng, phổ thông bách tính cũng giống vậy... Ngươi có thể trước khi chết thấy ta một lần, ta cũng thỏa mãn, nếu như không có chuyện khác, xin lỗi ta không tại tiếp!”"),g("br"),g("br"),n._v("Bặc Huân Cổ Thánh vẻ mặt mang theo lạnh lùng, dưới chân ngũ thải quang mang, không ngừng lấp lánh, thân ảnh dường như muốn lần nữa ẩn giấu ở bóng tối bên trong, biến mất không thấy gì nữa."),g("br"),g("br"),n._v("“Ta bị người làm hại, tính mạng đang như ngàn cân treo sợi tóc ở giữa, chỉ cầu tự tay mình giết kẻ thù...” Không nghĩ tới cái tên này, lạnh lùng như vậy, Thần Dung Hoàng tràn đầy gấp gáp."),g("br"),g("br"),n._v("Ta là muốn ngươi hỗ trợ ra tay, ai mẹ nó trước khi chết tới nhìn ngươi?"),g("br"),g("br"),n._v("Chúng ta giao tình còn không có thâm hậu như vậy đi..."),g("br"),g("br"),n._v("“Ta sớm đã không tại quyền lợi bên trong, không muốn chạm phải những thứ này...”"),g("br"),g("br"),n._v("Lắc đầu, Bặc Huân Cổ Thánh chẳng muốn nói nhảm, bàn tay vung lên, ngũ thải quang mang bao phủ toàn bộ vực sâu, bóng người đã đi vào bóng tối bên trong."),g("br"),g("br"),n._v("“Còn xin dừng bước!”"),g("br"),g("br"),n._v("Ngay tại hắn lập tức liền muốn hoàn toàn biến mất không gặp thời điểm, Trương Huyền nhíu nhíu mày, ngón tay hướng về phía trước một điểm."),g("br"),g("br"),n._v("Trên không ngũ thải quang mang lập tức bị hắn lôi kéo tới trong tay, trước đó để hắn chân khí khó mà vận chuyển nguyên từ lực, giờ phút này thay đổi phương hướng, hướng về phía bóng đen liền vọt tới."),g("br"),n._v("Rầm!"),g("br"),g("br"),n._v("Bị cỗ lực lượng này ép, vừa mới biến mất Bặc Huân Cổ Thánh từ trong bóng tối té ra ngoài, hình như bốn phía ngũ thải quang mang, đối với hắn tiến hành bài xích."),g("br"),g("br"),n._v("“Ngươi là ai... Sao có thể khống chế nguyên từ lực?”"),g("br"),g("br"),n._v("Lông mày giương lên, Bặc Huân Cổ Thánh tràn đầy khó tin."),g("br"),g("br"),n._v("Vì lĩnh ngộ cái này vực sâu lực lượng nguyên từ, hắn hao tốn không dưới ba ngàn nhiều năm, đến bây giờ, mới coi như có chút tâm đắc, có thể điều khiển loại lực lượng này, thi triển ra để cùng cấp bậc Cổ Thánh, đều sợ hãi uy lực."),g("br"),g("br"),n._v("Trước mắt vị này, vừa ra tay liền để ngũ thải quang mang đối với mình bài xích... Làm sao làm được?"),g("br"),g("br"),n._v("“Ta cũng không phải là khống chế, mà là tìm đúng thiếu hụt, để chân khí trong cơ thể, có thể dung hợp lực lượng nguyên từ, cùng nơi này lực lượng tiến hành cộng hưởng!”"),g("br"),g("br"),n._v("Nhẹ nhàng cười một tiếng, Trương Huyền ngón tay một điểm, dưới chân Yêu Dị huyền đao lập tức bị thu hồi nhẫn, mà hắn vẫn như cũ lơ lửng giữa không trung, dường như mảy may đều không nhận bốn phía nguyên từ lực ảnh hưởng."),g("br"),g("br"),n._v("Thiên đạo chân khí, liền Dị Linh tộc sát lục chân khí đều có thể biến hóa, cải biến thành nguyên từ lực, hơn nữa tới tương dung, không tính quá khó."),g("br"),g("br"),n._v("Vừa rồi đối phương sử dụng thời điểm, mượn thư viện dò xét một phen, đã có thể sử dụng."),g("br"),g("br"),n._v("“Tìm đúng thiếu hụt? Chân khí trong cơ thể dung hợp lực lượng nguyên từ?”"),g("br"),g("br"),n._v("Bặc Huân Cổ Thánh nhíu nhíu mày."),g("br"),g("br"),n._v("“Không sai!”"),g("br"),g("br"),n._v("Lên tiếng, Trương Huyền hít sâu một hơi, song chưởng bỗng nhiên nâng lên."),g("br"),g("br"),n._v("Rầm!"),g("br"),g("br"),n._v("Trong vực sâu vô số ngũ thải quang mang chiếu rọi mà lên, một mảng lớn cực quang, đem hắn xoay quanh, tôn lên hắn như là màu sắc cự nhân."),g("br"),g("br"),n._v("Thiên đạo chân khí, sửa đổi thuộc tính, cùng nguyên từ lực dẫn phát cộng hưởng, cái này vực sâu, hắn thấy, tựa như một cái ấm áp lực lượng cội nguồn, có thể thả ra vượt qua bản thân tu vi lực lượng."),g("br"),g("br"),n._v("“Ngươi quả nhiên có thể khống chế nguyên từ lực...”"),g("br"),g("br"),n._v("Cảm nhận được chính mình chưởng khống lực lượng, cũng không bằng đối phương tinh thuần, Bặc Huân Cổ Thánh khắp khuôn mặt là rung động."),g("br"),g("br"),n._v("Hắn nghiên cứu trọn ba ngàn năm, vậy mà không bằng trước mắt vị này vừa tới thanh niên."),g("br"),g("br"),n._v("“Nếu như ngươi có thể giúp Thần Dung Hoàng, loại này khống chế nguyên từ lực phương pháp, ta có thể truyền thụ cho ngươi!”"),g("br"),g("br"),n._v("Gặp hắn biết mình thủ đoạn, Trương Huyền nhìn qua."),g("br"),g("br"),n._v("“Cái này...” Bặc Huân Cổ Thánh dừng lại."),g("br"),g("br"),n._v("Vì nguyên từ lực, hắn đều đợi ở chỗ này hơn ba nghìn năm, nếu quả thật có thể cùng trước mắt vị này đồng dạng, thuận lợi khống chế, cũng sẽ không cần khổ cực như vậy."),g("br"),g("br"),n._v("Hơn nữa, hắn lĩnh ngộ nguyên từ lực mục đích, là tìm được một đầu thích hợp bản thân con đường, xung kích cảnh giới cao hơn, thật cùng đối phương như thế, ung dung khống chế, có thể đoán được, thực lực nhất định đột nhiên tăng mạnh, đột phá hiện tại tích huyết trùng sinh, cũng chưa biết chừng!"),g("br"),g("br"),n._v("“Là ai đưa ngươi đả thương?”"),g("br"),g("br"),n._v("Có chút ý động, nhưng mà Bặc Huân Cổ Thánh, cũng không gấp gáp làm ra quyết định, mà là quay đầu nhìn về phía trước mắt Thần Dung Hoàng."),g("br"),g("br"),n._v("Làm Linh tộc đệ nhất Hoàng giả, đệ nhất nhân, đều bị bị thương thành như vậy, hắn mặc dù tự nhận là thực lực không yếu, nhưng cũng rõ ràng, đối thủ nhất định sẽ không đơn giản."),g("br"),g("br"),n._v("Nếu như không có cơ hội chiến thắng, chắc chắn sẽ không vì đạt được cộng hưởng nguyên từ lực công pháp, liền thân ở hiểm địa."),g("br"),g("br"),n._v("“Là Linh Hoàng cùng Tinh Hoàng! Bọn họ cùng nhân tộc liên thủ, đối ta tiến hành đánh lén!” Thần Dung Hoàng nói."),g("br"),g("br"),n._v("“Là bọn họ?”"),g("br"),g("br"),n._v("Nghe được là hai đại Hoàng giả, Bặc Huân Cổ Thánh vẻ mặt trở nên khó coi: “Chuyện này, ta lực bất tòng tâm, vẫn là mời cao minh khác đi!”"),g("br"),g("br"),n._v("(Còn lại ban ngày!)"),g("br"),g("br"),n._v("Người đăng: ThấtDạ"),g("br"),g("br"),g("br"),g("br")]),n._v(" "),g("hr"),n._v(" "),g("p",[g("a",{attrs:{href:"./chap-1804"}},[n._v("Chương trước")]),n._v("  |  "),g("a",{attrs:{href:"./chap-1806"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);