(window.webpackJsonp=window.webpackJsonp||[]).push([[911],{1266:function(n,h,t){"use strict";t.r(h);var g=t(44),c=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1794-nguyen-tu-đong-hoa-【-canh-thu-hai-】"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1794-nguyen-tu-đong-hoa-【-canh-thu-hai-】"}},[n._v("#")]),n._v(" Chương 1794: Nguyên từ đồng hóa 【 canh thứ hai 】")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1794: Nguyên từ đồng hóa "),t("br"),t("br"),n._v("Hai đại Hoàng giả, mỗi một cái đều không yếu hơn hắn, càng bởi vì Hoàng giả thân phận, có vô số người ủng hộ, chạy tới cùng bọn hắn đối nghịch..."),t("br"),t("br"),n._v("Vì đạt được một bộ công pháp, liền đi tự tìm cái chết... Hắn còn không có nghĩ như vậy không ra."),t("br"),t("br"),n._v("“Bặc Huân Cổ Thánh hẳn là sợ?”"),t("br"),t("br"),n._v("Trương Huyền cười nhạt một tiếng."),t("br"),t("br"),n._v("“Hoàng giả ở giữa tranh đấu, ta không muốn tham dự, cũng không hứng thú!” Bặc Huân Cổ Thánh khoát tay áo: “Nếu như ngươi cho là ta sợ, vậy cũng không sao cả!”"),t("br"),t("br"),n._v("Có thể trở thành Cổ Thánh, tâm cảnh ít nhất đều đạt đến 30. 0, tuyệt không phải một đôi lời khích lệ mà nói, liền có thể ảnh hưởng quyết định."),t("br"),t("br"),n._v("“Đã Bặc Huân Cổ Thánh nói như vậy, chúng ta cũng không bắt buộc, bất quá... Nguyên từ lực, là giữa thiên địa đản sinh một loại đặc thù lực lượng, ngươi muốn lĩnh ngộ mượn cơ hội xung kích phá toái hư không cảnh giới, không gì đáng trách, nhưng... Làm như vậy, tương đương với cùng Thiên đạo chính diện chống lại, linh hồn cùng thân thể, đều sẽ gặp phải áp bức... Nếu như không có đoán sai, những năm này, không phải ngươi không muốn rời đi cái này vực sâu, mà là, rất khó rời đi a?”"),t("br"),t("br"),n._v("Trương Huyền con mắt nâng lên."),t("br"),t("br"),n._v("Đối phương vừa rồi khống chế nguyên từ lực, từ phía dưới bay ra, hắn liền nhân cơ hội mượn thư viện dò xét."),t("br"),t("br"),n._v("Trước mắt vị này, thoạt nhìn mạnh mẽ, trên thực tế, lại bị nguyên từ lực đồng hóa, một khi rời đi, tất nhiên sẽ dẫn tới thiên kiếp hạ xuống, sống không bằng chết."),t("br"),t("br"),n._v("Vì lẽ đó, đường đường Cổ Thánh tích huyết trùng sinh cấp bậc cường giả, lại một mực co đầu rút cổ ở đây, không nguyện ý rời đi."),t("br"),t("br"),n._v("“Thiên kiếp tuy là mạnh mẽ, nhưng lấy ta thực lực bây giờ, chống lại đi qua, sẽ không quá khó! Ta thật muốn muốn đi, ai cũng ngăn không được!”"),t("br"),t("br"),n._v("Sửng sốt một chút, Bặc Huân Cổ Thánh hừ lạnh."),t("br"),t("br"),n._v("Hắn là Cổ Thánh cường giả, liều mạng bị thương, vẫn có thể vượt qua, chỉ là có nguyện ý hay không, tổn thất bao nhiêu thôi."),t("br"),t("br"),n._v("“Như vậy liền tốt, có điều, vẫn là khuyên bảo một câu, Nguyên Từ thạch tuy tốt, tốt nhất đừng hòa vào huyết mạch, nếu không, để lại tai hoạ vô tận... Đến lúc đó, kinh mạch thuế biến, ai cũng không cách nào khống chế!”"),t("br"),t("br"),n._v("Lắc đầu, Trương Huyền không nói thêm lời, giáng xuống Thần Dung Hoàng một tiếng: “Chúng ta đi thôi!”"),t("br"),t("br"),n._v("“Đi?”"),t("br"),t("br"),n._v("Thần Dung Hoàng sững sờ."),t("br"),t("br"),n._v("Đi tới cái này, là muốn đem Bặc Huân Cổ Thánh lôi kéo đến trận doanh mình, lúc này mới nói vài câu, cái gì kết quả đều không có, liền muốn rời khỏi, mất đi một cường giả như vậy trợ giúp, đi tìm còn lại hai vị, tuyệt đối tự tìm cái chết!"),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Còn tại nghi ngờ, chỉ thấy Trương Huyền đã hướng nơi xa bay đi, dừng lại một chút, theo sau."),t("br"),t("br"),n._v("Trước đó cùng vị này, từng tiến vào Bạch Khê sơn, biết hắn nắm giữ siêu nhân bản lĩnh, nói vài câu rời đi, nhất định có dụng ý của mình."),t("br"),t("br"),n._v("Phi hành một đoạn thời gian, quả nhiên thấy thanh niên ngừng lại, lơ lửng giữa không trung, cười khanh khách hướng vừa rồi tới phương hướng nhìn lại."),t("br"),t("br"),n._v("“Chúng ta...”"),t("br"),t("br"),n._v("Cau mày, nghi ngờ nhìn qua."),t("br"),t("br"),n._v("“Chờ một hồi, vị này Bặc Huân Cổ Thánh, có lẽ sẽ đổi ý!”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("Thần Dung Hoàng tràn đầy kỳ lạ."),t("br"),t("br"),n._v("Đều nói đối phương rời đi vực sâu, sẽ bị Thiên đạo phản phệ, lại thế nào khả năng thay đổi chủ ý, bất chấp nguy hiểm trợ giúp bản thân?"),t("br"),t("br"),n._v("Bất quá, trước mắt vị này đã nói như vậy, khẳng định là có tính toán, không được hỏi thăm, đành phải đợi tại nguyên chỗ, cũng hướng vực sâu phương hướng nhìn sang."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Cái tên này, làm sao biết ta tại luyện hóa Nguyên Từ thạch?”"),t("br"),t("br"),n._v("Thấy Trương Huyền hai người, nói còn chưa dứt lời xoay người rời đi, Bặc Huân Cổ Thánh tràn đầy nghi ngờ."),t("br"),t("br"),n._v("Hắn sở dĩ có thể khống chế nơi này nguyên từ lực, cũng không phải là thiên phú cao bao nhiêu, mà là trốn ở vực sâu chỗ sâu nhất, luyện hóa Nguyên Từ thạch."),t("br"),n._v(" Loại này đá, cùng nam châm đồng dạng, có thể tản mát ra lực lượng nguyên từ, nắm trong tay loại lực lượng này, liền có thể khống chế đối thủ chân khí, để hắn không cách nào tới tranh đấu."),t("br"),t("br"),n._v("“Mặc kệ hắn giả thần giả quỷ... Bây giờ cách luyện hóa Nguyên Từ thạch, chỉ kém một bước cuối cùng, phải tăng tốc tốc độ...”"),t("br"),t("br"),n._v("Suy nghĩ một chút, không biết đối phương ý tứ, đến cùng là cái gì, Bặc Huân Cổ Thánh lắc đầu, không tiếp tục để ý, thân ảnh lần nữa hòa vào màu đen bên trong, sau một khắc, đã đi tới vực sâu chỗ sâu nhất."),t("br"),t("br"),n._v("Một cái năm màu đá xuất hiện ở trước mắt, sáng bóng mang lấp lánh, dường như bốn phía lực lượng nguyên từ, đều do hắn phát ra."),t("br"),t("br"),n._v("Bàn tay khoác lên phía trên, hít sâu một hơi, Bặc Huân Cổ Thánh lực lượng tuôn ra, nhanh chóng luyện hóa."),t("br"),t("br"),n._v("Vì tới gần nơi này cái đá, hắn hao tốn một ngàn năm thời gian, bàn tay có thể dựng vào đi, lại hao tốn một ngàn năm, hiện tại, chỉ cần chân khí hướng lực lượng nguyên từ thuế biến, liền có thể thành công luyện hóa!"),t("br"),t("br"),n._v("Mà khoảng cách một bước này, chỉ kém tới cửa một cước."),t("br"),t("br"),n._v("Có thể đoán được, một khi thành công, thì tương đương với đem trọn cái vực sâu mang ở trong người, không cần lo lắng thiên kiếp công kích, gặp phải cùng cấp bậc người, cũng có thể ung dung nghiền ép."),t("br"),t("br"),n._v("Cô cô cô cục cục!"),t("br"),t("br"),n._v("Chân khí truyền vào càng nhiều, cùng nguyên từ lực phù hợp cũng càng nhiều, đột nhiên, trước mắt Nguyên Từ thạch, giống như là triệt để bị luyện hóa, dần dần cùng bàn tay của hắn dung hợp."),t("br"),t("br"),n._v("“Thành công...”"),t("br"),t("br"),n._v("Con mắt sáng lên, Bặc Huân Cổ Thánh vội vàng khống chế sức mạnh, chậm chạp dung hợp."),t("br"),t("br"),n._v("Xì xì xì!"),t("br"),t("br"),n._v("Tản mát ra ngũ thải quang mang đá, triệt để hòa vào hắn bàn tay, trong nháy mắt, cả người, bị ánh sáng bao phủ, tựa như một cái màu tượng."),t("br"),t("br"),n._v("Cảm nhận được bản thân quả nhiên nắm trong tay lực lượng nguyên từ, Bặc Huân Cổ Thánh cười ha ha một tiếng, đang muốn khống chế sức mạnh, thử một chút hiện tại sức chiến đấu, đột nhiên thân thể cứng đờ, chân khí trong cơ thể, lâm vào vũng bùn đồng dạng, lại không giống trước đó như thế không trở ngại."),t("br"),t("br"),n._v("“Chuyện gì xảy ra?”"),t("br"),t("br"),n._v("Đồng tử co rụt lại, vội vàng hướng chân khí phương hướng nhìn lại, vừa nhìn phía dưới, vẻ mặt lập tức liếc."),t("br"),t("br"),n._v("Chỉ thấy kinh mạch của mình, lại không còn trước đó mềm dẻo, ngược lại cùng sắt thép đồng dạng, tản mát ra thanh đồng chi sắc, cứng rắn, để chân khí đều khó mà thông suốt."),t("br"),t("br"),n._v("“Là đá... Ta muốn biến thành hòn đá?”"),t("br"),t("br"),n._v("Không chỉ có là kinh mạch, toàn thân cơ bắp cùng huyết dịch, cũng bắt đầu cứng nhắc, hình như sắp mất đi cảm thấy."),t("br"),t("br"),n._v("Giờ phút này, ngu nữa cũng hiểu được, đến cùng chuyện gì xảy ra."),t("br"),t("br"),n._v("Hắn vì luyện hóa Nguyên Từ thạch, chân khí cải biến thành nguyên từ lực, trên thực tế, nguyên từ lực cũng lặng lẽ thay đổi hắn, tựa hồ muốn hắn, biến thành một khối to lớn Nguyên Từ thạch!"),t("br"),t("br"),n._v("Đồng dạng cùng nam châm đồng dạng, đến gần sắt thép, thời gian lâu dài, cũng sẽ xuất hiện từ lực."),t("br"),t("br"),n._v("Chính là bị hắn thay đổi kết cấu bên trong."),t("br"),t("br"),n._v("Chỉ là... Đổi lại bình thường đá ngược lại cũng thôi, nhưng hắn là sinh mệnh, một khi thân thể kinh mạch toàn bộ biến thành đá, còn có thể chiến đấu, còn có thể nói chuyện, nắm giữ độc lập ý thức ư?"),t("br"),t("br"),n._v("“Linh hồn, cũng nhận xâm nhập...”"),t("br"),t("br"),n._v("Mồ hôi lạnh không ngừng từ trên đầu toát ra."),t("br"),t("br"),n._v("Trong khoảng thời gian ngắn, không chỉ kinh mạch cùng thân thể, thậm chí huyết dịch cùng linh hồn, cũng đều nhận lấy xâm nhập, trở nên càng ngày càng cứng nhắc, lại không còn trước đó tinh, khí, thần."),t("br"),t("br"),n._v("“Di hoạn vô tận, kinh mạch thuế biến... Vị kia nhất định biết cái gì...”"),t("br"),t("br"),n._v("Ngay tại gánh không được, cho rằng bất cứ lúc nào cũng sẽ tử vong thời điểm, Bặc Huân Cổ Thánh đột nhiên nhớ tới trước đó người thanh niên kia mà nói, nắm đấm bỗng nhiên xiết chặt."),t("br"),t("br"),n._v("Đối phương không chỉ nhìn ra hắn tại luyện hóa Nguyên Từ thạch, dường như còn nhìn ra chút cái gì, lúc này muốn giải quyết thời khắc này khốn cảnh, chỉ sợ cũng chỉ có thể đi tìm đối phương."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Rõ ràng điểm ấy, nào còn dám do dự, lập tức từ vực sâu bay ra, thẳng tắp hướng Trương Huyền đám người biến mất phương hướng xông tới."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1805"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1807"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);