(window.webpackJsonp=window.webpackJsonp||[]).push([[912],{1267:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1795-han-la-ta-thieu-gia-【-canh-3-】"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1795-han-la-ta-thieu-gia-【-canh-3-】"}},[n._v("#")]),n._v(" Chương 1795: Hắn là ta thiếu gia 【 Canh [3] 】")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1795: Hắn là ta thiếu gia "),t("br"),t("br"),n._v("Một bên phi hành, một bên cảm thấy thân thể không khỏi bản thân khống chế, trở nên càng ngày càng cứng rắn, bất cứ lúc nào cũng sẽ từ không trung rơi xuống."),t("br"),t("br"),n._v("Ngay tại tùy thời đều không kiên trì nổi thời điểm, Thần Dung Hoàng cùng người thanh niên kia thân ảnh xuất hiện tại tầm mắt, dường như đã sớm biết bản thân sẽ đuổi theo, tại chỗ này chờ đợi."),t("br"),t("br"),n._v("“Còn xin đại nhân, xuất thủ cứu ta...”"),t("br"),t("br"),n._v("Cảm thấy nội tạng đã biến thành tảng đá, Bặc Huân Cổ Thánh lại không để ý tới hình ảnh, đi tới thanh niên trước mặt, tràn đầy sợ hãi."),t("br"),t("br"),n._v("Tích huyết trùng sinh cường giả, tương đương với nắm giữ vô số đầu sinh mệnh, nhưng toàn thân đều biến thành tảng đá, mạnh hơn năng lực, cũng không thi triển ra được, cuối cùng chỉ có vẫn lạc một con đường có thể đi."),t("br"),t("br"),n._v("Hắn tuy là không để ý tới thế sự, không muốn tham dự Thần Dung Hoàng sự tình, vẫn còn không muốn chết."),t("br"),t("br"),n._v("“Tu vi của ta, chỉ có Tâm Huyết Lai Triều, muốn cứu ngươi, chỉ có thể dựa vào Dung Hoàng bệ hạ, chỉ là hắn... Trên người chịu huyết hải thâm cừu, chỉ sợ không có thời gian!”"),t("br"),t("br"),n._v("Nhíu mày, Trương Huyền xua tay."),t("br"),t("br"),n._v("Vừa rồi hắn chết sống không đáp ứng, như vậy giờ phút này bọn họ cũng bất lực."),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Không nghĩ tới nhanh như vậy liền nâng lên tảng đá đập chân của mình, Bặc Huân Cổ Thánh vẻ mặt lúc đỏ lúc trắng: “Chỉ cần Dung Hoàng bệ hạ có thể cứu ta, nguyện nghe theo sai khiến!”"),t("br"),t("br"),n._v("Không đáp ứng, chết ngay bây giờ, đồng ý, còn có sống cơ hội, thậm chí vinh hoa phú quý dễ như trở bàn tay, lựa chọn ra sao, đã không cần rầu rĩ."),t("br"),t("br"),n._v("“Nếu ngươi đồng ý, vậy thì dễ dàng...”"),t("br"),t("br"),n._v("Nhẹ nhàng cười một tiếng, Trương Huyền truyền âm qua."),t("br"),t("br"),n._v("“Như vậy có thể?”"),t("br"),t("br"),n._v("Nghe xong truyền âm, Thần Dung Hoàng nhíu nhíu mày, có chút không dám tin tưởng."),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Vậy được...”"),t("br"),t("br"),n._v("Hít sâu một hơi, Thần Dung Hoàng cổ tay khẽ đảo, một chuôi trường đao xuất hiện tại lòng bàn tay, bàn tay run lên."),t("br"),t("br"),n._v("Rầm!"),t("br"),t("br"),n._v("Đao mang xé rách không gian, hướng về phía Bặc Huân Cổ Thánh liền bổ xuống, người sau thân thể cứng nhắc, phản ứng không kịp, liền bị đánh tại ngực, chém ra một đạo vết thương thật lớn."),t("br"),t("br"),n._v("Máu tươi theo miệng máu hướng ra phía ngoài chảy xuôi."),t("br"),t("br"),n._v("“Ngươi muốn làm gì?”"),t("br"),t("br"),n._v("Bặc Huân Cổ Thánh không nghĩ tới, trước mắt vị này trực tiếp động thủ, nổ đom đóm mắt."),t("br"),t("br"),n._v("“Còn có thể làm gì? Đương nhiên là cứu ngươi!” Mặt không hề cảm xúc, Trương Huyền trong giọng nói hơi không kiên nhẫn: “Nếu như không cho cứu, chúng ta bây giờ liền đi!”"),t("br"),t("br"),n._v("Bặc Huân Cổ Thánh vụt qua, suýt chút nữa không có từ không trung ngã xuống đi."),t("br"),t("br"),n._v("Ta trêu ai ghẹo ai, liền như thế không công chịu một đao?"),t("br"),t("br"),n._v("“Còn xin đại nhân xuất thủ cứu giúp...” Lòng tràn đầy buồn bực, nhưng cũng không dám nói nhiều, ôm quyền nói."),t("br"),t("br"),n._v("“Cái này còn tạm được!”"),t("br"),t("br"),n._v("ngantruyen.com"),t("br"),n._v("Gật gật đầu, Trương Huyền không nói thêm lời, đi tới trước mặt, ngón tay chỉ tới."),t("br"),t("br"),n._v("Huyết dịch các loại đầu ngón tay của hắn vừa tiếp xúc, lần nữa thả ra ngũ sắc quang mang, giống như là có đồ vật gì đó, từ đối phương trên người bị rút ra đi ra."),t("br"),t("br"),n._v("Quang mang càng ngày càng thịnh, tại Trương Huyền đầu ngón tay không ngừng xoay tròn, hội tụ, rất nhanh tạo thành một cái tảng đá hình dáng."),t("br"),t("br"),n._v("Chính là trước đó Bặc Huân Cổ Thánh luyện hóa Nguyên Từ thạch."),t("br"),t("br"),n._v("Kèm theo trong cơ thể ngũ thải quang mang càng ngày càng ít, người sau cảm thấy trước đó có chút người cứng ngắc, lần nữa khôi phục tri giác."),t("br"),t("br"),n._v("Trong tay đối phương tảng đá, giống như là phát giác bị người phá hủy chuyện tốt, không ngừng nhảy, toả ra dữ tợn kinh khủng ý niệm, muốn phá tan thanh niên chân khí bọc, lại bất kể như thế nào đều không làm được."),t("br"),t("br"),n._v("“Cái này tảng đá... Có ý niệm của mình?”"),t("br"),t("br"),n._v("Bặc Huân Cổ Thánh mí mắt nhảy lên."),t("br"),n._v("Cùng cái này tảng đá ở chung được không dưới ba ngàn năm, chưa hề phát giác được điểm ấy."),t("br"),t("br"),n._v("Ẩn chứa linh trí, liền cùng một ít kịch liệt vũ khí tương tự, luyện hóa càng thêm phức tạp, mà không phải cái kia dạng, dùng chân khí đồng hóa."),t("br"),t("br"),n._v("“Vốn không có, nhưng ngươi dùng chân khí luyện hóa nó, nó cũng đang dùng lực lượng của mình đối ngươi đồng hóa, thời gian lâu dài, ôn dưỡng ra thuộc về mình ý niệm!”"),t("br"),t("br"),n._v("Trương Huyền giải thích nói: “Ngươi sở dĩ không hay biết cảm giác, là bởi vì, cỗ này ý niệm, là tại ngươi luyện hóa đồng thời mới vừa vặn thức tỉnh, đưa ngươi triệt để biến thành Nguyên Từ thạch, nó chẳng khác nào có thân thể cùng linh hồn, ngươi cũng liền triệt để chết rồi...”"),t("br"),t("br"),n._v("Trước mắt vị này luyện hóa tảng đá, tảng đá cũng tại luyện hóa hắn."),t("br"),t("br"),n._v("Nguyên Từ thạch loại này thiên địa sinh ra bảo vật, liền xem như Cổ Thánh, cũng không dễ dàng như vậy liền chiếm làm của riêng."),t("br"),t("br"),n._v("Nếu không, Nguyên Đãng sơn vực sâu, cũng sẽ không tồn tại vài vạn năm, đều một mực tồn tại."),t("br"),t("br"),n._v("Bất quá, dưới tình huống bình thường, cái này tảng đá, coi như có thể sinh ra ý niệm, cũng cần không biết bao lâu, đem một vị Cổ Thánh biến thành tảng đá, càng không dễ dàng như vậy... Là Trương Huyền, lặng lẽ khống chế lực lượng nguyên từ, cho đối phương khải linh."),t("br"),t("br"),n._v("Loại thủ đoạn này, mượn rễ cây khải linh phương pháp, Bặc Huân Cổ Thánh tuy là mạnh mẽ, không có phát giác phía dưới, trực tiếp trúng chiêu."),t("br"),t("br"),n._v("Tuy là làm như vậy, có chút bức bách đối phương, nhưng cho dù không ra tay, kèm theo thời gian trôi qua, tình huống hiện tại, vẫn như cũ sẽ xuất hiện, bởi vậy, giờ phút này ra tay, vẫn như cũ tương đương giúp hắn giải quyết tai hoạ ngầm."),t("br"),t("br"),n._v("Không nghĩ tới suýt chút nữa chết tại một cái tảng đá trên tay, Bặc Huân Cổ Thánh khuôn mặt trắng bệch."),t("br"),t("br"),n._v("“Nguyên từ lực, làm một loại đặc thù lực lượng, tuyệt không phải dễ dàng như vậy liền có thể luyện hóa...”"),t("br"),t("br"),n._v("Gật gật đầu, Trương Huyền bàn tay nâng lên, ngũ thải quang mang trong tay hội tụ càng ngày càng nhiều."),t("br"),t("br"),n._v("Dữ tợn hòn đá, không ngừng nhảy, muốn lao ra hắn Thiên đạo chân khí bao phủ, làm thế nào đều không làm được."),t("br"),t("br"),n._v("“Luyện hóa một chút thử một chút...”"),t("br"),t("br"),n._v("Thấy đối phương trong cơ thể dung hợp lực lượng nguyên từ, bị rút ra không sai biệt lắm, Trương Huyền thở phào nhẹ nhõm, biết vị này sẽ không còn nguy hiểm tính mạng, trong lòng đột nhiên động một cái."),t("br"),t("br"),n._v("Khối này Nguyên Từ thạch, Bặc Huân Cổ Thánh muốn luyện hóa, rất khó, nhưng hắn nắm giữ Thiên đạo công pháp, Thiên đạo chân khí, có thể sửa đổi thuộc tính, biến thành loại lực lượng này, có lẽ, có thể thuận lợi hoàn thành."),t("br"),t("br"),n._v("Có ý nghĩ, tinh thần khẽ động, đi vào trong Đồ Thư Quán Xuân Thu đại điển bên trong, chân khí xoay một cái."),t("br"),t("br"),n._v("Xì xì xì!"),t("br"),t("br"),n._v("Trước mắt trong viên đá ngũ thải quang mang, lập tức mắt thường có thể thấy được tiêu hao, hóa thành khí lưu, tràn vào trong cơ thể của hắn."),t("br"),t("br"),n._v("Hắn ý niệm tại Xuân Thu đại điển bên trong tu luyện, tương đương với gấp mười lần tốc độ, phía ngoài Thần Dung Hoàng cùng Bặc Huân Cổ Thánh, chỉ thấy đầy trời ánh sáng bao phủ, ngũ thải quang mang đại thịnh, đến cùng xảy ra chuyện gì, ngược lại không nhìn thấy."),t("br"),t("br"),n._v("“Chẳng lẽ... Nguyên Từ thạch, muốn thôn phệ hắn?”"),t("br"),t("br"),n._v("Bặc Huân Cổ Thánh lông mày nhảy dựng."),t("br"),t("br"),n._v("Thứ này, vừa đem hắn đồng hóa suýt chút nữa mất mạng, bị trước mắt vị này rút ra đi ra, chẳng lẽ thẹn quá hoá giận, muốn đối phó hắn đi!"),t("br"),t("br"),n._v("Thật muốn như vậy, bọn họ cũng không có phương pháp cứu người."),t("br"),t("br"),n._v("“Cái này... Ta cũng không biết, có điều, hắn đã có thể giải quyết trong cơ thể ngươi lực lượng nguyên từ, cũng hẳn là có thể giải quyết cục diện bây giờ...”"),t("br"),t("br"),n._v("Thần Dung Hoàng cũng đầy là kỳ lạ, cuối cùng gật gật đầu."),t("br"),t("br"),n._v("Trước mắt vị này, có thể để cho Linh Thần đều lau mắt mà nhìn, tự nhiên có không giống bản lĩnh."),t("br"),t("br"),n._v("“Hắn... Là ngươi đệ tử mới thu?”"),t("br"),t("br"),n._v("Nghe Thần Dung Hoàng nói như vậy, Bặc Huân Cổ Thánh thở phào nhẹ nhõm, đồng thời tràn đầy nghi ngờ nhìn qua."),t("br"),t("br"),n._v("Hắn cùng trước mắt vị này đã sớm quen biết, trước kia chưa bao giờ thấy qua vị này, chẳng lẽ, là cho rằng thời gian không nhiều, mới thu học sinh?"),t("br"),t("br"),n._v("Bất quá... Học sinh, sao có thể thay hắn làm chủ? Hơn nữa, nhìn hai người đối thoại, dường như người thanh niên này, địa vị cao hơn một chút."),t("br"),t("br"),n._v("“Đệ tử? Bặc huynh chiết sát ta... Vị này chính là thiếu gia của ta!”"),t("br"),t("br"),n._v("Thần Dung Hoàng nói."),t("br"),t("br"),n._v("“Thiếu gia?”"),t("br"),t("br"),n._v("Bặc Huân Cổ Thánh càng ngày càng kì quái."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1806"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1808"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);