(window.webpackJsonp=window.webpackJsonp||[]).push([[913],{1268:function(n,h,t){"use strict";t.r(h);var g=t(44),c=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1796-hoa-tu-co-thanh-【-canh-thu-nhat-】"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1796-hoa-tu-co-thanh-【-canh-thu-nhat-】"}},[n._v("#")]),n._v(" Chương 1796: Hỏa Tư Cổ Thánh 【 canh thứ nhất 】")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Thần Dung Hoàng tổ phụ, là năm đó cùng Khổng sư tranh đấu Ngoan Nhân, vạn thế đều có lưu lại hắn danh, không người có thể vượt qua!"),t("br"),t("br"),n._v("Bởi vậy, vị này tuy là cùng hắn đều là Cổ Thánh, nhưng từ nhỏ liền bị xác lập vì Hoàng giả, địa vị kính trọng... Lúc nào toát ra cái thiếu gia?"),t("br"),t("br"),n._v("Đường đường Linh tộc thứ nhất Hoàng giả, xưng hô người khác vì thiếu gia..."),t("br"),t("br"),n._v("Ai có tư cách này?"),t("br"),t("br"),n._v("“Hắn là Linh Thần người yêu...” Thần Dung Hoàng giải thích."),t("br"),t("br"),n._v("“Linh Thần?”"),t("br"),t("br"),n._v("Bặc Huân Cổ Thánh rụt cổ một cái."),t("br"),t("br"),n._v("Linh tộc có thể truyền thừa xuống, chính là bởi vì Linh Thần, trước mắt vị này lại là nàng người yêu... Lai lịch thật là quá lớn."),t("br"),t("br"),n._v("Lần nữa nhìn về phía Trương Huyền, Bặc Huân Cổ Thánh tràn đầy kính sợ."),t("br"),t("br"),n._v("Thời khắc này Trương Huyền, không đi quản hai người thảo luận, toàn thân tâm đắm chìm trong nguyên từ lực rèn luyện bên trong."),t("br"),t("br"),n._v("Đại Thánh tứ trọng Bất Hủ cảnh, thân thể, linh hồn, bất hủ bất diệt, đổi lại bình thường, không có đối ứng Thiên đạo công pháp, muốn tăng lên, không biết tiêu phí bao lâu, mà bây giờ, Nguyên Từ thạch bên trong toả ra lực lượng, vừa vặn có thể đối với hắn tiến hành rèn luyện, để hắn dần dần hướng cảnh giới này tới gần."),t("br"),t("br"),n._v("Không biết qua bao lâu, thân thể lần nữa phát sinh thuế biến, bắp thịt toàn thân, linh hồn, phát ra hào quang chói sáng, thần thánh không thể xâm phạm."),t("br"),t("br"),n._v("Bất Hủ cảnh!"),t("br"),t("br"),n._v("Còn tưởng rằng cảnh giới này, không biết phải hao phí bao lâu, không nghĩ tới, nguyên từ lực nắm giữ như vậy công hiệu, một lần thành công."),t("br"),t("br"),n._v("Đột phá cảnh giới, Trương Huyền cũng không ngừng lại, tiếp tục thôn phệ bốn phía lan ra ngũ thải quang mang."),t("br"),t("br"),n._v("Cái này tảng đá, có thể để cho tích huyết trùng sinh cường giả đều bất lực, đủ thấy đáng sợ, kèm theo đi vào lực lượng trong cơ thể càng ngày càng nhiều, tu vi cũng mắt thường có thể thấy được nhanh chóng tăng thêm."),t("br"),t("br"),n._v("Bất Hủ cảnh sơ kỳ!"),t("br"),t("br"),n._v("Bất Hủ cảnh trung kỳ!"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Nửa canh giờ, liền đạt đến Bất Hủ cảnh đại viên mãn!"),t("br"),t("br"),n._v("Cô cô cô!"),t("br"),t("br"),n._v("Lực lượng hùng hồn, ở trong người không ngừng cuồn cuộn, đạt tới bình cảnh, cũng không còn cách nào tiến bộ, Trương Huyền lúc này mới chậm rãi mở mắt."),t("br"),t("br"),n._v("Học sinh, cha mẹ một cái tiếp một cái đạt tới loại cảnh giới này, rốt cục bản thân cũng đến."),t("br"),t("br"),n._v("Đạt tới Bất Hủ cảnh đại viên mãn, lực lượng so trước đó mạnh mẽ gấp mấy lần, phối hợp thêm Long Cốt thần thương, cho dù không thích hợp Yêu Dị huyền đao, huyết mạch kéo dài cấp bậc Cổ Thánh, có lẽ cũng có thể đánh một trận!"),t("br"),t("br"),n._v("Đương nhiên, Cổ Thánh mạnh mẽ chính là đối pháp tắc lĩnh ngộ, cùng đối với thiên địa hiểu, trên lực lượng, ngược lại không có trọng yếu như vậy."),t("br"),t("br"),n._v("Có thể chiến đấu, cùng chém giết đối phương, vẫn tồn tại như cũ lấy một khoảng cách lớn."),t("br"),t("br"),n._v("“Tùy thời có thể lấy xung kích Cổ Thánh...”"),t("br"),t("br"),n._v("Đạt tới loại cảnh giới này, lực lượng ngược lại là tiếp theo, trọng yếu nhất chính là đại biểu, khoảng cách Cổ Thánh chỉ còn lại có tới cửa một cước."),t("br"),t("br"),n._v("Hắn hiện tại tâm cảnh đã đột phá, trong tay cũng có Cổ Thánh lực lượng, chỉ cần lĩnh ngộ một loại pháp tắc, vượt qua Thiên đạo, liền có thể đi thử."),t("br"),t("br"),n._v("Thời khắc này tình huống, chỉ có trở thành Cổ Thánh, mới tính được là bên trên nhất phi trùng thiên, chân chính đứng ở thế giới chi đỉnh."),t("br"),t("br"),n._v("Biết cần thời gian củng cố, tạm thời không có khả năng xung kích thành công, Trương Huyền ngừng lại."),t("br"),t("br"),n._v("Bàn tay vung lên, đầy trời ánh sáng tiêu tán, Nguyên Từ thạch xuất hiện lần nữa tại lòng bàn tay, chỉ bất quá, rút nhỏ tiếp cận một nửa."),t("br"),t("br"),n._v("Trong thư viện tu luyện hơn nửa canh giờ, ngoại giới vẫn chưa tới mười phút đồng hồ."),t("br"),t("br"),n._v("“Tu vi của ngươi...”"),t("br"),t("br"),n._v("Quang mang tán hết, Thần Dung Hoàng ngay sau đó nhìn ra Trương Huyền thực lực bây giờ, trợn mắt hốc mồm."),t("br"),t("br"),n._v("Đây là... Lại lên cấp?"),t("br"),t("br"),n._v("Không phải mới đột phá Tâm Huyết Lai Triều ư?"),t("br"),t("br"),n._v("Nhất thời có loại, ta là ai, ta ở đâu cảm thấy, không hiểu tâm tắc."),t("br"),n._v(" Người khác đột phá, cần tiêu phí tâm huyết cùng tinh lực, cái tên này, cùng đùa giỡn giống như..."),t("br"),t("br"),n._v("Còn để cho người ta có sống hay không?"),t("br"),t("br"),n._v("Thật không biết đến cùng tu luyện thế nào."),t("br"),t("br"),n._v("Không để ý tới hai người biểu lộ, Trương Huyền nhìn về phía một bên Bặc Huân Cổ Thánh: “Trên người ngươi tai hoạ ngầm đã giải quyết, khối này Nguyên Từ thạch, cũng bị ta hủy diệt ý niệm, có thể làm trở thành pháp bảo tùy ý sử dụng, chỉ bất quá... Tốt nhất đừng thử nghiệm luyện hóa, nếu không, tình huống trước, khả năng sẽ còn xuất hiện!”"),t("br"),t("br"),n._v("Nói xong, đem Nguyên Từ thạch đưa tới."),t("br"),t("br"),n._v("Thứ này là đối phương hao tốn ba ngàn năm ôn dưỡng, mới có bộ dáng bây giờ, tuy là bảo vật, nhưng đã chiếm được chỗ tốt, không tiện đoạt người chỗ yêu."),t("br"),t("br"),n._v("“Đa tạ ân cứu mạng...” Nhận lấy Nguyên Từ thạch, Bặc Huân Cổ Thánh thở phào nhẹ nhõm, lần nữa nhìn về phía Thần Dung Hoàng: “Ngươi có kế hoạch gì, như thế nào báo thù, ta nghe ngươi!”"),t("br"),t("br"),n._v("Trước đó đáp ứng, hiện tại lại biết được vị này chân thực thân phận, trong lòng lại không có nửa điểm do dự."),t("br"),t("br"),n._v("“Linh Hoàng cùng Tinh Hoàng, hai người tập hợp tám vị Cổ Thánh, chúng ta bây giờ vẫn là quá yếu, tính toán đi mời Hỏa Tư Cổ Thánh, giúp ta một chút sức lực!”"),t("br"),t("br"),n._v("Suy tư phút chốc, Thần Dung Hoàng nói."),t("br"),t("br"),n._v("“Hỏa Tư Cổ Thánh?”"),t("br"),t("br"),n._v("Nhíu nhíu mày, Bặc Huân Cổ Thánh nhìn lại: “Ngươi nói thế nhưng là Thánh thú nhất tộc vị lão tổ kia? Năm đó, ngươi xuất binh chinh phạt, sớm đã kết xuống đại thù... Đi mời, nó sẽ giết ngươi đi!”"),t("br"),t("br"),n._v("Hỏa Tư Cổ Thánh, cũng không phải là Linh tộc cường giả, mà là sinh hoạt tại Linh tộc trong khu vực Thánh thú nhất tộc lão tổ."),t("br"),t("br"),n._v("Thực lực so với hắn chỉ mạnh không yếu."),t("br"),t("br"),n._v("Chỉ bất quá, vị này Thánh thú cùng Thần Dung Hoàng có đại thù, đi tìm nó quy thuận, không những sẽ không thành công, làm không cẩn thận, sẽ còn dẫn tới hiệu quả trái ngược đi!"),t("br"),t("br"),n._v("“Đạt tới tích huyết trùng sinh cảnh giới Cổ Thánh, hết thảy cứ như vậy mấy vị, có thể đếm được trên đầu ngón tay, không đi tìm nó, chúng ta không có khả năng chiến thắng...”"),t("br"),t("br"),n._v("Thần Dung Hoàng lắc đầu."),t("br"),t("br"),n._v("Cái khác rất nhiều vương giả, tuy là mạnh mẽ, nhưng hắn liều mạng, vẫn như cũ có thể chống cự... Có thể Linh Hoàng, Tinh Hoàng thực sự quá mạnh, nhất định phải có cùng cấp bậc cường giả mới có thể chống lại."),t("br"),t("br"),n._v("Cùng hắn tìm chút yếu, đi qua tự tìm đường chết, còn không bằng, tìm cùng cấp bậc cường giả."),t("br"),t("br"),n._v("“Thánh thú?” Trương Huyền nhìn qua."),t("br"),t("br"),n._v("“Bản thể là một đầu hỏa diễm cự thú, so với ta, cũng không kém quá nhiều, năm đó, cùng Linh tộc chiến đấu, ta dẫn dắt người, đem hắn đuổi ra ngoài, hai bên là có chút mâu thuẫn...” Thần Dung Hoàng nói."),t("br"),t("br"),n._v("Nếu có những người khác có thể lựa chọn, chắc chắn sẽ không tìm cái này, dù sao, hai bên có mâu thuẫn, rất dễ dàng xuất hiện hiệu quả trái ngược."),t("br"),t("br"),n._v("“Thiếu gia, không phải đối thuần thú có thủ đoạn đặc thù ư? Có lẽ...”"),t("br"),t("br"),n._v("Tràn đầy hi vọng nhìn qua."),t("br"),t("br"),n._v("Trước mắt vị thiếu gia này, Kim Thân cảnh thời điểm, liền tuỳ tiện thuần phục Bất Hủ cảnh đại viên mãn Thánh thú, vị này Hỏa Tư Cổ Thánh tuy là mạnh mẽ, không dễ thuần phục, nhưng giữ gìn mối quan hệ, hẳn là có hi vọng."),t("br"),t("br"),n._v("“Giữa các ngươi mâu thuẫn lớn bao nhiêu?”"),t("br"),t("br"),n._v("Cũng không đồng ý, Trương Huyền nhíu nhíu mày, hỏi."),t("br"),t("br"),n._v("Mâu thuẫn không đội trời chung, không cách nào hóa giải, ai đi cũng vô dụng."),t("br"),t("br"),n._v("“Là chủng tộc chi tranh, nó muốn dẫn dắt tộc nhân, chiếm cứ Linh tộc một mảnh khu vực, ta không có đồng ý, hai bên lấy thắng bại tới quyết định, ta bằng vào thực lực, đem hắn chiến thắng, đưa ra mấy ngàn vạn cây số...”"),t("br"),t("br"),n._v("Gãi đầu một cái, Thần Dung Hoàng nói."),t("br"),t("br"),n._v("Năm đó trẻ tuổi nóng tính, cũng không nương tay, không chỉ đem hắn đuổi đi, còn đem nó đánh thành bị thương nặng..."),t("br"),t("br"),n._v("“Đưa ra mấy ngàn vạn cây số? Toàn bộ chủng tộc di chuyển?”"),t("br"),t("br"),n._v("Đối phương nói chuyện, là hắn biết cái này mâu thuẫn hầu như không có cách nào hóa giải, bất đắc dĩ lắc đầu: “Loại này thù hận, ta chỉ sợ cũng bất lực!”"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Nghe được hắn đều không có phương pháp, Thần Dung Hoàng dừng lại một chút, hàm răng cắn chặt, ánh mắt lộ ra tàn nhẫn: “Thực sự không được, ta đi qua chịu đòn nhận tội, mặc cho giết mặc cho quát!”"),t("br"),t("br"),n._v("(Tấu chương xong)"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1807"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1809"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);