(window.webpackJsonp=window.webpackJsonp||[]).push([[914],{1270:function(n,h,t){"use strict";t.r(h);var c=t(44),g=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1797-ta-can-long-huyet-【-canh-thu-hai-】"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1797-ta-can-long-huyet-【-canh-thu-hai-】"}},[n._v("#")]),n._v(" Chương 1797: Ta cần long huyết 【 canh thứ hai 】")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Trương Huyền cau mày: “Mặc cho giết mặc cho quát cũng vô dụng, Thánh thú thù dai mà nói, tuyệt không phải đi qua xin lỗi liền có thể giải quyết!”"),t("br"),t("br"),n._v("Thân là thuần thú sư, biết loại này dời tộc đại thù, liên lụy đến căn bản, tuyệt không phải chịu đòn nhận tội là có thể giải quyết."),t("br"),t("br"),n._v("Nếu là hắn không có gặp công kích, lấy thực lực vô địch, cùng Dị Linh thứ nhất Hoàng giả thân phận, cấp cho hứa hẹn, đối phương tất nhiên sẽ đồng ý, mà bây giờ, không nhân cơ hội tìm ngươi phiền phức là tốt lắm rồi, hoài tưởng lôi kéo, tuyệt đối không thể."),t("br"),t("br"),n._v("“Vậy làm sao bây giờ?”"),t("br"),t("br"),n._v("Thần Dung Hoàng cũng rõ ràng điểm ấy, chỉ là ôm lấy một tia hi vọng thôi, giờ phút này nghe nói như thế, nhịn không được nhìn tới."),t("br"),t("br"),n._v("“Không có những biện pháp khác, trừ phi...” Trương Huyền vẻ mặt nghiêm túc: “Có thể tìm tới, Long tộc cường giả huyết dịch!”"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Liếc mắt nhìn nhau, Thần Dung Hoàng cùng Bặc Huân Cổ Thánh mỗi người sững sờ."),t("br"),t("br"),n._v("“Hỏa Tư Cổ Thánh cùng Long tộc không có bất cứ quan hệ nào...”"),t("br"),t("br"),n._v("Mặc dù Long tộc là vạn thú chi vương, nhưng Hỏa Tư Cổ Thánh bản tôn cùng Long tộc không có nửa phần tiền quan hệ, cho dù có Long tộc huyết mạch, lại có thể thế nào?"),t("br"),t("br"),n._v("Cả hai căn bản không treo câu ah."),t("br"),t("br"),n._v("Long tộc có huyết mạch áp chế, huyết mạch tinh thuần, có thể áp chế mỏng manh, để hắn thần phục, chút người này người rõ ràng, nhưng không phải cùng chủng tộc, liền không có cái gì hiệu quả."),t("br"),t("br"),n._v("“Ta muốn Long tộc huyết dịch, cũng không phải là đối với nó áp chế, mà là... Mở ra Long Cốt thần thương bên trên phong ấn!”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("Hắn đánh thuần thú pháp, muốn phục tùng Thánh thú, nhất định phải tại trên thực lực đem hắn nghiền ép."),t("br"),t("br"),n._v("Mấy ngày nay mặc dù tiến bộ rất nhanh, đạt đến Bất Hủ cảnh đại viên mãn, nhưng cùng tích huyết trùng sinh cấp bậc Cổ Thánh so, vẫn là kém không biết bao xa."),t("br"),t("br"),n._v("Trừ phi... Long Cốt thần thương bên trên phong ấn giải trừ."),t("br"),t("br"),n._v("Long Cốt thần thương giải phong phương pháp có hai cái, thứ nhất, bản thân đạt tới Cổ Thánh cấp bậc, dùng Cổ Thánh cấp bậc chân khí không ngừng ôn dưỡng."),t("br"),t("br"),n._v("Thứ hai, tìm kiếm càng thêm tinh thuần huyết dịch, để cốt long lột xác ra thân thể!"),t("br"),t("br"),n._v("Cái thứ nhất, trong thời gian ngắn, rất khó làm được, tuy là khoảng cách cảnh giới này chỉ kém tới cửa một cước, có thể cho đến trước mắt, còn không có chút đầu mối nào, không bằng tạm hoãn một cái."),t("br"),t("br"),n._v("Cái thứ hai, chỉ cần cốt long lột xác ra thân thể, liền có thể mở ra Nhiễm Cầu Cổ Thánh lưu lại phong ấn, từ đó một lần khôi phục Cổ Thánh chí bảo siêu tuyệt thực lực."),t("br"),t("br"),n._v("Đến lúc đó, hoàn toàn có thể đánh bại vị này cái gọi là Hỏa Tư Cổ Thánh, để hắn thần phục."),t("br"),t("br"),n._v("Tuy là Ngoan Nhân luyện hóa cánh tay về sau, đã mơ hồ đạt đến tích huyết trùng sinh cao nhất, có thể nghĩ thuần phục Thánh thú, cần chính là thực lực bản thân, dựa vào Ngoan Nhân, rất khó để hắn tin tưởng và nghe theo."),t("br"),t("br"),n._v("“Thuần túy Long tộc, cùng Linh Thần đồng dạng, thuộc về Thần tộc... Danh sư đại lục vị trí vị diện, không chịu nổi...”"),t("br"),t("br"),n._v("Biết mục đích của hắn, Thần Dung Hoàng cười khổ lắc đầu."),t("br"),t("br"),n._v("Danh sư đại lục cùng Linh tộc vị trí cổ chiến trường, có Long tộc, cơ bản huyết mạch đều không thế nào tinh thuần, liền giao long cũng không bằng."),t("br"),t("br"),n._v("Nguyên nhân chính là như vậy, có thể nói ra chính tông Thiên Long Bát Âm liền có thể ung dung để hắn thần phục."),t("br"),t("br"),n._v("Không có thuần khiết Long tộc, Long tộc huyết dịch, cũng đã rất khó tìm tìm."),t("br"),t("br"),n._v("“Ta biết nơi nào có Long tộc huyết dịch!”"),t("br"),t("br"),n._v("Lời nói không có kết thúc, một bên Bặc Huân Cổ Thánh mở miệng nói."),t("br"),t("br"),n._v("Trương Huyền vội vàng nhìn tới."),t("br"),t("br"),n._v("“Ngươi cần có, hẳn là cũng không phải chân chính Long tộc huyết mạch, đạt tới Cổ Thánh cấp bậc Long tộc chi huyết, cũng có thể giải phong a?”"),t("br"),t("br"),n._v("Bặc Huân Cổ Thánh hỏi."),t("br"),t("br"),n._v("  Chân chính long tộc, đại lục ở bên trên không có khả năng có, lại thế nào tìm cũng vô dụng, nhưng... Đạt tới Cổ Thánh cấp bậc Long tộc cường giả huyết dịch, còn có thể tìm tới."),t("br"),n._v(" Trương Huyền gật đầu."),t("br"),t("br"),n._v("Cổ Thánh đã là đại lục cao nhất cường giả, loại cấp bậc này huyết dịch, lại phối hợp nhất định thủ đoạn, hẳn là có thể ung dung phá giải phong ấn."),t("br"),t("br"),n._v("“Nếu như chỉ là Cổ Thánh cấp bậc Long tộc huyết mạch, ta biết nơi nào có...” Bặc Huân Cổ Thánh nói."),t("br"),t("br"),n._v("“Ngươi chẳng lẽ nói chính là...”"),t("br"),t("br"),n._v("Giống như là nhớ ra cái gì đó, Thần Dung Hoàng vẻ mặt trở nên khó coi."),t("br"),t("br"),n._v("“Không sai, chính là chỗ đó!”"),t("br"),t("br"),n._v("Gật gật đầu, Bặc Huân Cổ Thánh nói: “Năm đó, Linh Hoàng tổ phụ, Doanh Hoàng bệ hạ, từng chém giết qua một đầu Cổ Thánh cấp bậc Hắc Long, đem hắn huyết dịch, đặt ở bản thân Tẩm điện huyết trì, không chỉ có như vậy, còn mượn hơn mười vị Cổ Thánh cường giả tinh huyết, tiến hành ôn dưỡng... Mong đợi có thể đản sinh ra một đầu Hắc Long, thuần phục vì thú sủng, nhưng chẳng biết tại sao, cuối cùng đều không có thành công! Về sau bệ hạ từ trần, cái cung điện này liền bị phong cấm, lại không người đi vào... Không có gì bất ngờ xảy ra, huyết dịch chắc còn ở!”"),t("br"),t("br"),n._v("Long tộc, đối với bất kỳ sinh linh tới nói, đều có lực hấp dẫn thật lớn, nếu như có thể thuần phục thành thú sủng, toàn thể sức chiến đấu, khẳng định gia tăng thật lớn."),t("br"),t("br"),n._v("Thần Linh Hoàng tổ phụ, cùng Ngoan Nhân cùng lúc Doanh Hoàng bệ hạ, mười phần trầm mê, hao tốn không biết bao nhiêu tâm huyết, từ nơi nào tìm được một đầu, có giao long huyết mạch Hắc Long, đánh chết, lại hao tốn vô số nhân lực tài lực, mong đợi có thể lấy huyết mạch, sinh ra một đầu mới Long tộc Thánh thú, nhân cơ hội thuần phục, chỉ tiếc, không biết nơi nào xảy ra sai sót, cuối cùng sắp thành lại bại."),t("br"),t("br"),n._v("“Những này đều chỉ là tin đồn, thời gian xa xưa, có phải thật vậy hay không, cũng chưa biết chừng...”"),t("br"),t("br"),n._v("Thần Dung Hoàng cau mày."),t("br"),t("br"),n._v("Chuyện này, Linh tộc trong lịch sử là có ghi chép, nhưng đã là mấy vạn năm trước sự tình, đến cùng phải hay không thật đều đáng giá thương thảo, há có thể thật chứ?"),t("br"),t("br"),n._v("“Hẳn là thật, tới Nguyên Đãng sơn trước đó, ta từng đi qua Linh Hoàng cung điện, cầu lấy ra long lân thảo, loại vật này, chỉ có tại Long tộc sinh hoạt hoàn cảnh mới có thể sinh trưởng, Linh Hoàng cung điện không có Long tộc cường giả, lại có thể sinh trưởng vô cùng tốt, hẳn là cùng hắn liên quan đến!”"),t("br"),t("br"),n._v("Bặc Huân Cổ Thánh khẳng định nói."),t("br"),t("br"),n._v("Long lân thảo, Long khí tẩm bổ đặc thù dược liệu, nắm giữ cường thân kiện thể, tăng cường huyết mạch tác dụng, đặc biệt là đối Thánh thú, có cực lớn hiệu quả, mười phần trân quý."),t("br"),t("br"),n._v("Năm đó hắn tìm rất nhiều nơi đều tìm tìm không được, cuối cùng tại Linh Hoàng cung điện tìm tới, trong lòng cũng có chút hoài nghi."),t("br"),t("br"),n._v("“Ngươi nói nơi này, tại vương thành, Linh Hoàng chỗ này?”"),t("br"),t("br"),n._v("Nghe xong hai người đối thoại, Trương Huyền cũng hiểu được, nghi ngờ nhìn qua."),t("br"),t("br"),n._v("“Vâng!” Bặc Huân Cổ Thánh gật đầu."),t("br"),t("br"),n._v("Trương Huyền không nói thêm gì nữa, mà là mày nhăn lại."),t("br"),t("br"),n._v("“Thiếu gia, Linh Hoàng bị thiệt lớn, khẳng định đề phòng rất nghiêm, hắn cung điện, càng là quan trọng nhất, muốn đi vào trong đó, gần như không có khả năng...”"),t("br"),t("br"),n._v("Gặp hắn hỏi thăm, biết có chút động tâm, Thần Dung Hoàng vội vàng nói."),t("br"),t("br"),n._v("“Quả thực rất khó, có điều, không có nghĩa là không có cơ hội...”"),t("br"),t("br"),n._v("Tay trái vịn cái cằm, Trương Huyền nói."),t("br"),t("br"),n._v("“Nhưng...”"),t("br"),t("br"),n._v("“Như vậy, các ngươi trước đi tìm kiếm cái khác Cổ Thánh, nhìn có thể hay không lại lôi kéo một ít, ta hiện liền đi vương thành, yên tâm, lấy ta ngụy trang chi thuật, coi như tìm không thấy trong miệng các ngươi huyết trì, chạy trốn hẳn là cũng không khó!”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("Hiện tại chỗ nguy hiểm nhất, chính là Dị Linh vương thành, lấy thực lực của hắn bây giờ, tốt nhất đừng đi mạo hiểm, nhưng... Nếu như có thể mượn cơ hội kích hoạt Long Cốt thần thương, sức chiến đấu có thể trực tiếp đạt tới tích huyết trùng sinh cảnh giới... Giá trị tuyệt đối!"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Nghe hắn nói ngụy trang chi thuật, Thần Dung Hoàng không nói thêm lời."),t("br"),t("br"),n._v("Linh Thần ban cho ngụy trang phù lục, thập phần cường đại, lại thêm thiếu gia, tiềm hành mà đến, hầu như không có người biết hắn còn sống, có lẽ thật đúng là có thể một lần thành công."),t("br"),t("br"),n._v("(Giữa tháng, tiếp tục cầu phiếu! Nguyệt phiếu! Nguyệt phiếu!)"),t("br"),t("br"),n._v("(Tấu chương xong)"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1808"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1810"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);