(window.webpackJsonp=window.webpackJsonp||[]).push([[916],{1269:function(n,h,g){"use strict";g.r(h);var t=g(44),c=Object(t.a)({},(function(){var n=this,h=n.$createElement,g=n._self._c||h;return g("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[g("h1",{attrs:{id:"chuong-1798-luu-duong-trai-qua-【-canh-3-】"}},[g("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1798-luu-duong-trai-qua-【-canh-3-】"}},[n._v("#")]),n._v(" Chương 1798: Lưu Dương trải qua 【 Canh [3] 】")]),n._v(" "),g("hr"),n._v(" "),g("p",[n._v("Chương 1798: Lưu Dương trải qua "),g("br"),g("br"),n._v("“Thiếu gia nhất định phải cẩn thận!”"),g("br"),g("br"),n._v("Biết đối phương làm ra quyết định, không có người có thể sửa đổi, Thần Dung Hoàng lo lắng."),g("br"),g("br"),n._v("Vị này thật muốn có cái gì bất trắc, Linh Thần làm không cẩn thận, sẽ dưới cơn nóng giận, đem trọn cái Linh tộc đều triệt để hủy diệt."),g("br"),g("br"),n._v("Linh Thần thực lực, người khác biết không nhiều, hắn biết rất rõ ràng, chỉ bằng vào đến, liền có thể làm cho cả danh sư đại lục không chịu nổi mà tùy thời sụp đổ, thật muốn ra tay, đừng nói một chủng tộc, đại lục ở bên trên tất cả chủng tộc liên hợp cùng một chỗ, cũng khẳng định không đỡ được."),g("br"),g("br"),n._v("“Yên tâm!” Biết đối phương lo lắng, Trương Huyền khoát tay áo, nói: “Các ngươi cũng tận quản tìm kiếm cái khác có thể lôi kéo Cổ Thánh a, ta một thân một mình đi vào vương thành là được!”"),g("br"),g("br"),n._v("Đi tìm long huyết, người càng ít càng tốt, dựa vào ngụy trang phù lục thần kỳ, thêm vào Ngoan Nhân, Yêu Dị huyền đao, coi như thật gặp được nguy hiểm, chạy trốn không khó lắm."),g("br"),g("br"),n._v("Thảo luận cố định, hai bên chia ra hành động."),g("br"),g("br"),n._v("Lần nữa trở lại Dị Linh vương thành, Trương Huyền lần nữa tìm được Lưu Dương."),g("br"),g("br"),n._v("Bởi vì là Thần Dung Hoàng mang đến, mặc dù là cao quý Dị Linh nhất tộc thế hệ trẻ tuổi tài năng xuất chúng, địa vị vẫn như cũ hết sức khó xử."),g("br"),g("br"),n._v("Nếu không phải danh khí cực lớn, có rất nhiều cầm giữ độn, chỉ sợ sớm đã bị độc thủ."),g("br"),g("br"),n._v("“Đến cùng chuyện gì xảy ra?”"),g("br"),g("br"),n._v("Ngồi tại một cái phong kín trong tĩnh thất, Trương Huyền đem bốn phía phong cấm, cũng lại kìm nén không được nghi ngờ trong lòng."),g("br"),g("br"),n._v("Chính mình cái này học sinh, Thanh Nguyên đế quốc để thư lại rời đi, sau đó liền bặt vô âm tín, vốn cho rằng bị cái gì bất trắc, hoặc là lâm vào một ít hiểm địa, nằm mộng cũng nghĩ không ra, đến nơi này, còn chiếm được Thần Dung Hoàng truyền thừa."),g("br"),g("br"),n._v("Tại vị này Dị Linh thứ nhất Hoàng giả toàn lực bồi dưỡng ra, tu vi vậy mà cùng trước đó rất nhiều đệ tử đồng dạng, cũng đạt tới Bất Hủ cảnh đại viên mãn, so với bản thân, đều không kém chút nào."),g("br"),g("br"),n._v("Thấy lão sư hỏi thăm, Lưu Dương không có gấp trả lời, mà là đầu gối mềm nhũn, quỳ rạp xuống đất: “Học sinh không từ mà biệt, để lão sư lo lắng, tội đáng chết vạn lần!”"),g("br"),g("br"),n._v("Danh sư đại lục tôn sư trọng đạo, lão sư càng đối với hắn ân trọng như núi, mà hắn... Làm học sinh, vậy mà xoay người rời đi, số thực đại bất kính."),g("br"),g("br"),n._v("“Không có việc gì liền tốt!” Trương Huyền khoát tay áo."),g("br"),g("br"),n._v("Lúc đó tình huống, mặc dù đối phương làm không đúng, nhưng cũng có thể hiểu."),g("br"),g("br"),n._v("Trịnh Dương trở thành chiến tử, tiền đồ vô lượng; Triệu Nhã trở thành Băng Nguyên cung thiếu cung chủ, hùng cứ một phương; Vương Dĩnh, Viên Đào, Ngụy Như Yên, Lộ Xung... Mỗi một cái, đều có con đường thuộc về mình."),g("br"),g("br"),n._v("Mà hắn, lại suýt chút nữa liên lụy mình đã bị Danh Sư đường ngăn cấm, trong lòng áp lực có thể tưởng tượng được."),g("br"),g("br"),n._v("“Đa tạ lão sư khoan dung độ lượng!”"),g("br"),g("br"),n._v("Thấy lão sư cũng không trách tội ý tứ, Lưu Dương trong lòng tràn đầy cảm động, cũng không đứng dậy, đem trước sự tình, tỉ mỉ kể ra đi ra."),g("br"),g("br"),n._v("“Lúc ấy từ Thanh Nguyên thành rời đi, ta cảm thấy bản thân là cái phế vật, cùng những sư huynh đệ khác so, cái gì cũng sai, trong lòng vô cùng ảm đạm, không có nhìn phương hướng, ngộ nhập một cái Thánh thú sào huyệt, suýt chút nữa bị giết, trong lúc vô tình nghịch hướng vận chuyển lão sư truyền thụ cho công pháp, kết quả, thả ra có thể so với Dị Linh tộc nhân sát lục chân khí!”"),g("br"),g("br"),n._v("“Nghịch hướng vận chuyển công pháp?”"),g("br"),g("br"),n._v("Trương Huyền sững sờ."),g("br"),g("br"),n._v("“Vâng!”"),g("br"),g("br"),n._v("Lưu Dương lên tiếng, chân khí trong cơ thể xoay một cái, Trương Huyền quả nhiên cảm thấy, một cỗ nồng đậm sát lục chân khí, áp bức mà đến, cho người ta một loại Dị Linh hoàng thất cảm giác."),g("br"),g("br"),n._v("Độ tinh thuần so với Thần Dung Hoàng vậy mà đều không hề yếu."),g("br"),g("br"),n._v("“Bản rút gọn công pháp, vậy mà cũng có như thế công hiệu?” Trương Huyền con mắt sáng lên."),g("br"),g("br"),n._v("Trước đó vẫn cho là, ngụy trang ra Dị Linh tộc sát lục chân khí, là Thiên đạo chân khí đặc thù năng lực, không nghĩ tới, bản rút gọn Thiên đạo công pháp, nghịch hướng vận chuyển, cũng có hiệu quả như thế."),g("br"),g("br"),n._v("Đương nhiên, khả năng cũng cùng cá nhân liên quan đến, Trịnh Dương, Triệu Nhã đám người, đồng dạng tu luyện loại này pháp quyết, nhưng không có loại này kỳ ngộ."),g("br"),g("br"),n._v("“Ta nghịch chuyển công pháp, may mắn giết chết Thánh thú, nhưng cũng bị trọng thương, bởi vì khí tức quá mức nồng đậm, đưa tới Dung Hoàng bệ hạ, hắn phát hiện thiên phú của ta cùng năng lực, muốn đem bản thân truyền thừa, truyền thụ cho ta!”"),g("br"),n._v(" Lưu Dương rơi vào hồi ức: “Lão sư nguyện vọng chính là diệt đi Dị Linh tộc nhân, nếu như ta có thể lẫn vào trong đó, có lẽ sẽ có thể giúp trợ đến lão sư, ngay sau đó đáp ứng...”"),g("br"),g("br"),n._v("Trương Huyền nhíu nhíu mày."),g("br"),g("br"),n._v("Lưu Dương tu luyện chính là mình truyền thụ cho bản rút gọn Thiên đạo công pháp, thân thể sớm đã đạt được cải thiện, thể chất tăng cường, tại toàn bộ danh sư đại lục, thiên phú đều được cho khá cao."),g("br"),g("br"),n._v("Nghịch chuyển công pháp, hình thành sát lục chân khí, bị Thần Dung Hoàng tán thưởng, không gì đáng trách, nhưng... Thật trùng hợp đi!"),g("br"),g("br"),n._v("Đường đường Dị Linh tộc thứ nhất Hoàng giả, tại danh sư đại lục, phát hiện một cái “Dị Linh thiên tài” chẳng lẽ liền không có mảy may nghi ngờ ư?"),g("br"),g("br"),n._v("“Ta cùng Lạc Nhược Hi, từ Thanh Nguyên đế quốc rời đi, Nhược Hi là Linh Thần, Thần Dung Hoàng là thuộc hạ của nàng... Sẽ có hay không có chút liên quan?”"),g("br"),g("br"),n._v("Trong lòng hơi động, cũng lại kìm nén không được, nhịn không được hỏi: “Ngươi đi theo Dung Hoàng sau lưng, có thể thấy được qua Lạc Nhược Hi? Cũng chính là Hồng Viễn học viện Lạc lão sư!”"),g("br"),g("br"),n._v("Lạc Nhược Hi tại Hồng Viễn danh sư học viện làm lão sư thời điểm, cùng Lưu Dương, Vương Dĩnh đám người, gặp mặt qua."),g("br"),g("br"),n._v("Nếu như hai người gặp nhau, khẳng định có thể nhận ra."),g("br"),g("br"),n._v("“Chưa thấy qua Lạc lão sư...”"),g("br"),g("br"),n._v("Lưu Dương lắc đầu."),g("br"),g("br"),n._v("Trương Huyền nhíu mày."),g("br"),g("br"),n._v("Chẳng lẽ đoán sai?"),g("br"),g("br"),n._v("Nếu như không phải, như vậy đều sẽ bị Thần Dung Hoàng gặp lên, vận khí có phải hay không quá tốt rồi chút?"),g("br"),g("br"),n._v("“Cái kia... Ngươi có nghe nói qua Linh Thần?” Trương Huyền lần nữa nói."),g("br"),g("br"),n._v("“Dị Linh nhất tộc, người nào không biết Linh Thần?”"),g("br"),g("br"),n._v("Lưu Dương cười cười: “Nàng là Dị Linh tộc tín ngưỡng, liền xem như ba đại Hoàng giả, cũng không dám láo xược! Dung Hoàng bệ hạ, sở dĩ có thể tìm tới ta, nghe nói chính là đạt được Linh Thần chỉ dẫn... Bất quá, đối phương là Thần Linh, ta cũng chỉ nghe qua tin đồn, không có tư cách chiêm ngưỡng thôi!”"),g("br"),g("br"),n._v("“Linh Thần chỉ dẫn?”"),g("br"),g("br"),n._v("Trương Huyền cười cười."),g("br"),g("br"),n._v("Xem ra đoán không sai."),g("br"),g("br"),n._v("Thần Dung Hoàng có thể thu học sinh của mình làm truyền nhân, có Lạc Nhược Hi cái bóng ở bên trong."),g("br"),g("br"),n._v("Chỉ bất quá, đối phương gặp qua Lưu Dương, biết hắn là nhân tộc, để hắn lẫn vào Dị Linh tộc, đến cùng vì cái gì?"),g("br"),g("br"),n._v("Càng nghĩ càng không hiểu."),g("br"),g("br"),n._v("“Đi tới nơi này, ta phát hiện nghịch chuyển sau công pháp, hấp thu Huyết Nguyệt lực lượng về sau, tiến bộ cực nhanh, Dung Hoàng bệ hạ, đối ta cũng là vô cùng tốt, từng chuyên môn chủ trì tế tự, cho ta tăng thực lực lên! Bởi vậy, ngắn ngủi thời gian mấy tháng, đã đột phá Đại Thánh ràng buộc, đạt đến cảnh giới bây giờ!”"),g("br"),g("br"),n._v("Bị bản rút gọn Thiên đạo chân khí tẩy tủy, Lưu Dương tư chất vốn là rất tốt, lại có Lạc Nhược Hi lặng lẽ chăm sóc, càng là đạt được Dị Linh thứ nhất Hoàng giả tự tay bồi dưỡng, muốn không cường đại cũng khó khăn."),g("br"),g("br"),n._v("“Vì lẽ đó, ta từ thế hệ trẻ tuổi bên trong trổ hết tài năng... Tại vương thành đều xông ra không nhỏ danh khí, về sau Dung Hoàng bệ hạ có chuyện rời đi, Linh Hoàng, Tinh Hoàng trở về, nói hắn đã bị Danh Sư đường chém giết, ta cũng nhận gạt bỏ!”"),g("br"),g("br"),n._v("Lưu Dương đem hiện tại tình hình giải thích một chút."),g("br"),g("br"),n._v("Hắn là Dung Hoàng mang tới, lại tiếp nhận đối phương truyền thừa, nếu không phải là bởi vì rất có danh khí, làm người lại cơ cảnh, khả năng đã sớm giống như những người khác, chết xương cốt đều không thừa xuống."),g("br"),g("br"),n._v("Dù vậy, hiện tại cũng không thế nào dám đi ra ngoài, sợ hơi không cẩn thận, liền rơi vào đối phương cạm bẫy."),g("br"),g("br"),n._v("Đương nhiên, cũng có thể là bởi vì hắn, hiện tại không có thực quyền gì, Linh Hoàng cùng Tinh Hoàng mới không để ý tới, nếu như khống chế quân đội, khẳng định cũng đã sớm dữ nhiều lành ít."),g("br"),g("br"),n._v("PS: Tiến cử một bản cực kì đẹp đẽ sách cho mọi người, cẩn thận tỉ mỉ viết 《 nguyên lai ta không phải người bình thường 》, nhất định nhớ tới đều nhìn bên dưới, đặc sắc không thể bỏ qua ah!"),g("br"),g("br"),n._v("Người đăng: ThấtDạ"),g("br"),g("br"),g("br"),g("br")]),n._v(" "),g("hr"),n._v(" "),g("p",[g("a",{attrs:{href:"./chap-1809"}},[n._v("Chương trước")]),n._v("  |  "),g("a",{attrs:{href:"./chap-1811"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);