(window.webpackJsonp=window.webpackJsonp||[]).push([[917],{1272:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1799-chi-điem-luu-duong"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1799-chi-điem-luu-duong"}},[n._v("#")]),n._v(" Chương 1799: Chỉ điểm Lưu Dương")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("“Linh Hoàng cùng Tinh Hoàng, mất đường lui, được ăn cả ngã về không, tình cảnh của ngươi quả thực rất nguy hiểm, có điều, hiện tại chạy trốn, rất dễ dàng thêm vào phản bội tên tuổi, cũng không còn cách nào trở về...”"),t("br"),t("br"),n._v("Biết hắn thời khắc này tình huống, Trương Huyền suy nghĩ phút chốc, nghĩ không ra biện pháp tốt hơn, đành phải cổ tay khẽ đảo: “Nơi này là tích huyết trùng sinh cấp bậc Cổ Thánh lưu lại tinh huyết, mau chóng luyện hóa, thật có nguy hiểm, cũng tương đương có một cái mạng!”"),t("br"),t("br"),n._v("Nhận lấy huyết dịch, Lưu Dương tràn đầy kích động, không có quá nhiều do dự, há miệng nuốt vào."),t("br"),t("br"),n._v("Huyết dịch lực lượng, lập tức ở trong cơ thể sôi trào lên, Trương Huyền đứng dậy, từng đạo chân khí tràn vào trong đó, sau nửa canh giờ, đã triệt để luyện hóa."),t("br"),t("br"),n._v("Dung hợp huyết dịch, chân khí chậm rãi lưu chuyển, lực lượng trở nên càng thêm cường đại, có điều, khoảng cách có thể đột phá Cổ Thánh, còn cách một đoạn."),t("br"),t("br"),n._v("“Đây là ta phong cấm một bộ phận không gian, ẩn chứa Cổ Thánh lực lượng, có thể giúp ngươi xung kích Cổ Thánh! Đây là một bộ tăng lên tâm cảnh pháp quyết, cũng truyền thụ cho ngươi, tranh thủ mau chóng đột phá cảnh giới này.”"),t("br"),t("br"),n._v("Trương Huyền lần nữa lấy ra một bức tranh."),t("br"),t("br"),n._v("Dị Linh nhất tộc bấp bênh, muốn bình yên vượt qua, tốt nhất có thể đạt tới Cổ Thánh!"),t("br"),t("br"),n._v("Lưu Dương giờ phút này ngụy trang thành Dị Linh tộc nhân, Thần Dung Hoàng đều không có nhìn thấu, đối với hắn mà nói, quả thực là một cái rất lớn cơ duyên, có lẽ có thể ở trên con đường này, càng đi càng xa."),t("br"),t("br"),n._v("Một khi lần này Linh tộc nội chiến bên trong, đạt được đầy đủ địa vị, thậm chí khống chế cái này không thể khống chế chủng tộc, đối nhân tộc tới nói, tuyệt đối so với trăm vạn hùng binh, cũng phải có lợi."),t("br"),t("br"),n._v("“Đa tạ lão sư!”"),t("br"),t("br"),n._v("Lưu Dương cảm động."),t("br"),t("br"),n._v("Tại Dị Linh nhất tộc tuy là chỉ đợi mấy tháng, lại đã trải qua vô số kiếp nạn, sớm đã không còn là trước đó cái kia tùy hứng, tự ngạo thiếu niên."),t("br"),t("br"),n._v("Lão sư tiện tay lấy ra, thoạt nhìn đơn giản, thực tế trân quý mức độ, không thể ước lượng, truyền đến bên ngoài, tuyệt đối đều sẽ dẫn tới vô số người tranh đoạt, Cổ Thánh đều không ngoại lệ."),t("br"),t("br"),n._v("Hắn một cái như vậy bất tài đồ đệ, lão sư không những không trách tội, còn ban tặng trân quý như thế đồ vật... Nhất thời cho rằng xấu hổ không chịu nổi, lời nói đều nói không ra ngoài."),t("br"),t("br"),n._v("“Mỗi người kỳ ngộ đều không giống nhau, không cần hâm mộ người khác, như vậy sẽ chỉ làm tâm cảnh của ngươi, bị hạn chế, khó mà tiến bộ, tầm mắt cũng càng ngày càng nhỏ hẹp, cuối cùng chẳng khác gì so với người thường!”"),t("br"),t("br"),n._v("Biết ý nghĩ của hắn, Trương Huyền giải thích, thanh âm xuyên thấu nội tâm, nhắm thẳng vào linh hồn."),t("br"),t("br"),n._v("Mặc kệ là tu luyện giả vẫn là người bình thường, đều muốn gắng giữ lòng bình thường, hâm mộ, ghen ghét, oán hận... Những này suy nghĩ, sẽ chỉ làm hắn trong lòng khó yên, cuối cùng biến thành cảm xúc khôi lỗi."),t("br"),t("br"),n._v("Lưu Dương, tại rất nhiều đệ tử bên trong, cũng không xuất chúng, lại thêm gây họa, khó tránh khỏi có chút tự ti tâm lý, lúc này mới cắn răng một cái, xoay người rời đi, hy vọng có thể xông ra một cái thế giới thuộc về mình, cho mình nhìn, cũng cho Trịnh Dương, Triệu Nhã đám người nhìn."),t("br"),t("br"),n._v("Loại tâm tình này, đối người rèn luyện có chỗ tốt, có thể khiến người ta cứng cáp hơn không rút, cũng rất dễ dàng để hắn đi vào góc chết, đối đãi sự tình, không tại toàn diện."),t("br"),t("br"),n._v("Trước đó hắn ở bên người, không có chủ yếu đến loại này chi tiết, giờ phút này vừa vặn giải thích một chút, cũng tốt nhắc nhở đối phương, không nên trách đi nhầm vào lạc lối."),t("br"),t("br"),n._v("“Vâng!” Lưu Dương vẻ mặt lăng nhiên."),t("br"),t("br"),n._v("“Muốn trở thành cường giả chân chính, lòng dạ nhất định phải rộng lớn, Khổng sư, sở dĩ vô địch thiên hạ, vạn thế không người có thể vượt qua, không phải hắn thiên phú cao bao nhiêu, mà là tâm hắn hoài thiên hạ, tâm tình muôn dân, hữu giáo vô loại! Thiên hạ không có người có hắn loại này lòng dạ, vì lẽ đó hắn có thể vượt qua cổ kim, trở thành từ xưa đến nay đệ nhất nhân!”"),t("br"),t("br"),n._v("Đầu lâu nâng lên, Trương Huyền trong mắt mang theo ngưỡng mộ."),t("br"),t("br"),n._v("Tự thân từng tiến vào Khổng miếu, mới biết được vị cường giả này vĩ đại cùng đáng sợ."),t("br"),t("br"),n._v("Thực lực của hắn, tuy là có một không hai cổ kim, nhưng hậu nhân nói đến, đều là truyền thụ thiên hạ, mà nhất định thực lực, chỉ bằng vào điểm ấy, cũng có thể thấy được nhân cách mị lực."),t("br"),t("br"),n._v("Truyền thụ một hồi, thấy người học sinh này triệt để rõ ràng, khúc mắc cũng thuận lợi mở ra, Trương Huyền lúc này mới thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Người học sinh này khuyết điểm lớn nhất chính là quá hiếu thắng, bảo quản lấy cùng Trịnh Dương đám người lòng so sánh."),t("br"),t("br"),n._v("Đồng môn sư huynh đệ, hẳn là giúp đỡ lẫn nhau, dường như quá nhiều, rất dễ dàng tâm tính mất cân bằng, cuối cùng hoạ từ trong nhà."),t("br"),t("br"),n._v("“Học sinh rõ ràng!”"),t("br"),n._v("Lưu Dương bừng tỉnh hiểu ra, không tại rầu rĩ chuyện này, ánh mắt lộ ra vẻ áy náy, nghi ngờ nhìn lại: “Lão sư lẻ loi một mình trở lại vương thành, nhưng có sự tình gì?”"),t("br"),t("br"),n._v("“Ta tính toán đi Linh Hoàng cung điện, tìm kiếm long huyết!” Đối với học sinh, Trương Huyền cũng không có gì có thể giấu diếm, nói."),t("br"),t("br"),n._v("“Linh Hoàng cung điện?” Lưu Dương nhíu nhíu mày: “Lão sư, nơi này, ta tuy là không có đi qua, lại biết phòng ngự rất nghiêm, người ngoài căn bản không có cách nào đi vào!”"),t("br"),t("br"),n._v("“Ta biết, có điều, có nhất định phải tiến vào lý do!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("Hắn tới Dị Linh tộc mục đích, cũng không phải là vì giúp Thần Dung Hoàng báo thù, mà là muốn mượn tay của đối phương, liên hệ Linh Thần, tìm kiếm Lạc Nhược Hi."),t("br"),t("br"),n._v("Mà muốn làm được điểm ấy, đầu tiên muốn có thực lực, Long Cốt thần thương, tuyệt đối là tăng lên sức chiến đấu phương pháp nhanh nhất."),t("br"),t("br"),n._v("Nhìn lão sư thái độ, biết nhất định phải đi vào, Lưu Dương đứng dậy, mày nhíu lại thành cục: “Linh Hoàng cung điện, chừng mười ba tầng phòng ngự, liền xem như vu hồn, đều không thể đi vào! Xông vào lời nói, Cổ Thánh cũng chưa chắc có thể thành công, hơn nữa mấu chốt nhất là, long huyết ở nơi nào, cũng không biết, xông vào, một khi bị phát giác mục đích, còn muốn tìm tới liền khó khăn.”"),t("br"),t("br"),n._v("Khẳng định không thể xông vào, không nói trước thực lực không đủ, một khi bị bắt lại, hẳn phải chết không nghi ngờ, lại nói, long huyết chỉ là cái tin đồn, cụ thể ở nơi nào, cũng không biết liền dùng sức mạnh, rất dễ dàng không may xuất hiện."),t("br"),t("br"),n._v("“Khẳng định muốn trước hết nghĩ phương pháp trà trộn vào đi!” Chuyện này, Trương Huyền cũng cân nhắc rất nhiều lần, trong lòng đã sớm có phương pháp: “Chỉ có đi vào đối phương cung điện, mới có thể tìm cơ hội!”"),t("br"),t("br"),n._v("Thiên đạo thư viện mạnh mẽ, cũng cần trước nhìn thấy mới có thể xuất hiện thiếu hụt."),t("br"),t("br"),n._v("Đối Linh Hoàng cung điện, hoàn toàn không biết gì cả, liền xông vào, tuyệt đối là mãng phu gây nên."),t("br"),t("br"),n._v("“Ngươi giúp ta hỏi thăm một chút, gần nhất nhưng có cái gì tốt cơ hội. Tỷ như, hắn bị Thần Dung Hoàng gây thương tích, có thể tìm qua y sư hỗ trợ trị liệu?”"),t("br"),t("br"),n._v("Bị Ngoan Nhân cánh tay đâm thủng, thương tổn tới tu luyện căn bản, mặc dù đánh không nghe không đến vị này Linh Hoàng, thân thể đến cùng như thế nào tin tức, nhưng có thể suy đoán ra, tuyệt đối không có nhanh như vậy khôi phục."),t("br"),t("br"),n._v("Nói cách khác, đối phương vô cùng có khả năng, còn tại trị liệu, liền có khả năng cần y sư trợ giúp."),t("br"),t("br"),n._v("Như vậy liền có cơ hội, lẫn vào trong đó."),t("br"),t("br"),n._v("“Ta đi thăm dò!”"),t("br"),t("br"),n._v("Thấy lão sư, trong lòng đã có so đo, Lưu Dương cũng không nhiều lời, xoay người đi ra ngoài."),t("br"),t("br"),n._v("Tại Dị Linh tộc đợi hơn mấy tháng, lại là Thần Dung Hoàng coi trọng nhất truyền nhân, sớm đã nắm giữ thuộc về mình hệ thống tình báo."),t("br"),t("br"),n._v("Chưa tới một canh giờ, đi trở về."),t("br"),t("br"),n._v("“Lão sư đoán không sai, Linh Hoàng cung điện, quả thực tại nhận người, chỉ bất quá, cũng không phải là y sư...”"),t("br"),t("br"),n._v("Mang trên mặt vẻ kỳ quái, dường như được tin tức, Lưu Dương chính mình cũng có chút không dám tin tưởng."),t("br"),t("br"),n._v("“Không phải y sư? Đó là cái gì?”"),t("br"),t("br"),n._v("Trương Huyền cau mày."),t("br"),t("br"),n._v("Đã thân thể bị trọng thương, khẳng định cần y sư trị liệu, người bình thường như muốn chữa khỏi, hẳn là không dễ dàng như vậy, đoán chừng cần hội chẩn... Vì lẽ đó, hắn mới phỏng đoán, có hay không tìm tìm y sư tính toán."),t("br"),t("br"),n._v("Nếu như mà có, có thể nhân cơ hội lẫn vào trong đó."),t("br"),t("br"),n._v("“Là...” Lưu Dương dừng lại một chút, nói: “Bọn họ tại chiêu giám bảo sư!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1810"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1812"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);