(window.webpackJsonp=window.webpackJsonp||[]).push([[918],{1273:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1800-hung-hang-giam-bao-su-【-canh-thu-nhat-】"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1800-hung-hang-giam-bao-su-【-canh-thu-nhat-】"}},[n._v("#")]),n._v(" Chương 1800: Hung hăng giám bảo sư 【 canh thứ nhất 】")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1800: Hung hăng giám bảo sư "),t("br"),t("br"),n._v("“Giám bảo sư?”"),t("br"),t("br"),n._v("Trương Huyền lông mày nhảy dựng."),t("br"),t("br"),n._v("Chiêu y sư, độc sư, thậm chí một ít nắm giữ năng lực đặc thù người, cũng có thể lý giải, giám bảo sư... Có thể làm gì?"),t("br"),t("br"),n._v("Danh Sư đường quyết định thượng, trung, hạ cửu lưu chức nghiệp, tại Dị Linh nhất tộc, mặc dù không được hệ thống, cũng không có đối ứng công hội, nhưng cũng có lưu lại truyền thừa, không ít người nắm giữ loại năng lực này."),t("br"),t("br"),n._v("Chỉ bất quá, kém xa danh sư đại lục như vậy tinh tế thôi."),t("br"),t("br"),n._v("Giám bảo sư thuộc về hạ cửu lưu bên trong bình thường chức nghiệp, đối trị liệu thương thế không có bất kỳ cái gì trợ giúp, chiêu loại người này làm gì?"),t("br"),t("br"),n._v("Có thể hay không sai lầm?"),t("br"),t("br"),n._v("“Vâng, ta cũng không tin, chuyên môn phái người hỏi thăm nhiều lần, mới xác định được! Linh Hoàng sau khi trở về, thâm cư không ra ngoài, nhưng một mực không có chiêu y sư, mà đều là giám bảo sư!”"),t("br"),t("br"),n._v("Lưu Dương nói."),t("br"),t("br"),n._v("“Bị trọng thương, không tìm y sư trị liệu, lại tìm giám bảo sư... Chẳng lẽ, tại cho mình thương thế làm giá cả ước định?”"),t("br"),t("br"),n._v("Sự tình ra khác thường liền là yêu."),t("br"),t("br"),n._v("Việc này, chính tai nghe nói, đều cho rằng khó tin."),t("br"),t("br"),n._v("Trong đầu nhất thời toát ra mấy cái giám bảo sư, đứng tại một bên, nhìn lấy nằm ở trên giường Thần Linh Hoàng."),t("br"),t("br"),n._v("“Cái này thương tốt ah, ít nhất giá trị tám trăm viên tuyệt phẩm linh thạch!”"),t("br"),t("br"),n._v("“Không chỉ, ngươi nhìn thương thế kia, là mấy vạn năm trước Cổ Thánh lưu lại, tạo dáng kỳ lạ, vết thương mang theo đặc thù hoa văn, không có một vạn tuyệt phẩm linh thạch, tuyệt không thể bán ra...”"),t("br"),t("br"),n._v("“Một vạn liền muốn mua sắm? Nằm mơ a, bình thường Cổ Thánh thương đều so cái giá tiền này cao, huống chi cái này...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Làm sao càng nghĩ càng thấy đến hoang đường?"),t("br"),t("br"),n._v("“Mặc kệ mục đích của hắn như thế nào, đều muốn đi qua nhìn một chút...”"),t("br"),t("br"),n._v("Lắc đầu, đem trong đầu lung ta lung tung ý nghĩ dứt bỏ, Trương Huyền nói."),t("br"),t("br"),n._v("“Lão sư có tính toán gì?”"),t("br"),t("br"),n._v("Lưu Dương hỏi."),t("br"),t("br"),n._v("“Đã chiêu giám bảo sư, những này giám bảo sư, khẳng định sẽ khảo hạch địa phương a? Mang ta đi nhìn một chút!”"),t("br"),t("br"),n._v("Dung Hoàng bị giết, Linh Hoàng đã là Dị Linh tộc đệ nhất Hoàng giả, hắn cung điện muốn tuyển nhận giám bảo sư, chỉ cần thả ra tin tức, nhất định có thể dẫn tới vô số người đến đây."),t("br"),t("br"),n._v("Người tới, cũng không có khả năng toàn bộ đều thông qua khảo hạch, đã như vậy, vừa vặn đi qua nhìn một chút, thuận tiện kiếm cái thân phận."),t("br"),t("br"),n._v("“Quả thực có khảo hạch địa phương, ta có thể mang lão sư đi qua...” Lưu Dương gật đầu."),t("br"),t("br"),n._v("“Không cần, thân phận của ngươi bây giờ mẫn cảm, tụ tập không ít ánh mắt, ta một người đi, ngược lại càng thêm an toàn!” Trương Huyền xua tay."),t("br"),t("br"),n._v("“Cũng tốt!” Lưu Dương không có kiên trì."),t("br"),t("br"),n._v("Thân là Thần Dung Hoàng coi trọng nhất truyền nhân, sớm đã bị Linh Hoàng cùng Tinh Hoàng người nhìn kỹ, hiện tại nếu là mang một người ra ngoài, quả thực phiền phức không ít."),t("br"),t("br"),n._v("Rời đi tĩnh thất, Trương Huyền hướng Lưu Dương nói tới phương hướng đi đến."),t("br"),t("br"),n._v("Là cái bán ra bảo vật thương hội, muốn đi vào Linh Hoàng cung điện, đầu tiên muốn có thực học, mà ở trong đó có vô số bảo vật, vừa vặn là khảo hạch tốt nhất vị trí."),t("br"),t("br"),n._v("Ngụy trang thành một cái bình thường Dị Linh tộc nhân, Trương Huyền đi theo đám người, tiến vào bên trong."),t("br"),t("br"),n._v("Thương hội mười phần náo nhiệt, tuy là cùng nhân loại so, kém không ít, bảo vật không chút nào không kém, đủ loại trân quý dược liệu, Thánh thú khung xương, liền xem như hắn, không ít đều không gọi được tên."),t("br"),t("br"),n._v("“Khó trách khả năng hấp dẫn không ít người đến đây mạo hiểm, những dược liệu này, dược hiệu quả thực muốn vượt qua danh sư đại lục...”"),t("br"),t("br"),n._v("Vừa đi, Trương Huyền một bên gật đầu."),t("br"),t("br"),n._v("Ngô Khoáng đám người mạo hiểm đi tới Dị Linh tộc, tự nhiên không phải là vì chơi."),t("br"),n._v("Nơi này dược liệu, tuy là số lượng so danh sư đại lục thưa thớt, nhưng chịu đựng Huyết Nguyệt cùng sát lục chi khí xâm nhập, dược lực càng thêm hùng hậu."),t("br"),t("br"),n._v("Liền cùng trong sa mạc sinh trưởng cây cối đồng dạng, sinh mệnh lực mạnh hơn."),t("br"),t("br"),n._v("Bất quá, những vật này đối với những khác người mà nói, mười phần trân quý, đối với hắn mà nói, không tính là cái gì, nhìn một hồi, liền dọc theo Lưu Dương nói tới phương hướng, tiếp tục hướng phía trước."),t("br"),t("br"),n._v("“Ta nói cái này đồ vật là phế phẩm, vậy chính là phế phẩm, làm sao, dám nghi ngờ ánh mắt của ta?”"),t("br"),t("br"),n._v("Còn chưa đi xa, liền nghe một cái không vui thanh âm vang lên, quay đầu nhìn lại, ngay sau đó nhìn thấy một cái lão giả hai tay chắp sau lưng, đứng tại một cái trước gian hàng, ánh mắt lạnh lùng."),t("br"),t("br"),n._v("“Đại nhân, cái này gốc thanh diệp hoàng, thật sự là ta cửu tử nhất sinh mới đến, còn bởi vậy làm mất đi một bàn tay... Tuyệt đối không có nửa điểm nói ngoa!”"),t("br"),t("br"),n._v("Một người trung niên tràn đầy gấp gáp."),t("br"),t("br"),n._v("Thuận tiếng nhìn lại, quả nhiên thấy tay trái của người nọ sớm đã đứt rời, thương thế đến bây giờ, đều không có tốt đẹp."),t("br"),t("br"),n._v("“Chỉ cần là tiểu thương, đều sẽ như thế nói! Bản thân cỡ nào cỡ nào thê thảm, sau đó khuyếch đại một cái lai lịch, những thứ này... Cũng không cần ở trước mặt ta múa rìu qua mắt thợ.”"),t("br"),t("br"),n._v("Lão giả trên mặt biểu lộ không có biến hóa chút nào, lông mày nâng lên, vung một cái ống tay áo: “Thanh diệp hoàng cùng đoạn ngôn thảo, cực kỳ giống nhau, giá cả lại tăng không biết gấp bao nhiêu lần, ngươi cái này gốc rõ ràng chính là đoạn ngôn thảo, còn muốn giả mạo... Có tin ta hay không hiện tại liền lên báo, lấy bán thuốc giả tội danh, đưa ngươi bắt đi!”"),t("br"),t("br"),n._v("“Ta thật không có bán thuốc giả...”"),t("br"),t("br"),n._v("Đứt bàn tay người tàn tật, nghe được muốn bắt người, sợ tới mức khuôn mặt trắng bệch."),t("br"),t("br"),n._v("“Tốt, niệm tình ngươi cũng không dễ dàng, cái này thuốc giả, tuy là không đáng giá, ta vẫn là thiện tâm, mua, hai cái đại dung tệ!”"),t("br"),t("br"),n._v("Nói xong ngón tay búng một cái, hai cái tiền xu lăn xuống tới."),t("br"),t("br"),n._v("Đại dung tệ, là Thần Dung Hoàng phát hành kim tệ, sức mua có hạn, mười cái mới có thể mua sắm một khối hạ phẩm linh thạch, hai cái đại dung tệ, tương đương với tặng không."),t("br"),t("br"),n._v("“Hai cái... Ta cái này thanh diệp hoàng, có thể bán ra chí ít hai mươi vạn...”"),t("br"),t("br"),n._v("Đứt bàn tay người tàn tật một mặt lo lắng."),t("br"),t("br"),n._v("Thật thanh diệp hoàng, có thể đủ bán ra hơn hai mươi đại dung tệ, hiện tại chỉ cho hai cái, chênh lệch thực sự quá lớn."),t("br"),t("br"),n._v("“Ở đâu ra nói nhảm, lão gia nhà chúng ta là thông qua Linh Hoàng thân khảo hạch giám bảo sư, nói ngươi đây là đoạn ngôn thảo, chính là đoạn ngôn thảo, lại nói nhảm, tin hay không xốc của ngươi sạp hàng, cũng không ai dám nói cái gì?”"),t("br"),t("br"),n._v("Gặp hắn còn muốn nói nhảm, sau lưng lão giả một cái người hầu hừ lạnh."),t("br"),t("br"),n._v("Giám bảo sư chức nghiệp, tuy là không tính quá cao cấp, nhưng ở thương hội nhưng là nhất ngôn cửu đỉnh."),t("br"),t("br"),n._v("Nói ngươi đồ vật là thật, giả cũng là thật, nói là giả, thật cũng là giả."),t("br"),t("br"),n._v("Danh sư đại lục, bởi vì có công hội hạn chế, cả đời chỉ có ba lần phạm sai lầm cơ hội, mà ở đây không có hạn chế, đã thành thương hội bên trong, người người kính sợ tồn tại."),t("br"),t("br"),n._v("“Nhìn tới cái tên này, là không muốn dùng tiền, lại muốn cướp đối phương thanh diệp hoàng...”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Tuy là đối Dị Linh tộc dược vật, biết không nhiều, nhưng thân là giám bảo sư, dược liệu linh tính cùng dược lực, vẫn là có thể tuỳ tiện nhìn ra được."),t("br"),t("br"),n._v("Đối phương cướp đoạt bụi dược liệu này, dược hiệu mạnh mẽ, tuyệt đối không chỉ hai cái đại dung tệ."),t("br"),t("br"),n._v("Hiển nhiên, vị này giám bảo sư biết cái này, cố ý dùng đoạn ngôn thảo tới lừa gạt, muốn cứng rắn cướp."),t("br"),t("br"),n._v("Thật ra thì loại này cố ý đem bảo vật, giám định thành giả, lừa gạt người bán, cuối cùng giá rẻ mua sắm, giá cao bán ra sự tình, ở kiếp trước đều nhìn mãi quen mắt, huống chi nơi này."),t("br"),t("br"),n._v("Người xung quanh, cũng đều rõ ràng, lại không người dám nói cái gì."),t("br"),t("br"),n._v("Sợ vị này chạy tới, cũng tuỳ tiện giám định một lần... Bọn họ đồ vật liền lại không bán ra được."),t("br"),t("br"),n._v("“Đã như vậy, vừa vặn ta thiếu cái thân phận, liền ngươi...”"),t("br"),t("br"),n._v("Vốn còn muốn tiếp tục hướng phía trước đi xem một chút, Linh Hoàng khảo hạch giám bảo sư địa phương, có thể hay không tìm cơ hội, giờ phút này nhìn thấy cái tên này, Trương Huyền mỉm cười, lại không tiến lên."),t("br"),t("br"),n._v("Dù sao tìm ai đều là tìm, cái tên này đã như vậy quang minh chính đại bắt nạt người, cũng liền không có cái gì gánh nặng trong lòng."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1811"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1813"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);