(window.webpackJsonp=window.webpackJsonp||[]).push([[924],{1279:function(n,h,t){"use strict";t.r(h);var g=t(44),c=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1806-ngoan-nhan-ra-tay-【-canh-4-】"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1806-ngoan-nhan-ra-tay-【-canh-4-】"}},[n._v("#")]),n._v(" Chương 1806: Ngoan Nhân ra tay 【 Canh [4] 】")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1806: Ngoan Nhân ra tay "),t("br"),t("br"),n._v("Rất nhanh, tư liệu được đưa đến trước mặt."),t("br"),t("br"),n._v("Hết thảy hơn mười trang, bao gồm đối phương cuộc đời giám bảo sự tích."),t("br"),t("br"),n._v("Khép lại tư liệu, Mặc Linh Cổ Thánh ánh mắt híp lại."),t("br"),t("br"),n._v("Thông qua những này, đó có thể thấy được, vị này Ổ Thao, mặc dù là cái không kém giám bảo sư, nhưng giám bảo cấp bậc, tuyệt đối sẽ không cao siêu như vậy, một lần có thể nói là vận khí, liên tục vài chục lần, đều chuẩn xác không sai, cũng không phải là vận khí có thể giải thích."),t("br"),t("br"),n._v("“Nếu không... Đem cái tên này bắt tới thẩm vấn?”"),t("br"),t("br"),n._v("Đem nội dung nhìn một lần, trung niên hộ vệ cũng phát giác được có chút không đúng."),t("br"),t("br"),n._v("“Không cần, ta tự mình đi qua!”"),t("br"),t("br"),n._v("Mặc Linh Cổ Thánh mắt sáng lên."),t("br"),t("br"),n._v("Hắn nắm giữ hoàn chỉnh nhất vu hồn truyền thừa, loại nghề nghiệp này, đổi lại trước kia, tuyệt đối cho rằng, hắn gọi thứ hai, tuyệt đối không ai dám nói số một!"),t("br"),t("br"),n._v("Nhưng hôm nay loại này quan điểm triệt để đánh vỡ."),t("br"),t("br"),n._v("Một đầu chỉ có Bất Hủ cảnh vu hồn, ngay trước hắn mặt ẩn núp, suýt chút nữa không có phát hiện không nói, giao thủ mấy chiêu, lại còn để hắn thuận lợi chạy trốn... Coi như hắn tại Bất Hủ cảnh lúc, cũng khẳng định không làm được điểm ấy."),t("br"),t("br"),n._v("Tính như vậy, cùng cấp bậc vu hồn, chẳng lẽ so với chính mình còn cường đại hơn?"),t("br"),t("br"),n._v("Tràn đầy tò mò."),t("br"),t("br"),n._v("Đi tới mọi người giám bảo gian phòng, Mặc Linh Cổ Thánh con mắt lập tức tỏa tại vừa rồi vị kia Ổ Thao giám bảo sư trên người."),t("br"),t("br"),n._v("Vị này giám bảo sư, thoạt nhìn không có bất kỳ đặc thù, một tay cầm dược liệu, cau mày, mà đổi thành bên ngoài một tay, không ngừng tra duyệt tư liệu."),t("br"),t("br"),n._v("Nhìn cử chỉ, động tác, tuyệt đối là thực sự giám bảo sư, không có bất kỳ cái gì sai lầm."),t("br"),t("br"),n._v("“Trước không vội giám định thứ này, ta vừa vặn có kiện bảo vật, không cách nào xác định đẳng cấp, không bằng giúp ta giám định một cái!”"),t("br"),t("br"),n._v("Mấy bước đi tới trước mặt, mang trên mặt nụ cười thản nhiên."),t("br"),t("br"),n._v("Vị này vừa tiến vào đại sảnh, Trương Huyền liền chú ý tới, khóe miệng kìm lòng không được giật giật."),t("br"),t("br"),n._v("Hắn ngụy trang thiên y vô phùng, vì sao đối phương một mực đem lực chú ý tập trung ở trên người hắn!"),t("br"),t("br"),n._v("Chẳng lẽ nơi nào xuất hiện chỗ sơ suất?"),t("br"),t("br"),n._v("Trong lòng nghi ngờ, trên mặt nhưng không có mảy may biến hóa, khẽ gật đầu một cái: “Tốt!”"),t("br"),t("br"),n._v("Mặc Linh Cổ Thánh lật bàn tay một cái, một cái cùng cây thước đồng dạng đồ vật, xuất hiện tại lòng bàn tay."),t("br"),t("br"),n._v("Toàn thân vàng rực, vừa nhìn liền biết là kiện không kém pháp bảo, nhưng cụ thể cấp bậc, không có sử dụng, nhìn không ra."),t("br"),t("br"),n._v("Trương Huyền nhận lấy, ngón tay một chút, một quyển sách xuất hiện tại trong óc."),t("br"),t("br"),n._v("“Phá hồn thước, Cổ Thánh chí bảo, có thể khóa chặt linh hồn, để cho người ta khó mà chạy trốn...”"),t("br"),t("br"),n._v("“Lại là kiện Cổ Thánh chí bảo? Bất quá... Làm sao ít như vậy?”"),t("br"),t("br"),n._v("Trong lòng sững sờ."),t("br"),t("br"),n._v("Cổ Thánh chí bảo, cùng Cổ Thánh đồng dạng, ẩn chứa thiên địa pháp tắc, nhận Thiên đạo áp bức, trọng lượng cực lớn, cho dù thực lực bây giờ, muốn giơ lên, đều cần tiêu phí không ít công phu."),t("br"),t("br"),n._v("Đối phương cái này cây thước, rõ ràng là loại cấp bậc này bảo vật, cũng rất nhẹ, cùng bình thường Đại Thánh chí bảo giống nhau, để hắn nhịn không được có chút kỳ quái."),t("br"),t("br"),n._v("“Là hắn hồn lực...”"),t("br"),t("br"),n._v("Nhìn kỹ liếc mắt, trong lòng run lên."),t("br"),t("br"),n._v("Cũng không phải là cái này phá hồn thước nhẹ, mà là đối phương lặng lẽ dùng hồn lực đem hắn nhờ giơ lên, lúc này mới lộ ra không có trọng lượng."),t("br"),t("br"),n._v("Để cho mình giám định, lại cố ý đùa nghịch thủ đoạn, điều tra không rõ bảo vật chân chính thuộc tính, chẳng lẽ... Vị này đã phát giác cái gì?"),t("br"),t("br"),n._v("Không hổ là Cổ Thánh, quả thực không được lừa gạt."),t("br"),t("br"),n._v("Trong đầu nhanh chóng vận chuyển, muốn cân nhắc ra đào tẩu phương pháp, kết quả lại phát hiện, đối phương cách mình gần như vậy, trừ phi vận dụng Yêu Dị huyền đao cùng Ngoan Nhân, triệt để xích mích, nếu không, tuyệt đối không thể."),t("br"),t("br"),n._v("“Thế nào?”"),t("br"),n._v("Gặp hắn quan sát tỉ mỉ phá hồn thước, Mặc Linh Cổ Thánh mỉm cười, nhìn lại."),t("br"),t("br"),n._v("“Cái này cây thước, ta không nhìn ra được, nhìn chất liệu cùng trong đó toả ra lực lượng, vượt xa quá Đại Thánh chí bảo, nhưng lại không có Cổ Thánh chí bảo nặng như vậy...”"),t("br"),t("br"),n._v("Trương Huyền giải thích, lời còn chưa dứt, liền nghe đối diện Cổ Thánh thanh âm vang lên: “Nói như vậy lên, Ổ Thao giám bảo sư, gặp qua Cổ Thánh chí bảo?”"),t("br"),t("br"),n._v("Trong lòng “Lộp bộp!” Một cái, Trương Huyền biểu lộ không thay đổi: “Cũng chưa gặp qua, lại nhìn qua liên quan thư tịch, nghe nói Cổ Thánh chí bảo, trọng lượng cực lớn, một cái lớn nhỏ như vậy vũ khí, có thể so với một tòa vạn trượng núi lớn...”"),t("br"),t("br"),n._v("Lời còn chưa dứt, cười lạnh vang lên, ngay sau đó không gian bốn phía một trận vặn vẹo, Trương Huyền giống như là trong nháy mắt bị kéo xuống một thế giới khác."),t("br"),t("br"),n._v("“Ha ha! Không cần ngụy trang, ngươi căn bản cũng không phải là Ổ Thao giám bảo sư!”"),t("br"),t("br"),n._v("“Tiền bối...”"),t("br"),t("br"),n._v("Lui về sau hai bước, Trương Huyền tràn đầy bối rối."),t("br"),t("br"),n._v("“Dị Linh tộc giám bảo sư, truyền thừa vốn cũng không toàn bộ, dưới tình huống bình thường, Đại Thánh chí bảo đều giám định không ra, chớ nói chi là Cổ Thánh! Ngươi có thể đem sở hữu bảo vật, đều giám định không kém chút nào, lại có thể liếc mắt nhìn ra phá hồn thước cấp bậc không thấp... Ổ Thao, nếu quả thật có loại này bản lĩnh, khẳng định đã sớm danh khắp thiên hạ...”"),t("br"),t("br"),n._v("Mặc Linh Cổ Thánh hai tay chắp sau lưng, mang theo bình tĩnh tự tin."),t("br"),t("br"),n._v("Nếu như nói trước đó còn hơi nghi ngờ, giờ phút này có thể khẳng định, trước mắt vị này, chính là từ trong tay hắn chạy trốn vị kia vu hồn sư."),t("br"),t("br"),n._v("Cũng không phải hắn giám định không ra phá hồn thước, mà là... Đối phương thực sự quá nặng lấy."),t("br"),t("br"),n._v("Tuy là trong ánh mắt mang theo kinh hoàng cùng bối rối, nhưng tâm cảnh một điểm ba động đều không có, lại nhìn không ra cái gì, cũng sẽ không cần làm Cổ Thánh."),t("br"),t("br"),n._v("Phải biết, Cổ Thánh là thiên hạ cao nhất cường giả, chỉ cần là tu luyện giả, đều tôn sùng cùng kính sợ, đối phương không có chút rung động nào, còn có tâm tình để suy đoán cái này cây thước trọng lượng, muốn nói không có vấn đề, hắn đều không tin."),t("br"),t("br"),n._v("“Tiền bối nói, ta một chút cũng nghe không hiểu...”"),t("br"),t("br"),n._v("Trương Huyền lần nữa lui về phía sau, trong mắt mang theo bối rối, nhưng trong lòng kêu gọi Ngoan Nhân."),t("br"),t("br"),n._v("Tuy là không tìm được long huyết, nhưng cũng hết cách rồi, chỉ có thể đi đầu trốn."),t("br"),t("br"),n._v("“Ở lại đây đi!”"),t("br"),t("br"),n._v("Không để ý tới hắn “Bối rối”, Mặc Linh Cổ Thánh lăng không một trảo, toàn bộ không gian, lập tức trở nên trời u ám, một cỗ to lớn hồn lực, đem bốn phía bao phủ ra, giống như là muốn đem hắn giam cầm."),t("br"),t("br"),n._v("Xem ra, trước đó bị thiệt thòi, giờ phút này vừa ra tay, liền đem vu hồn lực lượng vận dụng đi ra."),t("br"),t("br"),n._v("“Phá!”"),t("br"),t("br"),n._v("Biết đã không có tiếp tục ngụy trang nhất định phải, Trương Huyền bàn tay run lên, Yêu Dị huyền đao lập tức xuất hiện, bỗng nhiên hướng về phía trước bổ tới."),t("br"),t("br"),n._v("Tê lạp!"),t("br"),t("br"),n._v("Hồn lực bị chém ra vết rách, không gian cũng giống là bị xé nứt, đao mang xuất hiện tại Mặc Linh Cổ Thánh trước mặt."),t("br"),t("br"),n._v("“Quả nhiên là gian tế!”"),t("br"),t("br"),n._v("Mặc Linh Cổ Thánh cười lạnh, trong tay phá hồn thước nâng lên."),t("br"),t("br"),n._v("Đinh đinh đinh đinh!"),t("br"),t("br"),n._v("Yêu Dị huyền đao tới đối đầu, Trương Huyền nhất thời cảm thấy gan bàn tay run lên, cái trước “Ông!” Một tiếng hướng về phía sau bay ra ngoài."),t("br"),t("br"),n._v("Đều là Cổ Thánh chí bảo, huyền đao cấp bậc rõ ràng so phá hồn thước phải yếu hơn một đoạn dài."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Huyền đao bị đánh bay, phá hồn thước lần nữa nghiền ép xuống, Trương Huyền lập tức cảm thấy linh hồn giống như là bị phong tỏa, bất cứ lúc nào cũng sẽ sụp đổ."),t("br"),t("br"),n._v("Phá hồn thước, liền Cổ Thánh cấp bậc linh hồn của cường giả đều có thể nghiền ép, huống chi hắn loại thực lực này."),t("br"),t("br"),n._v("“Ra tay đi!”"),t("br"),t("br"),n._v("Biết lại không phóng thích át chủ bài, làm không cẩn thận sẽ chết ở đây, Trương Huyền tinh thần khẽ động, một quyển sách xuất hiện trên không trung, ngay sau đó một cái bàn tay, từ bên trong chui ra, thẳng tắp bắt tới."),t("br"),t("br"),n._v("Hủy diệt thiên địa phá hồn thước, nơi tay chưởng bao phủ xuống, trong nháy mắt biến thành ôn nhuận cừu non, lại không còn lực lượng, nhảy lên hai cái, liền bị bóp tại đầu ngón tay."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1817"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1819"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);