(window.webpackJsonp=window.webpackJsonp||[]).push([[927],{1280:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1808-lam-nhai-co-thanh-【-canh-thu-hai-】"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1808-lam-nhai-co-thanh-【-canh-thu-hai-】"}},[n._v("#")]),n._v(" Chương 1808: Lam Nhai Cổ Thánh 【 canh thứ hai 】")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1808: Lam Nhai Cổ Thánh "),t("br"),t("br"),n._v("“Ta muốn đi vào Tẩm điện, hơn nữa tìm tới long huyết, ngươi nhưng có phương pháp gì?”"),t("br"),t("br"),n._v("Trương Huyền tiếp tục hỏi."),t("br"),t("br"),n._v("“Cái này không quá dễ dàng, Linh Hoàng bị thương về sau, Tẩm điện, liền xem như ta, đều quá thuận tiện đi vào! Hiện tại chỉ có Lam Nhai Cổ Thánh có thể tự do đi tới đi lui, Linh Hoàng tình huống cụ thể, khả năng cũng chỉ có hắn mới rõ ràng!”"),t("br"),t("br"),n._v("Dừng lại một chút, Mặc Linh Cổ Thánh tiếp tục nói: “Vị này Cổ Thánh, từ nhỏ liền theo Linh Hoàng, là hắn trung thành nhất thuộc hạ, mặc dù chỉ là một tầng huyết mạch kéo dài thực lực, nhưng ở Linh Hoàng nhất mạch, lại có được, địa vị cực cao!”"),t("br"),t("br"),n._v("Trương Huyền lông mày giương lên: “Có thể hay không nghĩ biện pháp đem hắn dẫn ra... Giết!”"),t("br"),t("br"),n._v("Đã vị này có thể đi vào Tẩm điện, không bằng cùng đối phó “Ổ Thao” đồng dạng, giết, ngụy trang thành bộ dáng của đối phương, lẫn vào trong đó."),t("br"),t("br"),n._v("Dị Linh nhất tộc, lại là chiến đấu phần tử Thần Linh Hoàng thuộc hạ, giết đến nhiều hơn nữa, đều không có mảy may cảm giác áy náy ah!"),t("br"),t("br"),n._v("“Giết?”"),t("br"),t("br"),n._v("Không nghĩ tới người hội trưởng này tàn nhẫn như vậy, Mặc Linh Cổ Thánh khóe miệng co quắp một chút, lộ ra vẻ lo lắng: “Giết hắn không khó, chỉ khi nào bị Linh Hoàng phát giác, chúng ta khả năng đều sẽ rơi vào đuổi giết, khó mà chạy trốn...”"),t("br"),t("br"),n._v("Lấy Linh Hoàng đối Lam Nhai Cổ Thánh coi trọng mức độ, thật muốn chết rồi, còn không đem toàn bộ vương thành đều lật qua?"),t("br"),t("br"),n._v("Trương Huyền chần chờ một chút, nói: “Chỉ cần sớm chuẩn bị tốt, không có người có thể phát hiện!”"),t("br"),t("br"),n._v("Tìm long huyết, chẳng khác nào tại trên người đối phương quấn một đao, rận quá nhiều không ngứa, cũng không có gì có thể quan tâm."),t("br"),t("br"),n._v("“Tốt, ta hiện tại liền an bài!”"),t("br"),t("br"),n._v("Gật gật đầu, Mặc Linh Cổ Thánh hồn thể vụt qua, trở lại thân thể."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Trương Huyền cũng đem Ngoan Nhân cùng Yêu Dị huyền đao thu về, mới vừa làm xong những này, trước mắt không gian một trận lay động, ngay sau đó lần nữa nhìn thấy vô số giám bảo sư, cùng rất nhiều hộ vệ xuất hiện ở trước mắt."),t("br"),t("br"),n._v("Vừa rồi hai người chiến đấu, đều bị phong tỏa tại một cái đặc thù không gian gấp phía trong, ngoại giới người, cũng không biết, xảy ra chuyện gì."),t("br"),t("br"),n._v("“Người tới!”"),t("br"),t("br"),n._v("Trở lại đại điện, Mặc Linh Cổ Thánh hừ một tiếng."),t("br"),t("br"),n._v("Vừa rồi trung niên hộ vệ tiến về phía trước một bước."),t("br"),t("br"),n._v("“Vị này Ổ Thao, ta điều tra, thân thế minh bạch, cũng không phải là muốn tìm vị kia, các ngươi tiếp tục kiểm tra, một cái cũng không thể bỏ qua!” Mặc Linh Cổ Thánh khẽ nói."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Trung niên hộ vệ liền vội vàng gật đầu."),t("br"),t("br"),n._v("Tuy là vị này Ổ Thao có chút khả nghi, nhưng đã Cổ Thánh đều không tra được, hẳn không có vấn đề."),t("br"),t("br"),n._v("“Ổ Thao giám bảo sư, ta chỗ ở vừa vặn có mấy món bảo vật, đến bây giờ không biết là cái gì, ngươi nếu có không, không ngại giúp ta chưởng chưởng nhãn!” Giao phó xong, Mặc Linh Cổ Thánh nhìn về phía một bên Trương Huyền."),t("br"),t("br"),n._v("“Có thể vì tiền bối cống hiến sức lực, là vinh hạnh của ta!”"),t("br"),t("br"),n._v("Trương Huyền vội vàng khom người."),t("br"),t("br"),n._v("“Làm phiền...” Mặc Linh Cổ Thánh cười cười, lần nữa nhìn về phía trung niên hộ vệ: “Ta hiện tại mang đi một người, sẽ không cho ngươi tạo thành phiền phức đi!”"),t("br"),t("br"),n._v("“Đương nhiên sẽ không...”"),t("br"),t("br"),n._v("Trung niên hộ vệ liền vội vàng gật đầu."),t("br"),t("br"),n._v("https://t"),t("br"),n._v("rｕyencuatui.net Một cái giám bảo sư mà thôi, hắn bên này còn có hơn hai trăm, đối toàn thể tiến độ, không có quá lớn ảnh hưởng."),t("br"),t("br"),n._v("“Vậy thì tốt, Ổ Thao giám bảo sư hiện tại cùng ta đi qua, các ngươi tiếp tục kiểm tra, tuyệt đối không được chậm trễ Linh Hoàng sự tình!”"),t("br"),t("br"),n._v("Phân phó xong, Mặc Linh Cổ Thánh bàn tay lớn vồ một cái, Trương Huyền ngay sau đó cảm thấy thân thể xiết chặt, lập tức hướng lên bầu trời bay đi, sau một khắc, đã xuất hiện tại một cái cung điện to lớn phía trước."),t("br"),t("br"),n._v("“Đây là Linh Hoàng cung Cam Tuyền điện, trời sinh suối nguồn, linh tính mười phần, mỗi ngày, Lam Nhai Cổ Thánh cũng sẽ ở cố định thời gian, tới lấy nước, chắc hẳn cùng Thần Linh Hoàng liên quan đến!”"),t("br"),t("br"),n._v("Mặc Linh Cổ Thánh nói."),t("br"),t("br"),n._v("Đẩy cửa tiến vào cung điện, Trương Huyền quả nhiên thấy trong đại điện ở giữa, một cái suối nguồn, “Cục cục, cục cục” hiện ra bọt nước."),t("br"),t("br"),n._v("Nước suối trong suốt trong suốt, mang theo linh khí nồng nặc, nhấm nháp một ngụm, mang theo ngọt."),t("br"),t("br"),n._v("“Nước này không sai!”"),t("br"),t("br"),n._v("Trương Huyền gật gật đầu."),t("br"),t("br"),n._v("Cái này nước suối, tuy là không bằng linh dịch, lại thiên địa tạo thành, sạch sẽ không tạp chất, luyện chế đan dược các loại, có thể có được cao hơn xác suất thành công."),t("br"),t("br"),n._v("“Hội trưởng, ngay ở chỗ này động thủ ư? Ta sợ khoảng cách Tẩm điện quá gần, quấy nhiễu những người khác!” Mặc Linh Cổ Thánh lo lắng."),t("br"),t("br"),n._v("Cổ Thánh cảm giác lực mười phần đáng sợ, vị này Lam Nhai Cổ Thánh cứ việc tu vi không cao, toàn lực ra tay, có thể nhất kích tất sát, có thể... Một khi có tin tức tiết lộ, hắn che giấu nhiều năm như vậy, đều sẽ công lao đổ biển."),t("br"),n._v("Vừa rồi đem Trương Huyền kéo vào không gian gấp, tuy là bí ẩn, trước mặt người đều không thể nhận ra cảm giác, nhưng đây cũng là đối thủ không muốn lấy truyền lại tin tức, một khi truyền lại, liền xem như hắn, đều không ngăn cản nổi."),t("br"),t("br"),n._v("“Nơi này khoảng cách Tẩm điện quả thực không xa, ta trước sắp đặt cái trận pháp, như vậy, liền sẽ không có bất kỳ khí tức tiết lộ!”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("“Sắp đặt trận pháp? Hiện tại... Còn kịp?”"),t("br"),t("br"),n._v("Mặc Linh Cổ Thánh cau mày."),t("br"),t("br"),n._v("Bất luận là bày trận vẫn là điêu khắc phù văn, đều là vô cùng tiêu phí công phu sự tình, cần sớm chuẩn bị, quang trận đồ, liền không biết muốn cân nhắc bao lâu... Lại thêm, vây khốn Cổ Thánh trận pháp, thấp nhất đều phải đạt tới cấp chín đỉnh phong... Mạnh nhất cửu tinh trận pháp sư, không có một hai tháng, cũng làm không được."),t("br"),t("br"),n._v("Bây giờ chuẩn bị, có chút quá muộn đi!"),t("br"),t("br"),n._v("“Không muộn... Một chút thời gian liền xong!”"),t("br"),t("br"),n._v("Biết hắn lo lắng cái gì, Trương Huyền cười cười, trong phòng quay một vòng, ngón tay búng một cái, mấy trăm viên trận kỳ, đồng thời xuất hiện trên không trung, yên tĩnh huyền phù."),t("br"),t("br"),n._v("“Đi!”"),t("br"),t("br"),n._v("Bàn tay hướng lên trên vung lên."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Rất nhiều trận kỳ nức nở, hướng bốn phía bay đi, thẳng tắp cắm vào cung điện bốn phía, lắc lư vài cái, biến mất không thấy gì nữa."),t("br"),t("br"),n._v("“Vậy thì xong rồi...”"),t("br"),t("br"),n._v("Mặc Linh Cổ Thánh trợn mắt lên."),t("br"),t("br"),n._v("Tuy là chưa thấy qua cửu tinh trận pháp sư sắp đặt trận pháp, nhưng là nghe qua, một cái nháy mắt đem trận kỳ cắm vào mặt đất, vậy thì hoàn thành?"),t("br"),t("br"),n._v("Có thể hay không quá trò đùa chút?"),t("br"),t("br"),n._v("“Rất lâu không có bày trận, thủ pháp không quen tay chút...”"),t("br"),t("br"),n._v("Trương Huyền một mặt áy náy."),t("br"),t("br"),n._v("Trong khoảng thời gian này, nhìn thư nhiều, đối với trận pháp lý giải càng ngày càng mạnh, nhưng quả thực rất lâu không chút ra tay sắp đặt qua trận pháp, nếu không, căn bản không cần đo đạc, tùy tiện tế ra trận kỳ, liền có thể hoàn thành."),t("br"),t("br"),n._v("“Thủ pháp không quen tay...”"),t("br"),t("br"),n._v("Khóe miệng giật một cái, Mặc Linh Cổ Thánh da mặt nhảy lên."),t("br"),t("br"),n._v("Nhanh như vậy, đều là mới lạ, không sinh sơ thời điểm, nên có bao nhanh?"),t("br"),t("br"),n._v("Ngươi cái này có chút quá khoa trương đi..."),t("br"),t("br"),n._v("Cái này tân nhiệm hội trưởng... Hình như không quá đáng tin cậy ah!"),t("br"),t("br"),n._v("Nhanh như vậy sắp đặt đi ra trận pháp, có phải hay không không có tác dụng gì, trong lòng thật đúng là không chắc."),t("br"),t("br"),n._v("Lộc cộc lộc cộc lộc cộc!"),t("br"),t("br"),n._v("Đang muốn cẩn thận hỏi thăm, ngay sau đó nghe được ngoài điện vang lên tiếng bước chân, dường như có người hướng bên này đi tới."),t("br"),t("br"),n._v("“Lam Nhai Cổ Thánh tới...”"),t("br"),t("br"),n._v("Liếc mắt nhìn thời gian, chính là Lam Nhai Cổ Thánh lấy nước thời điểm, Mặc Linh Cổ Thánh vội vàng truyền âm."),t("br"),t("br"),n._v("“Ngươi lui về phía sau bảy bước, sau đó che đậy trên người toàn bộ khí tức, ta để ngươi động thủ thời điểm, động thủ lần nữa!”"),t("br"),t("br"),n._v("Cũng nghe đến tiếng bước chân, Trương Huyền bàn giao."),t("br"),t("br"),n._v("“Lui về phía sau bảy bước?”"),t("br"),t("br"),n._v("Không biết đối phương trong hồ lô muốn làm cái gì, bất quá đối phương là hội trưởng, tự nhiên muốn nghe theo mệnh lệnh, làm Mặc Linh Cổ Thánh lúc này dựa theo dặn dò, lui về phía sau đến địa điểm chỉ định, toàn thân khí tức thu liễm ở trong người."),t("br"),t("br"),n._v("Kẹt kẹt!"),t("br"),t("br"),n._v("Mới vừa làm xong những này, cửa đại điện hộ từ từ mở ra, một cái cao lớn bóng người, đi vào."),t("br"),t("br"),n._v("Chính là Lam Nhai Cổ Thánh."),t("br"),t("br"),n._v("Vị này Cổ Thánh, đi vào đại điện, liếc mắt nhìn hai phía, trực tiếp hướng nước suối vừa đi đi."),t("br"),t("br"),n._v("Mặc Linh Cổ Thánh đồng tử co rụt lại."),t("br"),t("br"),n._v("Hắn liền đứng tại chỗ động cũng không động, cũng không có che giấu... Đối phương lại giống không thấy hắn đồng dạng?"),t("br"),t("br"),n._v("Cái tên này... Lúc nào mù?"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1819"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1821"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);