(window.webpackJsonp=window.webpackJsonp||[]).push([[928],{1281:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1809-chet-khong-nham-mat-lam-nhai-co-thanh-【-canh-3-】"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1809-chet-khong-nham-mat-lam-nhai-co-thanh-【-canh-3-】"}},[n._v("#")]),n._v(" Chương 1809: Chết không nhắm mắt Lam Nhai Cổ Thánh 【 Canh [3] 】")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1809: Chết không nhắm mắt Lam Nhai Cổ Thánh "),t("br"),t("br"),n._v("Tràn đầy khiếp sợ, vội vàng hướng cách đó không xa hội trưởng nhìn sang, vừa nhìn xuống, càng là ngẩn ngơ,"),t("br"),t("br"),n._v("Hội trưởng khoảng cách Lam Nhai Cổ Thánh không đến ba mét, người sau lại giống không có chút nào phát giác đồng dạng, đồng dạng không hề phát hiện thứ gì."),t("br"),t("br"),n._v("“Là trận pháp...”"),t("br"),t("br"),n._v("Có ngu đi nữa cũng rõ ràng chuyện gì xảy ra."),t("br"),t("br"),n._v("Tu vi của hắn cao, ẩn núp, đối phương không phát hiện được, có thể thông cảm được, nhưng hội trưởng... Khoảng cách gần như vậy, đều không phát hiện được, chỉ có thể nói, trận pháp này thực sự quá mức nghịch thiên."),t("br"),t("br"),n._v("Thần thức lặng lẽ lan ra, muốn nhìn một chút trận pháp, đến cùng như thế nào ẩn nấp thân hình, lại kinh ngạc phát hiện, đảo qua Lam Nhai Cổ Thánh cùng Trương Huyền, không có cảm nhận được nửa cái bóng người, liền giống như đối phương không tồn tại đồng dạng."),t("br"),t("br"),n._v("Rõ ràng mắt thường có thể nhìn thấy, thần thức lại không cách nào phát giác."),t("br"),t("br"),n._v("“Đây là... Không gian trận pháp?”"),t("br"),t("br"),n._v("Đồng tử co rụt lại."),t("br"),t("br"),n._v("Hắn đã sớm lĩnh ngộ Vu Hồn Xuyên Toa, càng có thể đem người kéo vào không gian gấp, nói rõ đối không gian lĩnh ngộ không thấp, có thể trận pháp này hiển nhiên càng cao minh hơn."),t("br"),t("br"),n._v("Liền Cổ Thánh cấp bậc Lam Nhai Cổ Thánh, đều không có phát giác dị thường, thần thức của mình cũng dò xét không đến... Chẳng lẽ, đã lĩnh ngộ trong truyền thuyết không gian chân giải?"),t("br"),t("br"),n._v("Khó trách nói sắp đặt cái trận pháp liền xong, không nghĩ tới lợi hại như vậy."),t("br"),t("br"),n._v("Buồn cười hắn còn nghi ngờ, có phải hay không đang khoác lác."),t("br"),t("br"),n._v("“Động thủ!”"),t("br"),t("br"),n._v("Ngay tại cảm xúc, bên tai lập tức vang lên một thanh âm, thần sắc cứng lại, phá hồn thước xuất hiện tại lòng bàn tay, lăng không vạch một cái, liền hướng trước mắt Lam Nhai Cổ Thánh xông tới."),t("br"),t("br"),n._v("Bất quá, còn chưa tới đến trước mặt, chỉ thấy vị này Cổ Thánh, thân thể vụt qua, “Phù phù!” Một chút ngã trên mặt đất, đã cắt đứt hô hấp."),t("br"),t("br"),n._v("Khóe miệng giật một cái, vội vàng nhìn lại, chỉ thấy hội trưởng cầm trong tay một chuôi yêu tà quỷ dị trường đao, đứng tại chỗ, tràn đầy xấu hổ: “Xấu hổ, không nghĩ tới cái tên này yếu như vậy, không cẩn thận, liền giết...”"),t("br"),t("br"),n._v("Vốn nghĩ, đối phương là Cổ Thánh, muốn lặng yên không một tiếng động chém giết, ít nhất cũng là Ngoan Nhân cùng Mặc Linh Cổ Thánh phối hợp, không nghĩ tới, trường đao cùng một chỗ, liền đâm xuyên qua trái tim..."),t("br"),t("br"),n._v("Chỉ thoáng cái, liền treo... Liền câu lời thoại đều không có, cũng quá thảm rồi đi!"),t("br"),t("br"),n._v("“Lam Nhai Cổ Thánh là Cổ Thánh một tầng huyết mạch kéo dài cấp bậc cao thủ, làm sao có thể dễ dàng như vậy bị giết?”"),t("br"),t("br"),n._v("Không chỉ Trương Huyền kỳ lạ, Mặc Linh Cổ Thánh cũng là ngẩn ngơ."),t("br"),t("br"),n._v("Vị này cùng hắn ở chung không biết bao nhiêu năm, mặc dù không có giao thủ qua, sức chiến đấu mạnh bao nhiêu, lại biết rõ rõ ràng ràng, hội trưởng Bất Hủ cảnh đại viên mãn, phối hợp Yêu Dị huyền đao, có thể tại đây loại cấp bậc cường giả bên dưới chạy trốn, thậm chí đánh một trận, đều nói qua được..."),t("br"),t("br"),n._v("Một đao liền bị vùi dập giữa chợ... Làm sao lại như vậy trò đùa?"),t("br"),t("br"),n._v("Có muốn hay không, đi qua bổ khuyết thêm hai thước?"),t("br"),t("br"),n._v("Như vậy sẽ có hay không có lấy roi đánh thi thể hiềm nghi?"),t("br"),t("br"),n._v("Tràn đầy nghi ngờ, lại không để ý tới ẩn tàng thân hình, vội vàng đi tới trước mặt, tinh thần quét qua, khóe miệng kìm lòng không được run rẩy."),t("br"),t("br"),n._v("Vị này... Quả nhiên đã chết, thần hồn đều diệt, một chút dấu vết đều không có lưu lại."),t("br"),t("br"),n._v("“Kỳ lạ...”"),t("br"),t("br"),n._v("Xoa xoa mi tâm, Mặc Linh Cổ Thánh nghĩ mãi mà không rõ."),t("br"),t("br"),n._v("Đây chính là Cổ Thánh, bất luận danh sư đại lục, vẫn là Dị Linh tộc thế giới, đều đứng tại cao nhất siêu tuyệt nhân vật, một đao đâm chết, một câu nói chưa nói, thậm chí kêu thảm đều không có phát ra tới..."),t("br"),t("br"),n._v("Để cho người ta có loại dường như đã có mấy đời cảm thấy."),t("br"),t("br"),n._v("“Chẳng lẽ là giả?”"),t("br"),t("br"),t("br"),n._v("Trương Huyền cũng nghi ngờ nhìn tới."),t("br"),t("br"),n._v("“Giả ngược lại không giả, ta cùng vị này ở chung được mấy ngàn năm, hóa thành tro, cũng sẽ không nhận sai, sở dĩ bị ngươi một đao đánh giết, hẳn là nhận lấy bị thương nặng, cơ sở bị tổn thương! Một thân thực lực, liền một phần mười cũng chưa tới...”"),t("br"),t("br"),n._v("Nhìn kỹ một hồi, Mặc Linh Cổ Thánh nói."),t("br"),t("br"),n._v("Thông qua quan sát, đó có thể thấy được vị này Lam Nhai Cổ Thánh, hết sức yếu ớt, trong cơ thể mang theo suy bại khí tức."),t("br"),n._v(" Vừa nhìn liền biết tổn thương cơ sở, coi như không động thủ, cũng có thể là sống không bao lâu."),t("br"),t("br"),n._v("Thế nhưng là... Quãng thời gian trước, mới thấy qua đối phương, long tinh hổ mãnh, nghe nói còn nhanh muốn đột phá Cổ Thánh tầng thứ hai... Làm sao một chút liền biến thành như vậy?"),t("br"),t("br"),n._v("“Làm sao bây giờ?”"),t("br"),t("br"),n._v("Nghiên cứu một hồi, không biết đến cùng chuyện gì xảy ra, Mặc Linh Cổ Thánh đành phải bất đắc dĩ lắc đầu, nhìn lại."),t("br"),t("br"),n._v("“Vốn còn muốn bắt cái sống, sau đó sưu hồn, biết Linh Hoàng tình huống, dò xét long huyết vị trí, hiện tại đã chết, chỉ có thể quên đi...”"),t("br"),t("br"),n._v("Trương Huyền cười khổ."),t("br"),t("br"),n._v("Cái khác Cổ Thánh, đều lợi hại như thế, vị này, quá không đáng tin cậy!"),t("br"),t("br"),n._v("Cầm đao chọc đi qua, vốn nghĩ đối phương sẽ có né tránh cùng ngăn cản, nằm mơ đều không có nghĩ đến, trực tiếp treo... Đằng sau chuẩn bị rất nhiều thủ đoạn, cũng không có cách nào thi triển, có loại chọn nhầm đối tượng cảm thấy, suy nghĩ một chút liền tràn đầy tâm tắc."),t("br"),t("br"),n._v("Bất quá, hắn ý nghĩ này nếu như cho chết đi Lam Nhai Cổ Thánh biết, khẳng định sẽ trực tiếp khóc lên... Làm sao, ta chết phương thức không đúng?"),t("br"),t("br"),n._v("Có muốn hay không, đem ta cứu sống, lại chết một lần cho ngươi xem một chút..."),t("br"),t("br"),n._v("“Còn muốn hay không ngụy trang thành hình dạng của hắn?”"),t("br"),t("br"),n._v("“Đương nhiên, nếu không như thế nào đi vào Tẩm điện?” Lắc đầu, Trương Huyền tinh thần khẽ động, toàn thân xương cốt “Cục cục” vang vọng, cả người tính cả linh hồn khí tức, đều trở nên cùng nằm rạp trên mặt đất Lam Nhai Cổ Thánh giống nhau như đúc."),t("br"),t("br"),n._v("Đưa tay đem hắn quần áo cởi ra, mặc ở trên người mình, trữ vật giới chỉ, cũng lấy tới, mang trên ngón tay."),t("br"),t("br"),n._v("Liếc mắt nhìn, Mặc Linh Cổ Thánh chấn động trong lòng."),t("br"),t("br"),n._v("Còn tưởng rằng chỉ là hình dáng bên trên ngụy trang, để cho người ta khó phân biệt thư hùng, nằm mơ đều không có nghĩ đến, cử chỉ phong thái, linh hồn khí tức, thậm chí tâm cảnh lực lượng, cho người cảm thấy, đều hoàn toàn tương đồng, nếu không phải Lam Nhai Cổ Thánh còn nằm trên mặt đất, không nhúc nhích, thật sự cho rằng, căn bản không có chết!"),t("br"),t("br"),n._v("Bình thường ngụy trang, chỉ là dung mạo giống nhau, linh hồn cùng tâm cảnh, không có khả năng tương đồng, mà trước mắt cái này... Người thân nhất đều không thể nhận ra!"),t("br"),t("br"),n._v("Quá nghịch thiên đi!"),t("br"),t("br"),n._v("“Lam Nhai Cổ Thánh, lấy nước suối về sau, bình thường sẽ đưa đến nơi nào?” Không để ý tới hắn khiếp sợ, Trương Huyền hỏi."),t("br"),t("br"),n._v("“Bình thường sẽ đi vào Tẩm điện, còn nơi nào, ta cũng không rõ ràng...” Mặc Linh Cổ Thánh vội vàng trả lời."),t("br"),t("br"),n._v("“Vậy là tốt rồi, vừa vặn đi qua điều tra một phen!”"),t("br"),t("br"),n._v("Trương Huyền ánh mắt sáng lên."),t("br"),t("br"),n._v("Nếu là đi Tẩm điện, vừa vặn có thể quang minh chính đại tiến vào bên trong."),t("br"),t("br"),n._v("Lăng không một trảo, Lam Nhai Cổ Thánh trong giới chỉ, chuẩn bị xong hồ lô xuất hiện, đem nước sạch rót đầy."),t("br"),t("br"),n._v("Làm xong những này, Trương Huyền liếc mắt nhìn Mặc Linh Cổ Thánh, bàn giao nói: “Ta một người đi vào liền xong!”"),t("br"),t("br"),n._v("Đối phương bây giờ bị Linh Hoàng kiêng kị, tự do ra vào Tẩm điện đều có chút khó khăn, còn không bằng chớ cùng ở phía sau, bản thân dễ dàng hơn một ít."),t("br"),t("br"),n._v("Nếu không, vừa mới đi vào, liền chỉ sợ phải gặp đến nghi ngờ, bị người phát hiện."),t("br"),t("br"),n._v("“Quá nguy hiểm, ta sợ xảy ra vấn đề...”"),t("br"),t("br"),n._v("Mặc Linh Cổ Thánh tràn đầy lo lắng."),t("br"),t("br"),n._v("Hội trưởng chỉ là Bất Hủ cảnh đại viên mãn thực lực, Tẩm điện theo hắn biết, ít nhất ba vị Cổ Thánh tồn tại, một khi bị phát giác, trốn đều trốn không thoát!"),t("br"),t("br"),n._v("“Yên tâm, thật muốn xảy ra vấn đề, ngươi xông tới chi viện...”"),t("br"),t("br"),n._v("Mỉm cười, Trương Huyền đem Lam Nhai Cổ Thánh thi thể thu vào trữ vật giới chỉ."),t("br"),t("br"),n._v("Cổ Thánh một thân đều là bảo vật, cũng không thể lãng phí."),t("br"),t("br"),n._v("Dừng lại một chút, biết mình thân phận bây giờ dường như xấu hổ, đi qua quả thực sẽ dẫn tới phiền phức, Mặc Linh Cổ Thánh đành phải gật gật đầu."),t("br"),t("br"),n._v("Không nói đối phương hội trưởng thân phận, chỉ nói danh sư đại lục thiên tài nhất danh sư, thật nếu gặp phải nguy hiểm, liền không thể khoanh tay đứng nhìn."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Làm ra quyết định, Trương Huyền không tại nhiều nói, thân thể vụt qua, đi ra cung điện, nhận ra một chút, thẳng tắp hướng Tẩm điện phương vị đi tới."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1820"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1822"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);