(window.webpackJsonp=window.webpackJsonp||[]).push([[929],{1284:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1810-long-huyet-tim-toi"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1810-long-huyet-tim-toi"}},[n._v("#")]),n._v(" Chương 1810: Long huyết tìm tới")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Trước đó vu hồn tại Linh Hoàng cung dạo qua một vòng, mỗi bên đại cung điện bố cục sớm đã ghi nhớ tại trong óc, thời gian không dài đi tới Tẩm điện."),t("br"),t("br"),n._v("Lấy ra lệnh bài, nhẹ nhàng vạch một cái, phong ấn chậm rãi mở ra, nhấc chân đi vào."),t("br"),t("br"),n._v("Linh Hoàng Tẩm điện cùng bên ngoài không có gì sai biệt, một cái sân rộng rãi, đủ loại cây cối rậm rạp sinh trưởng, vừa mắt là một mảnh dược viên, trồng đầy đủ loại trân quý dược liệu."),t("br"),t("br"),n._v("Thần thức lan ra, lặng lẽ nhìn một vòng, Trương Huyền nhíu nhíu mày."),t("br"),t("br"),n._v("Dươc viên này dược vật, chừng hơn mấy trăm loại, mỗi một cái đều ẩn chứa mạnh mẽ dược lực, nhưng không có trước đó Bặc Huân Cổ Thánh nói tới long lân thảo."),t("br"),t("br"),n._v("Chẳng lẽ, long huyết căn bản lại không tồn tại?"),t("br"),t("br"),n._v("Để ngụy trang thành đai lưng Long Cốt thần thương, cẩn thận cảm ứng, nơi nào có long huyết, Trương Huyền dọc theo con đường, chậm chạp hướng về phía trước."),t("br"),t("br"),n._v("“Lam Nhai, còn không mau một chút!”"),t("br"),t("br"),n._v("Đang từ từ tiến lên, dùng thần thức đối bốn phía dò xét, một thanh âm vang lên, quay đầu nhìn lại, chỉ thấy một cái lão giả vẻ mặt vô cùng nghi hoặc nhìn qua."),t("br"),t("br"),n._v("Hơn sáu mươi tuổi hình dáng, hai mắt thoạt nhìn có chút vẩn đục, khí tức trên thân, thoạt nhìn cũng không mạnh, nhưng vẫn như cũ cho người ta một loại đến từ linh hồn áp bức."),t("br"),t("br"),n._v("“Cổ Thánh nhị trọng, Chư Tử bách gia cảnh giới?”"),t("br"),t("br"),n._v("Trong nháy mắt rõ ràng đối phương thực lực cụ thể."),t("br"),t("br"),n._v("Khổng miếu phía trong, gặp qua hơn mười vị Cổ Thánh, trong giới chỉ càng là trần liệt mấy đầu Dị Linh Cổ Thánh thi thể, đối bọn hắn cảnh giới, sớm đã rõ như lòng bàn tay."),t("br"),t("br"),n._v("Trước mắt vị lão giả này, thoạt nhìn dung mạo không đáng để ý, tu vi vậy mà so Lam Nhai Cổ Thánh càng thêm cường đại."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Suy nghĩ tại trong óc lóe lên một cái rồi biến mất, Trương Huyền liền vội vàng gật đầu, vội vã đi tới."),t("br"),t("br"),n._v("Gặp hắn hành động có chút quái dị, lão giả nhíu nhíu mày, nhìn kỹ liếc mắt, chính là Lam Nhai Cổ Thánh, không có sai, lúc này mới thấp giọng: “Hôm nay là ngày cuối cùng, ngàn vạn không thể phạm sai lầm, nếu không, chúng ta đều muốn đầu người rơi xuống đất!”"),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Mặc dù không biết đối phương nói cái gì ý tứ, nhưng giờ phút này khẳng định không tiện hỏi thăm, lên tiếng, Trương Huyền đi theo đối phương sau lưng, hướng trong điện đi đến."),t("br"),t("br"),n._v("Đại điện không có chiếu sáng dạ minh châu, cũng không có nến các loại, lộ ra dị thường đen thui, vừa tiến vào trong, lập tức giống như là đi vào một thế giới khác, bốn phía khí tức, toàn bộ ngăn cách."),t("br"),t("br"),n._v("“Thật mạnh phong ấn!”"),t("br"),t("br"),n._v("Mặt không đổi sắc, Trương Huyền lặng lẽ nhìn quanh một vòng."),t("br"),t("br"),n._v("Không hổ là Linh Hoàng trong hang ổ sào huyệt, phù lục khắc hoạ đâu đâu cũng có, đem bốn phía hoàn mỹ phong ấn, giống như là cả tòa cung điện, đều hoàn toàn tách biệt với thế gian."),t("br"),t("br"),n._v("Nếu như không đi vào đại môn, Cổ Thánh tam trọng tích huyết trùng sinh cảnh giới cường giả, chỉ sợ đều không thể thăm dò nơi này tình huống thật."),t("br"),t("br"),n._v("Đại điện chính giữa, là cái hình tròn tế đàn, phía trên một đám lửa cháy hừng hực, bốn phía ngồi một vòng người mặc hắc giáp Dị Linh tộc tu luyện giả."),t("br"),t("br"),n._v("Vậy mà đều là Đại Thánh cấp bậc cường giả, chừng trên trăm."),t("br"),t("br"),n._v("Trương Huyền lông mày nhảy dựng."),t("br"),t("br"),n._v("Đại Thánh cấp bậc cường giả, tuy là không bằng Cổ Thánh thưa thớt, nhưng cũng được cho đỉnh phong tồn tại, lúc trước đánh chết mười một vạn Dị Linh tộc nhân, loại này cấp bậc cộng lại cũng chỉ mấy chục, nơi này lại có trên trăm..."),t("br"),t("br"),n._v("Linh Hoàng nội tình thâm hậu như vậy?"),t("br"),t("br"),n._v("“Nước suối đây?” Gặp hắn tại nguyên chỗ ngây người, lão giả nhíu nhíu mày: “Còn không mau một chút bỏ qua?”"),t("br"),t("br"),n._v("“Cho!”"),t("br"),t("br"),n._v("Trương Huyền cổ tay rung lên, đem hồ lô hướng về phía lão giả ném tới."),t("br"),t("br"),n._v("“Cho ta làm cái gì?”"),t("br"),t("br"),n._v("Đưa tay nhận lấy, lão giả ngẩn ngơ."),t("br"),t("br"),n._v("“Ngươi tới thả đi!”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("Không biết thứ này như thế nào sử dụng, một phần vạn làm sai, khẳng định lộ tẩy, còn không bằng làm vung tay chưởng quỹ."),t("br"),t("br"),n._v("“Linh Hoàng bệ hạ, tự thân bàn giao, để ngươi tới làm, ta thả, không phù hợp quy củ!”"),t("br"),t("br"),n._v("Lão giả nhíu nhíu mày."),t("br"),t("br"),n._v("“Bệ hạ sẽ không trách móc...” Trương Huyền gượng cười."),t("br"),t("br"),n._v("Nói đùa, nếu là ta biết làm sao thả, cần phải ngươi nói nhảm?"),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),n._v("Gặp hắn nói như vậy, lão giả tràn đầy kỳ lạ, đang muốn nói cái gì, liền cảm thấy mặt đất một trận run rẩy, đài cao không ngừng lay động."),t("br"),t("br"),n._v("“Không còn kịp rồi...”"),t("br"),t("br"),n._v("Sầm mặt lại, lại không để ý tới cùng đối phương nói nhảm, nhổ hồ lô bên trên nắp bình, nhẹ nhàng run lên."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Trong đó rót đầy nước suối, chảy ra đến, dọc theo trên tế đàn hoa văn, tạo thành một cái đặc thù hoa văn."),t("br"),t("br"),n._v("Hừng hực!"),t("br"),t("br"),n._v("Phía trên hỏa diễm, cùng dòng nước vừa tiếp xúc, lập tức thả ra kinh người nhiệt lượng, thiêu đốt càng thêm kịch liệt."),t("br"),t("br"),n._v("“Nhanh lên một chút bắt đầu!”"),t("br"),t("br"),n._v("Hỏa diễm tăng cường, hoa văn xuất hiện chói mắt biến hóa, lão giả một tiếng hô khẽ."),t("br"),t("br"),n._v("Tế đàn bốn phía Dị Linh tộc Đại Thánh, tất cả đều đồng loạt cắt đứt ngón tay, đem huyết dịch bắn tới."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Cùng huyết dịch vừa tiếp xúc, hỏa diễm thiêu đốt càng thêm hung mãnh, toàn bộ không gian, cũng bắt đầu vặn vẹo."),t("br"),t("br"),n._v("Trương Huyền lúc này mới phát hiện, hỏa diễm chính giữa, một đoàn máu đỏ tươi, đang bị không ngừng nung khô, hình như tại dùng đặc thù nào đó phương pháp tiến hành tinh luyện."),t("br"),t("br"),n._v("Huyết dịch này, đại khái lớn nhỏ cỡ nắm tay, phía trên một cỗ không cam lòng lực lượng, không ngừng gào thét, tựa như cự long vây quanh, không ngừng quanh quẩn một chỗ."),t("br"),t("br"),n._v("“Chủ nhân, đây là long huyết... Bọn họ tại tinh luyện long huyết...”"),t("br"),t("br"),n._v("Trong lòng hơi động, một cái lo lắng truyền âm bên tai bên trong vang lên."),t("br"),t("br"),n._v("Biến thành đai lưng Long Cốt thần thương, kích động không ngừng lay động."),t("br"),t("br"),n._v("“Đây là long huyết?”"),t("br"),t("br"),n._v("Trương Huyền chấn động."),t("br"),t("br"),n._v("Vốn cho rằng còn cần tiêu phí công phu đi tìm, không nghĩ tới tự nhiên chui tới cửa."),t("br"),t("br"),n._v("Chỉ bất quá, nhiều người như vậy vây quanh, lại tại tế đàn ở chính giữa, muốn trộm đi, gần như không có khả năng."),t("br"),t("br"),n._v("“Bọn họ đang làm gì?”"),t("br"),t("br"),n._v("Xác định long huyết, Trương Huyền tràn đầy nghi ngờ."),t("br"),t("br"),n._v("Đặt ở tế đàn bên trên, dùng tu luyện người máu tươi cùng hỏa diễm tiến hành nung khô, đối phương muốn làm cái gì?"),t("br"),t("br"),n._v("“Lượt chúng ta, Lam Nhai, còn không mau một chút...”"),t("br"),t("br"),n._v("Ngay tại kỳ lạ, lão giả thanh âm vang lên lần nữa, vội vàng quay đầu, chỉ thấy đối phương ngón tay búng một cái, đồng dạng một giọt tinh huyết, hướng trước mắt tế đàn bay đi."),t("br"),t("br"),n._v("Trương Huyền biết chậm sẽ bị nghi ngờ, cũng vội vàng lấy ra một giọt máu, bắn đi lên."),t("br"),t("br"),n._v("Xì xì xì!"),t("br"),t("br"),n._v("Tế đàn một thôn phệ máu của hắn, cả người lập tức giống như là bị rút sạch đồng dạng, vẻ mặt trắng bệch, quay đầu nhìn lại, chỉ thấy lão giả cùng cái khác rất nhiều Đại Thánh cường giả, cũng tất cả đều thân thể run rẩy, hao tổn cực lớn."),t("br"),t("br"),n._v("“Không chỉ thôn phệ huyết dịch, còn có thể căn cứ huyết dịch, thôn phệ người tinh, khí, thần?”"),t("br"),t("br"),n._v("Trương Huyền đồng tử co rụt lại."),t("br"),t("br"),n._v("Hiện tại rốt cuộc minh bạch, vì sao vị kia Lam Nhai Cổ Thánh như vậy dễ giết."),t("br"),t("br"),n._v("Hắn chỉ cống hiến một giọt máu, liền cảm thấy tinh thần uể oải, lực lượng giảm bớt không ít, cái tên này, một mực chủ trì cái tế đàn này, không ngừng cống hiến tinh huyết, thân thể chỉ sợ sớm đã suy yếu không ra bộ dáng."),t("br"),t("br"),n._v("Nằm trong loại trạng thái này, lại đụng tới bản thân vị này, bản thân tu vi, có thể so với Cổ Thánh một tầng, lại có một cái Cổ Thánh một tầng thần binh gia hỏa, đánh lén ra tay... Tự nhiên một chiêu liền bị vùi dập giữa chợ, liền phản kháng đều không có làm được."),t("br"),t("br"),n._v("Cũng không thể nói đối phương quá thảm, mà là hắn quá mạnh... Ưu tú, chính là như vậy không che giấu được."),t("br"),t("br"),n._v("Rống rống!"),t("br"),t("br"),n._v("Thôn phệ hắn cùng tinh huyết của ông lão, trong máu hình rồng, càng thêm rõ ràng, tiếng gầm gừ càng lớn, hình như bất cứ lúc nào cũng sẽ phá không mà ra."),t("br"),t("br"),n._v("“Đây là...”"),t("br"),t("br"),n._v("Nhìn thấy một màn trước mắt, Long Cốt thần thương lập tức run rẩy, tràn đầy kích động cùng thanh âm kinh ngạc vang lên: “Đây không phải tại tinh luyện long huyết, mà là tại... Sử dụng phương pháp đặc thù, tẩm bổ long huyết, muốn cho long huyết, tăng lên một cái cấp bậc!”..."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1821"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1823"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);