(window.webpackJsonp=window.webpackJsonp||[]).push([[93],{447:function(n,h,i){"use strict";i.r(h);var t=i(44),g=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-98-ta-muon-luan-đan"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-98-ta-muon-luan-đan"}},[n._v("#")]),n._v(" Chương 98: Ta muốn Luận đan")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("br"),i("br"),n._v(" “Ồ? Còn có cái phương pháp này?” "),i("br"),i("br"),n._v(" Trương Huyền ánh mắt sáng lên."),i("br"),i("br"),n._v(" Luyện chế nhất phẩm đan dược, không có nhiều năm thực tiễn, hắn cũng biết không thể thành công, Luận đan liền không giống nhau, nắm giữ Thiên Đạo thư viện, chỉ cần có đầy đủ thư tịch, đầy đủ tri thức, học tập lên vô cùng đơn giản!"),i("br"),i("br"),n._v(" “Cái phương pháp này tuy rằng rất đơn giản, nhưng coi như danh sư, tình nguyện học được luyện đan, cũng sẽ không đi làm!” Thấy hắn hưng phấn, Âu Dương Thành đánh gãy, không nhịn được lắc đầu."),i("br"),i("br"),n._v(" “Tại sao?” "),i("br"),i("br"),n._v(" Trương Huyền kỳ quái."),i("br"),i("br"),n._v(" Rõ ràng lý luận liền có thể đi qua, trở thành luyện đan sư, vì sao còn muốn đi học tập luyện đan? Tiêu tốn càng nhiều khi ở giữa?"),i("br"),i("br"),n._v(" “Nói thật, bất kỳ nghề nghiệp nào, không có thực tiễn, liền không thể càng thâm nhập hiểu rõ, lý luận học được nhiều hơn nữa, không trải qua thực tiễn rèn luyện, không ai có thể bảo đảm có phải là chính xác! Liền luyện đan sư đều không phải, bằng vào mượn sách bên trên nhìn thấy tri thức, cùng chân chính luyện đan sư biện luận? Ngươi cảm thấy ai có thể thắng lợi?” "),i("br"),i("br"),n._v(" Âu Dương Thành nói."),i("br"),i("br"),n._v(" “Chuyện này...” "),i("br"),i("br"),n._v(" Trương Huyền gật đầu."),i("br"),i("br"),n._v(" Trên giấy công phu cuối cùng cảm thấy nông, phải biết việc này muốn tự mình thực hành! Đây là lời nói thật, cũng là lời lẽ chí lý."),i("br"),i("br"),n._v(" Chỉ học lý luận, không đi thực tiễn, làm sao biết sai lầm chính xác? Dù cho có một chút sai lầm, đan dược cũng là luyện không được!"),i("br"),i("br"),n._v(" Sai một ly, đi một ngàn dặm!"),i("br"),i("br"),n._v(" “(Luận đan) phương pháp sau khi xuất hiện, trải qua từ năm đó, không trên dưới ngàn vị danh sư đều tiến hành, những người này mỗi người đều là hạng người kinh tài tuyệt diễm, đối với luyện đan tri thức hiểu rõ, vượt qua bất luận cái gì một vị chân chính luyện đan sư, đáng tiếc... Luận đan bắt đầu sau, nhiều lần thất bại, hơn một nghìn vị danh sư, chân chính thành công, khả năng không đủ một tay số lượng!” "),i("br"),i("br"),n._v(" Âu Dương Thành cảm thán."),i("br"),i("br"),n._v(" “Không đủ một tay?” "),i("br"),i("br"),n._v(" Trương Huyền sợ hết hồn."),i("br"),i("br"),n._v(" Một ngàn người thành công không đủ năm cái? Xác suất này thực sự quá thấp đi!"),i("br"),i("br"),n._v(" “Đúng đấy, lý luận cùng thực tiễn vẫn có chênh lệch rất lớn, chính vì như thế, Luận đan tuy rằng cũng là sát hạch luyện đan sư phương pháp, cũng đã không người nào sử dụng, từ từ bị người quên lãng!” "),i("br"),i("br"),n._v(" Âu Dương Thành lắc đầu."),i("br"),i("br"),n._v(" Nói đến Luận đan muốn so với luyện đan sát hạch dễ dàng, trên thực tế càng khó."),i("br"),i("br"),n._v(" Không có thực tiễn, chỉ đến lý luận, làm sao có thể thành công?"),i("br"),i("br"),n._v(" Có thể đem lý luận nói một điểm không sai, vậy đã nói rõ, hắn thực tiễn bên trên, khẳng định cũng sẽ không kém."),i("br"),i("br"),n._v(" Đã như vậy, cùng với Luận đan phiền phức, còn không bằng luyện đan sát hạch, trái lại càng đơn giản một ít."),i("br"),i("br"),n._v(" “Người khác không được... Ta được đó!” "),i("br"),i("br"),n._v(" Không để ý tới đối phương thở dài, Trương Huyền cảm xúc dâng trào, con mắt càng ngày càng sáng."),i("br"),i("br"),n._v(" Luận đan, nói với người khác tới, xác thực càng khó, có thể đối với hắn mà nói, đơn giản đến cực điểm!"),i("br"),i("br"),n._v(" Nắm giữ Thiên Đạo thư viện, có thể trực tiếp nhìn ra sai lầm cùng khuyết điểm, đơn thuần lý luận không trải qua thực tiễn, người khác không biết chính xác hay không, hắn thư viện một chiếu, lập tức rõ nét rõ ràng!"),i("br"),i("br"),n._v(" Dùng cái này cùng người Luận đan, sẽ không sai lầm, khẳng định có thể ung dung qua ải!"),i("br"),i("br"),n._v(" “Đã có cái phương pháp này, ta quyết định Luận đan sát hạch luyện đan sư!” Nghĩ tới đây, Trương Huyền không có quá nhiều do dự, nói thẳng."),i("br"),i("br"),n._v(" “A?” Vốn tưởng rằng nói rồi nhiều như vậy, đối phương sẽ bỏ qua, không nghĩ tới càng thêm để hắn kiên định, Âu Dương Thành có chút không nói gì: “Ngươi có biết, Luận đan thất bại hậu quả?” "),i("br"),i("br"),n._v(" “Hậu quả?” "),i("br"),i("br"),n._v(" “Đúng đấy, Luận đan chí ít cần cùng mười vị luyện đan sư, tiến hành cãi lại, nhiều như vậy luyện đan sư, đều là có thân phận địa vị, không có đánh đổi, nếu như người người đều muốn cử hành, bọn họ còn không tươi sống mệt chết?” Âu Dương Thành nói: “Bởi vậy, công hội quy định, học đồ xin Luận đan, thành công thì tốt, một khi thất bại, mỗi một vị luyện đan sư chí ít bồi thường mười vạn kim tệ! Thứ hai, chịu đựng một trăm sát thần côn làm như trừng phạt! Thứ ba, trong vòng mười năm không cho phép lần thứ hai sát hạch luyện đan sư!” "),i("br"),i("br"),n._v(" Trương Huyền líu lưỡi."),i("br"),i("br"),n._v(" Cái này trừng phạt xác thực rất nặng."),i("br"),i("br"),n._v(" Luận đan chí ít mười vị luyện đan sư, mỗi cái bồi thường mười vạn mà nói, nói cách khác chí ít cần một trăm vạn kim tệ."),i("br"),i("br"),n._v(" Nhiều tiền như vậy, đối với một cái luyện đan học đồ tới nói, tuyệt đối là giá trên trời bên trong giá trên trời, cả đời cũng chưa chắc có thể giãy giụa đi ra!"),i("br"),i("br"),n._v(" Sát thần côn, là một loại trừng phạt xưng hô, sẽ căn cứ người tu vi không giống, phóng thích không giống uy lực, một trăm sát thần côn, coi như mình thực lực bây giờ, bị đánh xong, cũng gần như tàn phế, một hai tháng không cách nào xuống giường."),i("br"),n._v("  Cho tới thứ ba dạng, nhân sinh có mấy cái mười năm? Trong vòng mười năm không cho phép sát hạch, tương đương với mười năm rời xa đan dược, lại muốn sát hạch, cơ bản chẳng khác nào cả đời vô vọng."),i("br"),i("br"),n._v(" “Hậu quả rất nghiêm khắc, lại rất khó đi qua, lúc này mới để phương pháp này lại không ai đi thử, theo ta được biết, cùng Thiên Huyền Vương quốc liền nhau hơn mười Vương quốc cảnh nội, mấy trăm năm qua, đã không đồng loạt sát hạch!” "),i("br"),i("br"),n._v(" Âu Dương Thành trong lời nói mang theo khuyên can mùi vị: “Ngươi thiên phú tốt như vậy, ta xem vẫn là làm từng bước học tập luyện đan đi, Luận đan cũng không thích hợp!” "),i("br"),i("br"),n._v(" “Ta đã quyết định, dự định tiến hành Luận đan, kính xin Âu Dương đại sư hỗ trợ sắp xếp!” Trương Huyền lắc đầu."),i("br"),i("br"),n._v(" Đùa giỡn, Luận đan liền có thể trở thành là luyện đan sư, đương nhiên muốn tiến hành."),i("br"),i("br"),n._v(" Không phải vậy, để hắn học luyện đan loại hình, không biết phải hao phí bao nhiêu thời gian."),i("br"),i("br"),n._v(" Thời gian không kịp không nói, cũng không nhiều như vậy tinh lực tiêu hao a!"),i("br"),i("br"),n._v(" “Hỗ trợ sắp xếp? Ngươi sẽ không.. Hiện tại liền muốn tiến hành chứ?” "),i("br"),i("br"),n._v(" Âu Dương đại sư một cái lảo đảo."),i("br"),i("br"),n._v(" Ngươi mới vừa trở thành học đồ, liền luyện đan là cái gì cũng không biết, liền muốn Luận đan? Thật hay giả?"),i("br"),i("br"),n._v(" Xác định không có nói đùa?"),i("br"),i("br"),n._v(" “Đúng đấy!” Trương Huyền gật đầu."),i("br"),i("br"),n._v(" “Ngươi... Biết Luận đan nội dung sao? Ngươi biết cái nào luyện đan sư sẽ hỏi dò ngươi cái gì không? Mới vừa trở thành học đồ còn không hệ thống học tập, liền Luận đan...” "),i("br"),i("br"),n._v(" Một bên Đỗ Mãn cũng tràn đầy không nói gì."),i("br"),i("br"),n._v(" “Ta không phải có thể đi sơ đẳng tàng thư khố sao? Những kiến thức này không hiểu, có thể đi nhìn a!” Thấy đối phương gấp gáp như vậy, Trương Huyền giải thích."),i("br"),i("br"),n._v(" “Đi xem xem?” "),i("br"),i("br"),n._v(" Đỗ Mãn, Âu Dương Thành mấy người suýt chút nữa khóc."),i("br"),i("br"),n._v(" Đại ca, ngươi còn biết đến xem a!"),i("br"),i("br"),n._v(" Ngươi mới vừa thi đậu học đồ, liền tàng thư khố đi đều không đi, liền muốn tham gia Luận đan sát hạch, ngươi xác định không phải đang nói đùa?"),i("br"),i("br"),n._v(" Tàng thư khố chen có mấy chục vạn sách tàng thư, tất cả xem xong không có ba, năm năm không thể nào làm được, liền đi vào đều chưa tiến vào, liền muốn Luận đan..."),i("br"),i("br"),n._v(" Là lá gan quá béo tốt, vẫn là đầu óc có vấn đề?"),i("br"),i("br"),n._v(" “Ân, một lúc nữa ta sẽ đi tàng thư khố đọc sách, yên tâm đi, sẽ không lãng phí thời gian, Luận đan sự tình, khả năng còn muốn làm phiền hai vị nhọc lòng!” Trương Huyền nói."),i("br"),i("br"),n._v(" “Một lúc nữa đọc sách...” "),i("br"),i("br"),n._v(" Mọi người càng là đầu óc chóng mặt."),i("br"),i("br"),n._v(" Ngươi đây là dự định lâm trận mới mài gươm?"),i("br"),i("br"),n._v(" Thư không xem, liền muốn Luận đan?"),i("br"),i("br"),n._v(" Như thế không đầu óc sự tình cũng có thể làm đi ra, cái tên này làm sao thi đậu học đồ?"),i("br"),i("br"),n._v(" “Yên tâm đi, ta biết nặng nhẹ, không làm không nắm chắc sự tình, các ngươi liền sắp xếp đi, ta cũng muốn đi đọc sách!” Trương Huyền thấy mọi người vẫn như cũ ngốc tại chỗ, không nhịn được nói."),i("br"),i("br"),n._v(" “Biết nặng nhẹ?” "),i("br"),i("br"),n._v(" “Không làm không nắm chắc sự tình?” "),i("br"),i("br"),n._v(" Ngươi muội a!"),i("br"),i("br"),n._v(" Ngươi biết lò luyện đan lớn dạng gì không? Ngươi biết đan dược là hình cầu chính là phương? Biết luyện đan sư làm sao tinh luyện dược liệu sao?"),i("br"),i("br"),n._v(" Cái gì cũng không biết, liền muốn sát hạch luyện đan sư, còn tiến hành Luận đan... Ngươi xác định chắc chắn?"),i("br"),i("br"),n._v(" “Được rồi!” Thấy hắn kiên trì, không cách nào khuyên can, Âu Dương Thành cùng Đỗ Mãn liếc mắt nhìn nhau, từng người lắc lắc đầu: “Ta hiện tại liền sắp xếp, bất quá, triệu tập nhiều như vậy luyện đan sư, có thể sẽ tiêu tốn một ít thời gian, ngày mai buổi chiều tiến hành Luận đan! Ngươi tốt nhất có thể trong khoảng thời gian này nghĩ rõ ràng phải làm gì, không phải vậy, một khi thật muốn bắt đầu, liền lại không khả năng cứu vãn!” "),i("br"),i("br"),n._v(" “Ừm!” Trương Huyền gật đầu, quăng một cái ống tay áo, một mặt tự tin cùng chính khí, đi về phía trước hai bước, đột nhiên xoay đầu lại: “Này, đúng rồi, sơ đẳng tàng thư khố ở nơi nào?” "),i("br"),i("br"),n._v(" “...” Âu Dương Thành."),i("br"),i("br"),n._v(" “...” Đỗ Mãn."),i("br"),i("br"),n._v(" “...” Tôn Đào, Chu Hoa Hoa."),i("br"),i("br"),n._v("Convert by: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-106"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-108"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);