(window.webpackJsonp=window.webpackJsonp||[]).push([[932],{1286:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1813-mac-linh-co-thanh-ra-tay"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1813-mac-linh-co-thanh-ra-tay"}},[n._v("#")]),n._v(" Chương 1813: Mặc Linh Cổ Thánh ra tay")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("“Ngươi...”"),t("br"),t("br"),n._v("Thần Linh Hoàng trực tiếp bối rối."),t("br"),t("br"),n._v("Trương Huyền cũng đợi tại nguyên chỗ, cảm thấy trong lòng mấy vạn con Thần thú bay lượn mà qua."),t("br"),t("br"),n._v("Cái hồ lô này, chính là phía trước giấu ở hắn đan điền Động Hư hồ lô, nuốt thiên ngoại phi thạch về sau, một mực nằm tại trong giới chỉ, không nhúc nhích, cùng chết đồng dạng, làm sao lại đột nhiên chạy đến?"),t("br"),t("br"),n._v("Hơn nữa còn đem huyết dịch nuốt?"),t("br"),t("br"),n._v("Đây chính là giải phong Long Cốt thần thương mấu chốt, cho nó nuốt, cốt long liền không cách nào diễn sinh huyết nhục, không cách nào phá giải phong ấn, chỉ bằng vào Ngoan Nhân một cái, đừng nói chạy trốn, làm không cẩn thận sẽ chết ở đây!"),t("br"),t("br"),n._v("“Máu, máu của ta...”"),t("br"),t("br"),n._v("Ngã xuống đất Long Cốt thần thương, không ngừng lay động, nước mắt chảy ra đến, lay động quá lớn, đầu suýt chút nữa từ trên cổ rơi xuống."),t("br"),t("br"),n._v("Đây chính là nó thuế biến cùng phá giải phong ấn mấu chốt, kết quả bị một cái hồ lô ăn..."),t("br"),t("br"),n._v("Ngươi không phải không miệng ư?"),t("br"),t("br"),n._v("Thế nào nuốt xuống?"),t("br"),t("br"),n._v("“Rồi...!”"),t("br"),t("br"),n._v("Nuốt long huyết, hồ lô nhéo một cái, ợ một cái, rất là thỏa mãn nhẹ nhàng vụt qua, lần nữa hướng Trương Huyền lao đến."),t("br"),t("br"),n._v("“Đem long huyết trả lại cho ta...”"),t("br"),t("br"),n._v("Thần Linh Hoàng giờ phút này phản ứng lại, cả người đều muốn điên rồi, một mặt phát điên lao đến, bàn tay duỗi ra trước mắt không gian đổ sụp một mảng lớn."),t("br"),t("br"),n._v("Trương Huyền mắt tối sầm lại."),t("br"),t("br"),n._v("Cổ Thánh tam trọng ở giữa giao chiến, thực lực của hắn bây giờ, đã không cách nào nhúng tay, vì lẽ đó, căn bản là không có nghĩ đến, xông tới đem huyết dịch đoạt tới."),t("br"),t("br"),n._v("Một mực ở một bên xem cuộc chiến, một khi cơ hội không tốt, xoay người bỏ chạy, ai biết cái này hồ lô, cướp xong huyết dịch liền xông về phía mình, còn thuận tiện đem Thần Linh Hoàng đưa tới..."),t("br"),t("br"),n._v("Cái gì thù, cái gì oán?"),t("br"),t("br"),n._v("Hảo ý đem ngươi đặt ở đan điền, dùng chân khí tẩm bổ, lại chuẩn bị thiên ngoại phi thạch... Liền như thế đối đãi ta?"),t("br"),t("br"),n._v("Trong lòng suýt chút nữa chửi má nó, Trương Huyền thân thể vụt qua, liền muốn tạm thời tránh mũi nhọn, ai biết, còn không có chạy trốn, thấy hoa mắt, hồ lô đã chui vào đan điền, tìm cái thoải mái địa phương nằm xuống."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Đầu nổ tung, Trương Huyền điên rồi."),t("br"),t("br"),n._v("Lúc này, lại không để ý tới đi tìm hồ lô phiền phức, Trương gia huyết mạch vận chuyển, thời gian trở nên chậm, đồng thời Thiên đạo thân pháp thi triển mà ra, hướng về phía trước sốt ruột lủi, thời gian nháy mắt đi tới Ngoan Nhân trước mặt."),t("br"),t("br"),n._v("Đối mặt Thần Linh Hoàng, hiện nay duy nhất đáng tin cậy cũng chỉ có cái tên này."),t("br"),t("br"),n._v("“Chết!”"),t("br"),t("br"),n._v("Thấy cướp đoạt huyết dịch hồ lô, chạy đến Trương Huyền phía trước biến mất, Thần Linh Hoàng biết chắc là cái tên này dùng thủ đoạn, tức giận đến run rẩy, song chưởng bài không mà xuống."),t("br"),t("br"),n._v("Không khí giống như là bị rút sạch, Trương Huyền chỉ cảm thấy, thân thể cứng nhắc, kìm lòng không được hướng đối phương bàn tay bay đi, giống như là chủ động tự tìm đường chết đồng dạng."),t("br"),t("br"),n._v("“Chủ nhân!”"),t("br"),t("br"),n._v("Ngoan Nhân lại không để ý tới cùng còn lại hai vị Cổ Thánh giao thủ, một cái khác bàn tay, từ trong thư tịch bay ra, lăng không một trảo, khoác lên Trương Huyền bả vai, tổ ngăn lại hắn tiến lên, cái tay còn lại, tại trong sách vở nhấn một cái, một bộ tàn khuyết không đầy đủ khung xương, từ đó chui ra."),t("br"),t("br"),n._v("Ký kết khế ước, chủ nhân người sau chết một lần, hắn hẳn phải chết không nghi ngờ, bất kể như thế nào, cũng muốn cứu đối phương tính mạng."),t("br"),t("br"),n._v("Rầm!"),t("br"),t("br"),n._v("Khung xương vừa xuất hiện, tựa như bão trung tâm, vô số linh khí hội tụ tới, bàn tay hướng phía dưới bỗng nhiên áp bức mà tới."),t("br"),t("br"),n._v("Theo Thiên Đạo chi sách rời đi, Ngoan Nhân khung xương sức chiến đấu, tăng lên không ít, trái tim không ngừng nhảy lên, dâng trào mạnh mẽ, cùng Thần Linh Hoàng công kích đụng một cái, người sau lập tức bay ngược mà ra."),t("br"),t("br"),n._v("Thần Linh Hoàng, bị thương trước thực lực, có thể sẽ không sợ hãi còn không có khôi phục Ngoan Nhân, nhưng giờ phút này... Căn bản không đỡ được!"),t("br"),t("br"),n._v("Nhìn thấy Ngoan Nhân quả nhiên có thể ngăn cản đối phương, Trương Huyền thở phào nhẹ nhõm, ý thức vội vàng tập trung ở đan điền, nhịn không được quát tháo: “Ngươi không phải đồng ý ta không đi vào đan điền ư?”"),t("br"),t("br"),n._v("Phía trước, cho cái hồ lô này thiên ngoại phi thạch, ép đối phương đồng ý, từ đan điền rời đi, nằm mơ đều không có nghĩ đến, thời khắc mấu chốt lại chui vào!"),t("br"),t("br"),n._v("Hơn nữa... Còn tìm đường chết đem Thần Linh Hoàng dẫn tới."),t("br"),n._v("Tạo cái gì nghiệt ah!"),t("br"),t("br"),n._v("Nếu không phải phản ứng nhanh, kích hoạt lên Trương gia huyết mạch, chỉ sợ hiện tại sớm đã không còn tính mạng."),t("br"),t("br"),n._v("“Ta liền nằm một hồi...”"),t("br"),t("br"),n._v("Uốn éo cái mông, hồ lô một bộ lợn chết không sợ bỏng nước sôi hình dáng."),t("br"),t("br"),n._v("Khí cắn răng, Trương Huyền còn muốn mắng nữa vài câu, đột nhiên cảm thấy sống lưng run lên, huyết mạch lực lượng lần nữa vận chuyển lên đến, nhanh chóng tiến lên."),t("br"),t("br"),n._v("Rầm rầm rầm!"),t("br"),t("br"),n._v("Vừa rời đi tại chỗ, phía trước đứng yên không gian, lập tức mất đi, gió lốc khuấy động, không gian vỡ vụn hình thành dư âm, đập ở sống lưng, yết hầu ngòn ngọt, một ngụm máu tươi phun ra."),t("br"),t("br"),n._v("Vội vàng quay đầu, ngay sau đó nhìn thấy vừa rồi vây công Ngoan Nhân hai đầu Cổ Thánh, chẳng biết lúc nào xuất hiện tại cách đó không xa."),t("br"),t("br"),n._v("Cùng Ngoan Nhân đối chiến, bọn họ khả năng chống lại không được, nhưng một cái Bất Hủ cảnh đại viên mãn, lại không giết được, thật sự không cần sống."),t("br"),t("br"),n._v("“Thời gian pháp tắc? Quả nhiên là nhân tộc gia hỏa, chết!”"),t("br"),t("br"),n._v("Một vị Cổ Thánh, nhìn thấy đánh lén ra tay, đều không có thành công, cau mày, lần nữa một chưởng vỗ rơi mà xuống."),t("br"),t("br"),n._v("Một vị khác, cũng biết không thể bị dở dang, đồng dạng một quyền oanh kích mà tới."),t("br"),t("br"),n._v("Quyền, chưởng còn chưa tới đến trước mặt, không gian bốn phía liền xuất hiện vặn vẹo, Trương Huyền tựa như rơi vào gió táp mưa rào bên trong, trốn đều trốn không xong."),t("br"),t("br"),n._v("“Thật đúng là coi ta là quả hồng mềm?”"),t("br"),t("br"),n._v("Thấy Ngoan Nhân giờ phút này đang toàn lực ngăn cản khống chế trận pháp Thần Linh Hoàng, không tì vết cứu người, Trương Huyền hàm răng cắn chặt, cổ tay khẽ đảo."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Hai đầu Cổ Thánh thi thể lập tức xuất hiện tại phía trước, ngăn tại quyền chưởng phía trước."),t("br"),t("br"),n._v("Lúc trước Thần Dung Hoàng cùng vây công mọi người đại chiến, hắn nhân cơ hội cầm đi mấy cái thi thể, tuy là trong khoảng thời gian này một mực bị thương nặng, chưa kịp luyện hóa, nhưng khi cái lá chắn sử dụng, vẫn là dư sức có thừa."),t("br"),t("br"),n._v("Bành! Bành!"),t("br"),t("br"),n._v("Công kích đồng thời đánh vào trên thi thể, để hắn bay ngược mà ra, Trương Huyền tránh thoát một kiếp."),t("br"),t("br"),n._v("Mi tâm khẽ động, đang muốn đem duy nhất một bản Thiên đạo chi sách, tế ra đến, đem cái này hai đầu Cổ Thánh đập chết một cái, liền nghe đến trên không nhất đạo dồn dập tiếng gió đi tới."),t("br"),t("br"),n._v("Ngay sau đó nhất đạo âm hàn khí tức, cuốn tới, như là dây thừng đem hắn lôi kéo, nhanh chóng hướng ngoài điện bay đi."),t("br"),t("br"),n._v("“Hội trưởng, đi mau...”"),t("br"),t("br"),n._v("Thanh âm ở bên tai vang lên, Mặc Linh Cổ Thánh phát hiện không đúng, vu hồn rời thân thể, vọt vào."),t("br"),t("br"),n._v("“Trốn chỗ nào...”"),t("br"),t("br"),n._v("Linh Hoàng trong cung hai đầu Cổ Thánh, bị Cổ Thánh thi thể chặn lại công kích, giờ phút này có đề phòng, sao có thể để hắn thuận lợi rời đi, xoay người lần nữa đồng loạt công kích."),t("br"),t("br"),n._v("Vu hồn xoay người, một chưởng nghênh tiếp."),t("br"),t("br"),n._v("Linh Hoàng cung đại điện đang kích động khí lưu bên dưới, sụp đổ xuống, bụi đất tung bay, mang theo Trương Huyền Mặc Linh Cổ Thánh, cũng nhân cơ hội rời đi cung điện phạm vi."),t("br"),t("br"),n._v("Bản thân hắn là tích huyết trùng sinh cấp bậc cường giả, so hai vị này phải cường đại hơn, chỉ cần Linh Hoàng không có ra tay, hai người này muốn ngăn lại, gần như không có khả năng."),t("br"),t("br"),n._v("“Hội trưởng, ta đã đem phía ngoài trận pháp mở ra, ngươi mau rời đi, ta chặn lại hai vị này...”"),t("br"),t("br"),n._v("Rất mau đem Trương Huyền đưa ra Tẩm điện phạm vi, Mặc Linh Cổ Thánh vội vàng truyền âm."),t("br"),t("br"),n._v("“Long Cốt thần thương cùng Ngoan Nhân còn tại bên trong, rời đi, bọn chúng liền chạy không xong...”"),t("br"),t("br"),n._v("Thân thể vụt qua, Trương Huyền dừng ở tại chỗ."),t("br"),t("br"),n._v("Long Cốt thần thương bị thương, Ngoan Nhân cũng bị Thần Linh Hoàng cùng trận pháp vây khốn, trong thời gian ngắn không thể rời bỏ, bản thân thật muốn chạy, hai thứ này làm không cẩn thận, lại không cách nào trở về."),t("br"),t("br"),n._v("Đã tổn thất ba đầu Cổ Thánh thi thể, hai thứ này tuyệt không thể có sai lầm!..."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1824"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1826"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);