(window.webpackJsonp=window.webpackJsonp||[]).push([[934],{1289:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1815-chay-tron-【-canh-3-】"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1815-chay-tron-【-canh-3-】"}},[n._v("#")]),n._v(" Chương 1815: Chạy trốn 【 Canh [3] 】")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1815: Chạy trốn "),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Lực lượng tự bầu trời cuốn tới, hướng Trương Huyền nghiền ép."),t("br"),t("br"),n._v("Năm đại Cổ Thánh liên thủ, thiên địa như là đun sôi, trước mắt không còn là khe hở, mà là đáy nồi đồng dạng đen kịt, không gian như là bụi bặm, lại hình như là hạt cát, bị đụng tới, sẽ lập tức hồn phi phách tán, chết không có chỗ chôn."),t("br"),t("br"),n._v("Nhìn thấy cỗ lực lượng này, Mặc Linh Cổ Thánh sợ tới mức thân thể run lên, vẻ mặt trở nên trắng bệch: “Đi mau...”"),t("br"),t("br"),n._v("Năm người này rõ ràng tu luyện một loại nào đó hợp kích phương pháp, nếu không không có uy lực này."),t("br"),t("br"),n._v("Chiêu này hạ xuống, tích huyết trùng sinh đỉnh phong, cũng chưa chắc chống đỡ được, huống chi là hắn."),t("br"),t("br"),n._v("Còn muốn lấy, vu hồn sư công hội, rốt cục xuất hiện hội trưởng, nhất định có thể giải quyết đi Dị Linh tộc nhân tai hoạ ngầm, khôi phục bọn họ thân phận thật sự, để con cháu đời sau, có tư cách lần nữa trở lại danh sư đại lục... Nằm mơ cũng không ngờ tới, cái tên này như vậy không đáng tin cậy, mới nhận nhau, liền muốn vẫn lạc."),t("br"),t("br"),n._v("Chuyện này là sao!"),t("br"),t("br"),n._v("Vội vàng đi tới đối phương trước mặt, hồn thể kéo bàn tay của hắn, liền muốn tránh né công kích, lại thấy đối phương lắc đầu, cánh tay vung một cái, tránh thoát hắn lôi kéo."),t("br"),t("br"),n._v("“Đến hay lắm!”"),t("br"),t("br"),n._v("Ngay tại nghi ngờ, ngay sau đó nhìn thấy thanh niên, khóe miệng nâng lên, một bộ tìm đường chết biểu lộ."),t("br"),t("br"),n._v("“...”"),t("br"),t("br"),n._v("Da đầu run lên."),t("br"),t("br"),n._v("Vu hồn sư, đến cùng làm chuyện gì xấu, gặp được như vậy một cái không đáng tin cậy hội trưởng?"),t("br"),t("br"),n._v("Buồn bực sắp bùng nổ, cuối cùng vẫn hội tụ lực lượng lần nữa bắt tới, còn không có cùng thân thể của đối phương tiếp xúc, chỉ thấy thanh niên hai tay giơ lên: “Thu!”"),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Màu đen lực lượng, rơi vào đối phương bàn tay vị trí bên trên, vị này Trương Huyền, cũng không cùng tưởng tượng đồng dạng, hóa thành bụi bặm, mà là không nhúc nhích, năm đại Cổ Thánh liên hợp hình thành công kích, tại cả người hắn phía trên, khoảng cách không đến hai centimet địa phương, chia làm năm phần, hướng bốn phía vọt tới."),t("br"),t("br"),n._v("“Đây là... Trận pháp?”"),t("br"),t("br"),n._v("Cảm nhận được lực lượng khổng lồ, bảo vệ thanh niên thân thể, Mặc Linh Cổ Thánh vội vàng nhìn xuống dưới, ngay sau đó nhìn thấy, toàn bộ Linh Hoàng trong hoàng cung tất cả phù văn đồng thời phát sáng lên, tính cả trận pháp, cùng thời khắc đó vận chuyển."),t("br"),t("br"),n._v("Vô số linh khí, tại thanh niên phía trên, tạo thành một cái hình ngũ giác năng lượng lồng ánh sáng."),t("br"),t("br"),n._v("Công kích của đối phương, chính là bị cái này lồng ánh sáng hấp thu, lần nữa hội tụ đến trong trận pháp."),t("br"),t("br"),n._v("“Đây là... Cưỡng ép hấp thu đối phương công kích lực lượng?”"),t("br"),t("br"),n._v("Thân thể run lên, Mặc Linh Cổ Thánh đứng chết trân tại chỗ, triệt để bối rối."),t("br"),t("br"),n._v("Năm đại Cổ Thánh liên hợp công kích, cường đại cỡ nào, trong lòng của hắn rõ ràng, coi như ba cái bản thân, liên hợp cùng một chỗ, cũng đừng hòng chống đỡ được."),t("br"),t("br"),n._v("Nhưng trước mắt thanh niên, không chỉ ngăn lại, còn đem cỗ lực lượng này hấp thu lên, cái này... Là hắn vừa rồi mười cái hô hấp phía trong sắp đặt đi ra trận pháp?"),t("br"),t("br"),n._v("Nhiều như vậy trận pháp vây quanh bên dưới, sắp đặt ra một cái đơn độc trận pháp, liền rất không dễ dàng, hắn bố trí đại trận, vậy mà có thể đem tất cả trận pháp toàn bộ dung hợp lại cùng nhau, phát huy ra càng cường đại hơn uy lực..."),t("br"),t("br"),n._v("Đã không phải là nghịch thiên có thể miêu tả!"),t("br"),t("br"),n._v("“Phá!”"),t("br"),t("br"),n._v("Không đi quản vị này vu hồn sư khiếp sợ, Trương Huyền hai tay hướng trên không vỗ tới."),t("br"),t("br"),n._v("Phía trên hình ngũ giác trận pháp, lập tức đem đối phương vừa mới công kích lực lượng, lần nữa tụ lại, hóa thành to lớn lồng giam, hướng năm người bay đi."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("“Nguy rồi...”"),t("br"),t("br"),n._v("Năm đại Cổ Thánh hiển nhiên cũng không nghĩ tới, bản thân liên hợp công kích, bị đối phương ung dung phá giải, hơn nữa bị lấy đạo của người trả lại cho người, từng cái vẻ mặt trắng bệch, lần nữa liên hợp lại, hướng phía dưới công kích."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),n._v(" Song phương lực lượng đối đầu, năm đại Cổ Thánh đồng thời máu tươi phun mạnh, đồng loạt lui về phía sau."),t("br"),t("br"),n._v("Linh Hoàng Cung trận pháp, vốn là có thể phòng ngự tích huyết trùng sinh đỉnh phong cấp bậc cường giả, giờ phút này lại phối hợp Trương Huyền trận pháp, cùng mọi người công kích, một chút phát ra lực lượng, vượt ra khỏi tưởng tượng, cho dù bọn họ liên thủ, đều chống lại không được."),t("br"),t("br"),n._v("“Ngoan Nhân, mang theo Long Cốt thần thương, theo ta đi...”"),t("br"),t("br"),n._v("Một kích thành công, Trương Huyền cũng không tiếp tục, mà là quay đầu nhìn về phía đang cùng Thần Linh Hoàng chiến đấu Ngoan Nhân, nói một tiếng."),t("br"),t("br"),n._v("Cùng thời khắc đó, hình ngũ giác lần nữa thả ra một đạo hắc quang, lăng không bổ xuống dưới."),t("br"),t("br"),n._v("Hắc quang chém xuống trận pháp dây dưa, Ngoan Nhân nhẹ nhàng vụt qua, tránh thoát Thần Linh Hoàng áp chế, xương tay lại vồ một cái, trên đất Long Cốt thần thương cùng ba đầu Cổ Thánh thi thể, bị bắt tới."),t("br"),t("br"),n._v("“Đi!”"),t("br"),t("br"),n._v("Làm xong những này, Trương Huyền bàn tay lần nữa run lên, trên không hình ngũ giác đâm rách không gian, Ngoan Nhân, Mặc Linh Cổ Thánh tính cả hắn, bỗng nhiên nhảy dựng, chui vào trong đó, thời gian nháy mắt biến mất không thấy gì nữa."),t("br"),t("br"),n._v("“Nhanh lưu lại hắn...”"),t("br"),t("br"),n._v("Thấy đối phương chạy trốn, Thần Linh Hoàng sắp nổ, lần nữa nhìn về phía năm đại Cổ Thánh."),t("br"),t("br"),n._v("“Ngươi vẫn là trước nghiên cứu một chút, hắn vì sao có thể khống chế của ngươi hộ cung trận pháp đi!”"),t("br"),t("br"),n._v("“Trận pháp này lợi hại như thế, liều mạng, chúng ta đều biết bị thương...”"),t("br"),t("br"),n._v("Năm đại Cổ Thánh hừ một tiếng."),t("br"),t("br"),n._v("Tuy là rất muốn đạt được đối phương trong miệng đồ vật, nhưng bởi vậy bị trọng thương mà nói, tuyệt đối phải không đền mất."),t("br"),t("br"),n._v("Bởi vậy, bị đối phương một đòn lui về phía sau, bọn họ liền ngừng lại, không phải đánh không lại đối phương, mà là có chỗ kiêng kị."),t("br"),t("br"),n._v("“Hừ...”"),t("br"),t("br"),n._v("Khí sắc mặt trắng bệch, Thần Linh Hoàng biết, cùng đối phương chỉ là liên minh, mà không phải phụ thuộc, có thể giúp đỡ ngăn cản một chút, cũng rất không tệ, muốn hắn bốc lên nguy hiểm tính mạng, đem người lưu lại, quả thực không có khả năng hoàn thành."),t("br"),t("br"),n._v("Dị Linh tộc, trời sinh liền rất cường đại, tuổi thọ cũng dài, dựa theo tình huống bình thường, phát triển bên trên vài vạn năm, diệt đi danh sư đại lục, mười phần ung dung, có thể một mực không thành công, nguyên nhân chủ yếu nhất, chính là... Mỗi một cái đều quá ích kỷ!"),t("br"),t("br"),n._v("Căn bản liên hợp không đến cùng một chỗ!"),t("br"),t("br"),n._v("Không nói bọn họ ba đại Hoàng giả, triệu tập những vương giả này, cũng đều đồng dạng, nếu như đều cùng danh sư đồng dạng, mọi người đồng tâm hiệp lực, một lòng đoàn kết, làm sao đến mức một mực cố thủ ở đây."),t("br"),t("br"),n._v("Đương nhiên trọng yếu nhất vẫn là, thiếu một cái chí cường, có thể để cho tất cả mọi người thần phục Hoàng giả, giống như phía trước Ngoan Nhân đồng dạng."),t("br"),t("br"),n._v("Thần Dung Hoàng mặc dù là Linh tộc đương đại đệ nhất nhân, nhưng cũng so những người khác mạnh mẽ không được quá nhiều, không có chân chính lực chấn nhiếp đo."),t("br"),t("br"),n._v("Lấy ra một cái đan dược, ăn vào, vẻ mặt hơi khôi phục một chút, Thần Linh Hoàng hướng phía dưới trận pháp nhìn lại."),t("br"),t("br"),n._v("Linh Hoàng Cung trận pháp, là hắn cùng mình tiền bối, hao tốn không biết bao nhiêu tâm huyết, tiêu hao bao nhiêu nhân lực, mới bố trí đi ra, chỉ nghe từ hắn hoặc là chỉ định người khống chế, làm sao lại nghe theo tên kia chỉ huy, chống lại năm đại Cổ Thánh?"),t("br"),t("br"),n._v("Hơn nữa bạo phát ra lực lượng càng thêm cường đại..."),t("br"),t("br"),n._v("Liếc mắt nhìn, nhìn không ra cái gì, ngón tay vạch một cái, một giọt máu lơ lửng giữa không trung."),t("br"),t("br"),n._v("Trận pháp này, khống chế mấu chốt là huyết mạch, chỉ cần thi triển ra huyết mạch, nhất định có thể triệt để khống chế, thuận lợi kích hoạt."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Huyết dịch xuất hiện, phía dưới trận pháp quả nhiên đung đưa, từng đạo lực lượng lóe lên, nhanh chóng hướng trung tâm hội tụ."),t("br"),t("br"),n._v("Vừa định đem cỗ lực lượng này khống chế trong tay, lần nữa hình thành to lớn phòng ngự, lần nữa bố trí tốt Linh Hoàng Cung phòng vệ, phía dưới một trận kịch liệt lay động, ngay sau đó một cái âm thanh lớn vang lên, giống như là muốn đem màng nhĩ nổ nát vụn."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Lực lượng hùng hồn từ nơi trung tâm nhất khuấy động, Linh Hoàng Cung vài vạn năm kiến trúc, trong tích tắc, bị to lớn công kích, toàn bộ sụp đổ, biến thành tro tàn."),t("br"),t("br"),n._v("Phía dưới trận pháp, vậy mà không nhận hắn khống chế... Tự bạo!"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1826"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1828"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);