(window.webpackJsonp=window.webpackJsonp||[]).push([[938],{1293:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1818-tim-hoa-tu-co-thanh-【-canh-3-】"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1818-tim-hoa-tu-co-thanh-【-canh-3-】"}},[n._v("#")]),n._v(" Chương 1818: Tìm Hỏa Tư Cổ Thánh 【 Canh [3] 】")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1818: Tìm Hỏa Tư Cổ Thánh "),t("br"),t("br"),n._v("Gật gật đầu, Trương Huyền bàn tay đưa tới, nhẹ nhàng một trảo."),t("br"),t("br"),n._v("Lập tức cảm thấy một cỗ vô tận lực lượng từ cán thương bên trong lan ra đi ra, dường như nhẹ nhàng run lên, liền có thể đâm xuyên không gian, đi vào càng sâu xa hơn thế giới."),t("br"),t("br"),n._v("Phía trước mạnh mẽ không thể địch nổi Cổ Thánh, giờ phút này, trong mắt hắn, một thương liền có thể đâm chết, không đỡ nổi một đòn."),t("br"),t("br"),n._v("“Ngươi cũng đã biết Hỏa Tư Cổ Thánh chỗ ở, ở phương hướng nào?”"),t("br"),t("br"),n._v("Trường thương nơi tay, Trương Huyền chiến ý tăng vọt, quay đầu nhìn về phía một bên Mặc Linh Cổ Thánh."),t("br"),t("br"),n._v("“Nó là Thánh thú một tổ lão tổ, năm đó cùng Thần Dung Hoàng quyết đấu, bị đuổi ra khỏi Dị Linh nhất tộc cư trú vị trí, hình như ngay tại cái này hỏa vũ chiến trường lân cận!”"),t("br"),t("br"),n._v("Chẳng biết tại sao hắn lại như vậy hỏi, Mặc Linh Cổ Thánh chần chờ một chút, đạo."),t("br"),t("br"),n._v("Hắn là sinh trưởng ở Dị Linh tộc, mặc dù không phải Dị Linh tộc nhân, nhưng không so với mới biết ít."),t("br"),t("br"),n._v("“Ngay tại chung quanh đây? Quá tốt rồi, mang ta đi nó sào huyệt...” Trương Huyền cười ha ha một tiếng."),t("br"),t("br"),n._v("Tấn cấp Long Cốt thần thương mục đích, chính là vì đem cái tên này thuần phục, vừa vặn tại lân cận, vậy thì thuận đường đem sự tình xử lý."),t("br"),t("br"),n._v("“Cái này...” Mặc Linh Cổ Thánh một mặt rầu rĩ: “Nghe nói, cái tên này tính tình không tốt lắm, tìm nó làm gì?”"),t("br"),t("br"),n._v("Chính mình cái này hội trưởng tư duy, vẫn là rất khó mà lý giải, đột nhiên muốn tìm Hỏa Tư Cổ Thánh vì chuyện gì?"),t("br"),t("br"),n._v("“Ta còn thiếu một đầu Thánh thú làm thú sủng, vừa vặn đi qua đem cái tên này thuần phục...”"),t("br"),t("br"),n._v("Trương Huyền cười cười."),t("br"),t("br"),n._v("“Thuần phục?”"),t("br"),t("br"),n._v("Mặc Linh Cổ Thánh tràn đầy im lặng."),t("br"),t("br"),n._v("Đối phương là Thánh thú lão tổ, năm đó Thần Dung Hoàng đều không có để hắn khuất phục, ngươi muốn thuần phục... Đùa giỡn a?"),t("br"),t("br"),n._v("“Hội trưởng... Ngươi trận pháp là rất lợi hại, nhưng thuần thú, không phải tưởng tượng đơn giản như vậy, cần thời gian dài lôi kéo tình cảm, duy trì thân mật, mới có thể để cho hắn chân chính thuyết phục...”"),t("br"),t("br"),n._v("Cũng nhịn không được nữa, khuyên can đạo."),t("br"),t("br"),n._v("Người hội trưởng này, là rất lợi hại, thủ đoạn cũng rất nhiều, có thể thiếu giọt là được, muốn vừa ra là vừa ra..."),t("br"),t("br"),n._v("Đường đường Thánh thú lão tổ, nếu là dễ dàng như vậy thuần phục, cũng không đến mức để Thần Dung Hoàng đô đầu lớn."),t("br"),t("br"),n._v("“Ta biết, không đi qua thử một chút, làm thế nào biết nhất định không thành công?”"),t("br"),t("br"),n._v("Chẳng muốn giải thích, Trương Huyền mỉm cười."),t("br"),t("br"),n._v("“Tốt a!”"),t("br"),t("br"),n._v("Thấy hội trưởng, quyết tâm muốn đi, Mặc Linh Cổ Thánh không nói thêm lời, lấy ra bản đồ cùng la bàn xác định phương vị, lúc này mới chỉ hướng một chỗ: “Hỏa Tư Cổ Thánh cùng rất nhiều Thánh thú chỗ ở, ngay tại cách nơi này, không đến mười vạn cây số địa phương!”"),t("br"),t("br"),n._v("“Không đến mười vạn cây số? Được!”"),t("br"),t("br"),n._v("đọＣ ngantruyen.com"),t("br"),n._v("Nhẹ nhàng cười một tiếng, Trương Huyền trường thương run lên."),t("br"),t("br"),n._v("Tê lạp!"),t("br"),t("br"),n._v("Không gian xuất hiện một cái lối đi, thân thể nhảy lên, chui vào: “Đi thôi!”"),t("br"),t("br"),n._v("Mặc Linh Cổ Thánh theo sát trên đó."),t("br"),t("br"),n._v("Từ thông đạo chui ra ngoài, đã đi tới mười vạn cây số bên ngoài, ngay sau đó hai người nhìn thấy một chỗ hoang vu núi lớn, quả nhiên có vô số Thánh thú, bay lượn trong đó."),t("br"),t("br"),n._v("Dị Linh tộc Thánh thú, cùng danh sư đại lục Thánh thú, không kém nhiều, chỉ bất quá, nhận lấy Huyết Nguyệt ảnh hưởng, tròng mắt đỏ hoe, thoạt nhìn càng thêm hung tàn thô bạo, lực lượng cũng càng thêm quỷ dị."),t("br"),t("br"),n._v("Trong núi phi hành Thánh thú, đa dạng, số lượng kinh người."),t("br"),t("br"),n._v("“Ngươi đi tới bái thiếp, liền nói đến đây viếng thăm Hỏa Tư Cổ Thánh!”"),t("br"),t("br"),n._v("Hai người ngừng lại, cũng không xông vào, Trương Huyền quay đầu nói."),t("br"),t("br"),n._v("Gật gật đầu, Mặc Linh Cổ Thánh bàn tay run lên, một trương bái thiếp từ ngón tay bay ra, thẳng tắp hướng trước mắt núi hoang bay lượn mà đi, còn tại trên không, liền tản mát ra tựa như thanh âm như sấm, vang vọng sơn cốc."),t("br"),t("br"),n._v("“Vu hồn sư Mặc Linh Cổ Thánh, đi theo hội trưởng Trương Huyền, đến đây viếng thăm Hỏa Tư Cổ Thánh, mong rằng gặp một lần!”"),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Không nghĩ tới đột nhiên có người viếng thăm, phía trước ở trên trời phi hành rất nhiều Thánh thú, đồng thời quay đầu bay tới, lơ lửng giữa không trung, nhìn về phía Trương Huyền hai người, trên mặt không tốt."),t("br"),n._v("Bọn họ bị Thần Dung Hoàng đuổi tới mảnh này hoang vu chỗ, đã sớm tràn đầy oán hận."),t("br"),t("br"),n._v("Hội trưởng Trương Huyền, chưa nghe nói qua, nhưng Mặc Linh Cổ Thánh danh khí tại toàn bộ Dị Linh tộc vẫn là rất lớn."),t("br"),t("br"),n._v("“Lão tổ đang bế quan tu luyện, tổng thể không gặp khách, hai vị còn xin về đi!”"),t("br"),t("br"),n._v("Một đầu Bất Hủ cảnh đại viên mãn Thánh thú, bay tới."),t("br"),t("br"),n._v("“Không tiếp khách? Mong rằng nói cho các ngươi lão tổ, hai người chúng ta lần này tới, là có tin tức tốt! Đối với các ngươi Thánh thú nhất tộc, có cực lớn trợ giúp!” Trương Huyền cười cười."),t("br"),t("br"),n._v("“Tin tức tốt?” Bất Hủ cảnh đại viên mãn Thánh thú cười lạnh: “Các ngươi loại trừ đùa nghịch tâm cơ, còn có thể có cái gì tốt tin tức? Còn xin lập tức rời đi, nếu không, đừng trách chúng ta không khách khí!”"),t("br"),t("br"),n._v("Rầm!"),t("br"),t("br"),n._v("Trên không bay lượn rất nhiều Thánh thú, đồng thời đem chu vi lên, đằng đằng sát khí."),t("br"),t("br"),n._v("“Ngươi quá lo lắng!” Thấy đối phương tràn đầy cảnh giác, Trương Huyền vẻ mặt không thay đổi: “Các ngươi những ngày gần đây, có thể nghe nói, Thần Dung Hoàng bị nhân loại chém giết, Thần Linh Hoàng, Thần Tinh Hoàng bị thương nặng muốn chết, toàn bộ Linh tộc, đã biến thiên?”"),t("br"),t("br"),n._v("“Hơi có nghe nói!”"),t("br"),t("br"),n._v("Bất Hủ cảnh đại viên mãn Thánh thú không biết hắn tại sao lại đột nhiên nói như vậy, dừng lại một chút, đạo."),t("br"),t("br"),n._v("Tuy là bọn chúng bị chạy tới xa xôi vị trí, nhưng Dị Linh nhất tộc xuất hiện biến cố lớn như vậy, vẫn là nghe được một ít tin tức."),t("br"),t("br"),n._v("“Thần Dung Hoàng bị giết, phía trước tuân thủ ước định, tự nhiên có thể không cần tuân thủ, hiện tại chính là Thánh thú nhất tộc quay về tốt nhất thời điểm, chẳng lẽ... Chư vị muốn cả một đời đều canh giữ ở loại này hoang vu chỗ, không có ngon miệng đồ ăn, linh khí còn như vậy mỏng manh... Cứ thế mãi, toàn bộ Thánh thú nhất tộc, chỉ sợ đều sẽ bởi vậy thực lực sụt giảm đi!”"),t("br"),t("br"),n._v("Trương Huyền cười nói."),t("br"),t("br"),n._v("“Cái này...” Bất Hủ cảnh đại viên mãn Thánh thú, nói không ra lời."),t("br"),t("br"),n._v("Đối phương nói không sai."),t("br"),t("br"),n._v("Không có ngon miệng đồ ăn, không sao, nhưng linh khí mỏng manh, Thánh thú tấn cấp liền sẽ trở nên chậm, hơn nữa thành tựu cuối cùng cũng sẽ nhận hạn chế."),t("br"),t("br"),n._v("Trong thời gian ngắn, không tính là gì, thời gian dài, dần dần suy yếu, đối mặt Dị Linh nhất tộc, không còn năng lực phản kháng."),t("br"),t("br"),n._v("“Ta biết ngươi không làm được quyết định, liền đem ta nói những này, cùng ngươi biết tin tức, nói cho các ngươi biết lão tổ, gặp hay là không gặp, nó tự nhiên sẽ có quyết định!”"),t("br"),t("br"),n._v("Thấy nó loại vẻ mặt này, Trương Huyền lại không giải thích, mà là khoát tay áo."),t("br"),t("br"),n._v("“Được!”"),t("br"),t("br"),n._v("Biết đối phương nói nếu như là thật, đối Thánh thú nhất tộc, quả thực có vượt thời đại ý nghĩa, đầu này Bất Hủ cảnh đại viên mãn Thánh thú, chần chờ phút chốc, cuối cùng gật gật đầu, thân thể khổng lồ xoay một cái, thẳng tắp hướng sơn mạch bay đi."),t("br"),t("br"),n._v("Thấy nó rời đi, Trương Huyền nhẹ nhàng cười một tiếng."),t("br"),t("br"),n._v("Muốn thuần phục đối phương, đầu tiên muốn trước nhìn thấy... Nếu như gặp mặt, tự nhiên phải có kỹ xảo."),t("br"),t("br"),n._v("Qua tiếp cận nửa canh giờ, đầu thánh thú này mới chậm rãi bay tới, to lớn móng vuốt hướng về phía trước duỗi ra: “Lão tổ cho mời!”"),t("br"),t("br"),n._v("“Đa tạ!”"),t("br"),t("br"),n._v("Không nói thêm lời, Trương Huyền hai người theo sát ở phía sau bay tới đằng trước."),t("br"),t("br"),n._v("Trước mắt sơn mạch, quả thực đủ hoang vu, chỉ sinh trưởng một chút thưa thớt cây cối, tràn đầy gai nhọn, cỏ xanh cây cối thậm chí dược liệu, hầu như không có."),t("br"),t("br"),n._v("Nếu như không phải nham thạch cùng bùn đất, cũng hoài nghi có phải hay không đến sa mạc."),t("br"),t("br"),n._v("Khe núi không có nước suối, không khí cũng có vẻ hơi khô hanh, quả thực không thích hợp sinh tồn."),t("br"),t("br"),n._v("Đi lại rất nhiều Thánh thú, cũng từng cái thoạt nhìn trên mặt đói."),t("br"),t("br"),n._v("Liền giống như danh sư đại lục Thiên Huyền vương quốc đồng dạng, cao cấp Thánh thú đi qua, đợi đến thời gian lâu dài, chỉ sợ cũng phải hạ thấp tu vi, trở nên lại không phía trước lực lượng."),t("br"),t("br"),n._v("Mạnh hơn buôn bán tinh anh, lưu lạc đến hoàn toàn tách biệt với thế gian đảo hoang, đồng dạng sẽ trở nên cùng dã nhân đồng dạng."),t("br"),t("br"),n._v("“Lão tổ ngay tại cái này trong động, ta chờ ở bên ngoài đợi...”"),t("br"),t("br"),n._v("Rất mau tới đến một cái to lớn trước sơn động mặt, Bất Hủ cảnh đại viên mãn Thánh thú, ngừng lại."),t("br"),t("br"),n._v("“Được!”"),t("br"),t("br"),n._v("Trương Huyền cũng không nhiều lời, cùng Mặc Linh Cổ Thánh hướng trong động bay đi."),t("br"),t("br"),n._v("Còn chưa tới đến trước mặt, liền cảm thấy một cỗ gió nóng đập vào mặt, tựa hồ muốn đầu người sốt sốt ruột."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1829"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1831"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);