(window.webpackJsonp=window.webpackJsonp||[]).push([[940],{1295:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1820-chien-hoa-tu-co-thanh"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1820-chien-hoa-tu-co-thanh"}},[n._v("#")]),n._v(" Chương 1820: Chiến Hỏa Tư Cổ Thánh")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1820: Chiến Hỏa Tư Cổ Thánh"),t("br"),t("br"),n._v("Ầm!"),t("br"),t("br"),n._v("Sơn động đung đưa, dung nham tựa như sôi trào, không ngừng cuồn cuộn, Hỏa Tư Cổ Thánh hai mắt híp lại, thanh âm bên trong mang theo nghiền nát thế giới lực lượng: “Ngươi lặp lại lần nữa?”"),t("br"),t("br"),n._v("“Ngươi muốn làm gì?”"),t("br"),t("br"),n._v("Mặc Linh Cổ Thánh vội vàng hướng về phía trước."),t("br"),t("br"),n._v("Hội trưởng trực tiếp như vậy, hắn cũng bất ngờ, có điều, đã lấy đối phương cầm đầu, tự nhiên làm cái gì, nói cái gì, cũng sẽ không lui về phía sau."),t("br"),t("br"),n._v("“Đã sớm nghe nói qua các ngươi vu hồn sư ngạo mạn, năm đó không phục Khổng sư, vì vậy dẫn đến Danh Sư đường đồ sát toàn tộc, cuối cùng chạy trốn tới nơi này... Vốn cho rằng là cái truyền thuyết, hiện tại xem ra hẳn là thật!”"),t("br"),t("br"),n._v("Tiến về phía trước một bước, sóng nhiệt bốn phía khuấy động, Hỏa Tư Cổ Thánh lần nữa cười lạnh, trên người hơi nóng, muốn đem không gian lôi kéo thành mảnh vỡ."),t("br"),t("br"),n._v("Làm Tổ làm tông mấy ngàn năm, còn lần đầu tiên, có người dám chạy tới, nói muốn thu nó vì thú sủng, lúc trước Thần Dung Hoàng, cũng không dám nói như vậy nói chuyện, một cái Bất Hủ cảnh tiểu tử... Lá gan thật là lớn!"),t("br"),t("br"),n._v("Không dạy dỗ một hồi, mặt mũi ở đâu?"),t("br"),t("br"),n._v("“Chúng ta vu hồn sư, làm cái gì, còn chưa tới phiên ngươi tới xen vào!”"),t("br"),t("br"),n._v("Mặc Linh Cổ Thánh hừ lạnh."),t("br"),t("br"),n._v("“Vậy sao?”"),t("br"),t("br"),n._v("To lớn đầu lâu nhìn lại, một đạo hỏa quang, lập tức lật đổ mà xuống, Mặc Linh Cổ Thánh đồng tử co rụt lại, hai tay nâng lên."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Hỏa diễm cùng bàn tay đối đầu, Mặc Linh Cổ Thánh dưới chân xuất hiện một cái hố sâu to lớn, lui về sau mấy chục bước."),t("br"),t("br"),n._v("Không nghĩ tới cái tên này nói ra tay liền ra tay, không có chút nào dấu hiệu, Mặc Linh Cổ Thánh khí sắp nổ tung, thân thể chợt lóe, liền vọt lên, người giữa không trung lực lượng tự nhiên, trong sơn động, nhất thời âm phong vù vù, như là muốn kết lên hàn băng."),t("br"),t("br"),n._v("Vu hồn sư là sợ lửa ngọn lửa, nhưng học được hội trưởng chỉ điểm pháp quyết, loại này cảm giác áp bách đã biến mất, chân chính đối chiến, cũng chưa chắc thất bại!"),t("br"),t("br"),n._v("“Vẫn là ta tới đi!”"),t("br"),t("br"),n._v("Còn chưa tới đến trước mặt, thanh âm nhàn nhạt vang lên, Mặc Linh Cổ Thánh ngừng lại, nhìn về phía cách đó không xa quái vật khổng lồ, một mặt cảnh giác."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Trương Huyền hướng về phía trước, đi tới dung nham trước mặt, nhìn về phía trên không huyền phù Hỏa Tư Cổ Thánh: “Như vậy, ngươi ta đánh cược như thế nào?”"),t("br"),t("br"),n._v("Hỏa Tư Cổ Thánh cũng không trả lời, chỉ là căng chằm chằm tới."),t("br"),t("br"),n._v("Mặc Linh Cổ Thánh nó chưa thấy qua, lại nghe nói qua tin đồn, thực lực mạnh mẽ, cao ngạo vô cùng, giờ phút này nhưng trung thành như vậy nghe theo vị này dặn dò, khẳng định có lấy hơn người thủ đoạn."),t("br"),t("br"),n._v("“Ta cá cược rất đơn giản, ta cùng ngươi chiến đấu, ngươi thua, thần phục với ta, làm ta thú sủng. Ngươi thắng, ta đem Thần Dung Hoàng mang tới, để hắn tự thân đồng ý, đối với các ngươi Thánh thú có lợi điều kiện!”"),t("br"),t("br"),n._v("Trương Huyền cười nhạt một tiếng: “Như thế nào? Chẳng lẽ ta loại thực lực này, ngươi đều không dám nghênh chiến a?”"),t("br"),t("br"),n._v("“Hừ!”"),t("br"),t("br"),n._v("Hỏa Tư Cổ Thánh hừ lạnh, to lớn móng vuốt lăng không đập tới: “Liền để ta lĩnh hội một chút, vu hồn sư hội trưởng chân chính thủ đoạn!”"),t("br"),t("br"),n._v("Ầm!"),t("br"),t("br"),n._v("Móng vuốt ở dưới không gian vỡ vụn ra, lực lượng giống như thực chất, đem Trương Huyền vị trí, toàn bộ phong tỏa."),t("br"),t("br"),n._v("Mặc Linh Cổ Thánh nắm đấm xiết chặt, mang trên mặt lo lắng."),t("br"),t("br"),n._v("Đối phương chiêu này uy lực, liền xem như hắn, đều khó mà chống lại."),t("br"),t("br"),n._v("Chẳng lẽ... Vị này Hỏa Tư Cổ Thánh, mấy ngàn năm không thấy, tu vi lần nữa tăng lên?"),t("br"),t("br"),n._v("“Là mượn địa lợi...”"),t("br"),t("br"),n._v("Sau khi hết khiếp sợ, rất nhanh hiểu được."),t("br"),t("br"),n._v("Cũng không phải là thực lực của đối phương chợt tăng bao nhiêu, tu vi đạt tới bọn họ loại cảnh giới này, dù là một tia đột phá, đều rất khó."),t("br"),n._v("Sở dĩ thoạt nhìn mạnh mẽ, chủ yếu là bởi vì, đối phương mượn trong sơn động, dung nham lực lượng, để hỏa diễm cự thú bản thân ưu thế phát huy càng thêm tràn trề."),t("br"),t("br"),n._v("Có thể nói, nằm trong loại trạng thái này đối phương, hắn đã không phải là đối thủ."),t("br"),t("br"),n._v("Nhiều nhất có thể chạy trốn."),t("br"),t("br"),n._v("Hắn đều chiến thắng có điều, hội trưởng đâu?"),t("br"),t("br"),n._v("Vội vàng nhìn lại, ngay sau đó nhìn thấy hội trưởng, nhẹ nhàng cười một tiếng, bàn tay run lên, trường thương xuất hiện tại lòng bàn tay."),t("br"),t("br"),n._v("Như là đạp phá mà đi, giẫm lên lan ra mà lên dung nham, hướng lên phía trên bay tới, trường thương trong tay run lên, nhân thương hợp nhất, tựa như trong nháy mắt biến thành một đầu bay lượn cửu thiên cự long, hướng về phía phía trên móng vuốt liền vọt tới."),t("br"),t("br"),n._v("Ầm!"),t("br"),t("br"),n._v("Rồng trên không trung, phát ra một tiếng ngắn gọn kêu to, toàn bộ sơn động ở trong thanh âm, xuất hiện từng đạo vết rách, vô số đá vụn từ bên trên bay thấp mà xuống."),t("br"),t("br"),n._v("“Đây là... Thiên Long Bát Âm?”"),t("br"),t("br"),n._v("Mặc Linh Cổ Thánh dại ra."),t("br"),t("br"),n._v("Cái này long khiếu, chỉ ở trong thư tịch thấy qua, nghe nói là Chân Long có thể phát ra gầm rú, vô số sinh linh, không chịu nổi hét một tiếng chi uy, liền sẽ bị tại chỗ chấn vỡ tâm trí, trở nên điên điên khùng khùng."),t("br"),t("br"),n._v("Long Cốt thần thương không phải mới vừa đột phá ư?"),t("br"),t("br"),n._v("Hơn nữa huyết mạch không thuần... Làm sao lại phát ra loại này gầm rú?"),t("br"),t("br"),n._v("Lộc cộc lộc cộc lộc cộc lộc cộc!"),t("br"),t("br"),n._v("Trong lòng của hắn rung động, đối diện Hỏa Tư Cổ Thánh dường như cũng không dám tin tưởng, kìm lòng không được lui về phía sau mấy bước, ánh mắt lộ ra ngạc nhiên."),t("br"),t("br"),n._v("Cứ việc nó không có Long tộc huyết mạch, Thiên Long Bát Âm không có trên bản chất áp bức, có thể loại thanh âm này từ một con rồng trên người phát ra, vạn thú chi Hoàng mang tới chấn nhiếp, vẫn là để tâm thần xuất hiện vết rách, không dám mặt trước ngăn cản."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Vừa mới lui về phía sau, ngay sau đó liền thấy trước mắt Hắc Long, to lớn đuôi khuấy động, phía dưới dung nham, đồng dạng quay cuồng lên, như cơn lốc cuốn lên, tạo thành mấy chục đầu dung nham Hỏa Long."),t("br"),t("br"),n._v("Lay động đầu, lúc này mới thấy rõ, cũng không phải là Hắc Long lực lượng, mà là thanh niên đứng tại chỗ, trường thương trong tay xoay tròn, dung nham tại thân thương nhanh chóng khuấy động bên dưới, từng đầu cự long tại khuấy động bên dưới, hướng nó vọt tới."),t("br"),t("br"),n._v("“Đây là ta địa phương, dùng ta đồ vật đối phó ta, ngươi nghĩ quá đơn giản!”"),t("br"),t("br"),n._v("Không nghĩ tới bản thân chiến trường chính, bị đối phương mượn dùng, hơn nữa phát huy ra cường đại như thế sức chiến đấu, Hỏa Tư Cổ Thánh thô to cái mũi, phun ra một đám lửa, hai chân nâng lên, hướng phía dưới một bước."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Nhất đạo màu vàng kim gợn sóng hướng bốn phía nhộn nhạo mà đi, vô số dung nham cự long, tới vừa tiếp xúc, lập tức nhao nhao nứt ra, lần nữa vẽ dịch giọt, rơi vào phía dưới."),t("br"),t("br"),n._v("Nơi này nó đợi mấy ngàn năm, bên trong địa hỏa dung nham, đều là bản thân thiên chuy bách luyện qua, tuy là trong thời gian ngắn, nghe theo đối phương chỉ huy, chỉ cần nguyện ý, vẫn là tuỳ tiện có thể vì đó khống chế."),t("br"),t("br"),n._v("“Vậy sao?”"),t("br"),t("br"),n._v("Thu thương mà đứng, Trương Huyền mỉm cười, cũng không bởi vì đối phương lần nữa khống chế dung nham mà cảm thấy chán nản, ngược lại mang theo nồng đậm tự tin."),t("br"),t("br"),n._v("Hỏa Tư Cổ Thánh lần nữa nhìn qua, trong mắt không còn lúc trước coi thường: “Có thể trở thành vu hồn sư hội trưởng, quả nhiên không đơn giản... Bất quá, thực lực của ngươi, quả thực chỉ có Bất Hủ cảnh đại viên mãn, sở dĩ có thể phát huy ra cường đại như thế sức chiến đấu, là bởi vì, chuôi này trường thương a?”"),t("br"),t("br"),n._v("Vừa giao thủ, nó liền nhìn ra, cái này cái gọi là hội trưởng, thực lực chân thật, cũng không mạnh, có thể cùng hắn chiến đấu, chủ yếu bởi vì chuôi này thương."),t("br"),t("br"),n._v("“Không sai!”"),t("br"),t("br"),n._v("Trương Huyền cũng không phủ nhận."),t("br"),t("br"),n._v("“Vũ khí không có chủ nhân, không phát huy ra mạnh nhất sức chiến đấu, ngươi có thể thi triển ra lợi hại như vậy tuyệt chiêu, để cho ta đều suýt chút nữa rơi vào chật vật, quả thực rất tinh mắt cùng kinh nghiệm chiến đấu...”"),t("br"),t("br"),n._v("Gặp hắn thừa nhận, Hỏa Tư Cổ Thánh thoả mãn gật gật đầu, ngay sau đó khẽ nói: “Bất quá, chỉ là như thế, muốn thắng ta... Vẫn là nghĩ quá đơn giản!”"),t("br"),t("br"),n._v("Tiếng nói kết thúc, phía dưới dung nham lần nữa sôi trào lên, từ dưới đất không ngừng dâng lên mà ra, tựa như thủy triều."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1831"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1833"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);