(window.webpackJsonp=window.webpackJsonp||[]).push([[941],{1296:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1821-hoa-tu-co-thanh-ngao-khi"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1821-hoa-tu-co-thanh-ngao-khi"}},[n._v("#")]),n._v(" Chương 1821: Hỏa Tư Cổ Thánh ngạo khí")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1821: Hỏa Tư Cổ Thánh ngạo khí"),t("br"),t("br"),n._v("Không chỉ có như vậy, sơn động phía trên, cũng có vô số hỏa diễm rơi xuống, từng mảnh từng mảnh đem không gian bốn phía, đốt thành liệt diễm Địa Ngục."),t("br"),t("br"),n._v("Mặc Linh Cổ Thánh liên tiếp lui về phía sau."),t("br"),t("br"),n._v("Tuy là đạt được hội trưởng công pháp, để phòng ngự hỏa diễm năng lực, tăng lên một ít, có thể cái này đầy trời biển lửa, vẫn như cũ không đỡ được, may mắn tại thân thể vẫn còn, hồn thể đi ra mà nói, chỉ sợ đã bị thương!"),t("br"),t("br"),n._v("“Hội trưởng... Không có sao chứ!”"),t("br"),t("br"),n._v("Trên mặt biến ảo không ngừng, tràn đầy lo lắng, ngay sau đó nghe được cười to vang lên, ngay sau đó liền thấy hội trưởng thân ảnh, yên tĩnh huyền phù trên không, chẳng những không có né tránh, ngược lại hít sâu một hơi."),t("br"),t("br"),n._v("“Đến hay lắm!”"),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Kèm theo thanh âm, một cỗ to lớn hấp lực, từ trên người hắn lan ra, tựa như cá voi hút nước, ngân hà đổ ngược, lửa cháy ngập trời, hướng trên người hắn cuồng dũng tới."),t("br"),t("br"),n._v("Mà hắn thân thể bỗng nhiên tăng vọt, cũng đạt tới cao hơn mười mét thấp, dưới chân giẫm lên dung nham, toàn thân đỏ thẫm, tựa như một đầu to lớn hỏa diễm cự nhân."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Hỏa Tư Cổ Thánh hiển nhiên cũng không nghĩ tới hắn cường đại như thế, sửng sốt một chút, đang muốn thi triển tuyệt chiêu, liền thấy tràn vào thanh niên trong cơ thể hỏa diễm, bay ngược mà ra."),t("br"),t("br"),n._v("Hừng hực!"),t("br"),t("br"),n._v("Màu đỏ thắm hỏa diễm, từ trong cơ thể hắn dâng trào đi ra, gặp phải không khí kịch liệt thiêu đốt, rất nhanh nhiệt độ càng ngày càng cao, trở nên đen thui, dần dần tạo thành màu mực."),t("br"),t("br"),n._v("“Là... Chí tôn thiên hỏa?”"),t("br"),t("br"),n._v("Cảm nhận được hắc hỏa lộ ra nhiệt độ, Hỏa Tư Cổ Thánh da đầu nổ tung, thân thể to lớn, kìm lòng không được lui về phía sau."),t("br"),t("br"),n._v("Thân là hỏa diễm cự thú, lấy lửa mà sống, thiên hỏa kiếp tự nhiên trải qua, đối với những khác tu luyện giả, kinh khủng như vậy, nhưng đối với nó tới nói, chỉ thường thôi."),t("br"),t("br"),n._v("Có thể... Những cái kia chỉ là bình thường thiên hỏa, loại này chí tôn thiên hỏa, coi như hỏa diễm cự thú, cũng chống lại không được!"),t("br"),t("br"),n._v("Xì xì xì xì...!"),t("br"),t("br"),n._v("Toàn thân lông tóc chạm đến thoáng cái, giống như là bị thiêu đốt, phát ra đốt trụi mùi vị, Hỏa Tư Cổ Thánh lần nữa lui về phía sau."),t("br"),t("br"),n._v("Đối mặt thi triển ra chiêu số, nó... Sợ hãi!"),t("br"),t("br"),n._v("Vốn cho rằng, đối phương là vu hồn sư, thiên tính âm hàn, sợ hãi Thuần Dương lực lượng, mượn hỏa diễm, tùy tiện có thể gây khó dễ, nằm mơ cũng không ngờ tới... Bản thân am hiểu nhất hỏa diễm, đi vào đối phương trong cơ thể, vậy mà tạo thành đáng sợ như vậy đồ vật!"),t("br"),t("br"),n._v("Chí tôn thiên hỏa, không gì không thiêu cháy, cho dù thân là tích huyết trùng sinh cường giả, cũng không dám trực tiếp đối mặt."),t("br"),t("br"),n._v("“Ngươi không nói, điểm ấy thủ đoạn đối phó ngươi, quá đơn giản ư? Vậy cũng chớ lui!”"),t("br"),t("br"),n._v("Thấy nó lập tức liền muốn kề đến sơn động vách đá, Trương Huyền lần nữa cười một tiếng, trường thương trong tay phá không mà đi."),t("br"),t("br"),n._v("Ô ô!"),t("br"),t("br"),n._v("Hắc Long gào thét, đầy trời hắc hỏa, tại nó khua bên dưới, cuồn cuộn không ngừng, mây đen áp thành thành muốn phá vỡ."),t("br"),t("br"),n._v("Mặc Linh Cổ Thánh lúc này đã chạy trốn tới bên ngoài sơn động."),t("br"),t("br"),n._v("Bình thường hỏa diễm hắn đều không chịu nổi, huống chi chí tôn thiên hỏa."),t("br"),t("br"),n._v("Dùng Hỏa Tư Cổ Thánh am hiểu nhất hỏa diễm, ép đối phương sắp phát điên, hội trưởng đến cùng làm sao làm được?"),t("br"),t("br"),n._v("Thật ra thì Trương Huyền vốn cũng không nghĩ tới có thể như vậy, nhưng đối phương thi triển ra hỏa diễm về sau, đi qua chí tôn thiên hỏa rèn luyện thân thể cùng linh hồn, đột nhiên run rẩy lên, tựa hồ đối với những này mười phần khát vọng."),t("br"),t("br"),n._v("Ngay sau đó đem hỏa diễm hút vào trong cơ thể, phối hợp thêm Thiên đạo chân khí, vậy mà trực tiếp đem hắn diễn biến thành chí tôn thiên hỏa!"),t("br"),t("br"),n._v("Nếu như đối phương thi triển ra là bình thường hỏa diễm, Thiên đạo chân khí chính là khí oxy, gia trì về sau, lập tức sôi trào, bộc phát ra cao hơn nhiệt lượng."),t("br"),t("br"),n._v("Thình thịch thình thịch bành!"),t("br"),t("br"),n._v("Bị ngọn lửa màu đen gắt gao đè ở trên vách tường, Hỏa Tư Cổ Thánh toàn thân lông tóc đều bị đốt trụi, máu tươi cũng bắt đầu dần dần bốc hơi, có nhiều chỗ lộ ra kim sắc xương cốt."),t("br"),n._v(" Xem ra, chỉ cần tiếp tục nữa, một đời dùng lửa vi tôn Thánh thú lão tổ, sẽ bị ngọn lửa, đốt thành tro bụi."),t("br"),t("br"),n._v("“Thần phục hay không?”"),t("br"),t("br"),n._v("Nhẹ nhàng cười một tiếng, Trương Huyền nhìn lại."),t("br"),t("br"),n._v("“Trừ phi giết ta...”"),t("br"),t("br"),n._v("Hỏa Tư Cổ Thánh cắn răng."),t("br"),t("br"),n._v("Thân là Thánh thú lão tổ, tích huyết trùng sinh cấp bậc siêu cấp cường giả, nắm giữ thuộc về mình kiêu ngạo."),t("br"),t("br"),n._v("Thật muốn thần phục một cái Bất Hủ cảnh đại viên mãn tiểu nhân vật, về sau còn có loại nào mặt mũi, thống lĩnh tộc nhân, an tâm làm thú?"),t("br"),t("br"),n._v("“Nói như vậy lên, ngươi là không thực hiện cá cược?” Trương Huyền nhìn qua."),t("br"),t("br"),n._v("“Cá cược là ngươi, ta cũng không đồng ý...” Hỏa Tư Cổ Thánh thân thể run rẩy."),t("br"),t("br"),n._v("Đối phương mới vừa rồi là nói cá cược, nhưng nó không có đồng ý liền bắt đầu động thủ."),t("br"),t("br"),n._v("Nói cách khác, cái gọi là đánh cược, không tính là gì."),t("br"),t("br"),n._v("“Ngươi thật muốn chết?”"),t("br"),t("br"),n._v("Trường thương liên tục công kích, thiên hỏa thiêu đốt bên dưới, Hỏa Tư Cổ Thánh thương thế càng ngày càng nặng, lại không chút nào thần phục ý tứ, Trương Huyền nhíu nhíu mày."),t("br"),t("br"),n._v("Hắn đánh thuần thú pháp, không riêng gì bởi vì thực lực vượt qua Thánh thú, càng quan trọng hơn là Thiên đạo chân khí, đối hắn có tẩm bổ tác dụng, có thể cải thiện huyết mạch, để hắn tăng lên cấp bậc."),t("br"),t("br"),n._v("Cũng chính là cái gọi là, đại bổng thêm mứt táo."),t("br"),t("br"),n._v("Bây giờ muốn thuần phục Cổ Thánh, dựa vào Long Cốt thần thương cùng thiên hỏa, quả thực đối phương khó mà chống lại, có thể... Thiên đạo chân khí hiệu quả, liền hầu như không còn."),t("br"),t("br"),n._v("Không còn cái này, chỉ bằng vào uy hiếp... Hiệu quả liền yếu đi rất nhiều."),t("br"),t("br"),n._v("Huống chi đối phương là Thánh thú lão tổ, sớm đã tự do đã quen, thà chết, cũng không nguyện ý trở thành nhân loại phụ thuộc."),t("br"),t("br"),n._v("“Động thủ đi! Bất quá, ta bọn đồ tử đồ tôn, cũng không đắc tội ngươi, ngươi đường đường vu hồn sư công hội hội trưởng, Cổ Thánh sức chiến đấu cường giả, chắc hẳn, cũng sẽ không bỏ xuống dáng vẻ, đối phó bọn chúng!”"),t("br"),t("br"),n._v("Hỏa Tư Cổ Thánh cắn răng."),t("br"),t("br"),n._v("Tài nghệ không bằng người, chết thì chết tai, nhưng phía ngoài rất nhiều đồ tử đồ tôn, là Thánh thú nhất tộc tương lai, nếu như cái tên này, trắng trợn sát lục, thật sự không có người có thể chống lại."),t("br"),t("br"),n._v("Bất quá, Cổ Thánh đối phó Cổ Thánh, bình thường tu luyện giả đối phó bình thường tu luyện giả, chắc hẳn vị này, không đến mức bỏ đi mặt mũi, đi làm loại này chuyện vô sỉ."),t("br"),t("br"),n._v("“Đồ tử đồ tôn?”"),t("br"),t("br"),n._v("Không nghe lời này còn tốt, nghe nói như thế, Trương Huyền đột nhiên ánh mắt sáng lên, nói: “Ta cũng không phải là Cổ Thánh, chỉ là Bất Hủ cảnh đại viên mãn, không có quá nhiều dáng vẻ... Ngươi cần phải nghĩ kỹ, thần phục với ta, ta có thể mang các ngươi Thánh thú nhất tộc, rời đi nơi này, thoát ly khốn cảnh, không đồng ý... Ngươi chết một lần, coi như ta không động thủ, Thánh thú nhất tộc, cũng khó tránh khỏi gặp phải Linh tộc vây công!”"),t("br"),t("br"),n._v("“Chết sống có số, giàu có nhờ trời! Ta có thể bảo vệ bọn chúng nhất thời, không bảo vệ được bọn chúng vĩnh viễn... Nếu quả thật nếu là như vậy, ta cũng không thể nói gì hơn!”"),t("br"),t("br"),n._v("Hỏa Tư Cổ Thánh cắn răng."),t("br"),t("br"),n._v("Nhìn đối phương thái độ kiên quyết, căn bản không có khả năng thần phục, Trương Huyền cau mày, một lát sau, nhớ tới cái gì, con mắt đột nhiên sáng lên, mỉm cười: “Ngươi không thần phục tại ta, là bởi vì phía sau ngươi có một chủng tộc, ngươi là bọn họ lão tổ, không có khả năng trở thành người khác thú sủng, nhưng nếu như... Tộc nhân của ngươi, đều nguyện ý để ngươi làm sủng vật của ta đâu?”"),t("br"),t("br"),n._v("“Tộc nhân của ta, từng cái cao ngạo đều, chính mình cũng sẽ không thần phục với người, để cho bọn họ đồng ý ta thần phục? Nằm mơ...”"),t("br"),t("br"),n._v("Không nghĩ tới, đối phương nói như vậy, Hỏa Tư Cổ Thánh chẳng thèm ngó tới."),t("br"),t("br"),n._v("Đầu óc không có hỏng a, tộc nhân của mình coi như ngu nữa, cũng không có khả năng, đồng ý lão tổ trở thành thú sủng a?"),t("br"),t("br"),n._v("Thật muốn như vậy, tôn nghiêm ở đâu?"),t("br"),t("br"),n._v("“Cái này chưa chắc!” Nhẹ nhàng cười một tiếng, Trương Huyền bàn tay run lên, Long Cốt thần thương xuất hiện tại lòng bàn tay, quay đầu dặn dò: “Mặc Linh Cổ Thánh, ngươi ở đây bảo vệ Hỏa Tư, đừng để nó rời đi, ta đi ra ngoài một chuyến, chẳng mấy chốc sẽ trở về...”"),t("br"),t("br"),n._v("Nói xong, thân thể nhảy lên, biến mất tại nguyên chỗ."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1832"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1834"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);