(window.webpackJsonp=window.webpackJsonp||[]).push([[944],{1298:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1824-chuan-bi-tro-ve-vuong-thanh"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1824-chuan-bi-tro-ve-vuong-thanh"}},[n._v("#")]),n._v(" Chương 1824: Chuẩn bị trở về vương thành")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Vòng qua mấy cái đỉnh núi, rất mau nhìn đến khắp núi khắp nơi Thánh thú, huyền phù tại cách đó không xa trước một hang núi, dị thường chỉnh tề, tựa như quân đội."),t("br"),t("br"),n._v("“Nơi này không phải linh khí mỏng manh ư? Đám thánh thú này, vì sao tinh khí thần như vậy thống nhất, chiến lực so trước đó thấy qua còn cường đại hơn không ít?”"),t("br"),t("br"),n._v("Thần Dung Hoàng lần nữa giật mình."),t("br"),t("br"),n._v("Năm đó xua đuổi đám thánh thú này thời điểm, từng dẫn dắt quân đội tới chiến đấu qua, đám thánh thú này cứ việc không yếu, nhưng còn xa không bằng hiện tại."),t("br"),t("br"),n._v("Vốn cho rằng trục xuất tới nơi này, không suy yếu là tốt lắm rồi, không nghĩ tới thế mà trở nên cường đại như vậy."),t("br"),t("br"),n._v("Ngay tại rung động, liền thấy bên ngoài sơn động không gian một hồi vặn vẹo, hai bóng người bất ngờ xuất hiện."),t("br"),t("br"),n._v("Đi đầu một cái, toàn thân hỏa diễm thiêu đốt, trên người bộ lông màu vàng óng, để lộ ra màu đen hoa văn, đang có lấy hướng màu đen chuyển hóa xu thế."),t("br"),t("br"),n._v("Một thân tu vi, kèm theo hỏa diễm, cho người ta một loại đun sôi thời không mùi vị."),t("br"),t("br"),n._v("“Thực lực của nó...”"),t("br"),t("br"),n._v("Nắm đấm xiết chặt, Thần Dung Hoàng vẻ mặt nghiêm túc."),t("br"),t("br"),n._v("Trước mắt vị này, chính là muốn tìm vị kia Hỏa Tư Cổ Thánh, năm đó bị bản thân đả thương, tu vi thế nào biết hết sức rõ ràng, vốn cho rằng lại khó tiến bộ, nằm mơ đều không có nghĩ đến, đạt đến loại cảnh giới này!"),t("br"),t("br"),n._v("So với hắn toàn thịnh kỳ, đều không kém nhiều!"),t("br"),t("br"),n._v("Đi tới vắng vẻ chỗ, không chỉ tộc nhân thực lực không bị tổn thương, thực lực bản thân còn trở nên mạnh hơn..."),t("br"),t("br"),n._v("Chuyện gì xảy ra?"),t("br"),t("br"),n._v("Lúc trước còn cho rằng, dựa vào mấy người bọn họ, Hiểu chi lấy lý, lấy tình động, có lẽ có thể lôi kéo tới, gia nhập trận doanh, nhìn thấy thực lực của nó, nhất thời cảm thấy lại không còn hi vọng."),t("br"),t("br"),n._v("“Vị kia chính là Mặc Linh Cổ Thánh? Làm sao tiến bộ nhiều như vậy, ba năm trước đây ta gặp qua một lần, không có lợi hại như vậy ah!”"),t("br"),t("br"),n._v("Cách đó không xa một vị Cổ Thánh đè thấp thanh âm vang lên."),t("br"),t("br"),n._v("Thần Dung Hoàng lúc này mới chú ý tới Hỏa Tư Cổ Thánh bên người vị kia Cổ Thánh, vừa nhìn xuống, lông mày cũng kìm lòng không được nhảy lên."),t("br"),t("br"),n._v("Mặc Linh Cổ Thánh hắn cũng đã gặp, vu hồn tiến bộ lên càng khó, nhưng bây giờ... Tu vi cũng đạt tới làm người ta không thể tưởng tượng tình trạng..."),t("br"),t("br"),n._v("Ngây thơ muốn vong ta?"),t("br"),t("br"),n._v("“Cảm ơn chủ nhân tái sinh chi ân! Chúng ta nguyện đi theo chủ nhân, xông pha khói lửa, không chối từ!”"),t("br"),t("br"),n._v("Trên không Hỏa Tư Cổ Thánh, cũng không phát hiện ẩn giấu ở sơn phong phía sau Thần Dung Hoàng đám người, mà là khẽ khom người hướng về phía sơn động ôm quyền."),t("br"),t("br"),n._v("Nhận chủ về sau, không có gấp rời đi, mà là mượn lòng đất dung nham chữa thương."),t("br"),t("br"),n._v("Sử dụng chủ nhân phương pháp, không chỉ thương thế rất nhanh khôi phục, tu vi dường như có tiến thêm một bước xu thế, để nó không nói được kích động, đối vị này trẻ tuổi chủ nhân, ý cảm kích càng đậm."),t("br"),t("br"),n._v("“Không cần khách khí!”"),t("br"),t("br"),n._v("Thanh âm nhàn nhạt vang lên, một bóng người từ trong sơn động chậm rãi bay ra."),t("br"),t("br"),n._v("Nhìn thấy thân ảnh này, Thần Dung Hoàng chấn động mạnh một cái."),t("br"),t("br"),n._v("“Hắn là thiếu gia...”"),t("br"),t("br"),n._v("Vốn cho rằng là Thần Linh Hoàng, muốn tìm cơ hội đánh lén ra tay, tới đồng quy vu tận, khi thấy rõ xuất hiện dung mạo, Thần Dung Hoàng bối rối."),t("br"),t("br"),n._v("t r u y e n c u a t u i n Ｅ t"),t("br"),n._v("Chính là đi vương thành tìm kiếm long huyết vị thiếu gia kia!"),t("br"),t("br"),n._v("Không phải là đi Thần Linh Hoàng hoàng cung ư?"),t("br"),t("br"),n._v("Làm sao lại ở đây..."),t("br"),t("br"),n._v("Chẳng lẽ, đối phương nói tới người chủ nhân kia là hắn?"),t("br"),t("br"),n._v("Đã đem Hỏa Tư Cổ Thánh thuần phục?"),t("br"),t("br"),n._v("Đường đường Dị Linh tộc đệ nhất Hoàng giả, từ trước tới nay trí tuệ Vô Song, mà bây giờ, tựa như đứng máy đồng dạng, đầu óc không chuyển động được nữa."),t("br"),t("br"),n._v("“Đã tới, liền đến đi!”"),t("br"),t("br"),n._v("Ngay tại khiếp sợ, tiếng cười vang lên, Trương Huyền nhìn lại."),t("br"),t("br"),n._v("Hỏa Tư Cổ Thánh đám người không có phát hiện, Trương Huyền nhưng cảm nhận được nguyên từ lực, Minh Lý Chi Nhãn xoay một cái, đã phát hiện Thần Dung Hoàng đám người."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Gặp bị phát giác, lại không che giấu, thẳng tắp bay tới."),t("br"),n._v(" “Dung Hoàng...”"),t("br"),t("br"),n._v("Thấy là đối thủ cũ, Hỏa Tư Cổ Thánh híp mắt lại, lửa giận thiêu đốt, bất cứ lúc nào cũng sẽ bùng nổ."),t("br"),t("br"),n._v("“Đừng động thủ!”"),t("br"),t("br"),n._v("Trương Huyền xua tay."),t("br"),t("br"),n._v("“Vâng!” Chủ nhân dặn dò, Hỏa Tư Cổ Thánh không dám vi phạm, nhưng trong lòng vẫn như cũ tức giận, đang kỳ lạ chủ nhân vì sao muốn ngăn cản, liền thấy vị này Dị Linh tộc đệ nhất Hoàng giả, đi tới trước mặt, ôm quyền khom người: “Thiếu gia!”"),t("br"),t("br"),n._v("“Thiếu gia...”"),t("br"),t("br"),n._v("Con mắt trợn tròn, Hỏa Tư Cổ Thánh vội vàng nhìn về phía Mặc Linh Cổ Thánh, chỉ thấy hắn cũng không hiểu ra sao, dường như khó tin."),t("br"),t("br"),n._v("Xưng hô thiếu gia, cũng chính là hạ nhân... Tuy là địa vị so người hầu cao hơn một điểm, nhưng cũng không kém nhiều!"),t("br"),t("br"),n._v("Đường đường Thần Dung Hoàng, Dị Linh tộc đệ nhất nhân, cam tâm làm xuống người, cái này... Làm sao cảm thấy không đúng đâu?"),t("br"),t("br"),n._v("“Chủ nhân, hắn là ngươi hạ nhân?”"),t("br"),t("br"),n._v("Cũng lại kìm nén không được."),t("br"),t("br"),n._v("Trương Huyền gật gật đầu, cười giới thiệu: “Thần Dung Hoàng gọi ta là thiếu gia, xem như hạ nhân. Dung Hoàng, ta mới vừa thu Hỏa Tư Cổ Thánh vì thú sủng, về sau có cái gì mâu thuẫn, như vậy xoá bỏ.”"),t("br"),t("br"),n._v("Nghe hắn thừa nhận, Thần Dung Hoàng mặc dù khó tin, vẫn là kìm lòng không được gật gật đầu, nhìn về phía cách đó không xa Hỏa Tư Cổ Thánh, một mặt phức tạp: “Chúng ta lại gặp mặt...”"),t("br"),t("br"),n._v("Bọn họ đều là Dị Linh tộc chói mắt nhất cường giả, cả hai giao đấu, không ai không biết, không người không hay, mệnh danh mấy ngàn năm qua, trận chiến mạnh nhất..."),t("br"),t("br"),n._v("Hiện nay, đối chiến đương sự người, một cái thành hạ nhân, một cái thành thú sủng..."),t("br"),t("br"),n._v("Đều triệt để hiệu trung vị này tên là Trương Huyền người trẻ tuổi."),t("br"),t("br"),n._v("Suy nghĩ một chút đều làm người thổn thức."),t("br"),t("br"),n._v("“Ừm...” Hỏa Tư Cổ Thánh gật đầu."),t("br"),t("br"),n._v("Vốn cho rằng, chủ nhân cùng nó nói, muốn dẫn dắt tộc nhân quay về, là một câu nói suông, nhìn thấy Thần Dung Hoàng đều là hạ nhân, giờ mới hiểu được... Là thật."),t("br"),t("br"),n._v("“Dung Hoàng, ta đã đồng ý Hỏa Tư Cổ Thánh, để nó giúp ngươi đoạt lại thứ thuộc về chính mình, mà ngươi cũng phải hứa hẹn, Thánh thú cùng Dị Linh tộc nhân, cùng sinh tồn, không đem xem như dị loại!”"),t("br"),t("br"),n._v("Trương Huyền nhìn tới."),t("br"),t("br"),n._v("“Xin nghe thiếu gia dặn dò!”"),t("br"),t("br"),n._v("Thần Dung Hoàng vội vàng gật đầu."),t("br"),t("br"),n._v("Hiện tại, coi như muốn phản bác, cũng không có tư cách."),t("br"),t("br"),n._v("Khó trách có thể được đến Linh Thần ưu ái, vị thiếu gia này, quả thực quá cường đại!"),t("br"),t("br"),n._v("Liền hắn đều kiêng dè không thôi Hỏa Tư Cổ Thánh, thời gian nháy mắt, liền bị thuần phục, khéo léo như thế... Thật không biết làm được bằng cách nào."),t("br"),t("br"),n._v("“Tốt, hiện tại đã nắm giữ cùng Linh Hoàng, Tinh Hoàng quyết đấu thực lực, cũng nên đi vương thành!”"),t("br"),t("br"),n._v("Biết một người một thú, muốn triệt để hóa giải ở giữa mâu thuẫn, còn cần không biết bao lâu, Trương Huyền cũng không thúc giục, mở miệng nói."),t("br"),t("br"),n._v("Bọn hắn hiện tại, Cổ Thánh tam trọng cấp bậc có bốn vị, Thần Dung Hoàng, Bặc Huân Cổ Thánh, Mặc Linh Cổ Thánh cùng Hỏa Tư Cổ Thánh, lại thêm Long Cốt thần thương cùng Ngoan Nhân mà nói, đã đạt đến sáu vị nhiều."),t("br"),t("br"),n._v("Cổ Thánh nhị trọng cũng có bốn cái."),t("br"),t("br"),n._v("Tính cả luyện hóa những cái kia Vô Hồn Kim Nhân, Cổ Thánh cấp bậc bên này đã có hơn mười vị, so với Thần Linh Hoàng bên kia chỉ mạnh không yếu."),t("br"),t("br"),n._v("Coi như người sau thêm vào Tinh Hoàng, cũng không có bất kỳ sợ hãi."),t("br"),t("br"),n._v("Loại tình huống này, cũng là thời điểm, trở lại vương thành, tuyên bố Thần Dung Hoàng còn sống, vạch trần chân tướng, tới quang minh chính đại đánh một trận!"),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Mọi người tất cả đều vẻ mặt nghiêm túc gật gật đầu."),t("br"),t("br"),n._v("“Các con, các ngươi trước tiên ở nơi này chờ lấy, chờ ta giải quyết vương thành sự tình, liền mang các ngươi trở lại trước kia chỗ ở...”"),t("br"),t("br"),n._v("Biết lần này việc quan hệ quan trọng, Hỏa Tư Cổ Thánh dặn dò một tiếng."),t("br"),t("br"),n._v("Nhiều như vậy Thánh thú, khẳng định không thể mang, dẫn tới cùng Dị Linh nhất tộc đại chiến không nói, làm không cẩn thận, toàn quân bị diệt."),t("br"),t("br"),n._v("Kế trước mắt chính là để hắn ở đây bảo vệ, mọi người trở về, chỉ cần chém giết kẻ cầm đầu Linh Hoàng, Tinh Hoàng, đem chính quyền lần nữa đoạt lại, còn lại liền đơn giản."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1835"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1837"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);