(window.webpackJsonp=window.webpackJsonp||[]).push([[946],{1302:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1826-phu-chung"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1826-phu-chung"}},[n._v("#")]),n._v(" Chương 1826: Phù chủng")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1826: Phù chủng"),t("br"),t("br"),n._v("“Dung Hoàng quả nhiên không có chết?”"),t("br"),t("br"),n._v("“Hắn trở về?”"),t("br"),t("br"),n._v("Tất cả mọi người đồng thời xôn xao."),t("br"),t("br"),n._v("Lúc trước bọn họ nghe Lưu Dương nói một lần, có còn mang trong lòng lo nghĩ, giờ phút này, tất cả đều tan thành mây khói."),t("br"),t("br"),n._v("Dung Hoàng quả nhiên không có chết, nói như vậy, Thần Linh Hoàng nói chính là giả... Sự thực là hắn thông đồng nhân loại, mưu sát hoàng thất, tội không thể tha!"),t("br"),t("br"),n._v("“Mau qua tới!”"),t("br"),t("br"),n._v("Mọi người khiếp sợ, Lưu Dương cũng đầy tâm hỉ duyệt."),t("br"),t("br"),n._v("Người khác không biết, hắn trước thời hạn liền tiếp xúc Dung Hoàng cùng lão sư, biết hai người chuẩn bị gì."),t("br"),t("br"),n._v("Liên hợp tất cả có thể liên hợp thế lực, giờ phút này tới, nói rõ đã tổ chức, có thể chống lại Linh Hoàng đội ngũ, muốn lần nữa đoạt quyền."),t("br"),t("br"),n._v("Vèo!"),t("br"),t("br"),n._v("Cũng lại kìm nén không được, thẳng tắp hướng trong thành bay đi."),t("br"),t("br"),n._v("Những người khác gặp minh chủ tiến lên, không dám do dự theo sát trên đó."),t("br"),t("br"),n._v("Thời gian không dài, đi tới vương thành ở chính giữa, chịu trách nhiệm tế tự địa phương, một tòa đài cao, cao vút trong mây, bốn phía trên bậc thang, máu tươi chảy xuôi, đem hắn khuyếch đại thành đỏ tươi chi sắc, cho người ta một loại yêu dị cảm giác."),t("br"),t("br"),n._v("Hình vòng cầu thang, từng cán lá cờ cắm ở phía trên, bay phần phật theo gió, trên bầu trời trời u ám, vòng xoáy khổng lồ bao phủ, dường như có vô cùng lực lượng hội tụ tại vòng xoáy ở trung tâm, bất cứ lúc nào cũng sẽ tuôn ra mà xuống."),t("br"),t("br"),n._v("Thần Linh Hoàng, đang huyền phù tại trên đài cao, tùy ý cuồng bạo khí lưu, dọc theo thân thể xoay tròn."),t("br"),t("br"),n._v("Đài cao phía dưới, quỳ một mảnh đen kịt Dị Linh tộc nhân, tất cả đều giống như là mất đi ý thức, không nhúc nhích, cộng lại chừng mấy vạn, xem ra, chính là lấy tính mạng của bọn hắn tiến hành hiến tế."),t("br"),t("br"),n._v("Lại đến phương, thì là vô số bảo vật, dược liệu, khoáng thạch... Nhiều vô số kể, chính là lúc trước, Trương Huyền ngụy trang thành Ổ Thao, đi vào giám định những cái kia."),t("br"),t("br"),n._v("“Thần linh, ngươi cùng nhân tộc liên hợp, đối ta tiến hành đánh lén, tin nghĩa tại bất chấp; Đồ sát tộc nhân, tiến hành hiến tế, đưa tiền bối cơ nghiệp tại không còn! Ngày hôm nay, liền là của ngươi giờ chết!”"),t("br"),t("br"),n._v("Thần Linh Hoàng đối diện, Thần Dung Hoàng yên tĩnh huyền phù, nhìn thi thể đầy đất cùng máu tươi, nổ đom đóm mắt."),t("br"),t("br"),n._v("Sớm biết cái tên này điên cuồng, không nghĩ tới điên cuồng như vậy."),t("br"),t("br"),n._v("Liên hợp nhân tộc, giết hắn thì cũng thôi đi, trực tiếp bắt mười vạn tộc nhân, tiến hành hiến tế... Tội ác lớn, tội lỗi chồng chất!"),t("br"),t("br"),n._v("“Giết ta? Chỉ bằng ngươi?”"),t("br"),t("br"),n._v("Cười lạnh, Thần Linh Hoàng trong mắt mang theo khinh miệt."),t("br"),t("br"),n._v("Đổi lại lúc trước Dung Hoàng, hắn có thể sẽ kiêng kị, mà bây giờ, bị thương nặng muốn chết, bản thân ăn bữa hôm lo bữa mai, còn muốn lấy giết người... Nằm mơ!"),t("br"),t("br"),n._v("“Còn có chúng ta đâu?”"),t("br"),t("br"),n._v("Vù vù!"),t("br"),t("br"),n._v("Tiếng gió rít gào, vài bóng người, bất ngờ xuất hiện, chính là Mặc Linh Cổ Thánh, Hỏa Tư Cổ Thánh đám người."),t("br"),t("br"),n._v("“Mặc Linh ngươi cái ăn cây táo rào cây sung đồ vật...”"),t("br"),t("br"),n._v("Linh Hoàng híp mắt lại, lần nữa nhìn về phía Thần Dung Hoàng: “Dung Hoàng, ngươi cùng hắn liên hợp, chắc hẳn không biết, sở dĩ có thể rơi vào ngày hôm nay bộ này cục diện, hắn không thể bỏ qua công lao a?”"),t("br"),t("br"),n._v("Nếu không phải Mặc Linh Cổ Thánh liên hệ Chư Tử bách gia cường giả, chỉ bằng hắn, tuyệt không có khả năng tổn thương Dung Hoàng mảy may."),t("br"),t("br"),n._v("“Hiện tại khiêu khích quan hệ giữa chúng ta, cho rằng hữu dụng sao?”"),t("br"),t("br"),n._v("Dung Hoàng khẽ nói."),t("br"),t("br"),n._v("Mặc Linh Cổ Thánh lúc trước quả thực tại giúp đối phương, nhưng bây giờ đi theo tại thiếu gia sau lưng, mọi người mặt trận thống nhất, lại so đo đã không có cái gì ý nghĩa."),t("br"),t("br"),n._v("Hơn nữa, hắn hiện tại lớn nhất kẻ thù, chính là Linh Hoàng, đem hắn giết, chết cũng nhắm mắt."),t("br"),t("br"),n._v("“Khiêu khích?”"),t("br"),t("br"),n._v("Linh Hoàng đang muốn tiếp tục nói chuyện, ngay sau đó thấy được cách đó không xa một đầu to lớn Thánh thú, vẻ mặt trở nên càng thêm khó coi: “Hỏa Tư Cổ Thánh, năm đó, Dung Hoàng đưa ngươi nhất tộc, đưa ra cố thổ, thù hận không đội trời chung, ngươi chẳng lẽ cũng phải cùng hắn liên hợp? Chỉ cần ngươi giúp ta, ta không chỉ cùng ngươi chung sống hoà bình, các ngươi Thánh thú nhất tộc lúc trước chỗ ở, cũng sẽ triệt để trả lại, thậm chí còn đền bù ngươi những năm này thua thiệt...”"),t("br"),t("br"),n._v("Một vị Mặc Linh Cổ Thánh không cảm thấy cái gì, cũng không sợ, nhưng lại thêm Hỏa Tư, cũng có chút khó giải quyết."),t("br"),n._v(" Hỏa Tư Cổ Thánh, chân chính sức chiến đấu, so với hắn toàn thịnh kỳ, đều chỉ mạnh không yếu, gần với Thần Dung Hoàng, lúc này xông lên, thương thế hắn nặng như vậy, tuyệt đối không phải là đối thủ."),t("br"),t("br"),n._v("“Ta nói qua, Thánh thú nhất tộc có Thánh thú nhất tộc kiêu ngạo, nếu như có thể cùng ngươi liên thủ, đã sớm đồng ý!”"),t("br"),t("br"),n._v("Hỏa Tư Cổ Thánh móng vuốt vẫy một cái."),t("br"),t("br"),n._v("Thật ra thì tại Thần Dung Hoàng tìm nó lúc trước, Linh Hoàng liền đặc biệt tới qua mấy lội, chỉ bất quá, đều bị tại chỗ đưa ra ngoài cửa."),t("br"),t("br"),n._v("Sầm mặt lại, Linh Hoàng híp mắt lại."),t("br"),t("br"),n._v("Vốn nghĩ, Thần Dung Hoàng thân thể bị trọng thương, coi như có thể lôi kéo, cũng nhiều nhất binh tôm tướng cá, không đáng để lo, không nghĩ tới Mặc Linh Cổ Thánh ngang nhiên phản bội, Hỏa Tư Cổ Thánh cũng trực tiếp quy thuận..."),t("br"),t("br"),n._v("Đổi lại lúc trước, nghĩ cũng không dám nghĩ."),t("br"),t("br"),n._v("Đến cùng làm sao làm được?"),t("br"),t("br"),n._v("“Dung Hoàng, ngươi thật sự có chút thủ đoạn, so ta tưởng tượng còn muốn lợi hại hơn một ít, bất quá... Chỉ bằng các ngươi, muốn giết ta, còn không làm được!”"),t("br"),t("br"),n._v("Biết nói nhiều vô ích, Thần Linh Hoàng thở ra một hơi, lần nữa khôi phục tự tin: “Các ngươi cũng đều ra đi!”"),t("br"),t("br"),n._v("Rầm!"),t("br"),t("br"),n._v("Tiếng nói kết thúc, lúc trước tại Linh Hoàng Cung, Trương Huyền gặp phải rất nhiều Dị Linh tộc Cổ Thánh, đồng thời bay tới, đồng loạt đứng thành một hàng, tản mát ra khí tức cường đại."),t("br"),t("br"),n._v("Chính là lúc trước rất nhiều vương giả."),t("br"),t("br"),n._v("“Không đúng...”"),t("br"),t("br"),n._v("Liếc mắt nhìn những người này, Thần Dung Hoàng cùng Mặc Linh Cổ Thánh liếc mắt nhìn nhau, đồng thời vẻ mặt ngưng lại."),t("br"),t("br"),n._v("Những người này lúc trước, sở dĩ trợ giúp Thần Linh Hoàng, là trao đổi ích lợi, cái gọi là liên minh, cũng không kiên cố, mà bây giờ, nhưng kiên định canh giữ ở bốn phía, dường như chỉ cần bọn họ tiến lên, liền sẽ vọt thẳng tới, không quan tâm."),t("br"),t("br"),n._v("Lúc nào những người này, trung thành như vậy?"),t("br"),t("br"),n._v("“Là đem bọn hắn khống chế được...”"),t("br"),t("br"),n._v("Lông mày nhăn lại, Trương Huyền vẻ mặt cũng nghiêm túc lên."),t("br"),t("br"),n._v("Lấy hắn đối linh hồn nghiên cứu, tự nhiên đó có thể thấy được, những người này, tuy là còn có ý thức của mình, cũng đã bị người khống chế, thành cùng loại với khôi lỗi đồng dạng đồ vật."),t("br"),t("br"),n._v("Nếu như chỉ là liên minh, uy hiếp dụ dỗ, cùng đủ loại đạo lý, có thể để cho hắn sụp đổ, mà khôi lỗi, chỉ nghe theo lệnh lệnh... Liền không dễ dàng như vậy!"),t("br"),t("br"),n._v("Làm không cẩn thận chính là một hồi ác chiến!"),t("br"),t("br"),n._v("“Dùng linh hồn khống chế khôi lỗi? Cái này... Cái này sao có thể?”"),t("br"),t("br"),n._v("Mặc Linh Cổ Thánh khó tin."),t("br"),t("br"),n._v("Hắn cả một đời đều đang nghiên cứu hồn phách, đối linh hồn sớm đã lĩnh ngộ được cốt tủy, có thể để hắn khống chế Cổ Thánh... Vẫn như cũ không có khả năng hoàn thành."),t("br"),t("br"),n._v("Trước mắt vị này, thoáng cái khống chế nhiều như vậy thực lực không kém gì bản thân Cổ Thánh, thấy tận mắt, cũng không dám tin tưởng."),t("br"),t("br"),n._v("“Là phù chủng!”"),t("br"),t("br"),n._v("Nhớ tới cái gì, Thần Dung Hoàng thân thể cứng nhắc, cắn răng nói."),t("br"),t("br"),n._v("“Phù chủng?”"),t("br"),t("br"),n._v("Trương Huyền đám người trong mắt tất cả đều lộ ra kỳ quái chi sắc."),t("br"),t("br"),n._v("Thứ này, không riêng gì hắn, ngay cả Hỏa Tư Cổ Thánh, Mặc Linh Cổ Thánh đám người, đều không biết."),t("br"),t("br"),n._v("“Là Tiên Sứ cường giả, vật lưu lại, có thể gieo trồng ở trong người, một khi kích hoạt, liền cho dù là Cổ Thánh, đều muốn vô điều kiện nghe theo chỉ huy, nếu không, phù chủng bùng nổ, hẳn phải chết không nghi ngờ!”"),t("br"),t("br"),n._v("Thần Dung Hoàng nói."),t("br"),t("br"),n._v("“Tiên Sứ?”"),t("br"),t("br"),n._v("“Năm đó tổ phụ cùng Khổng sư quyết chiến bị giết, tộc nhân ăn bữa hôm lo bữa mai, bất cứ lúc nào cũng sẽ bị diệt, ngay sau đó tế tự trời cao, tiêu phí vô số đại giới, mời tới Thượng Thương thế giới Tiên Sứ cường giả, đối phương, vì khống chế cục diện, liền cho mỗi một vị Cổ Thánh, đều gieo loại vật này, cha ta, cũng trồng cái này!”"),t("br"),t("br"),n._v("Thần Dung Hoàng giải thích: “Về sau Tiên Sứ bị Khổng sư bắt đi, phù chủng tác dụng biến mất, mọi người mới may mắn thoát khỏi tại khó! Nếu không, Dị Linh nhất tộc hoàng thất, năm đó liền chỉ sợ toàn quân bị diệt.”"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1837"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1839"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);