(window.webpackJsonp=window.webpackJsonp||[]).push([[949],{1304:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1828-lay-mau"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1828-lay-mau"}},[n._v("#")]),n._v(" Chương 1828: Lấy máu")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1828: Lấy máu"),t("br"),t("br"),n._v("Dị Linh tộc chết bao nhiêu Cổ Thánh, đối Trương Huyền tới nói, không tính là gì, hắn coi trọng nhất chính là, tích huyết trùng sinh cấp bậc cường giả máu tươi."),t("br"),t("br"),n._v("Thứ này chỉ cần đầy đủ, xung kích Cổ Thánh liền sẽ dễ dàng không ít."),t("br"),t("br"),n._v("Vì lẽ đó, một thương đâm xuyên cái tên này bàn tay, một bên dùng bình ngọc chứa máu, bận bịu quên cả trời đất."),t("br"),t("br"),n._v("“Ngươi mẹ nó muốn chết...”"),t("br"),t("br"),n._v("Vị này Cổ Thánh sắp phát điên."),t("br"),t("br"),n._v("Trúng phù chủng, mặc dù nghe theo người khác mệnh lệnh, ý thức của mình nhưng vẫn là có, đường đường tích huyết trùng sinh cấp bậc Cổ Thánh, Linh tộc rất nhiều vương giả một trong, bị một cái Đại Thánh đâm thủng, vốn là buồn bực, hung thủ thế mà không trốn, còn còn không kiêng nể gì như thế chứa máu..."),t("br"),t("br"),n._v("Trắng trợn khiêu khích ah!"),t("br"),t("br"),n._v("Hàm răng cắn chặt, nổi giận âm thanh bên trong, vị này Cổ Thánh cũng lại không để ý tới đuổi giết Thần Dung Hoàng cùng Lưu Dương đám người, một cái khác bàn tay, phá không mà xuống."),t("br"),t("br"),n._v("Hắn muốn đem cái này vô pháp vô thiên gia hỏa trực tiếp đập chết."),t("br"),t("br"),n._v("Tích huyết trùng sinh Cổ Thánh lực lượng, giống như là giang hải thủy triều, vô biên vô hạn, nuốt giận ra tay, càng là thanh thế kinh người."),t("br"),t("br"),n._v("Phốc!"),t("br"),t("br"),n._v("Lực lượng hùng hồn còn chưa tới đến thanh niên trước mặt, trường thương lần nữa phá không mà đến, chỉ thoáng cái, đem cái tay này cũng đâm xuyên."),t("br"),t("br"),n._v("Lạch cạch! Lạch cạch!"),t("br"),t("br"),n._v("Máu tươi tuôn ra."),t("br"),t("br"),n._v("Cổ Thánh đau đớn một tiếng la lên, lần nữa nhìn thấy thanh niên lao đến, lại lấy ra một cái bình ngọc, không ngừng tiếp lấy máu tươi, sợ lãng phí một giọt."),t("br"),t("br"),n._v("“...”"),t("br"),t("br"),n._v("Khí run rẩy, vận chuyển chân khí, khóa lại mạch máu, vết thương mặc dù dữ tợn, cũng đã không có máu tươi chảy ra."),t("br"),t("br"),n._v("Tu vi đạt tới hắn loại cảnh giới này, không muốn để cho huyết dịch chảy ra, liền sẽ không chảy ra một giọt."),t("br"),t("br"),n._v("“Ừm?”"),t("br"),t("br"),n._v("Bên này máu tươi dừng lại, ngay tại sưu tập thanh niên nhíu nhíu mày, ngừng lại, một mặt không vui."),t("br"),t("br"),n._v("“Tiểu tử, dám sưu tập máu tươi của ta, ngươi hôm nay phải chết...”"),t("br"),t("br"),n._v("Thở phào nhẹ nhõm, Cổ Thánh tràn đầy dữ tợn, lời còn chưa dứt, chỉ thấy thanh niên không quá cao hứng bàn tay một trảo, trường thương xuất hiện lần nữa, lần nữa một đâm."),t("br"),t("br"),n._v("Cũng không biết đâm về nơi nào, nhưng không gian một hồi vặn vẹo, mũi thương đã đi tới bắp đùi trước mặt."),t("br"),t("br"),n._v("Phốc!"),t("br"),t("br"),n._v("Xuất hiện lần nữa một cái động lớn."),t("br"),t("br"),n._v("Cổ Thánh ngẩn ngơ, ngay sau đó lần nữa nhìn thấy thanh niên cầm bình ngọc lại bay tới, một mặt đắc ý tiếp lấy máu tươi."),t("br"),t("br"),n._v("“Mẹ nó...”"),t("br"),t("br"),n._v("Vị này Cổ Thánh sắp khóc."),t("br"),t("br"),n._v("Cho tới giờ khắc này, hắn đã rõ ràng, đối phương muốn giết hắn, dễ như trở bàn tay, có thể hết lần này tới lần khác không động thủ, đem hắn cho rằng là lấy máu tiêu bản."),t("br"),t("br"),n._v("Cũng lại kìm nén không được, xoay người liền muốn chạy trốn."),t("br"),t("br"),n._v("Phốc!"),t("br"),t("br"),n._v("Trên mông ra cái lỗ thủng, máu tươi phun ra."),t("br"),t("br"),n._v("Trương Huyền một cái bình ngọc tiếp đầy, tiếp tục tiếp cái thứ hai, chỉ chốc lát, liền tiếp hơn mười cái bình, mỗi lần đối phương ngăn lại huyết mạch, liền đâm ra một thương, dù sao đâm vào cũng không phải một chỗ, cũng không thèm để ý."),t("br"),t("br"),n._v("Đâm đến về sau, vị này Cổ Thánh cũng đã có kinh nghiệm, không trốn nữa đi, mà là ngồi tại nguyên chỗ, không nhúc nhích, hai mắt trừng trừng, giống như là mất hồn đồng dạng, trên người mười mấy cái lỗ thủng, vòi hoa sen đồng dạng, hướng ra phía ngoài phun máu."),t("br"),t("br"),n._v("Dù sao tránh không khỏi, càng tránh né đâm vào càng nhiều, còn không bằng cứ như vậy đi..."),t("br"),t("br"),n._v("Thời gian không dài, mất máu quá nhiều, vị này Cổ Thánh, một đầu từ không trung ngã đi xuống."),t("br"),t("br"),n._v("  Tu vi đạt tới loại cảnh giới này, bởi vì mất máu quá nhiều mà chết, đoán chừng cũng là từ trước tới nay vị thứ nhất."),t("br"),t("br"),n._v("Chém giết một vị, Trương Huyền hướng những người khác nhìn lại."),t("br"),t("br"),n._v("Hỏa Tư Cổ Thánh, Mặc Linh Cổ Thánh, cùng mấy vị khác Cổ Thánh liên hợp, chiến lực kinh người, coi như Thần Dung Hoàng bị thương nặng xuống, không còn lúc trước, cũng là một cỗ không thể khinh thường lực lượng."),t("br"),t("br"),n._v("Bị gieo xuống phù chủng rất nhiều Cổ Thánh tuy là mạnh mẽ, nhưng hai bên đều có thủ đoạn, trong thời gian ngắn, khó phân sàn sàn nhau."),t("br"),t("br"),n._v("Tu vi đạt tới bọn họ loại này cấp bậc, muốn đánh giết đối phương, đã không dễ dàng như vậy, tình huống bình thường đều là, chiến đấu không biết bao nhiêu ngày, cuối cùng người thất bại, bỏ trốn, người thắng trận, người cũng bị thương nặng, tuổi thọ tổn hao nhiều."),t("br"),t("br"),n._v("Nguyên nhân chính là như vậy, Cổ Thánh đối với nhân tộc cùng Dị Linh tộc, đều cùng đạn hạt nhân đồng dạng, chỉ dùng tới uy hiếp, chân chính chiến đấu cơ hội, cũng không phải là rất nhiều."),t("br"),t("br"),n._v("Bên này chiến đấu say sưa, chính giữa Thần Linh Hoàng, cũng không bị quấy rầy, tế đàn bên trên vô số bảo vật, bởi vì tế tự, toàn bộ thiêu đốt lên, tạo thành hừng hực liệt hỏa."),t("br"),t("br"),n._v("Một cỗ đâm thủng bầu trời lực lượng, hạ xuống từ trên trời, dọc theo hắn toàn thân huyệt đạo, hướng trong kinh mạch rót tuôn."),t("br"),t("br"),n._v("Lúc trước nhận thương thế, quả nhiên tại đây cỗ lực lượng trùng kích vào, dần dần khôi phục, thậm chí càng thêm cường đại."),t("br"),t("br"),n._v("“Nguy rồi, nhất định phải ngăn cản, mặc cho cái tên này khôi phục thương thế, lại nghĩ vượt qua liền khó khăn...”"),t("br"),t("br"),n._v("Trương Huyền lông mày nâng lên."),t("br"),t("br"),n._v("Thần Linh Hoàng thời kỳ toàn thịnh sức chiến đấu, mặc dù không bằng Thần Dung Hoàng, nhưng cũng là nhân tộc, Dị Linh tộc số lượng không nhiều siêu cấp đại cao thủ, nếu không, cũng không có khả năng khống chế một phương, vô số Cổ Thánh đều phản bác không được."),t("br"),t("br"),n._v("Bị thương nặng phía dưới không tính là bao nhiêu lợi hại, cầm trong tay Long Cốt thần thương liền có thể vượt qua, nhưng một khi khôi phục, còn muốn đánh giết liền phiền toái."),t("br"),t("br"),n._v("“Phá!”"),t("br"),t("br"),n._v("Cũng lại kìm nén không được, trường thương run lên, thẳng tắp hướng Thần Linh Hoàng vọt tới, đen thui Long Cốt thần thương, vỡ ra không gian, thẳng đối người sau đầu, giống như là muốn đem hắn thoáng cái đâm xuyên."),t("br"),t("br"),n._v("“Thiếu gia, không được...”"),t("br"),t("br"),n._v("Nhìn thấy hắn vọt thẳng đi qua, một bên Thần Dung Hoàng đồng tử co rụt lại, vội vàng la lên."),t("br"),t("br"),n._v("Bất quá, lúc này đã chậm, trường thương còn chưa tới đến Thần Linh Hoàng trước mặt, một cỗ lực lượng liền từ không trung lỗ đen tự nhiên mà xuống, cùng thân thương đụng một cái, Trương Huyền nhất thời cho rằng gan bàn tay giống như là muốn bị xé nứt, máu tươi chảy xuôi mà ra."),t("br"),t("br"),n._v("Trường thương cũng tại chấn động phía dưới rời tay mà bay, “Ông!” Một tiếng cắm vào trên mặt đất, đâm ra một cái to lớn lỗ thủng."),t("br"),t("br"),n._v("“Chuyện gì xảy ra?”"),t("br"),t("br"),n._v("Dưới tình thế cấp bách, huyết mạch lực lượng vận chuyển, vội vàng lui về phía sau, Trương Huyền lúc này mới tránh thoát ánh sáng bắn phá, trở lại trường thương trước mặt, đem hắn rút lên, đầy mặt nghi ngờ."),t("br"),t("br"),n._v("Liền lần này, Long Cốt thần thương vậy mà xuất hiện một tia nhỏ bé vết rách, dường như bị thương không nhẹ."),t("br"),t("br"),n._v("Bất quá, đối với loại cấp bậc này vũ khí tới nói, chỉ cần Cổ Thánh chi huyết đầy đủ, rất nhanh liền có thể khôi phục."),t("br"),t("br"),n._v("“Thiếu gia, tế tự thời điểm, nối tiếp chính là một thế giới khác Thần Linh, ngươi xông tới cắt đứt, tương đương với đang mạo phạm Thần Chỉ, tự nhiên muốn nhận trừng phạt...”"),t("br"),t("br"),n._v("Thần Dung Hoàng vội vàng truyền âm tới."),t("br"),t("br"),n._v("“Mạo phạm Thần Chỉ?”"),t("br"),t("br"),n._v("Trương Huyền đồng tử co rút lại."),t("br"),t("br"),n._v("Khó trách, Long Cốt thần thương cường đại như vậy đều bị thoáng cái đánh bay, liền đánh trả lực lượng đều không có, không nghĩ tới còn có loại thuyết pháp này."),t("br"),t("br"),n._v("Bất quá, suy nghĩ một chút cũng liền giật mình."),t("br"),t("br"),n._v("Tế tự, tương đương với đem đồ tốt dâng hiến cho Thần Linh, Thần Linh lại căn cứ vật tư nhiều ít, cấp cho ban thưởng, thời khắc này, là cùng hắn kết nối... Lúc này xông tới làm loạn, tương đương với miệt thị Thần Linh tôn nghiêm, tự nhiên muốn nhận trừng phạt."),t("br"),t("br"),n._v("Khó trách, Thần Dung Hoàng rõ ràng nhìn thấy tế tự bắt đầu, trong lòng tức giận, nhưng cũng không dám xông tới, nguyên lai có cái tầng quan hệ này."),t("br"),t("br"),n._v("“Nếu như không ngăn cản, tùy ý tế tự nghi thức xong thành, thương thế của hắn khôi phục, chúng ta làm không cẩn thận, đều sẽ chết ở chỗ này...”"),t("br"),t("br"),n._v("Trương Huyền lông mày nhăn lại."),t("br"),t("br"),n._v("Thần Chỉ quả thực là không thể mạo phạm, có thể... Không đi ngăn cản, tùy ý lực lượng hoàn thành, Thần Linh Hoàng thương thế triệt để khôi phục, bọn họ sẽ không còn người có thể vượt qua."),t("br"),t("br"),n._v("Đến lúc đó, bọn họ cái gọi là đoạt quyền, báo thù liền thành chê cười, làm không cẩn thận, lại còn toàn bộ chết ở chỗ này."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1839"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1841"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);