(window.webpackJsonp=window.webpackJsonp||[]).push([[95],{450:function(n,h,g){"use strict";g.r(h);var t=g(44),i=Object(t.a)({},(function(){var n=this,h=n.$createElement,g=n._self._c||h;return g("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[g("h1",{attrs:{id:"chuong-1062-la-han"}},[g("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1062-la-han"}},[n._v("#")]),n._v(" Chương 1062: Là hắn")]),n._v(" "),g("hr"),n._v(" "),g("p",[n._v("Chương 1062: Là hắn "),g("br"),g("br"),n._v("“Ngươi quả nhiên thông minh, ngắn như vậy thời gian, liền phát hiện ta thiếu hụt...”"),g("br"),g("br"),n._v("Nguyên thần hừ một câu, đang muốn tiếp tục nói chuyện, liền thấy phía dưới Phùng Huân một mặt cao hứng đối Giang Nguyên cái ót đập phá một quyền, thân thể nhoáng một cái, suýt chút nữa từ không trung rơi xuống."),g("br"),g("br"),n._v("“Muốn phát hiện cũng không khó, nguyên thần đoạt xá, dựa theo đạo lý, là không thể thi triển nguyên bản thân thể có võ kỹ, ngươi nhưng thi triển, nói rõ... Giang Nguyên linh hồn chỉ là bị tạm thời áp chế, cũng không tử vong, từ điểm đó, suy đoán ra thực lực ngươi nhận hạn chế không khó.”"),g("br"),g("br"),n._v("Trương Huyền nói."),g("br"),g("br"),n._v("“Thì ra là thế, ta làm sao không nghĩ tới...”"),g("br"),g("br"),n._v("Hàn hội trưởng ánh mắt sáng lên, giống như là nghĩ tới điều gì."),g("br"),g("br"),n._v("“Đến cùng chuyện gì xảy ra?” Ngô sư nhịn không được nhìn qua."),g("br"),g("br"),n._v("“Cái này dính dáng Xuất Khiếu cảnh một bí mật lớn, Xuất Khiếu cảnh cường giả đoạt xá, tình huống bình thường chỉ có thể thi triển mình am hiểu võ kỹ, vừa rồi Giang chiến sư nhưng thi triển Điệp Phong Thủ, nói rõ linh hồn của hắn vẫn còn, đối phương chỉ là dùng phương pháp đặc thù đem hắn khống chế... Nguyên thần mặc dù mạnh mẽ, nhưng ở đối phương trong cơ thể, khống chế đối phương, tương đương với sang sông rồng, đối chiến địa đầu xà, cho dù người sau yếu chút, cũng sẽ để hắn thoát thân không ra, thực lực đại tổn!”"),g("br"),g("br"),n._v("Hàn hội trưởng nói."),g("br"),g("br"),n._v("Tu vi thật sự của hắn, phải so Ngô sư cao hơn một chút, đối Xuất Khiếu cảnh hiểu rõ càng nhiều."),g("br"),g("br"),n._v("“Cái kia... Công kích Giang chiến sư thân thể, vì cái gì cái này Thiên Diệp Vương nguyên thần sẽ bị thương?” Ô Thiên Khung hỏi."),g("br"),g("br"),n._v("Những người khác cũng nhìn lại, không biết rõ."),g("br"),g("br"),n._v("Nếu là đoạt xá, có lẽ cùng Giang Nguyên thân thể không có bất cứ quan hệ nào, lại nói, liền xem như Thiên Diệp Vương bản nhân, nguyên thần xuất khiếu về sau, thân thể cũng sẽ cùng người chết đồng dạng, chém giết cũng sẽ không ảnh hưởng đến nguyên thần, trước mắt loại tình huống này, có phần quá kì quái."),g("br"),g("br"),n._v("“Cái này... Ta cũng không rõ lắm...” Lắc đầu, Hàn hội trưởng lông mày cũng nhăn thành mụn nhọt."),g("br"),g("br"),n._v("Vì sao lại xuất hiện loại tình huống này, hắn cũng không làm rõ ràng được."),g("br"),g("br"),n._v("“Rất đơn giản!”"),g("br"),g("br"),n._v("Nghe được đối thoại của bọn họ, Trương Huyền nhẹ nhàng cười một tiếng: “Nguyên thần đoạt xá, có thể đem người linh hồn trực tiếp nghiền ép tử vong, nhưng mà, cứ như vậy, bản thân linh hồn khí tức sẽ rất khó ẩn tàng, dễ dàng bị quen thuộc người phát hiện, nhất là Thiên Diệp Vương loại này Dị Linh tộc vương giả, sát khí hùng hồn, chân chính đoạt xá, tuyệt không có khả năng giấu giếm được ánh mắt của chúng ta.”"),g("br"),g("br"),n._v("Mọi người gật đầu."),g("br"),g("br"),n._v("Dị Linh tộc nhân tu luyện chém giết chân khí, linh hồn đều mang chém giết hương vị, thật muốn đoạt xá, vẫn là rất dễ dàng bị phát hiện."),g("br"),g("br"),n._v("“Vì không cho chúng ta phát giác, bình thường đoạt xá không thể dùng, hoàn toàn đem Giang Nguyên hồn phách ngăn chặn cũng không được, chỉ có thể dùng  bí pháp, loại bí pháp này, có thể ẩn tàng nguyên thần, hiển lộ Giang Nguyên khí tức, danh sư đã không phát hiện được... Nhưng mà, thiếu hụt chính là, công kích Giang Nguyên linh hồn, hắn cũng sẽ thụ tổn hại!”"),g("br"),g("br"),n._v("Nhẹ nhàng cười một tiếng, Trương Huyền tiếp tục ngẩng đầu nhìn lại: “Thiên Diệp Vương, không biết ta nói đúng hay không?”"),g("br"),g("br"),n._v("“Nói không sai, có điều, ngươi cho rằng như vậy liền có thể đối phó ta? Ngươi nghĩ quá ngây thơ rồi...”"),g("br"),g("br"),n._v("Trên bầu trời Thiên Diệp Vương nguyên thần, đang muốn nói tiếp, lần nữa cảm thấy đầu tê rần, lung lay hai cái, lời nói câm đến miệng bên trong."),g("br"),g("br"),n._v("Cúi đầu nhìn lại, chỉ thấy phía dưới Phùng Huân vẫn như cũ chăm chỉ không ngừng đập Giang Nguyên cái ót, tựa hồ muốn bị người đánh buồn bực phát tiết ra ngoài."),g("br"),g("br"),n._v("“Ngươi...”"),g("br"),g("br"),n._v("Khí sắp bùng nổ, đang muốn nói chuyện, liền nghe phía dưới Trương Huyền thanh âm, tiếp tục vang lên: “Chuyện này ta muốn thông, bất quá vẫn là có một việc, một mực không hiểu... Chúng ta đám người này, đến cùng có cái gì có thể đáng đường đường Thiên Diệp Vương, không tiếc hao tổn tu vi, chia ra nguyên thần, đều muốn ẩn núp đi vào?”"),g("br"),g("br"),n._v("“Chia ra nguyên thần?”"),g("br"),g("br"),n._v("Mọi người tất cả đều sững sờ."),g("br"),g("br"),n._v("“Cái này nguyên thần, cũng không phải là Thiên Diệp Vương bản nhân, mà là chia ra một bộ phận... Đường đường Thanh Điền nhất mạch thứ nhất vương giả, làm sao có thể chỉ có loại thực lực này.”"),g("br"),g("br"),n._v("Trương Huyền cười nói."),g("br"),g("br"),n._v("Hắn thấy tận mắt Ngọc Diệp Vương nguyên thần xuất khiếu sau chiến đấu, uy lực vô cùng, cùng bản ngăn cản không được."),g("br"),g("br"),n._v("Thiên Diệp Vương bài danh cao hơn, thực lực mạnh hơn, làm sao có thể đem toàn bộ nguyên thần, cùng Giang Nguyên dung hợp?"),g("br"),g("br"),n._v("Làm như vậy, hắn không phải là đem tính mạng phó thác đến trong tay người khác?"),g("br"),g("br"),n._v("Cho nên, chỉ bằng vào hai điểm này, liền có thể suy đoán ra, cái này nguyên thần, căn bản cũng không phải là Thiên Diệp Vương, mà chỉ là tàn hồn hoặc là ý niệm loại hình đồ vật thôi."),g("br"),g("br"),n._v("“Một bộ phận cứ như vậy mạnh?”"),g("br"),g("br"),n._v("Mọi người đồng thời giật nảy mình."),g("br"),g("br"),n._v("Một phần nhỏ, còn bị Giang Nguyên cuốn lấy, liền đánh bọn họ suýt chút nữa toàn quân bị diệt... Vị này Thiên Diệp Vương có phần quá kinh khủng."),g("br"),g("br"),n._v("“Ngươi thật sự rất thông minh, đã như vậy, ta danh nhân không nói tiếng lóng, đem đồ vật giao ra đi, ta có thể tha các ngươi...”"),g("br"),g("br"),n._v("Thấy phía dưới thanh niên, liền cái này đều có thể đoán ra, Thiên Diệp Vương có chút ngoài ý muốn, lập tức cười lạnh một tiếng, đang muốn uy hiếp vài câu, lần nữa nhoáng một cái, từ không trung ngã xuống bảy, tám mét, nhịn không được quay đầu nhìn về phía bên dưới Phương Chính đang không ngừng đập Phùng Huân, nghiến răng nghiến lợi: “Còn không ngừng tay!”"),g("br"),g("br"),n._v("“A?”"),g("br"),g("br"),n._v("Bị lớn tiếng trách mắng, Phùng Huân sửng sốt một chút, đang muốn tiếp tục, chỉ thấy Trương Huyền lắc đầu: “Được rồi, trước không vội động thủ, có một số việc hỏi rõ ràng lại nói!”"),g("br"),g("br"),n._v("“Tốt a!” Phùng Huân nhẹ gật đầu, tràn đầy lưu luyến không rời."),g("br"),g("br"),n._v("Thấy hắn dừng tay, Thiên Diệp Vương lúc này mới thở phào nhẹ nhõm, lần nữa nhìn về phía Trương Huyền: “Đừng nói nhảm, bản đồ trong tay ngươi đi, giao ra đi!”"),g("br"),g("br"),n._v("“Bản đồ?” Trương Huyền sững sờ: “Vật kia, không phải là bị ngươi cướp đi ư?”"),g("br"),n._v(" Cái tên này, rõ ràng có đánh giết mọi người thực lực, nhưng không tiếc hao tổn nguyên thần, lẻn vào trong đó, bản thân liền rất kỳ quái, giờ phút này đột nhiên nói cái gì bản đồ, Trương Huyền thì càng không hiểu rõ."),g("br"),g("br"),n._v("Cái này di tích là có địa đồ, nhưng không phải ban đầu ở Độc điện, bị cái tên này cướp đi ư?"),g("br"),g("br"),n._v("Bản thân hao hết vất vả lẫn vào trong đó, nhưng cái gì đều không có nhận được, lúc ấy còn buồn bực thời gian rất lâu."),g("br"),g("br"),n._v("“Ta được đến chỉ là một bản di tích giới thiệu, hơn nữa đều là dùng Cổ Thần ngữ viết mà thành, căn bản là xem không hiểu... Chân chính đồ, hai ngàn năm trước, bị Ngô Dương Tử cướp đi, tất nhiên trốn ở trong tay các ngươi!”"),g("br"),g("br"),n._v("Thiên Diệp Vương hừ lạnh."),g("br"),g("br"),n._v("“Bị Ngô Dương Tử tiền bối cướp đi?”"),g("br"),g("br"),n._v("Sửng sốt một chút, Trương Huyền giống như là nghĩ đến cái gì, kìm lòng không được ngây ngốc một chút, lập tức ngẩng đầu: “Ngô tiền bối bị các ngươi Dị Linh tộc nhân bắt đi, cuối cùng hài cốt làm tọa hóa, cho dù có bảo vật gì, cũng sớm đã bị các ngươi nhận được, Phùng Huân, cái tên này khẳng định không nói lời nói thật, đánh cho ta chết mới thôi...”"),g("br"),g("br"),n._v("“Được rồi!”"),g("br"),g("br"),n._v("Phùng Huân hưng phấn mà gật đầu, lần nữa vọt tới Giang Nguyên trước mặt, nắm đấm không ngừng vung vẩy, mạnh mẽ đập xuống."),g("br"),g("br"),n._v("Đùng đùng!"),g("br"),g("br"),n._v("Nắm đấm vào thịt, trên không Thiên Diệp Vương nguyên thần, không ngừng lay động, tức giận đến sắp bùng nổ, khóe mắt: “Xem như ngươi lợi hại! Trương Huyền đúng không, hôm nay trước buông tha ngươi, về sau đừng để ta gặp được, nếu không ta sẽ đưa ngươi xé vỡ nát!”"),g("br"),g("br"),n._v("Rống xong, Thiên Diệp Vương “Soạt!” Thoáng cái, nguyên thần trực tiếp hướng lên vọt tới, lập tức biến mất ở trước mặt mọi người, hình như chưa hề xuất hiện qua đồng dạng."),g("br"),g("br"),n._v("Thấy hắn chạy trốn, Ngô sư đám người đang muốn đuổi theo, chỉ thấy Trương Huyền khoát tay áo."),g("br"),g("br"),n._v("“Không cần đuổi, mặc dù hắn hiện tại cưỡng ép chạy trốn, nguyên khí tổn hao nhiều, cũng không phải chúng ta có thể chống lại, lại nói, chúng ta còn thân ở trong trận pháp, đâu đâu cũng có nguy hiểm, ngộ nhỡ bị đưa vào bẫy rập, liền được không bù mất...”"),g("br"),g("br"),n._v("Cái tên này biết tiếp tục đợi ở chỗ này, khẳng định sẽ bị tươi sống giết chết, liều mạng hao tổn, cũng theo Giang Nguyên trong cơ thể tránh thoát, nhanh chóng chạy trốn, chỉ bằng vào phần này quả quyết, liền biết, đuổi theo sẽ chỉ chịu thiệt."),g("br"),g("br"),n._v("“Trương sư ngươi là thế nào phát hiện, cái này Giang Nguyên có thể là Thiên Diệp Vương ngụy trang? Chỉ bằng vào không nói lời nào... Hẳn là còn nhìn không ra đi!” Biết hắn nói rất có lý, Ngô sư đám người lại không theo đuổi, Hàn hội trưởng tràn đầy kỳ quái nhìn qua."),g("br"),g("br"),n._v("Mặc dù vừa rồi Trương sư nói rất rõ ràng, vị này Giang Nguyên một đường không nói chuyện, cũng không chủ động, nhưng... Hắn từ đầu đến cuối đều cảm thấy, không có khả năng đơn giản như vậy."),g("br"),g("br"),n._v("“Rất đơn giản, ta giết chết Thạch Diệp Vương sự tình, chỉ có chúng ta những người này biết, Hồng Diệp Vương nhưng có thể biết, đồng thời đem ta nói ra, nói rõ... Chúng ta bên trong tất nhiên có nội gian, đem tin tức tiết lộ! Lại thêm hắn không bị trận pháp ảnh hưởng, một mực khiêu khích... Rất dễ dàng hiểu!”"),g("br"),g("br"),n._v("Trương Huyền gật đầu."),g("br"),g("br"),n._v("Cùng Hàn hội trưởng đoán đồng dạng, chỉ bằng vào trước đó nói hai điểm, đương nhiên không có khả năng kết luận Giang Nguyên bị Thiên Diệp Vương đoạt xá, nhưng kết hợp Hồng Diệp Vương lời nói, cũng rất dễ dàng xác định."),g("br"),g("br"),n._v("“Thì ra là thế...”"),g("br"),g("br"),n._v("Mọi người tất cả đều gật đầu, lần nữa nhìn về phía Trương Huyền, tràn đầy bái phục."),g("br"),g("br"),n._v("Như vậy chi tiết, bọn họ cũng không có chú ý, trước mắt vị này nhưng như vậy suy đoán ra nhiều như vậy, phần này năng lực, khiến cho người bái phục."),g("br"),g("br"),n._v("“Làm sao bây giờ?”"),g("br"),g("br"),n._v("Ngô sư nhìn qua."),g("br"),g("br"),n._v("Mặc dù giải quyết Thiên Diệp Vương uy hiếp, trên thực tế mọi người còn tại trong trận pháp, không cách nào thoát thân."),g("br"),g("br"),n._v("“Hiện tại...” Chần chờ một chút, Trương Huyền đang muốn nói chuyện, lông mày đột nhiên nhíu một cái, ho khan một tiếng: “Khụ khụ, Phùng Huân, có thể, không cần lại đánh, Thiên Diệp Vương đã chạy trốn, đánh cũng vô dụng...”"),g("br"),g("br"),n._v("Mọi người đồng loạt nhìn lại, lúc này mới phát hiện Phùng Huân vẫn như cũ đối Giang Nguyên cái ót, chăm chỉ không ngừng, từng quyền không ngừng đập."),g("br"),g("br"),n._v("Công kích của hắn rất có đúng mực, vừa có thể để cho đối phương cảm thấy đau đớn, lại không bị chết vong."),g("br"),g("br"),n._v("“Trốn? Ta còn tưởng rằng... Còn có một bộ phận ở lại chỗ này, giúp Giang chiến sư khu trừ sạch sẽ đây...” Lúc này mới kịp phản ứng, Phùng Huân tràn đầy lúng túng vò đầu."),g("br"),g("br"),n._v("“Hắn đã chạy trốn, khẳng định là dứt bỏ cùng Giang Nguyên dung hợp cái kia bộ phận nguyên thần...”"),g("br"),g("br"),n._v("Lắc đầu, Trương Huyền hai bước đi tới trước mặt tại Giang Nguyên trên cổ tay sờ thoáng cái, lập tức lấy ra một cái hộp ngọc, ngón tay bay lượn, mấy chục cây ngân châm, đối với người sau đầu rơi xuống đi qua."),g("br"),g("br"),n._v("Bất kể nói thế nào, vị này Giang Nguyên chỉ là người bị hại, có thể cứu sống, đương nhiên tốt nhất."),g("br"),g("br"),n._v("Ngân châm tại chân khí của hắn khởi động bên dưới, không ngừng xoay tròn, một lát sau, tựa như thi thể Giang Nguyên, giống như là tỉnh lại, chậm rãi mở mắt."),g("br"),g("br"),n._v("Ầm ầm!"),g("br"),g("br"),n._v("Cùng thời khắc đó, khí tức trên thân, bay lên, thế mà trong nháy mắt đột phá Thánh vực tam trọng đỉnh phong, đạt đến tứ trọng Nguyên Thần cảnh!"),g("br"),g("br"),n._v("“Thiên Diệp Vương cùng hắn dung hợp cái kia bộ phận nguyên thần, không có cách nào chạy trốn, bị hắn thành công luyện hóa, dưới cơ duyên xảo hợp, đột phá cảnh giới, cũng coi như vì họa được phúc...”"),g("br"),g("br"),n._v("Trương Huyền nhẹ nhàng cười một tiếng."),g("br"),g("br"),n._v("Thiên Diệp Vương là Thánh vực ngũ trọng Xuất Khiếu cảnh cường giả, lưu lại nguyên thần, cho dù một tia, cũng thập phần cường đại, Giang Nguyên trong lúc vô tình thôn phệ thứ này, tu vi tăng nhiều, cũng là trong dự liệu sự tình."),g("br"),g("br"),n._v("“Ta đây là ở đâu? Đầu vì cái gì như thế đau? Ai đánh ta...”"),g("br"),g("br"),n._v("Còn không có chú ý tới mình đã đột phá, sờ lấy cái ót, Giang Nguyên tỉnh lại, một mặt mê man."),g("br"),g("br"),n._v("“Hắn!”"),g("br"),g("br"),n._v("Mọi người cùng quét quét chỉ hướng Phùng Huân."),g("br"),g("br"),n._v("“...” Phùng Huân."),g("br"),g("br"),g("br"),g("br")]),n._v(" "),g("hr"),n._v(" "),g("p",[g("a",{attrs:{href:"./chap-1070"}},[n._v("Chương trước")]),n._v("  |  "),g("a",{attrs:{href:"./chap-1072"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);