(window.webpackJsonp=window.webpackJsonp||[]).push([[950],{1305:function(n,h,t){"use strict";t.r(h);var g=t(44),c=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1829-vu-hon-xung-kich-co-thanh-thuong"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1829-vu-hon-xung-kich-co-thanh-thuong"}},[n._v("#")]),n._v(" Chương 1829: Vu hồn xung kích Cổ Thánh (thượng)")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1829: Vu hồn xung kích Cổ Thánh (thượng)"),t("br"),t("br"),n._v("“Ta cũng biết! Nhưng bây giờ, loại trừ làm các loại, không có những biện pháp khác!”"),t("br"),t("br"),n._v("Hai chưởng đẩy lùi một vị xông tới Cổ Thánh, Thần Dung Hoàng cũng đầy là bất đắc dĩ."),t("br"),t("br"),n._v("Hắn cũng muốn đem cái tên này giết, có thể tế tự một khi bắt đầu, liền không dừng được, nếu không chính là miệt thị Thần Linh, đối Thần không vừa lòng."),t("br"),t("br"),n._v("Bọn họ nhiều người như vậy, đối mặt khôi phục Thần Linh Hoàng, có lẽ còn có sức đánh một trận, có thể đối mặt Thần Linh, chỉ có hẳn phải chết một con đường có thể đi."),t("br"),t("br"),n._v("“Kế trước mắt, chính là trước đem những người này toàn bộ giết, hoặc là để hắn mất đi sức chiến đấu, đến lúc đó, coi như Thần Linh Hoàng khôi phục, cũng chỉ là cô độc, chúng ta cũng có càng lớn cơ hội!”"),t("br"),t("br"),n._v("Thần Dung Hoàng nói tiếp."),t("br"),t("br"),n._v("“Ừm!” Trương Huyền gật gật đầu."),t("br"),t("br"),n._v("Quả thực, hiện tại biện pháp tốt nhất chính là như vậy."),t("br"),t("br"),n._v("Rút củi dưới đáy nồi, trước đem thuộc hạ của hắn toàn bộ giết, đến lúc đó, khôi phục như lúc ban đầu, chỉ cần là tích huyết trùng sinh cảnh giới, dựa vào hắn, Hỏa Tư Cổ Thánh, Mặc Linh Cổ Thánh cùng Long Cốt thần thương, cuối cùng hươu chết vào tay ai, ai cũng khó mà nói."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Hai người trò chuyện quá trình bên trong, một đầu Dị Linh tộc Cổ Thánh hướng Trương Huyền nhào tới."),t("br"),t("br"),n._v("Binh khí trong tay, là kiện phán quan bút, cũng đạt tới Cổ Thánh cấp bậc, lăng không một quấy, thiên địa xoay tròn, linh hồn dường như vỡ ra tới."),t("br"),t("br"),n._v("Xem ra, hắn nhìn ra Trương Huyền thực lực không đủ, Long Cốt thần thương lại cực kỳ cường đại, định dùng công kích linh hồn thủ đoạn, đem hắn đánh giết."),t("br"),t("br"),n._v("Thần thương chỉ là kiện vũ khí, có thể chặn lại vật chất công kích, linh hồn phương diện liền yếu đi."),t("br"),t("br"),n._v("Quả nhiên, công kích đến đến, Trương Huyền kìm lòng không được cảm thấy một hồi chóng mặt."),t("br"),t("br"),n._v("Hắn hồn lực cùng cấp bậc không người có thể địch, có thể đối mặt Cổ Thánh gia trì binh khí công kích, cũng có chút không đỡ được."),t("br"),t("br"),n._v("“Khà khà!”"),t("br"),t("br"),n._v("Một chiêu đắc thủ, đầu này Dị Linh Cổ Thánh thẳng tắp lao đến, trong tay phán quan bút, lần nữa khuấy động."),t("br"),t("br"),n._v("“Hội trưởng!”"),t("br"),t("br"),n._v("Gặp Trương Huyền trúng chiêu, Mặc Linh Cổ Thánh tràn đầy gấp gáp, muốn xông lại, nhưng cảm thấy áp lực đột nhiên tăng thêm, tới đối chiến Cổ Thánh, liên tiếp thi triển tuyệt chiêu, đem hắn cứ thế mà đè ép trở về."),t("br"),t("br"),n._v("Một bên Thần Dung Hoàng đối mặt đồng dạng tình cảnh."),t("br"),t("br"),n._v("Đối phương vừa rồi nhìn thấy Trương Huyền một thương thương đâm mặc đồng bạn, biết cầm trong tay binh khí hắn, sức chiến đấu kinh khủng nhất, tính toán liên hợp lại, đem hắn đánh giết lại nói."),t("br"),t("br"),n._v("“Chết đi!”"),t("br"),t("br"),n._v("Biết những người khác ngăn cản lại Thần Dung Hoàng đám người không có khả năng duy trì bao lâu, vị này Cổ Thánh, cười lạnh liên tục, thân thể xoay một cái vọt tới trước mặt, một chưởng hướng về phía Trương Huyền đầu liền đập xuống mà xuống."),t("br"),t("br"),n._v("Lần này, một khi đánh trúng, coi như luyện hóa tích huyết trùng sinh cấp bậc huyết dịch, cũng sẽ đi đời nhà ma, lại khó khôi phục."),t("br"),t("br"),n._v("“Thiếu gia!”"),t("br"),t("br"),n._v("“Hội trưởng!”"),t("br"),t("br"),n._v("“Chủ nhân!”"),t("br"),t("br"),n._v("Ba đại Cổ Thánh đồng tử đồng thời co rút lại, muốn xông tới, nhưng bất lực."),t("br"),t("br"),n._v("Liền tại bọn hắn cho rằng lần này Trương Huyền nhất định khó mà chạy trốn thời điểm, chỉ thấy vị này một mặt chóng mặt, dường như cái gì cũng không biết thanh niên, đột nhiên mỉm cười, bàn tay trường thương, bỗng nhiên nhảy lên."),t("br"),t("br"),n._v("Phốc!"),t("br"),t("br"),n._v("Chỉ thoáng cái, liền đâm xuyên qua, vị này Cổ Thánh trái tim, đem hắn xuyên qua trên không trung."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Máu tươi tự nhiên."),t("br"),t("br"),n._v("Bình ngọc xuất hiện, vội vàng thu thập."),t("br"),t("br"),n._v("“...”"),t("br"),t("br"),n._v("Tất cả mọi người đều ngẩn ngơ, ngay cả ngăn cản mọi người Dị Linh tộc nhân, cũng đồng thời bối rối."),t("br"),n._v("Vị này Đại Thánh, cũng liền dựa dẫm Long Cốt thần thương, mới nắm giữ cùng tích huyết trùng sinh cường giả năng lực chiến đấu, dựa theo tình huống bình thường, bị Cổ Thánh cận thân hẳn phải chết không nghi ngờ, làm sao lại một thương đâm xuyên đối phương?"),t("br"),t("br"),n._v("Chẳng lẽ vừa rồi chóng mặt, là ngụy trang?"),t("br"),t("br"),n._v("“Nguy hiểm thật...”"),t("br"),t("br"),n._v("Không đi trả lời mọi người khiếp sợ, Trương Huyền một bên sưu tập máu tươi, một bên thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Đối phương lấy linh hồn pháp bảo công kích, hắn vừa rồi quả thực trúng chiêu, sở dĩ có thể mau theo khôi phục lại, là bởi vì, Khổng miếu bên trong được viên kia thánh tích thạch!"),t("br"),t("br"),n._v("Hồn ấn, hắn ban cho Lộ Xung, nhưng thánh tích thạch nhưng vẫn luôn tùy thân mang theo."),t("br"),t("br"),n._v("Thứ này có thôn phệ linh hồn, tẩm bổ hồn phách tác dụng, công kích của đối phương tuy là lợi hại, đổi lại bình thường, không cách nào khôi phục, nhưng có món pháp bảo này, liền dễ dàng nhiều."),t("br"),t("br"),n._v("Đâm xuyên vị này Cổ Thánh, đem máu tươi sưu tập sạch sẽ, Trương Huyền chẳng muốn tiếp tục hiểu, đem phán quan bút thu vào nhẫn về sau, lúc này mới cổ tay khẽ đảo, cứu hắn một mạng thánh tích thạch xuất hiện tại lòng bàn tay."),t("br"),t("br"),n._v("Tuy là không có thứ này, Ngoan Nhân cũng sẽ ra tay, nhưng có thể đem đối phương khuấy động đi ra công kích linh hồn hấp thu, cũng hóa thành tẩm bổ, thứ này trân quý mức độ, có thể tưởng tượng được."),t("br"),t("br"),n._v("Cúi đầu nhìn lại, thánh tích thạch tản mát ra ôn nhuận quang mang, màu sắc cũng cùng lúc trước có chút khác biệt, để lộ ra nhàn nhạt màu xanh da trời."),t("br"),t("br"),n._v("Minh Lý Chi Nhãn chiếu rọi, trong đó từng đạo mạnh mẽ khí tức, ở trong đó quanh quẩn, bàn tay tới tiếp xúc, linh hồn nhất thời cảm thấy một hồi ấm áp, giống như là đi vào suối nước nóng."),t("br"),t("br"),n._v("“Đây là thuần túy hồn lực?”"),t("br"),t("br"),n._v("Đồng tử co rụt lại."),t("br"),t("br"),n._v("Thân là mạnh mẽ vu hồn sư, hắn có thể ung dung nhận ra, trong này ẩn chứa, không phải cái khác, chính là tinh thuần hồn lực, có thể tuỳ tiện cho linh hồn tẩm bổ, để hồn phách trở nên càng thêm cường đại."),t("br"),t("br"),n._v("Hắn nhớ rõ, cái này thánh tích trong đá không có cái gì, làm sao lại đột nhiên nhiều ra nhiều như vậy hồn lực?"),t("br"),t("br"),n._v("“Chẳng lẽ...”"),t("br"),t("br"),n._v("Vội vàng cúi đầu, Minh Lý Chi Nhãn chiếu rọi xuống, từng đạo chỉ, lấy tảng đá làm trung tâm, chậm rãi hội tụ."),t("br"),t("br"),n._v("“Thần Linh Hoàng lấy mười vạn Dị Linh tộc cường giả Thánh vực tế tự, những người này linh hồn, vậy mà tất cả đều bị thánh tích thạch nuốt...”"),t("br"),t("br"),n._v("Nhìn thấy cái này, ở đâu không hiểu chuyện gì xảy ra, Trương Huyền dở khóc dở cười."),t("br"),t("br"),n._v("Thánh tích thạch, cùng hồn ấn đồng dạng, nắm giữ thôn phệ linh hồn hơn nữa tinh lọc năng lực, loại năng lực này đối tu luyện giả tới nói, có chút tà dị, vì lẽ đó bị danh sư đại lục cấm chế sử dụng, vu hồn sư cũng bởi vì bị người thống hận."),t("br"),t("br"),n._v("Trương Huyền thân là danh sư, tự nhiên khinh thường tại sử dụng loại phương pháp này, thật không nghĩ đến, Thần Linh Hoàng đánh giết mấy vạn tộc nhân về sau, linh hồn thế mà quái lạ, liền bị tảng đá kia thôn phệ."),t("br"),t("br"),n._v("“Có nhiều như vậy tinh khiết hồn lực, ta linh hồn có hay không có thể trực tiếp trùng kích Cổ Thánh?”"),t("br"),t("br"),n._v("Lông mày giương lên, Trương Huyền trong lòng tràn đầy kích động."),t("br"),t("br"),n._v("Tuy là không đề xướng sử dụng thánh tích thạch, thôn phệ người khác linh hồn, nhưng nếu đã nuốt, lại luyện hóa thành tinh thuần như thế hồn lực, lại cự tuyệt sử dụng, thật sự là kẻ ngu."),t("br"),t("br"),n._v("Hắn vu hồn đã sớm đạt đến Bất Hủ cảnh đại viên mãn, có nhiều như vậy hồn lực bổ sung, có lẽ có thể xung kích một chút Cổ Thánh, đạt tới loại này, người người đều muốn đi đến cảnh giới."),t("br"),t("br"),n._v("“Thử một chút!”"),t("br"),t("br"),n._v("Cũng nhịn không được nữa, Trương Huyền hít sâu một hơi, bàn giao Long Cốt thần thương cùng Ngoan Nhân, bảo vệ thân thể, tinh thần khẽ động, vu hồn từ mi tâm nhảy ra ngoài."),t("br"),t("br"),n._v("Xì xì xì xì...!"),t("br"),t("br"),n._v("Vu hồn cùng thánh tích thạch vừa tiếp xúc, trong đó nóng hổi hồn lực liền nhất thời tuôn trào ra, nguyên bản cầm cố lại khó mà gia tăng hồn phách, mắt thường có thể thấy được nhanh chóng tăng thêm."),t("br"),t("br"),n._v("“Không đúng... Ta còn không có vu hồn đột phá Cổ Thánh phương pháp!”"),t("br"),t("br"),n._v("Cảm nhận được hồn thể, càng ngày càng cao lớn, cả người bị chống bất cứ lúc nào cũng sẽ bùng nổ, Trương Huyền khóe miệng giật một cái."),t("br"),t("br"),n._v("Đến thăm lấy cao hứng, lúc này mới nhớ tới, lúc trước nghiên cứu đột phá Cổ Thánh phương pháp, phần lớn là chân khí, mà liên quan tới vu hồn, vậy mà một quyển sách đều không có nhìn qua."),t("br"),t("br"),n._v("Cũng không biết vu hồn đột phá, nên trải qua cái gì, lại nên đi dạng gì bước đi."),t("br"),t("br"),n._v("“Mặc Linh Cổ Thánh, ngươi nhưng có vu hồn đột phá Cổ Thánh kinh nghiệm cùng bí tịch? Nhanh cho ta mấy quyển...”"),t("br"),t("br"),n._v("Cũng nhịn không được nữa, ngẩng đầu hướng trên không Mặc Linh Cổ Thánh hô lên."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1840"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1842"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);