(window.webpackJsonp=window.webpackJsonp||[]).push([[958],{1313:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1837-than-linh-hoang-nhan-chu"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1837-than-linh-hoang-nhan-chu"}},[n._v("#")]),n._v(" Chương 1837: Thần Linh Hoàng nhận chủ")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1837: Thần Linh Hoàng nhận chủ"),t("br"),t("br"),n._v("“Cũng chưa chắc thất bại, mặc kệ...”"),t("br"),t("br"),n._v("Dựa vào Long Cốt thần thương, Ngoan Nhân, Hỏa Tư Cổ Thánh, Mặc Linh Cổ Thánh... Đối phương cho dù là phá toái hư không, muốn chém giết mọi người, cũng chưa chắc có thể làm được, đã như vậy, còn có cái gì có thể lo lắng?"),t("br"),t("br"),n._v("Lòng khẩn trương cảnh, một cái chớp mắt khôi phục lại, trên mặt không vui không buồn, để cho người ta nhìn không ra hỉ nộ ái ố."),t("br"),t("br"),n._v("“Thần Linh” ánh mắt tại trên thân hai người bơi lội, một lát sau nhíu nhíu mày: “Các ngươi hướng trời cao cầu nguyện, dùng chính là bản thân tinh huyết, đem huyết dịch cho ta, nhất định có thể tra ra thật giả!”"),t("br"),t("br"),n._v("Vốn cho rằng, chỉ là một đám thổ dân, dựa vào năng lực của hắn cùng thân phận, tùy tiện liền có thể nhìn thấu, nằm mơ đều không có nghĩ đến, nhìn hồi lâu, cái rắm đều không có nhìn ra một chút."),t("br"),t("br"),n._v("Cảm giác hai cái này, đều là thật."),t("br"),t("br"),n._v("Thiên hạ, không có khả năng có hai cái Linh Hoàng, có thể giả, cho dù ngụy trang giống như, huyết mạch không có khả năng triệt để tương đồng a?"),t("br"),t("br"),n._v("Rất nhanh, hai giọt huyết dịch bay tới, thần thức đảo qua, “Thần Linh” tự tin mặt, trở nên mê mang."),t("br"),t("br"),n._v("Vậy mà cũng giống nhau như đúc!"),t("br"),t("br"),n._v("Làm cái gì?"),t("br"),t("br"),n._v("“Thần Linh, cái tên này giả mạo ta, lừa gạt thượng sứ, tội không thể tha...” Gặp hắn quan sát huyết dịch, còn tưởng rằng quan sát xảy ra điều gì, Thần Linh Hoàng cắn răng."),t("br"),t("br"),n._v("“Ngậm miệng!”"),t("br"),t("br"),n._v("Đùng!"),t("br"),t("br"),n._v("“Thần Linh” một bàn tay quất vào trên mặt của hắn, đem hắn tại nguyên chỗ đánh mấy vòng."),t("br"),t("br"),n._v("Một mặt nhãn lực cứng đều không có, nhận không ra chính tràn đầy khó xử, ngay ở chỗ này cái rắm lải nhải, muốn chết phải không?"),t("br"),t("br"),n._v("“Ta...” Bụm mặt, Thần Linh Hoàng tức giận run rẩy."),t("br"),t("br"),n._v("“Thật Thần Linh Hoàng, đối Thượng Thương tế tự, mới có thể để cho ta hạ xuống, giả khẳng định không biết cụ thể dùng bao nhiêu bảo vật, giá trị lại là bao nhiêu... Hai người các ngươi đều viết một chút, đem nội dung cho ta xem một chút, giá trị chính xác, tự nhiên là thật!”"),t("br"),t("br"),n._v("Chần chờ một chút, “Thần Linh” nói."),t("br"),t("br"),n._v("Đã Thần Linh Hoàng tế tự Thượng Thương, van xin khôi phục thương thế, lại tiêu phí lớn đánh đổi, để cho mình hạ xuống, đối với tế tự mà ra chi phí, khẳng định biết rõ rõ ràng ràng."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Thần Linh Hoàng sửng sốt một chút, ngay sau đó gật gật đầu: “Được!”"),t("br"),t("br"),n._v("Nói xong lấy ra một cái ngọc bài, tinh thần khẽ động, bản thân hiểu biết bảo vật giá trị, khắc ấn đi lên."),t("br"),t("br"),n._v("Gặp hắn bắt đầu, Trương Huyền dừng lại một chút, cũng lấy ra một cái ngọc bài, bắt đầu viết."),t("br"),t("br"),n._v("Thế nào tế tự, thế nào câu thông Thượng Thương, lại cần tiêu phí bao nhiêu, hắn là không rõ ràng, không thể không nói, vị này “Thần Linh” rất thông minh, chỉ từ điểm ấy, liền tóm lấy người ngụy trang điểm yếu."),t("br"),t("br"),n._v("Bất quá, lúc trước ngụy trang thành giám bảo sư Ổ Thao, tất cả bảo vật, đều nhìn qua một lần, vừa rồi bởi vì tế tự mà chết mọi người, linh hồn cũng đều bị thánh tích thạch thôn phệ, được hắn cùng phân thân hấp thu, cụ thể số lượng, cấp bậc, cũng đều rõ rõ ràng ràng."),t("br"),t("br"),n._v("Trầm tư một chút, lập tức đem chuẩn xác giá trị và số lượng, ghi lại ở phía trên."),t("br"),t("br"),n._v("Đồng thời nhận lấy hai người ngọc bài, “Thần Linh” thần thức quét tới, con mắt nhất thời nheo lại, nhìn chằm chằm cách đó không xa Thần Linh Hoàng, tựa như bất cứ lúc nào cũng sẽ đem người chém giết."),t("br"),t("br"),n._v("“Tiền bối...”"),t("br"),t("br"),n._v("Nhìn thấy bộ dáng của hắn, Thần Linh Hoàng trong lòng toát ra linh cảm không lành, lời còn chưa dứt, chỉ thấy bàn tay của đối phương phá không mà xuống."),t("br"),t("br"),n._v("“Ngay cả ta cũng dám lừa gạt, ngươi muốn chết...”"),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Thiên băng địa liệt đồng dạng, Thần Linh Hoàng thân thể bị giam cầm ở, chạy trốn đều không làm được, sức mạnh vô cùng vô tận tự nhiên, dường như muốn một chút đem hắn đập thành thịt vụn."),t("br"),t("br"),n._v("“Không...”"),t("br"),t("br"),n._v("Không nghĩ tới, vị này thật, phải bị bản thân tiêu phí to lớn đánh đổi xin xuống “Thần Linh” giết chết, Thần Linh Hoàng buồn bực sắp chóng mặt, gào lên một tiếng, toàn thân huyệt đạo mở ra, chân khí trong cơ thể hung mãnh tuôn ra."),t("br"),t("br"),n._v("Thời gian nháy mắt, thân thể liền trở nên cao hơn hai trượng, tích huyết trùng sinh đỉnh phong lực lượng, hỏa diễm giống như thiêu đốt."),t("br"),n._v(" Lạch cạch!"),t("br"),t("br"),n._v("Hỏa diễm dập tắt, Thần Linh Hoàng từ không trung rơi xuống, đập ở trong vương thành ở giữa, tế đàn sụp đổ, xuất hiện một cái hố sâu to lớn."),t("br"),t("br"),n._v("“Thật mạnh...”"),t("br"),t("br"),n._v("Trương Huyền lông mày nhảy dựng."),t("br"),t("br"),n._v("Hắn ở bên cạnh, có thể rõ ràng nhìn ra, trước mắt vị này “Thần Linh” cũng không thi triển ra toàn lực, tiện tay vỗ một cái, tích huyết trùng sinh đỉnh phong cảnh giới Thần Linh Hoàng, dùng hết toàn lực, đều không đỡ được..."),t("br"),t("br"),n._v("Đây là phá toái hư không thực lực?"),t("br"),t("br"),n._v("Lúc trước còn cho rằng, dựa vào Long Cốt thần thương, cùng Hỏa Tư Cổ Thánh đám người, hẳn là có thể một trận chiến, hiện tại mới hiểu được... Không thể nào làm được."),t("br"),t("br"),n._v("Chênh lệch thực tế quá lớn, không phải nhiều người là có thể giải quyết."),t("br"),t("br"),n._v("Liền giống như Cổ Thánh cùng Đại Thánh, kém là sinh mệnh đẳng cấp, mà không số lượng."),t("br"),t("br"),n._v("Rung động trong lòng, đồng thời tràn đầy lo nghĩ, chẳng lẽ... Thần Linh Hoàng đối với mình tế tự đồ vật giá trị, biết cũng không rõ ràng? Ngược lại bản thân, viết đúng?"),t("br"),t("br"),n._v("Nếu không, cái này “Thần Linh” tại sao lại động thủ với hắn?"),t("br"),t("br"),n._v("Trương Huyền đoán không sai, Thần Linh Hoàng giờ phút này là thật đã sụp đổ."),t("br"),t("br"),n._v("Hắn chuẩn bị vô số bảo vật, hầu như đem trọn cái Linh tộc có danh vọng giám bảo sư đều mời tới, vẫn như cũ không có làm rõ, cụ thể giá trị... Vì cái gì cái này giả biết?"),t("br"),t("br"),n._v("Dung mạo, linh hồn khí tức, huyết mạch... Liền cái này đều không kém chút nào, hắn đều cảm thấy mình là giả, đối phương là sự thật."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Từ trong hố lớn vọt ra, hàm răng cắn “Cục cục!” Vang vọng, Thần Linh Hoàng lau sạch sẽ khóe miệng tràn ra máu tươi, bỗng nhiên ngẩng đầu lên sọ, khí tức trùng kích vào, tóc dựng lên, trong ánh mắt mang theo không cho phép dao động kiên định."),t("br"),t("br"),n._v("“Nhìn tới đến từ Thượng Thương Thần Linh, cho là ta là giả?”"),t("br"),t("br"),n._v("“Thế nào, ngươi còn không muốn thừa nhận?”"),t("br"),t("br"),n._v("“Thần Linh” híp mắt lại, lòng bàn tay một cỗ lực lượng xoay tròn, bất cứ lúc nào cũng sẽ bạo liệt mà ra, đem người sau nổ thành bột phấn."),t("br"),t("br"),n._v("“Tiền bối cho rằng như vậy, ta không cách nào phản bác, chỉ có thể nói, đối phương ngụy trang chi thuật quá mạnh, nhưng...”"),t("br"),t("br"),n._v("Hít sâu một hơi, Thần Linh Hoàng khắp khuôn mặt là dữ tợn: “Ta nguyện ý lấy sinh mệnh của mình hiến tế, dâng hiến cho Thần Linh, cam nguyện làm người hầu của ngươi, người hầu, cả đời nghe theo mệnh lệnh của ngươi, không oán không hối, mà hắn...”"),t("br"),t("br"),n._v("Nói đến đây dừng lại một chút, lạnh lùng nhìn về phía cách đó không xa Trương Huyền: “Dám ư?”"),t("br"),t("br"),n._v("Nói xong, một giọt tinh huyết lập tức từ mi tâm bay ra, thẳng tắp hướng trước mắt “Thần Linh” bay đi."),t("br"),t("br"),n._v("Chỉ cần người sau tiếp thu, liền sẽ cùng khế ước Thánh thú đồng dạng, biến thành trở thành tùy thời kêu gọi hạ nhân."),t("br"),t("br"),n._v("Sinh tử cũng không khỏi bản thân khống chế."),t("br"),t("br"),n._v("“Cái tên này...”"),t("br"),t("br"),n._v("Trương Huyền trong lòng “Lộp bộp!” Một chút."),t("br"),t("br"),n._v("Khó trách lúc trước, cái tên này nói, Thần Dung Hoàng không có hắn hung ác, hắn liền sinh mệnh cũng có thể không muốn... Lúc trước còn tưởng rằng nói là tế tự, không nghĩ tới đã sớm có quyết định, tính toán đem bản thân cả người hiến tế cho đối phương!"),t("br"),t("br"),n._v("Khó trách, lôi kéo những vương giả kia, tử vong, đều không thèm để ý, cũng không có bất kỳ bối rối, nguyên lai lớn nhất át chủ bài chính là vị này “Thần Linh”."),t("br"),t("br"),n._v("Trở thành hạ nhân, tôi tớ, tự nhiên trăm phần trăm thu hoạch được tín nhiệm, thân là chủ nhân, trợ giúp hạ nhân bình phục phản loạn, cũng là chuyện đương nhiên."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("“Thần Linh” ngón tay duỗi ra, đem huyết dịch thu vào mi tâm, sau một khắc liền nắm giữ đối phương ý niệm cùng linh hồn, có thể nhất niệm đánh giết."),t("br"),t("br"),n._v("Làm được những này, quay đầu nhìn về phía cách đó không xa Trương Huyền, ánh mắt lại không còn lúc trước ổn định, mà là mang theo lạnh lùng."),t("br"),t("br"),n._v("Trương Huyền trái tim tùy theo xiết chặt."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1848"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1850"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);