(window.webpackJsonp=window.webpackJsonp||[]).push([[96],{449:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1063-truong-huyen-tho-lo"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1063-truong-huyen-tho-lo"}},[n._v("#")]),n._v(" Chương 1063: Trương Huyền thổ lộ")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1063: Trương Huyền thổ lộ"),t("br"),t("br"),n._v("Bị đánh như thế, Giang Nguyên biết kẻ cầm đầu, buồn bực cắn răng, không có chút gì do dự, đối với Phùng Huân liền vọt lên."),t("br"),t("br"),n._v("Mặc dù mọi người đều rất nguy hiểm, có thể cơn giận này nếu là nhịn, đường đường Chiến sư, huyết tính cũng liền không có, còn thế nào chiến đấu?"),t("br"),t("br"),n._v("“Trương sư, Ngô sư... Cứu ta...”"),t("br"),t("br"),n._v("Giang Nguyên thực lực đột phá, đã đạt tới Nguyên Thần cảnh, Phùng Huân đương nhiên không phải là đối thủ, bị một hồi cuồng ẩu, cũng nhịn không được nữa, vội vàng cầu cứu."),t("br"),t("br"),n._v("Ngô sư, Trương Huyền đám người xoay người sang chỗ khác, đưa lưng về phía đối phương, giả bộ như không nghe thấy."),t("br"),t("br"),n._v("“Trận pháp này quả thực rất khó giải quyết!”"),t("br"),t("br"),n._v("“Đúng vậy a, nếu là có phương pháp liền tốt...”"),t("br"),t("br"),n._v("“Hôm nay thời tiết cũng rất tốt ah!”"),t("br"),t("br"),n._v("“Trời sáng khí trong, gió mát ấm áp dễ chịu...”"),t("br"),t("br"),n._v("Cứu ngươi? Nói đùa, vừa rồi ngươi đánh người thời điểm, cao hứng như vậy, đều khiến người phát tiết một chút đi! Nếu không thì không phải cũng quá thật xin lỗi vị này Giang Nguyên?"),t("br"),t("br"),n._v("Đánh một hồi, Giang Nguyên lúc này mới hết giận."),t("br"),t("br"),n._v("Lúc này, Phùng Huân đầu đã sưng tấy uốn tóc, không nói ra được thê."),t("br"),t("br"),n._v("Nhìn thấy hắn bộ dáng này, Trương Huyền ánh mắt sáng lên, đột nhiên nhớ ra cái gì đó: “Đúng a, ta làm sao không nhớ ra được... Chúng ta không cách nào phá trận, có thể để cho Hồng Diệp Vương giúp chúng ta phá nha...”"),t("br"),t("br"),n._v("“Giúp chúng ta phá?” Mọi người sững sờ."),t("br"),t("br"),n._v("“Không sai...” Trương Huyền nhẹ nhàng cười một tiếng, cổ tay khẽ đảo, lòng bàn tay nhiều ra một đống trận kỳ."),t("br"),t("br"),n._v("Lần trước Hàn hội trưởng cùng mọi người lấy ra, cấp bảy trận kỳ, đến bây giờ đều không dùng hết."),t("br"),t("br"),n._v("“Đi!”"),t("br"),t("br"),n._v("Một cái trận đồ trong đầu hiện lên, quát khẽ một tiếng, trận kỳ trực tiếp bay ra ngoài."),t("br"),t("br"),n._v("Hắn bây giờ có được Thất Tinh đỉnh phong trận pháp sư năng lực, mặc dù thực lực không đủ, không cách nào bố trí ra Thất Tinh đỉnh phong cấp bậc trận pháp, nhưng bố trí ra bình thường cấp bảy trận pháp, vẫn là dễ như trở bàn tay."),t("br"),t("br"),n._v("Trận kỳ rơi xuống đất, quay một vòng, bàn chân bỗng nhiên đạp mạnh."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Trận pháp khởi động, lập tức một đoàn sương trắng, đem mọi người toàn bộ bao phủ ở bên trong."),t("br"),t("br"),n._v("“Trương sư có ý tứ là... Chúng ta trốn ở trong trận pháp, dụ dỗ Hồng Diệp Vương đến đây?”"),t("br"),t("br"),n._v("Hàn hội trưởng mắt sáng rực lên."),t("br"),t("br"),n._v("Trước đó, bọn họ bị nhốt trong trận, thuộc về địch tối ta sáng, rất dễ dàng bị động chịu đòn, mà bây giờ bị bao phủ tại một cái Thất Tinh mê trận bên trong, đối phương không thấy mình, thành công ẩn tàng, cho dù Hồng Diệp Vương muốn làm gì, cũng chỉ sợ không dễ dàng như vậy."),t("br"),t("br"),n._v("Thành công đem bất lợi điều kiện chuyển thành có lợi."),t("br"),t("br"),n._v("Bất quá, làm như vậy, thoạt nhìn rất đơn giản, không có bất kỳ cái gì kỹ thuật hàm lượng, vừa vặn vì Thất Tinh đỉnh phong trận pháp sư, biết trong đó độ khó."),t("br"),t("br"),n._v("Tại trong trận pháp bày trận, muốn đối chung quanh trận pháp như lòng bàn tay, thậm chí rất nhiều biến hóa đều hoàn toàn biết mới được, nếu không, đối phương đại trận biến đổi, chung quanh khí tràng biến hóa, ngươi trận pháp liền sẽ tự sụp đổ, lộ ra chân ngựa."),t("br"),t("br"),n._v("Đổi lại những người khác, cho dù nghĩ đến cũng làm không được, đoán chừng cũng chỉ có Trương sư tên biến thái này mới có thể hoàn thành."),t("br"),t("br"),n._v("“Đúng...” Trương Huyền nhẹ gật đầu."),t("br"),t("br"),n._v("Nghe hắn xác nhận, mọi người tất cả đều hiểu được, mắt sáng rực lên, chỉ có Phùng Huân đứng tại một bên, đầy đầu sương mù: Nhìn ta bị đánh như thế, thế mà nghĩ đến cái này..."),t("br"),t("br"),n._v("Trận pháp này cùng ta bị đánh có lông quan hệ? Ngươi là thế nào nghĩ tới? Đến cùng cái gì não mạch kín?"),t("br"),t("br"),n._v("“Có trận pháp này cắt đứt, mọi người không cần tiếp tục lo lắng bị công kích, trước từng người điều chỉnh một chút, khôi phục thực lực...”"),t("br"),t("br"),n._v("Trương Huyền khai báo."),t("br"),t("br"),n._v("Đi vào cái này Thiên cung về sau, mọi người một mực bị công kích, sớm đã thể xác tinh thần mệt mỏi."),t("br"),t("br"),n._v("“Được...” Mọi người nhẹ gật đầu, từng người lấy ra khôi phục thể lực đan dược, linh thạch, khoanh chân ngồi dưới đất tu luyện."),t("br"),t("br"),n._v("Thấy tạm thời không có nguy hiểm, Trương Huyền cũng thở phào nhẹ nhõm, đang muốn nghiệm chứng cái nào đó ý nghĩ, liền ngửi được làn gió thơm bay tới, Lạc Nhược Hi đi tới qua trước mặt, nhìn về phía hắn ánh mắt phức tạp: “Đa tạ!”"),t("br"),t("br"),n._v("“Không cần khách khí!”"),t("br"),t("br"),n._v("Biết nàng là vì chuyện vừa rồi cảm tạ mình, Trương Huyền cười cười."),t("br"),t("br"),n._v("Đối phương vốn cũng không phải Thiên Diệp Vương, chỉ là luận sự mà thôi."),t("br"),t("br"),n._v("“Cám ơn ngươi tin tưởng ta...”"),t("br"),t("br"),n._v("Lắc đầu, cô gái dường như nhớ ra cái gì đó, mang trên mặt vẻ cô đơn, có điều, rất nhanh quét sạch sành sanh, xòe bàn tay ra: “Ngươi nếu thật muốn phá trận lời nói, ta có lẽ có thể giúp ngươi!”"),t("br"),t("br"),n._v("“Ồ? Ngươi biết trận pháp này là cái gì?” Trương Huyền ánh mắt sáng lên."),t("br"),t("br"),n._v("“Ta không biết, có điều, trận pháp này tầng tầng lớp lớp, một cái tiểu trận, tiếp lấy một cái tiểu trận, vô cùng vô tận, thật muốn xông vào lời nói, liền sẽ ngã vào vô cùng tuần hoàn bên trong, mãi mãi cũng không cách nào chạy ra... Trừ phi, nhảy ra trận pháp!”"),t("br"),t("br"),n._v("Lạc Nhược Hi nói."),t("br"),t("br"),n._v("“Nhảy ra trận pháp? Nhảy thế nào ra ngoài?” Trương Huyền cười khổ."),t("br"),t("br"),n._v("Hắn cũng biết đạo lý này, nếu là có thể nhảy ra ngoài, khẳng định sớm đã có biện pháp, đáng tiếc, thư viện chỉ có thể nhìn một cái tiểu trận thiếu hụt, không cách nào quản lý toàn cục, cũng liền không biết toàn bộ đại trận hướng đi cùng xu thế."),t("br"),t("br"),n._v("“Vừa rồi chúng ta đã liên tục xông mấy cái sân nhỏ, mỗi một cái sân nhỏ đều có đường kính vài trăm mét lớn nhỏ... Dựa theo phương vị tính toán, cho dù trốn không thoát Thiên cung, giờ phút này cũng tất nhiên đến biên giới... Có thể trên thực tế, cho dù chúng ta lại xông mười mấy cái sân nhỏ, cũng đồng dạng ra không được, ngươi không cảm thấy kỳ quái sao?”"),t("br"),t("br"),n._v("Lạc Nhược Hi nói."),t("br"),n._v("“Cái này... Ngươi ý tứ chẳng lẽ nói trận pháp này, có thể là ẩn chứa không gian bí mật?” Trương Huyền con ngươi co rụt lại."),t("br"),t("br"),n._v("Dựa theo khoảng cách suy tính, rõ ràng có thể ra ngoài, lại không cách nào rời khỏi, chỉ có một khả năng... Không gian trận pháp!"),t("br"),t("br"),n._v("“Đúng thế, ta vì cái gì không nghĩ tới, Khưu Ngô cổ thánh bản thân liền am hiểu không gian, nguyên nhân chính là như vậy, mới có thể bố trí ra lợi hại như vậy phong ấn... Lại nói, chúng ta vừa đến liền gặp trận pháp không gian... Ta hẳn là rất dễ dàng liền có thể nghĩ tới...”"),t("br"),t("br"),n._v("Không cần đối phương trả lời, Trương Huyền vỗ trán một cái, bừng tỉnh hiểu ra."),t("br"),t("br"),n._v("Bọn họ vừa đến liền lâm vào trận pháp không gian, dựa theo tình huống bình thường, đi vào trận pháp này về sau, hẳn là rất nhanh đi không gian trên phương diện muốn... Làm sao lại không để ý đến đây!"),t("br"),t("br"),n._v("“Một câu đánh thức người trong mộng, đa tạ Nhược Hi, thật sự là yêu ngươi chết mất...”"),t("br"),t("br"),n._v("Tràn đầy hưng phấn, Trương Huyền thoáng cái nhảy lên, đối với cô gái khuôn mặt hôn một cái, cười ha ha."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Không nghĩ tới hắn sẽ làm như vậy, chuyện đột nhiên xảy ra, Lạc Nhược Hi chưa kịp tránh né, đôi mắt thanh tú trợn tròn, gương mặt xinh đẹp phấn hồng."),t("br"),t("br"),n._v("Cái tên này thoạt nhìn thật đàng hoàng, làm sao... Làm sao..."),t("br"),t("br"),n._v("“Không để ý tới ngươi...”"),t("br"),t("br"),n._v("Quay người lại, đi vào trận pháp, tìm một chỗ khoanh chân ngồi xuống, lúc này mới cảm thấy vẻ mặt như là phát sốt, tràn đầy nóng hổi."),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Vừa rồi nhất thời hưng phấn, kìm lòng không được, mới làm ra quá mức cử động, nhìn thấy cô gái xoay người rời đi, Trương Huyền lúc này mới kịp phản ứng, có chút xấu hổ, có điều, sờ lên bờ môi... Thơm quá."),t("br"),t("br"),n._v("“Nàng sẽ không về sau cũng không tiếp tục để ý đến ta đi?”"),t("br"),t("br"),n._v("Bất kể kiếp này hay là kiếp trước, đều là cái không nói qua yêu độc thân cẩu, đột nhiên trải qua loại sự tình này, lập tức có chút bối rối, cùng trước đó chỉ điểm sơn hà khí thế hoàn toàn khác biệt."),t("br"),t("br"),n._v("Bất quá hắn cũng biết, bây giờ không phải là cân nhắc những chuyện này thời điểm, vận chuyển một hồi tâm cảnh, để cho mình khôi phục bình tĩnh, cổ tay khẽ đảo, Thiên Nghĩ Phong Mẫu xuất hiện ở trước mắt."),t("br"),t("br"),n._v("Đối với không gian trận pháp, hắn hoàn toàn không biết gì cả, nhưng mà Thiên Nghĩ Phong Mẫu, trời sinh liền có loại năng lực này, có lẽ tìm tới đường ra, dẫn đầu nhóm người mình chạy đi."),t("br"),t("br"),n._v("Đem tình huống trước mắt, đối phong mẫu nói một lần."),t("br"),t("br"),n._v("Phong mẫu nhẹ gật đầu, nằm rạp trên mặt đất, ngửi lên."),t("br"),t("br"),n._v("Thiên Nghĩ Phong Mẫu, chỉ là trời sinh nắm giữ bố trí không gian năng lực, là một loại bản năng, nhưng mà đối cái gọi là không gian pháp tắc, cũng không lý giải."),t("br"),t("br"),n._v("Cái này rất giống ong mật xây tổ đồng dạng, hoàn mỹ đang hình sáu cạnh sào huyệt, kiến trúc sư đều làm không được... Cũng không thể nói, ong mật tinh thông công trình bằng gỗ, kiến trúc cơ học, là cái học bá đi!"),t("br"),t("br"),n._v("Ngồi trên mặt đất ngửi một lát, Thiên Nghĩ Phong sào đứng lên."),t("br"),t("br"),n._v("“Chủ nhân, đây là cái không gian gấp, ta có thể mang theo đi tìm ra đường...”"),t("br"),t("br"),n._v("“Quá tốt rồi...”"),t("br"),t("br"),n._v("Thấy nó quả nhiên có thể tìm tới, Trương Huyền con mắt tỏa ánh sáng, vội vàng chuyển người qua đến, báo tin Ngô sư đám người."),t("br"),t("br"),n._v("Lần này, dùng chính là truyền âm."),t("br"),t("br"),n._v("Rất nhanh, mọi người liền tập hợp."),t("br"),t("br"),n._v("Mặc dù thời gian nghỉ ngơi không dài, nhưng mà tất cả mọi người thân kinh bách chiến, đã khôi phục hơn phân nửa thực lực."),t("br"),t("br"),n._v("“Tất cả mọi người đi theo phong mẫu đằng sau, đừng tụt lại phía sau...”"),t("br"),t("br"),n._v("Dặn dò một tiếng, Trương Huyền lúc này mới đi tới Lạc Nhược Hi trước mặt, thấy cô gái cũng không có tức giận bộ dạng, mới thở phào nhẹ nhõm: “Chúng ta cũng theo sau đi!”"),t("br"),t("br"),n._v("Nói, nắm cô gái bàn tay."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Vùng vẫy một hồi, phát hiện hắn càng nắm càng chặt, cô gái nhịn không được nhíu mày."),t("br"),t("br"),n._v("Cho màu sắc mở nhuộm nhà, càng ngày càng quá đáng có phải không?"),t("br"),t("br"),n._v("Đang muốn rút trở về, thuận tiện dạy bảo thoáng cái, liền nghe đến đối diện thanh niên thanh âm chậm rãi vang lên."),t("br"),t("br"),n._v("“Huyễn Vũ sơn mạch, lần thứ nhất nhìn thấy ngươi, yên tĩnh thản nhiên, đứng tại thác nước trước mặt, cùng hoàn cảnh chung quanh hoà vào một thể, như là một bộ mực in tranh, ta liền biết nhân sinh của ta có mục tiêu!”"),t("br"),t("br"),n._v("“Nhìn thấy ngươi bị rất nhiều linh thú vây công, nhịn không được ra tay, về sau trong sơn động, linh hồn gặp phải phản phệ, là ngươi xuất thủ cứu ta. Đợi ta tỉnh lại, ngươi đã rời khỏi... Ta không biết tên của ngươi, cũng không biết ngươi là ai, tưởng là kiếp này cũng không còn cách nào nhìn thấy, thất vọng mất mát, hình như mất đi sinh mệnh trọng yếu nhất bảo bối.”"),t("br"),t("br"),n._v("“Về sau tại danh sư học viện, lần nữa thấy được ngươi, kích động sắp bùng nổ, cảm thấy nhất định phải hỏi ra tên của ngươi, biết ngươi là ai...”"),t("br"),t("br"),n._v("Nhớ lại cùng cô gái này quá khứ, Trương Huyền trên mặt lộ ra nụ cười ngọt ngào."),t("br"),t("br"),n._v("Sống lại gặp qua nhiều như vậy nữ tử, có uyển ước, có dịu dàng, có Haki, có y như là chim non nép vào người, có đối với hắn có đặc thù tình cảm... Nhưng mà chẳng biết tại sao, chỉ đối trước mắt cái này, có động tâm cùng nhớ cảm giác."),t("br"),t("br"),n._v("Cho nên, không muốn bỏ qua!"),t("br"),t("br"),n._v("“Chúng ta cùng đi Phong Thánh đài, mặc dù ở nơi đó thấy được Khổng sư ý niệm, nhận được hắn tự tay viết lưu lại thư tịch... Thế nhưng là để cho ta vui vẻ nhất vẫn là trên đường đi có thể cùng ngươi cùng một chỗ, mỗi ngày tu luyện hoàn tất, liền có thể nhìn thấy ngươi ngồi tại ta cách đó không xa, an tĩnh như vậy, như vậy Silent như vẽ, theo ta nói chuyện phiếm, theo ta cùng nhau nghiên cứu công pháp thiếu thốn cùng ưu điểm... Nói thật, ta thật muốn để khi đó thời gian vĩnh cửu dừng lại, cứ như vậy một mực tại trước mắt...”"),t("br"),t("br"),n._v("“Ngươi nói với ta muốn cùng đi di tích, ngươi biết ta cao hứng biết bao nhiêu ư? Tất cả mọi người nói nơi này mười phần nguy hiểm, có đi không về, nhưng mà ta nhưng cảm thấy, có thể cùng ngươi cùng một chỗ, lại nguy hiểm đều đáng giá...”"),t("br"),t("br"),n._v("“Lúc này mới biết được nội tâm của mình, là thật muốn cùng ngươi cùng một chỗ, vĩnh viễn cùng một chỗ, dù là bỏ mình, cũng sẽ không hối hận!”"),t("br"),t("br"),n._v("Nhìn về phía cô gái, Trương Huyền trong ánh mắt toát ra vẻ kiên định."),t("br"),t("br"),n._v("“Nhược Hi, thỉnh cho phép ta một mực như vậy dắt ngươi, từ từ tiếp tục đi, được không?”"),t("br"),t("br"),n._v("(Trương Huyền đều thổ lộ, mọi người nguyệt phiếu không có sao? Còn có, ta ngày hôm qua chỉ chương, là đảo ngược ah, còn chưa xem xong, liền nói ta kiêu ngạo, thật được không? Lão nhai giờ phút này vẫn như cũ quỳ trên mặt đất, nước mắt ào ào, cảm giác bản thân chơi thoát.. Độc giả đại ca, độc giả đại gia, ta sai rồi...)"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1071"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1073"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);