(window.webpackJsonp=window.webpackJsonp||[]).push([[961],{1316:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1839-sau-cung-tuyet-chieu"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1839-sau-cung-tuyet-chieu"}},[n._v("#")]),n._v(" Chương 1839: Sau cùng tuyệt chiêu")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1839: Sau cùng tuyệt chiêu"),t("br"),t("br"),n._v("“Ngươi rất thông minh! Cũng rất thức thời!”"),t("br"),t("br"),n._v("Gặp một câu nói liền để một vị Cổ Thánh phản loạn, “Thần Linh” nhẹ nhàng cười một tiếng, lộ ra vẻ hài lòng, lần nữa hướng những người khác nhìn tới: “Các ngươi đây?”"),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Thần Dung Hoàng đưa tới ba vị Cổ Thánh, từng cái thân thể run rẩy, không biết trả lời như thế nào."),t("br"),t("br"),n._v("Rõ ràng lập tức liền phải thắng, toát ra cái dạng này cường giả, để cho bọn họ căn bản không có cách nào lựa chọn."),t("br"),t("br"),n._v("“Dung Hoàng, xin lỗi, lần này, cũng không phải là ta không giúp ngươi... Nợ ân tình của ngươi, kiếp sau trả lại!”"),t("br"),t("br"),n._v("Dừng lại mấy hơi thở, lại một vị Cổ Thánh quay người lại, hướng “Thần Linh” bay đi."),t("br"),t("br"),n._v("“Sinh tử trước mặt, các ngươi lựa chọn như thế nào, đều không có cái gì...”"),t("br"),t("br"),n._v("Lắc đầu, Thần Dung Hoàng nhìn về phía còn lại hai vị Cổ Thánh."),t("br"),t("br"),n._v("Lại thấy hai người đồng thời ánh mắt: “Sống lâu như vậy, đã sớm chán ghét, nếu như có thể chết ở Thượng Thương thần linh chi thủ, đủ để lưu danh sử sách, chuyến đi này không tệ! Dung Hoàng, ngươi từng cứu ta, ta cùng ngươi cùng tiến thối!”"),t("br"),t("br"),n._v("“Ta cũng vậy, ta Kim Tháp Cổ Thánh, đồng ý ngươi, không có ý định sống trở về...”"),t("br"),t("br"),n._v("Có người tham sống sợ chết, cũng có chút người chân chính trung nghĩa."),t("br"),t("br"),n._v("Dị Linh tộc nhân, cũng không thiếu người trung nghĩa."),t("br"),t("br"),n._v("“Đa tạ...”"),t("br"),t("br"),n._v("Gặp biết rõ hẳn phải chết, còn nguyện ý đứng tại phía bên mình, Thần Dung Hoàng tràn đầy cảm kích, nhìn về phía Hỏa Tư Cổ Thánh."),t("br"),t("br"),n._v("“Nhìn ta làm gì? Lão tử không sợ không sợ trời đất, sợ nhất tạm thời lật lọng, Thượng Thương Thần Linh lại như thế nào? Muốn giết ta, cũng phải đi một lớp da!”"),t("br"),t("br"),n._v("Hỏa Tư Cổ Thánh cười lạnh, hàm răng âm u tĩnh mịch."),t("br"),t("br"),n._v("Nó thừa nhận chủ nhân ân tình, tự nhiên muốn tới cùng tiến thối."),t("br"),t("br"),n._v("“Ngươi khẳng định cũng không cần nói...”"),t("br"),t("br"),n._v("Lần nữa nhìn về phía Mặc Linh Cổ Thánh, thấy hắn ánh mắt kiên định, Thần Dung Hoàng biết nói nhiều vô ích, nhìn về phía Trương Huyền: “Thiếu gia, làm sao bây giờ!”"),t("br"),t("br"),n._v("“Còn có thể làm sao, đương nhiên là liều mạng!”"),t("br"),t("br"),n._v("Mỉm cười, Trương Huyền hừ nhẹ một tiếng, trường thương lần nữa run lên."),t("br"),t("br"),n._v("“Phá!”"),t("br"),t("br"),n._v("Thẳng tắp vọt ra ngoài, trường thương như rồng, bốn phía lóe ra từng đạo hư ảnh, đem vị này “Thần Linh” bao phủ ở bên trong."),t("br"),t("br"),n._v("“Động thủ!”"),t("br"),t("br"),n._v("Biết không đường lui, Thần Dung Hoàng cũng vừa vặn nghênh tiếp."),t("br"),t("br"),n._v("Sáu đại Cổ Thánh liên hợp, thiên địa dường như tùy thời sụp đổ, toàn bộ Dị Linh vương thành, động đất đồng dạng, vô số kiến trúc sụp đổ, đủ loại trận pháp, phù văn lần nữa tác phẩm hội họa linh khí, di tán tại bốn phía."),t("br"),t("br"),n._v("Cổ Thánh cường giả thực tế quá lợi hại, một cái liền có thể hủy diệt vô số sinh linh, huống chi nhiều như vậy đồng thời ra tay."),t("br"),t("br"),n._v("“Một đám không biết trời cao đất rộng sâu kiến!”"),t("br"),t("br"),n._v("Bị vây công ở giữa, “Thần Linh” cười lạnh, hai tay cuồn cuộn, cũng không thấy có gì chiêu số, mọi người công kích, liền biến thành vô hình, không có chút nào hiệu quả."),t("br"),t("br"),n._v("Trong tay Long Cốt thần thương bị đối phương gảy liên tục ba lần, Trương Huyền gan bàn tay đổ máu, tinh thần khẽ động."),t("br"),t("br"),n._v("Một quyển sách xuất hiện, ngay sau đó đôi cánh tay, từ đó lan ra mà ra, thẳng tắp đối trước mắt “Thần Linh” bắt tới."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Cùng “Thần Linh” bàn tay đối đầu cùng một chỗ, một mực trên mặt không có bất kỳ cái gì gợn sóng người sau, con mắt sáng lên: “Phá toái hư không cường giả?”"),t("br"),t("br"),n._v("Hiển nhiên, hắn liếc mắt liền nhìn ra Ngoan Nhân khi còn sống thực lực, giống như hắn, cũng đạt tới phá toái hư không, thậm chí so hắn hiện tại, mạnh hơn."),t("br"),t("br"),n._v("Loại này linh khí mỏng manh chỗ, vậy mà có thể sinh ra lợi hại như thế cường giả... Suy nghĩ một chút cũng không dám tin tưởng."),t("br"),t("br"),n._v("“Lưu lại cho ta đi!”"),t("br"),t("br"),n._v("Hít sâu một hơi, hướng về phía trước vồ liên tục."),t("br"),t("br"),n._v("Tê lạp!"),t("br"),t("br"),n._v("Không gian bị xé rách ra đến, Ngoan Nhân cánh tay, lại không cách nào chống lại, bị tại chỗ trói buộc."),t("br"),t("br"),n._v("Ngoan Nhân hiện tại nửa người trên khung xương, triệt để thu thập hoàn chỉnh, còn thiếu hụt bắp đùi cùng chân, thực lực chân chính, chỉ có tích huyết trùng sinh đỉnh phong, cùng đối phương tỷ thí, kém rất lớn một đoạn, căn bản không phải đối thủ."),t("br"),t("br"),n._v("“Thiếu hụt!”"),t("br"),t("br"),n._v("Gặp Ngoan Nhân công kích, dẫn tới đối phương biểu hiện ra thực lực, Trương Huyền vội vàng câu thông thư viện."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Lắc lư một cái, cũng không xuất hiện thư tịch."),t("br"),t("br"),n._v("Trương Huyền lông mày nhảy dựng."),t("br"),n._v("Nếu như biết thiếu hụt, mọi người có lẽ còn có thể đánh lén, thư viện không phát hiện được, còn thế nào đánh?"),t("br"),t("br"),n._v("“Chẳng lẽ cái tên này không phải thế giới này, thế giới này Thiên đạo, không cách nào dò xét?”"),t("br"),t("br"),n._v("Trong lòng toát ra một cái ý nghĩ."),t("br"),t("br"),n._v("Đối phương từ một thế giới khác mà đến, không thuộc về thế giới này sinh linh, thực lực lại cường đại quá phận..."),t("br"),t("br"),n._v("Có thể hay không bởi vì như vậy, mới không có cách nào dò xét?"),t("br"),t("br"),n._v("“Lui!”"),t("br"),t("br"),n._v("Không biết thiếu hụt, tiếp tục đánh xuống, làm không cẩn thận toàn bộ ngã xuống, Trương Huyền một tiếng hô khẽ, trường thương vạch một cái, hướng về phía sau nhảy ra."),t("br"),t("br"),n._v("Thần Dung Hoàng đám người đối với hắn mệnh lệnh, không có bất kỳ cái gì nghi ngờ, cũng đồng thời thả ra công kích mạnh nhất."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Bầu trời tựa như thả ra to lớn pháo hoa, cuồng bạo sóng khí, để phạm vi vài trăm mét phía trong, không ai dám tiếp cận, vừa mới dừng lại, Trương Huyền đang muốn đâm rách không gian, mang theo mọi người trốn đi, liền phát hiện chắc chắn như sắt, đối phương dường như nhìn ra ý nghĩ của hắn, cách đó không xa “Thần Linh” đã đem phạm vi mấy trăm cây số phía trong không gian, triệt để giam giữ lại."),t("br"),t("br"),n._v("“Nếu để cho các ngươi từ trước mắt ta chạy trốn, cũng không cần sống...”"),t("br"),t("br"),n._v("Cười nhạt một tiếng, “Thần Linh” chậm rãi hướng về phía trước, mỗi đi một bước, liền cho người ta về mặt tâm linh bất khả kháng hoành cảm giác áp bách."),t("br"),t("br"),n._v("Cái tên này quá mạnh, bất luận từ chỗ nào một mặt, đều giống như không có thiếu hụt, hoàn mỹ không giống loài người."),t("br"),t("br"),n._v("“Đem cái kia phá toái hư không cường giả khung xương cho ta, ta có thể tha các ngươi không chết!”"),t("br"),t("br"),n._v("Gặp cấp cho cảm giác áp bách, để mọi người không dám tiếp tục chạy trốn, “Thần Linh” nhẹ nhàng cười một tiếng."),t("br"),t("br"),n._v("“Chủ nhân, không thể bỏ qua bọn họ...”"),t("br"),t("br"),n._v("Đồng tử co rụt lại, Thần Linh Hoàng vội vàng hét."),t("br"),t("br"),n._v("“Láo xược! Ta làm sao quyết định, cần ngươi tới chỉ điểm?”"),t("br"),t("br"),n._v("Xoay người liếc mắt nhìn, Thần Linh Hoàng lập tức gặp phải trọng kích, lần nữa bay ngược mà ra, “Thần Linh” vung một cái ống tay áo, hừ một câu."),t("br"),t("br"),n._v("“Thế nào?”"),t("br"),t("br"),n._v("Xử lý xong đối phương, “Thần Linh” lần nữa hướng Trương Huyền nhìn tới."),t("br"),t("br"),n._v("Hắn đã nhìn ra, trong mọi người nói chuyện quản dụng nhất, chính là cái này tu vi yếu nhất gia hỏa, cái kia khung xương, hiển nhiên cũng nghe từ mệnh lệnh của hắn."),t("br"),t("br"),n._v("“Muốn khung xương?”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu: “Nằm mơ!”"),t("br"),t("br"),n._v("Ngoan Nhân từ rất sớm lúc trước liền đi theo phía sau hắn, lập xuống công lao hiển hách, vì bản thân sinh mệnh, liền tặng người... Tuyệt đối không thể!"),t("br"),t("br"),n._v("“Vậy ta đành phải không khách khí...”"),t("br"),t("br"),n._v("Thấy hắn nói kiên quyết như thế, “Thần Linh” cười lạnh một tiếng, bàn tay nâng lên, thẳng tắp hướng Trương Huyền vỗ xuống."),t("br"),t("br"),n._v("Vũ trụ sụp đổ, thiên địa trầm luân."),t("br"),t("br"),n._v("Trương Huyền trong nháy mắt, cảm giác tinh thần cùng linh hồn của mình, đều giống như bị tước đoạt đồng dạng, lập tức liền muốn triệt để tử vong."),t("br"),t("br"),n._v("“Liều mạng... Chết đi cho ta!”"),t("br"),t("br"),n._v("Biết lại không có gì giữ lại, Trương Huyền hàm răng cắn chặt, tinh thần khẽ động."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Một quyển sách từ chỗ mi tâm bay ra, thấy gió liền dài, vụt qua biến thành mấy chục trượng lớn nhỏ."),t("br"),t("br"),n._v("Cuối cùng một bản Thiên đạo chi sách!"),t("br"),t("br"),n._v("Ở lại thư viện, một mực vô dụng, giờ phút này cũng lại không quản được nhiều như vậy, trực tiếp tế đi ra."),t("br"),t("br"),n._v("“Đây là cái gì?”"),t("br"),t("br"),n._v("Không nghĩ tới đối phương, sắp chết đến nơi, không còn thủ đoạn, cầm một quyển sách muốn đập chết bản thân, “Thần Linh” suýt chút nữa cười tắt thở: “Ngươi cho rằng, một bản sách nát đối ta có tác dụng? Có thể thương tổn được ta mảy may? Cười chết người...”"),t("br"),t("br"),n._v("Răng rắc!"),t("br"),t("br"),n._v("Lời còn chưa dứt, đập ở trên đầu."),t("br"),t("br"),n._v("Mắt tối sầm lại, “Thần Linh” cảm giác ý thức bắt đầu tán loạn."),t("br"),t("br"),n._v("Phù phù!"),t("br"),t("br"),n._v("Nằm trên mặt đất, cắt đứt hô hấp."),t("br"),t("br"),n._v("“...”"),t("br"),t("br"),n._v("Trong nháy mắt, bốn phía lặng ngắt như tờ."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1850"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1852"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);