(window.webpackJsonp=window.webpackJsonp||[]).push([[965],{1320:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1843-linh-than-lai-xuat-hien"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1843-linh-than-lai-xuat-hien"}},[n._v("#")]),n._v(" Chương 1843: Linh Thần lại xuất hiện?")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1843: Linh Thần lại xuất hiện?"),t("br"),t("br"),n._v("Thiên Diệp sơn thẳng đứng vạn trượng, thẳng đứng đâm vào bầu trời, như là đâm vào trên đất giáo."),t("br"),t("br"),n._v("“Nghe nói núi này, là năm đó Khổng sư cùng thiên nhân giao chiến, đối phương ném xuống một kiện binh khí biến thành mà thành...” Một bên bay lên trên, Mặc Linh Cổ Thánh vừa nói."),t("br"),t("br"),n._v("Thán phục thiên nhiên quỷ phủ thần công, Trương Huyền cũng đầy là cảm khái."),t("br"),t("br"),n._v("Đoàn người nhanh chóng đi tới phía trên ngọn núi, là cái phạm vi hơn ba mươi mẫu đất bằng, ở chính giữa một cái đường kính khoảng ba mét hình tròn bệ đá, từ xa nhìn lại, giống như là cái tự nhiên tế đàn."),t("br"),t("br"),n._v("“Đây là câu thông Thượng Thương địa phương?”"),t("br"),t("br"),n._v("Trương Huyền nhíu nhíu mày."),t("br"),t("br"),n._v("Thần Dung Hoàng nhẹ gật đầu, càng thêm khí vô lực."),t("br"),t("br"),n._v("“Cần gì bảo vật tế tự? Ta đi chuẩn bị!” Thấy hắn cái bộ dáng này, Trương Huyền không tốt nói nhiều, mở miệng nói."),t("br"),t("br"),n._v("Thần Linh Hoàng tế tự, vô số dược vật, khoáng thạch, trân quý đồ vật... Càng là thiêu đốt mười vạn người sinh mệnh, mới phá vỡ không gian bích lũy, triệu hoán “Thần Linh” xuất hiện, mình muốn thấy Lạc Nhược Hi, hẳn là cũng không dễ dàng như vậy."),t("br"),t("br"),n._v("“Không cần, tế tự đồ vật, đã chuẩn bị xong...”"),t("br"),t("br"),n._v("Thần Dung Hoàng quay đầu nhìn về phía Lưu Dương."),t("br"),t("br"),n._v("Lưu Dương cũng không nhiều lời, đi tới bệ đá trước mặt, chờ chỉ chốc lát, trước đó hắn phân phó vị kia Bất Hủ cảnh cường giả, lúc này mới đi tới trước mặt, đưa qua một chiếc nhẫn."),t("br"),t("br"),n._v("Cổ tay khẽ đảo, một đống kim sắc lá cờ xuất hiện tại trước mặt, đâm vào bệ đá bốn phía."),t("br"),t("br"),n._v("“Bắt đầu đi!”"),t("br"),t("br"),n._v("Làm xong những này, Lưu Dương dặn dò một tiếng."),t("br"),t("br"),n._v("Vị này Bất Hủ cảnh cường giả nhẹ gật đầu, xoay người rời đi, ước chừng qua một canh giờ, Trương Huyền đột nhiên cảm thấy trong lòng một sợ, vội vàng quay đầu, ngay sau đó nhìn thấy chung quanh tám cái phương hướng, đều có một đạo quang mang bắn ra, giống như là bị trên bệ đá trận kỳ thu hút, rất nhanh lao qua."),t("br"),t("br"),n._v("“Là tám tòa thái miếu, đồng thời tế tự, mấy vạn người đồng thời bẩm báo Thượng Thương, mọi người đồng tâm hiệp lực...”"),t("br"),t("br"),n._v("Nhìn một vòng, Mặc Linh Cổ Thánh hiểu được."),t("br"),t("br"),n._v("“Thái miếu?”"),t("br"),t("br"),n._v("“Là Dị Linh tộc nhân, tế tự tổ tiên địa phương, mỗi lần loại này tế tự, đều cần có người chuyên môn chủ trì, hơn vạn Dị Linh tộc nhân tập hợp, mới có thể hoàn thành, tám đại thái miếu đồng thời tế tự, cũng chỉ có dung Hoàng mới có như vậy lực hiệu triệu, những người khác muốn hoàn thành, căn bản làm không được...”"),t("br"),t("br"),n._v("Mặc Linh Cổ Thánh giải thích."),t("br"),t("br"),n._v("Trương Huyền giật mình."),t("br"),t("br"),n._v("Trước đó, đối phương từng nói qua, muốn tế tự, cần trước giúp hắn chém giết Linh Hoàng, Tinh Hoàng, đạt được toàn bộ Dị Linh tộc ủng hộ, mới có thể hoàn thành, hiện tại xem ra quả là thế."),t("br"),t("br"),n._v("Loại này cỡ lớn tế tự, không phải một, hai cá nhân liền có thể hoàn thành."),t("br"),t("br"),n._v("Vừa rồi Lưu Dương dặn dò, hẳn là vị kia Bất Hủ cảnh cường giả, đi chuẩn bị những vật này."),t("br"),t("br"),n._v("Tám đạo ánh sáng hội tụ tại trận kỳ phía trên, bệ đá rất nhanh biến thành ôn nhuận chi sắc, gương đồng dạng, đem rất nhiều ánh sáng tụ lại, thẳng đứng hướng lên bầu trời chiếu xạ."),t("br"),t("br"),n._v("“Tới phiên ta...”"),t("br"),t("br"),n._v("Cười nhạt một tiếng, Thần Dung Hoàng đi tới ở chính giữa, khoanh chân ngồi xuống."),t("br"),t("br"),n._v("Nhìn thấy động tác của hắn, Lưu Dương hốc mắt một đỏ."),t("br"),t("br"),n._v("“Ta Ngột Thần, bằng lòng lấy sinh mệnh của mình tế tự, cầu nguyện Thượng Thương, khẩn cầu gặp mặt Linh Thần...”"),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Kèm theo thanh âm vang lên, Thần Dung Hoàng trong cơ thể giống như là sinh ra một đám lửa, cháy hừng hực, đảo mắt liền đem hắn thôn phệ."),t("br"),t("br"),n._v("Trương Huyền biến sắc."),t("br"),t("br"),n._v("Còn tưởng rằng cái gọi là tế tự, chỉ cần tiêu tốn bảo vật liền có thể, không nghĩ tới cần hi sinh đối phương sinh mệnh."),t("br"),t("br"),n._v("Dù là đối phương, thân chịu trọng thương, ngày hôm nay chưa hẳn có thể sống sót, nhưng vì bản thân thấy Lạc Nhược Hi mà bùng cháy, cũng không phải hắn bằng lòng nhìn thấy."),t("br"),t("br"),n._v("Thân thể nhoáng một cái, đi tới trước mặt, trong cơ thể Thiên đạo chân khí, lập tức đem hắn bao phủ."),t("br"),n._v("“Dừng lại...”"),t("br"),t("br"),n._v("Muốn gặp Lạc Nhược Hi, nhưng cũng không hy vọng, vì thế, chảy ra máu tươi."),t("br"),t("br"),n._v("Không nguyện ý nợ ân, dù là một chút, đây là hắn làm việc nguyên tắc."),t("br"),t("br"),n._v("“Dù sao ta đã không còn sống lâu nữa, có thể vì thiếu gia làm việc, cam tâm tình nguyện!” Không có hấp thu Trương Huyền tuôn đi qua Thiên đạo chân khí, Thần Dung Hoàng cười nhạt một tiếng."),t("br"),t("br"),n._v("Khuôn mặt không thay đổi, giống như là bùng cháy tại thân thể bốn phía không phải hỏa, mà là ôn tuyền."),t("br"),t("br"),n._v("Đối với tu luyện giả, tự bạo dễ dàng, trong nháy mắt ý niệm cắt đứt, không cần lo lắng đau đớn cùng dày vò, có thể như vậy, lấy bản thân hiến tế, dùng liệt diễm từ bên trong ra ngoài thiêu đốt, một chút không chết được, khổ sở kiên trì, tựa như đang chịu đựng lăng trì... Đây mới là thống khổ nhất."),t("br"),t("br"),n._v("“Ta gặp lại Nhược Hi, bản thân sẽ nghĩ biện pháp...”"),t("br"),t("br"),n._v("Trong lòng không hiểu khó chịu, Trương Huyền vung tay lên, đối phương đã không đồng ý, vậy liền hủy đi bệ đá cùng trận kỳ, cưỡng ép ngăn cản."),t("br"),t("br"),n._v("Sinh mệnh đổi lấy tế tự, hắn không muốn tiếp nhận, cũng không nguyện ý."),t("br"),t("br"),n._v("Bất quá, lực lượng còn không có hạ xuống, một đạo mạnh mẽ khí tức, hạ xuống từ trên trời, thời gian nháy mắt liền đem Thần Dung Hoàng bao phủ ở bên trong."),t("br"),t("br"),n._v("Bị tức hơi thở bao phủ, dung Hoàng trên người bùng cháy hỏa diễm, dần dần bắt đầu tắt, không ngừng suy yếu linh hồn cùng thân thể, vậy mà chậm rãi có sinh cơ."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Giữa thiên địa, như là nhiều hơn một cái cánh cửa khổng lồ, một thân ảnh, xuất hiện tại tế đàn, đồ sộ vô cùng, đỉnh thiên lập địa, tản mát ra không cho người khinh nhờn uy nghiêm."),t("br"),t("br"),n._v("“Nhược Hi...”"),t("br"),t("br"),n._v("Vội vàng nhìn, Trương Huyền sững sờ: “Ngươi là ai?”"),t("br"),t("br"),n._v("Tế tự xuất hiện vị này Linh Thần, cũng không phải là Lạc Nhược Hi, mà là một cái khuôn mặt người xa lạ."),t("br"),t("br"),n._v("Bất kể khí tức vẫn là ánh mắt, đều có thể xác nhận, chưa bao giờ thấy qua."),t("br"),t("br"),n._v("“Linh Thần đã trở lại thuộc về mình địa phương, ta chỉ là nàng tôi tớ mà thôi!”"),t("br"),t("br"),n._v("Thân ảnh xoay đầu lại, nhìn về phía Trương Huyền: “Ngươi hẳn là Trương Huyền đi!”"),t("br"),t("br"),n._v("Trương Huyền nắm đấm căng thẳng."),t("br"),t("br"),n._v("Không cần hắn nói chuyện, trên không thân ảnh nhìn lại, vô hỉ vô bi: “Linh Thần trước khi đi, giao cho ta, nếu như cần trợ giúp gì, có thể ra tay... Ta mặc dù không muốn, vẫn là đáp ứng! Bất quá, vẫn là muốn nhắc nhở ngươi một câu, Linh Thần địa vị kính trọng, tuyệt không phải ngươi một cái phàm phu tục tử có thể đi nghĩ tới, khuyên ngươi... Vẫn là bỏ ý niệm này đi đi, nếu không, hại người hại mình!”"),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Trong tiếng nói, mang theo nồng đậm cảm giác áp bách, Trương Huyền mặc dù nắm giữ tích huyết trùng sinh cấp bậc sức chiến đấu, vẫn như cũ cảm thấy toàn thân huyết dịch giống như là bị giam cầm, một chút phản kháng lực lượng đều không có."),t("br"),t("br"),n._v("Đối phương chỉ là cái hư ảnh, liền so trước đó thần Linh Hoàng triệu hoán đến “Thần Linh” phải mạnh mẽ hơn nhiều!"),t("br"),t("br"),n._v("“Chuyện của ta... Còn không nhọc quan tâm! Ngươi chỉ nói cho ta, Linh Thần đến cùng là ai, ở nơi nào là được...”"),t("br"),t("br"),n._v("“Linh Thần tự nhiên ở tại Linh Thần cung! Tốt, nói đến thế thôi, nếu như ngươi có thể phá vỡ không gian trói buộc, cùng Khổng sư đồng dạng, cùng ta gặp mặt, ta tự sẽ nói cho ngươi, tung tích của nàng, làm không được, cũng đừng trông cậy vào...”"),t("br"),t("br"),n._v("Hừ một tiếng, thân ảnh vung tay áo một cái, ánh sáng càng lúc càng mờ nhạt."),t("br"),t("br"),n._v("“Chậm đã!” Trương Huyền ngẩng đầu."),t("br"),t("br"),n._v("Thân ảnh dừng lại một chút."),t("br"),t("br"),n._v("“Linh Thần có phải hay không dặn dò ngươi, cần trợ giúp gì, ngươi có thể ra tay?” Trương Huyền nói."),t("br"),t("br"),n._v("Thân ảnh nhíu nhíu mày, bất quá vẫn là nhẹ gật đầu: “Tốt! Bất quá, ta sẽ chỉ ra tay một lần, ngươi cần nghĩ kĩ yêu cầu, đừng lãng phí cơ hội.”"),t("br"),t("br"),n._v("“Ta nghĩ tốt!” Khoát tay áo, Trương Huyền một chỉ: “Vị này Ngột Thần, là Linh Thần đồng tử, bản thân bị trọng thương, bất cứ lúc nào cũng sẽ tử vong, còn mời xuất thủ cứu giúp...”"),t("br"),t("br"),n._v("“Thiếu gia...”"),t("br"),t("br"),n._v("Thần Dung Hoàng con mắt đỏ lên."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1854"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1856"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);